import React, { useState, useEffect } from 'react';
import {
    Users,
    Package,
    FileText,
    CheckCircle,
    XCircle,
    Clock,
    Plus,
    Edit,
    Trash2,
    Eye,
    Filter,
    Download,
    BarChart3,
    TrendingUp,
    AlertTriangle,
    Settings,
    Moon,
    Sun,
    Bell,
    Search,
    RefreshCw,
    MapPin,
    Calendar,
    DollarSign,
    Truck,
    UserCheck,
    Building,
    Wheat,
    Home,
    Phone,
    Mail,
    Shield,
    Activity,
    PieChart,
    Target,
    Award,
    Zap
} from 'lucide-react';

const RegionDashboard = () => {
        // State management
        const [darkMode, setDarkMode] = useState(false);
        const [activeTab, setActiveTab] = useState('overview');
        const [loading, setLoading] = useState(false);
        const [error, setError] = useState('');
        const [success, setSuccess] = useState('');

        // Data states
        const [admins, setAdmins] = useState([]);
        const [farmers, setFarmers] = useState([]);
        const [products, setProducts] = useState([]);
        const [requests, setRequests] = useState([]);
        const [reports, setReports] = useState([]);
        const [deliveries, setDeliveries] = useState([]);
        const [statistics, setStatistics] = useState({});
        const [myReports, setMyReports] = useState([]);
        const [myProducts, setMyProducts] = useState([]);
        const [otherProducts, setOtherProducts] = useState([]);

        // Filter states
        const [requestFilter, setRequestFilter] = useState('all');
        const [reportFilter, setReportFilter] = useState('all');
        const [searchTerm, setSearchTerm] = useState('');
        const [farmerFilter, setFarmerFilter] = useState('all');
        const [productFilter, setProductFilter] = useState('all');
        const [deliveryFilter, setDeliveryFilter] = useState('all');

        // Modal states
        const [showAdminModal, setShowAdminModal] = useState(false);
        const [showProductModal, setShowProductModal] = useState(false);
        const [showReportModal, setShowReportModal] = useState(false);
        const [showFarmerModal, setShowFarmerModal] = useState(false);
        const [showRequestModal, setShowRequestModal] = useState(false);
        const [showDeliveryModal, setShowDeliveryModal] = useState(false);
        const [selectedItem, setSelectedItem] = useState(null);
        const [modalMode, setModalMode] = useState('create'); // create, edit, view

        // Form states
        const [adminForm, setAdminForm] = useState({
            full_name: '',
            phone_number: '',
            password: '',
            role: 'Zone',
            region_name: '',
            zone_name: '',
            woreda_name: '',
            kebele_name: ''
        });

        const [productForm, setProductForm] = useState({
            name: '',
            category: '',
            sub_category: '',
            price: '',
            amount: '',
            unit: '',
            description: '',
            manufacturer: '',
            expiry_date: ''
        });

        const [reportForm, setReportForm] = useState({
            reported_admin_id: '',
            report_type: 'Misconduct',
            title: '',
            description: '',
            evidence: '',
            priority: 'Medium'
        });

        const [farmerForm, setFarmerForm] = useState({
            full_name: '',
            phone_number: '',
            password: '',
            region_name: '',
            zone_name: '',
            woreda_name: '',
            kebele_name: '',
            land_size_hectares: '',
            crop_types: '',
            crops_season: '',
            land_type: '',
            farming_experience: '',
            irrigation_type: '',
            farming_method: '',
            primary_crops: '',
            secondary_crops: '',
            soil_type: '',
            has_livestock: false,
            livestock_types: '',
            annual_income: '',
            education_level: ''
        });

        const [requestStatusForm, setRequestStatusForm] = useState({
            status: 'Approved',
            feedback: ''
        });

        // API base URL
        const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

        // Utility function for API calls
        const apiCall = async(endpoint, options = {}) => {
            const token = localStorage.getItem('token');
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                    ...options.headers
                },
                ...options
            };

            const response = await fetch(`${API_BASE}${endpoint}`, config);
            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'API call failed');
            }

            return data;
        };

        // Data fetching functions
        const fetchAdmins = async() => {
            try {
                const data = await apiCall('/admin/admins');
                setAdmins(data);
            } catch (err) {
                setError('Failed to fetch admins: ' + err.message);
            }
        };

        const fetchFarmers = async() => {
            try {
                const data = await apiCall('/admin/farmers');
                setFarmers(data);
            } catch (err) {
                setError('Failed to fetch farmers: ' + err.message);
            }
        };

        const fetchProducts = async() => {
            try {
                const data = await apiCall('/admin/getproducts');
                setProducts(data);
            } catch (err) {
                setError('Failed to fetch products: ' + err.message);
            }
        };

        const fetchMyProducts = async() => {
            try {
                const data = await apiCall('/admin/myproducts');
                setMyProducts(data);
            } catch (err) {
                setError('Failed to fetch my products: ' + err.message);
            }
        };

        const fetchOtherProducts = async() => {
            try {
                const data = await apiCall('/admin/otherproducts');
                setOtherProducts(data);
            } catch (err) {
                setError('Failed to fetch other products: ' + err.message);
            }
        };

        const fetchRequests = async(status = 'all') => {
            try {
                const endpoint = status === 'all' ?
                    '/admin/requests/status' :
                    `/admin/requests/filter?status=${status}`;
                const data = await apiCall(endpoint);
                setRequests(data);
            } catch (err) {
                setError('Failed to fetch requests: ' + err.message);
            }
        };

        const fetchReports = async() => {
            try {
                const data = await apiCall('/admin/reports');
                setReports(data);
            } catch (err) {
                setError('Failed to fetch reports: ' + err.message);
            }
        };

        const fetchMyReports = async() => {
            try {
                const data = await apiCall('/admin/reports/my');
                setMyReports(data);
            } catch (err) {
                setError('Failed to fetch my reports: ' + err.message);
            }
        };

        const fetchStatistics = async() => {
            try {
                const data = await apiCall('/admin/reports/statistics');
                setStatistics(data);
            } catch (err) {
                setError('Failed to fetch statistics: ' + err.message);
            }
        };

        // CRUD operations
        const createAdmin = async() => {
            try {
                setLoading(true);
                await apiCall('/admin/register', {
                    method: 'POST',
                    body: JSON.stringify(adminForm)
                });
                setSuccess('Admin created successfully');
                setShowAdminModal(false);
                resetAdminForm();
                fetchAdmins();
            } catch (err) {
                setError('Failed to create admin: ' + err.message);
            } finally {
                setLoading(false);
            }
        };

        const updateAdmin = async() => {
            try {
                setLoading(true);
                await apiCall(`/admin/edit/${selectedItem.id}`, {
                    method: 'PUT',
                    body: JSON.stringify(adminForm)
                });
                setSuccess('Admin updated successfully');
                setShowAdminModal(false);
                resetAdminForm();
                fetchAdmins();
            } catch (err) {
                setError('Failed to update admin: ' + err.message);
            } finally {
                setLoading(false);
            }
        };

        const createFarmer = async() => {
            try {
                setLoading(true);
                await apiCall('/admin/createfarmer', {
                    method: 'POST',
                    body: JSON.stringify({...farmerForm, role: 'Farmer' })
                });
                setSuccess('Farmer created successfully');
                setShowFarmerModal(false);
                resetFarmerForm();
                fetchFarmers();
            } catch (err) {
                setError('Failed to create farmer: ' + err.message);
            } finally {
                setLoading(false);
            }
        };

        const updateFarmerProfile = async() => {
            try {
                setLoading(true);
                await apiCall(`/admin/farmers/${selectedItem.id}/profile`, {
                    method: 'PUT',
                    body: JSON.stringify(farmerForm)
                });
                setSuccess('Farmer profile updated successfully');
                setShowFarmerModal(false);
                resetFarmerForm();
                fetchFarmers();
            } catch (err) {
                setError('Failed to update farmer profile: ' + err.message);
            } finally {
                setLoading(false);
            }
        };

        const createProduct = async() => {
            try {
                setLoading(true);
                await apiCall('/admin/addproduct', {
                    method: 'POST',
                    body: JSON.stringify(productForm)
                });
                setSuccess('Product created successfully');
                setShowProductModal(false);
                resetProductForm();
                fetchProducts();
                fetchMyProducts();
            } catch (err) {
                setError('Failed to create product: ' + err.message);
            } finally {
                setLoading(false);
            }
        };

        const updateProduct = async() => {
            try {
                setLoading(true);
                await apiCall(`/admin/editproduct/${selectedItem.id}`, {
                    method: 'PUT',
                    body: JSON.stringify(productForm)
                });
                setSuccess('Product updated successfully');
                setShowProductModal(false);
                resetProductForm();
                fetchProducts();
                fetchMyProducts();
            } catch (err) {
                setError('Failed to update product: ' + err.message);
            } finally {
                setLoading(false);
            }
        };

        const deleteProduct = async(id) => {
            if (!window.confirm('Are you sure you want to delete this product?')) return;

            try {
                setLoading(true);
                await apiCall(`/admin/deleteproduct/${id}`, {
                    method: 'DELETE'
                });
                setSuccess('Product deleted successfully');
                fetchProducts();
                fetchMyProducts();
            } catch (err) {
                setError('Failed to delete product: ' + err.message);
            } finally {
                setLoading(false);
            }
        };

        const updateRequestStatus = async(requestId, status, feedback = '') => {
            try {
                setLoading(true);
                await apiCall(`/admin/requests/${requestId}/status`, {
                    method: 'PUT',
                    body: JSON.stringify({ status, feedback })
                });
                setSuccess(`Request ${status.toLowerCase()} successfully`);
                fetchRequests(requestFilter);
