import React from 'react';
import { useForm } from 'react-hook-form';

const LoginForm = ({ onLogin }) => {
    const { register, handleSubmit } = useForm();
    const onSubmit = (data) => {
        // This is a simulated login for demonstration.
        // In a real app, you would verify credentials and get the user's actual role/location.
        const mockUserCredentials = {
            role: data.role,
            locationLevel: 'zone',
            locationId: 'zone-123'
        };
        onLogin(mockUserCredentials);
    };

    return ( <
        div className = "flex items-center justify-center h-screen bg-gray-100" >
        <
        div className = "w-full max-w-md p-8 space-y-6 bg-white rounded-xl shadow-lg" >
        <
        h2 className = "text-2xl font-bold text-center text-gray-900" > Sign in to your account < /h2> <
        form onSubmit = { handleSubmit(onSubmit) }
        className = "space-y-4" >
        <
        div >
        <
        label htmlFor = "role"
        className = "block text-sm font-medium text-gray-700" > Role < /label> <
        select id = "role" {...register("role", { required: true }) }
        className = "mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" >
        <
        option value = "" > Select a role < /option> <
        option value = "Regional Admin" > Regional Admin < /option> <
        option value = "Zone Admin" > Zone Admin < /option> <
        option value = "Woreda Admin" > Woreda Admin < /option> <
        option value = "Head of Department" > Head of Department < /option> <
        option value = "Data Encoder" > Data Encoder < /option> <
        /select> <
        /div> <
        div >
        <
        label htmlFor = "password"
        className = "block text-sm font-medium text-gray-700" > Password < /label> <
        input id = "password"
        type = "password" {...register("password") }
        className = "mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" /
        >
        <
        /div> <
        button type = "submit"
        className = "w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500" >
        Sign in
        <
        /button> <
        /form> <
        /div> <
        /div>
    );
};

export default LoginForm;
