import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import './index.css';

// ----------------------
// Mock API Functions
// ----------------------
// These functions simulate API calls to a backend server connected to a MySQL database.
// You will need to replace these with actual API calls to your server endpoints.

const mockUsers = [
    { id: 'user1', name: 'John Doe', email: 'john.doe@example.com' },
    { id: 'user2', name: 'Jane Smith', email: 'jane.smith@example.com' },
];

const mockProducts = [
    { id: 'prod1', name: 'Chemical Fertilizer', category: 'Chemical Input Supply' },
    { id: 'prod2', name: 'Pesticide', category: 'Chemical Input Supply' },
    { id: 'prod3', name: 'Seeds', category: 'Biological Input Supply' },
];

const fetchMockUsers = async() => {
    // Simulate a network delay
    await new Promise(resolve => setTimeout(resolve, 500));
    return mockUsers;
};

const fetchMockProducts = async() => {
    // Simulate a network delay
    await new Promise(resolve => setTimeout(resolve, 500));
    return mockProducts;
};

const deleteMockItem = async(type, id) => {
    // Simulate a network delay
    await new Promise(resolve => setTimeout(resolve, 500));
    console.log(`Simulating deletion of ${type} with id: ${id}`);
    // Simulate success
    return { success: true };
};

const registerMockUser = async(data) => {
    // Simulate a network delay
    await new Promise(resolve => setTimeout(resolve, 500));
    console.log('Simulating new user registration:', data);
    // Simulate success
    return { success: true };
};


// ----------------------
// Sidebar Component
// ----------------------
const Sidebar = ({ isCompressed, onToggleSidebar, onNavigate, onToggleTheme }) => {
        const sidebarItems = [
            { id: 'dashboard', label: 'Dashboard', icon: 'M12 2L2 7l10 5 10-5-10-5zm0 14.5v-9L2 12l10 5.5z' },
            { id: 'master-data', label: 'Master Data', icon: 'M10 20v-6h4v6h5v-8h3L12 3 2 12h3v8z' },
            { id: 'reports', label: 'Reports', icon: 'M14 2H6a2 2 0 00-2 2v16a2 2 0 002 2h12a2 2 0 002-2V8l-6-6zM6 20V4h7v4h4v12H6z' },
            { id: 'registration', label: 'Register User', icon: 'M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z' }
        ];

        return ( <
                aside className = { `h-screen bg-gray-100 dark:bg-gray-800 text-gray-800 dark:text-white transition-all duration-300 ease-in-out ${isCompressed ? 'w-20' : 'w-64'} p-4 flex flex-col rounded-xl shadow-lg` } >
                <
                div className = "flex items-center justify-between mb-8" > {!isCompressed && < h1 className = "text-2xl font-bold" > Menu < /h1>} <
                    button onClick = { onToggleSidebar }
                    className = "p-2 rounded-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors" >
                    <
                    svg className = "w-6 h-6"
                    fill = "currentColor"
                    viewBox = "0 0 24 24"
                    xmlns = "http://www.w3.org/2000/svg" >
                    <
                    path d = { isCompressed ? "M4 6h16M4 12h16M4 18h16" : "M4 6h16M4 12h16M4 18h16" }
                    /> <
                    /svg> <
                    /button> <
                    /div>

                    <
                    nav className = "flex-1" >
                    <
                    ul > {
                        sidebarItems.map(item => ( <
                                li key = { item.id }
                                className = "mb-2" >
                                <
                                button onClick = {
                                    () => onNavigate(item.id) }
                                className = "flex items-center w-full p-3 rounded-lg text-left transition-colors hover:bg-gray-200 dark:hover:bg-gray-700" >
                                <
                                svg className = "w-6 h-6 mr-3"
                                fill = "currentColor"
                                viewBox = "0 0 24 24"
                                xmlns = "http://www.w3.org/2000/svg" >
                                <
                                path d = { item.icon }
                                /> <
                                /svg> {
                                    !isCompressed && < span className = "font-medium" > { item.label } < /span>} <
                                        /button> <
                                        /li>
                                ))
                        } <
                        /ul> <
                        /nav>

                        <
                        div className = "mt-auto" >
                        <
                        button
                        onClick = { onToggleTheme }
                        className = "w-full flex items-center p-3 rounded-lg text-left transition-colors hover:bg-gray-200 dark:hover:bg-gray-700" >
                        <
                        svg className = "w-6 h-6 mr-3"
                        fill = "currentColor"
                        viewBox = "0 0 24 24"
                        xmlns = "http://www.w3.org/2000/svg" >
                        <
                        path d = "M12 2a10 10 0 100 20A10 10 0 0012 2zm-1 16a7.995 7.995 0 01-6.72-3.78A7.995 7.995 0 0111 4.72V18z" / >
                        <
                        /svg> {
                            !isCompressed && < span > Dark Mode < /span>} <
                                /button> <
                                /div> <
                                /aside>
                        );
                    };

                    // ----------------------
                    // Dashboard Components (Distinct Layouts)
                    // ----------------------

                    // Main Dashboard
                    const MainDashboard = () => {
                        return ( <
                            div className = "p-8 bg-gradient-to-br from-blue-500 to-blue-700 dark:from-gray-700 dark:to-gray-900 text-white rounded-xl shadow-2xl" >
                            <
                            h2 className = "text-4xl font-extrabold mb-4 animate-fade-in-down" > Main Dashboard < /h2> <
                            p className = "text-blue-100 dark:text-gray-300 mb-8" > A quick overview of key metrics. < /p> <
                            div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-6" >
                            <
                            div className = "bg-white bg-opacity-20 backdrop-filter backdrop-blur-lg p-6 rounded-xl shadow-lg transform transition-transform duration-300 hover:scale-105" >
                            <
                            h3 className = "text-xl font-semibold" > Total Users < /h3> <
                            p className = "text-3xl font-bold mt-2" > 1, 250 < /p> <
                            /div> <
                            div className = "bg-white bg-opacity-20 backdrop-filter backdrop-blur-lg p-6 rounded-xl shadow-lg transform transition-transform duration-300 hover:scale-105" >
                            <
                            h3 className = "text-xl font-semibold" > Active Products < /h3> <
                            p className = "text-3xl font-bold mt-2" > 250 < /p> <
                            /div> <
                            div className = "bg-white bg-opacity-20 backdrop-filter backdrop-blur-lg p-6 rounded-xl shadow-lg transform transition-transform duration-300 hover:scale-105" >
                            <
                            h3 className = "text-xl font-semibold" > Pending Requests < /h3> <
                            p className = "text-3xl font-bold mt-2" > 15 < /p> <
                            /div> <
                            /div> <
                            /div>
                        );
                    };

                    // Master Data Dashboard
                    const MasterData = () => {
                        const [products, setProducts] = useState([]);
                        const [users, setUsers] = useState([]);
                        const [showDeleteModal, setShowDeleteModal] = useState(false);
                        const [itemToDelete, setItemToDelete] = useState(null);
                        const [itemType, setItemType] = useState('');

                        useEffect(() => {
                            const fetchMasterData = async() => {
                                const productsData = await fetchMockProducts();
                                setProducts(productsData);
                                const usersData = await fetchMockUsers();
                                setUsers(usersData);
                            };
                            fetchMasterData();
                        }, []);

                        const handleDeleteClick = (item, type) => {
                            setItemToDelete(item);
                            setItemType(type);
                            setShowDeleteModal(true);
                        };

                        const confirmDeletion = async() => {
                            if (!itemToDelete) return;
                            await deleteMockItem(itemType, itemToDelete.id);
                            if (itemType === 'product') {
                                setProducts(products.filter(p => p.id !== itemToDelete.id));
                            } else {
                                setUsers(users.filter(u => u.id !== itemToDelete.id));
                            }
                            setShowDeleteModal(false);
                            setItemToDelete(null);
                            setItemType('');
                        };

                        return ( <
                            div className = "p-8 bg-white dark:bg-gray-800 text-gray-900 dark:text-white rounded-xl shadow-lg" >
                            <
                            h2 className = "text-3xl font-bold mb-6 text-green-600 dark:text-green-400" > Master Data Management < /h2> <
                            p className = "text-gray-600 dark:text-gray-400 mb-8" > Manage users and products in the system.Use caution when deleting items. < /p> <
                            div className = "grid grid-cols-1 lg:grid-cols-2 gap-8" > { /* Products Section */ } <
                            div className = "bg-gray-100 dark:bg-gray-700 p-6 rounded-xl shadow-inner" >
                            <
                            h3 className = "text-2xl font-semibold mb-4 text-green-700 dark:text-green-300" > Products < /h3> <
                            ul className = "space-y-4" > {
                                products.map(product => ( <
                                    li key = { product.id }
                                    className = "flex items-center justify-between p-4 bg-white dark:bg-gray-600 rounded-lg shadow-sm" >
                                    <
                                    span className = "font-medium" > { product.name } < /span> <
                                    button onClick = {
                                        () => handleDeleteClick(product, 'product') }
                                    className = "bg-red-500 text-white p-2 rounded-full hover:bg-red-600 transition-colors" >
                                    <
                                    svg className = "w-5 h-5"
                                    fill = "currentColor"
                                    viewBox = "0 0 24 24"
                                    xmlns = "http://www.w3.org/2000/svg" >
                                    <
                                    path d = "M6 19c0 1.1.9 2 2 2h8c1.1 0 2-.9 2-2V7H6v12zM19 4h-3.5l-1-1h-5l-1 1H5v2h14V4z" / >
                                    <
                                    /svg> <
                                    /button> <
                                    /li>
                                ))
                            } <
                            /ul> <
                            /div>

                            { /* Users Section */ } <
                            div className = "bg-gray-100 dark:bg-gray-700 p-6 rounded-xl shadow-inner" >
                            <
                            h3 className = "text-2xl font-semibold mb-4 text-green-700 dark:text-green-300" > Users < /h3> <
                            ul className = "space-y-4" > {
                                users.map(user => ( <
                                    li key = { user.id }
                                    className = "flex items-center justify-between p-4 bg-white dark:bg-gray-600 rounded-lg shadow-sm" >
                                    <
                                    span className = "font-medium" > { user.name } < /span> <
                                    button onClick = {
                                        () => handleDeleteClick(user, 'user') }
                                    className = "bg-red-500 text-white p-2 rounded-full hover:bg-red-600 transition-colors" >
                                    <
                                    svg className = "w-5 h-5"
                                    fill = "currentColor"
                                    viewBox = "0 0 24 24"
                                    xmlns = "http://www.w3.org/2000/svg" >
                                    <
                                    path d = "M6 19c0 1.1.9 2 2 2h8c1.1 0 2-.9 2-2V7H6v12zM19 4h-3.5l-1-1h-5l-1 1H5v2h14V4z" / >
                                    <
                                    /svg> <
                                    /button> <
                                    /li>
                                ))
                            } <
                            /ul> <
                            /div> <
                            /div>

                            { /* Confirmation Modal */ } {
                                showDeleteModal && ( <
                                    ConfirmationModal message = { `Are you sure you want to delete this ${itemType}? This action cannot be undone.` }
                                    onConfirm = { confirmDeletion }
                                    onCancel = {
                                        () => setShowDeleteModal(false) }
                                    />
                                )
                            } <
                            /div>
                        );
                    };

                    // Reports Dashboard
                    const Reports = () => {
                        return ( <
                            div className = "p-8 bg-white dark:bg-gray-800 text-gray-900 dark:text-white rounded-xl shadow-lg" >
                            <
                            h2 className = "text-3xl font-bold mb-4 text-purple-600 dark:text-purple-400" > Reports and Analytics < /h2> <
                            p className = "text-gray-600 dark:text-gray-400 mb-8" > Dive deep into your data with these insightful reports. < /p> <
                            div className = "grid grid-cols-1 md:grid-cols-2 gap-6 mt-6" >
                            <
                            div className = "bg-gray-100 dark:bg-gray-700 p-6 rounded-xl shadow-md" >
                            <
                            h3 className = "text-xl font-semibold text-purple-700 dark:text-purple-300" > Product Usage Report < /h3> <
                            p className = "text-gray-600 dark:text-gray-400 mt-2" > See which products are most popular over time. < /p> <
                            /div> <
                            div className = "bg-gray-100 dark:bg-gray-700 p-6 rounded-xl shadow-md" >
                            <
                            h3 className = "text-xl font-semibold text-purple-700 dark:text-purple-300" > User Activity Log < /h3> <
                            p className = "text-gray-600 dark:text-gray-400 mt-2" > Track user logins, actions, and changes. < /p> <
                            /div> <
                            /div> <
                            /div>
                        );
                    };

                    // Registration Dashboard
                    const RegistrationForm = () => {
                            const { register, handleSubmit, formState: { errors }, watch } = useForm();
                            const password = watch("password", "");

                            const onSubmit = async(data) => {
                                await registerMockUser(data);
                                alert('User registered successfully!');
                            };

                            return ( <
                                    div className = "p-8 max-w-lg mx-auto bg-white dark:bg-gray-800 rounded-xl shadow-lg mt-8" >
                                    <
                                    h2 className = "text-3xl font-bold mb-6 text-center text-yellow-600 dark:text-yellow-400" > New User Registration < /h2> <
                                    p className = "text-center text-gray-600 dark:text-gray-400 mb-8" > Create new user accounts
                                    for your team. < /p> <
                                    form onSubmit = { handleSubmit(onSubmit) }
                                    className = "space-y-6" >
                                    <
                                    div >
                                    <
                                    label className = "block text-gray-700 dark:text-gray-300 font-bold mb-2" > Username < /label> <
                                    input type = "text"
                                    className = "w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500 bg-gray-100 dark:bg-gray-700 dark:text-white" {...register("username", { required: "Username is required" }) }
                                    /> {
                                        errors.username && < p className = "text-red-500 text-sm mt-1" > { errors.username.message } < /p>} <
                                            /div> <
                                            div >
                                            <
                                            label className = "block text-gray-700 dark:text-gray-300 font-bold mb-2" > Password < /label> <
                                            input
                                        type = "password"
                                        className = "w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500 bg-gray-100 dark:bg-gray-700 dark:text-white" {...register("password", {
                                                required: "Password is required",
                                                minLength: { value: 6, message: "Password must be at least 6 characters" }
                                            })
                                        }
                                        /> {
                                            errors.password && < p className = "text-red-500 text-sm mt-1" > { errors.password.message } < /p>} <
                                                /div> <
                                                div >
                                                <
                                                label className = "block text-gray-700 dark:text-gray-300 font-bold mb-2" > Confirm Password < /label> <
                                                input
                                            type = "password"
                                            className = "w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500 bg-gray-100 dark:bg-gray-700 dark:text-white" {...register("passwordConfirmation", {
                                                    required: "Please confirm your password",
                                                    validate: value => value === password || "The passwords do not match"
                                                })
                                            }
                                            /> {
                                                errors.passwordConfirmation && < p className = "text-red-500 text-sm mt-1" > { errors.passwordConfirmation.message } < /p>} <
                                                    /div> <
                                                    button
                                                type = "submit"
                                                className = "w-full bg-yellow-500 text-white font-bold py-2 px-4 rounded-lg hover:bg-yellow-600 transition-colors" >
                                                    Register <
                                                    /button> <
                                                    /form> <
                                                    /div>
                                            );
                                        };

                                        // ----------------------
                                        // Custom Modal
                                        // ----------------------
                                        const Modal = ({ show, onClose, title, message }) => {
                                            if (!show) return null;
                                            return ( <
                                                div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50" >
                                                <
                                                div className = "bg-white dark:bg-gray-800 p-8 rounded-xl shadow-2xl w-full max-w-md mx-4" >
                                                <
                                                div className = "flex justify-between items-center mb-4" >
                                                <
                                                h3 className = "text-xl font-bold dark:text-white" > { title } < /h3> <
                                                button onClick = { onClose }
                                                className = "text-gray-400 hover:text-gray-600 dark:hover:text-gray-300" >
                                                <
                                                svg className = "w-6 h-6"
                                                fill = "none"
                                                stroke = "currentColor"
                                                viewBox = "0 0 24 24"
                                                xmlns = "http://www.w3.org/2000/svg" >
                                                <
                                                path strokeLinecap = "round"
                                                strokeLinejoin = "round"
                                                strokeWidth = "2"
                                                d = "M6 18L18 6M6 6l12 12" > < /path> <
                                                /svg> <
                                                /button> <
                                                /div> <
                                                p className = "text-gray-700 dark:text-gray-300 mb-6" > { message } < /p> <
                                                div className = "flex justify-end" >
                                                <
                                                button onClick = { onClose }
                                                className = "px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors" >
                                                OK <
                                                /button> <
                                                /div> <
                                                /div> <
                                                /div>
                                            );
                                        };

                                        const ConfirmationModal = ({ message, onConfirm, onCancel }) => {
                                            return ( <
                                                div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50" >
                                                <
                                                div className = "bg-white dark:bg-gray-800 p-8 rounded-xl shadow-2xl w-full max-w-md mx-4" >
                                                <
                                                h3 className = "text-xl font-bold dark:text-white mb-4" > Confirm Deletion < /h3> <
                                                p className = "text-gray-700 dark:text-gray-300 mb-6" > { message } < /p> <
                                                div className = "flex justify-end space-x-4" >
                                                <
                                                button onClick = { onCancel }
                                                className = "px-4 py-2 bg-gray-300 dark:bg-gray-600 text-gray-800 dark:text-white rounded-lg hover:bg-gray-400 transition-colors" >
                                                Cancel <
                                                /button> <
                                                button onClick = { onConfirm }
                                                className = "px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors" >
                                                Confirm <
                                                /button> <
                                                /div> <
                                                /div> <
                                                /div>
                                            );
                                        };

                                        // ----------------------
                                        // Main Application Component
                                        // ----------------------
                                        export default function App() {
                                            const [isSidebarCompressed, setIsSidebarCompressed] = useState(false);
                                            const [isDarkMode, setIsDarkMode] = useState(false);
                                            const [currentPage, setCurrentPage] = useState('dashboard');
                                            const [showModal, setShowModal] = useState(false);
                                            const [modalContent, setModalContent] = useState('');

                                            const toggleSidebar = () => {
                                                setIsSidebarCompressed(!isSidebarCompressed);
                                            };

                                            const toggleTheme = () => {
                                                setIsDarkMode(!isDarkMode);
                                            };

                                            const renderPage = () => {
                                                switch (currentPage) {
                                                    case 'dashboard':
                                                        return <MainDashboard / > ;
                                                    case 'master-data':
                                                        return <MasterData / > ;
                                                    case 'reports':
                                                        return <Reports / > ;
                                                    case 'registration':
                                                        return <RegistrationForm / > ;
                                                    default:
                                                        return <MainDashboard / > ;
                                                }
                                            };

                                            return ( <
                                                div className = { isDarkMode ? 'dark' : '' } >
                                                <
                                                div className = "flex h-screen w-screen bg-gray-200 dark:bg-gray-900 text-gray-900 dark:text-white transition-colors duration-300" >
                                                <
                                                Sidebar isCompressed = { isSidebarCompressed }
                                                onToggleSidebar = { toggleSidebar }
                                                onNavigate = { setCurrentPage }
                                                onToggleTheme = { toggleTheme }
                                                /> <
                                                main className = { `flex-1 p-8 overflow-auto transition-all duration-300 ease-in-out` } > { renderPage() } <
                                                /main> <
                                                Modal show = { showModal }
                                                onClose = {
                                                    () => setShowModal(false) }
                                                title = "Notification"
                                                message = { modalContent }
                                                /> <
                                                /div> <
                                                /div>
                                            );
                                        }
