import React, { useState, useEffect, useCallback } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, onAuthStateChanged, signInAnonymously } from 'firebase/auth';
import { getFirestore, doc, setDoc, collection, query, onSnapshot, serverTimestamp, addDoc, updateDoc } from 'firebase/firestore';
import { useForm } from 'react-hook-form';
import { FaSignOutAlt, FaUser, FaClipboardList, FaFileSignature, FaSeedling, FaTruck } from 'react-icons/fa';

// The following global variables are provided by the Canvas environment.
// We must always use them for Firebase setup.
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : '';

// ----------------------
// Firebase Initialization
// ----------------------
// Initialize Firebase app and services
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

// Mock User Data for demonstration
const users = [
    { email: 'admin@example.com', password: 'password', role: 'admin' },
    { email: 'manager@example.com', password: 'password', role: 'manager' },
    { email: 'farmer1@example.com', password: 'password', role: 'farmer' },
    { email: 'farmer2@example.com', password: 'password', role: 'farmer' },
];

// ----------------------
// Main App Component
// ----------------------
export default function App() {
    const [currentUser, setCurrentUser] = useState(null);
    const [authReady, setAuthReady] = useState(false);
    const [requests, setRequests] = useState([]);
    const [locations, setLocations] = useState({ regions: [], zones: [], warehouses: [] });
    const [showModal, setShowModal] = useState(false);
    const [modalContent, setModalContent] = useState('');

    const userId = currentUser ? currentUser.uid : null;
    const userRole = currentUser ? currentUser.role : null;

    const Modal = ({ show, onClose, title, message }) => {
        if (!show) return null;

        return ( <
            div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex justify-center items-center z-50" >
            <
            div className = "bg-white p-6 rounded-lg shadow-xl max-w-sm w-full" >
            <
            h3 className = "text-xl font-semibold text-gray-900 mb-4" > { title } < /h3> <
            p className = "text-gray-700 mb-6" > { message } < /p> <
            div className = "flex justify-end" >
            <
            button onClick = { onClose }
            className = "px-4 py-2 bg-indigo-600 text-white font-medium text-sm rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2" >
            Close <
            /button> <
            /div> <
            /div> <
            /div>
        );
    };

    // Firebase Auth and Data Listeners
    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, async(user) => {
            if (user) {
                // If a user is authenticated via Canvas, we'll try to find their mock role
                const mockUser = users.find(u => u.email === user.email);
                if (mockUser) {
                    setCurrentUser({...user, role: mockUser.role });
                } else {
                    // Default to 'farmer' for any other authenticated user
                    setCurrentUser({...user, role: 'farmer' });
                }
            } else {
                setCurrentUser(null);
            }
            setAuthReady(true);
        });

        const initializeAuth = async() => {
            try {
                if (initialAuthToken) {
                    await signInWithCustomToken(auth, initialAuthToken);
                } else {
                    await signInAnonymously(auth);
                }
            } catch (error) {
                console.error("Firebase Auth Error:", error);
            }
        };

        if (!auth.currentUser) {
            initializeAuth();
        }

        return () => unsubscribe();
    }, [auth, initialAuthToken]);


    // Data Subscription Hook
    useEffect(() => {
        if (!authReady || !userId) return;

        const privateRequestsPath = `artifacts/${appId}/users/${userId}/requests`;
        const privateLocationsPath = `artifacts/${appId}/users/${userId}/locations`;

        const unsubscribeRequests = onSnapshot(collection(db, privateRequestsPath), (snapshot) => {
            const fetchedRequests = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
            setRequests(fetchedRequests);
        }, (error) => {
            console.error("Error fetching requests:", error);
        });

        const unsubscribeLocations = onSnapshot(collection(db, privateLocationsPath), (snapshot) => {
            const fetchedLocations = { regions: [], zones: [], warehouses: [] };
            snapshot.docs.forEach(doc => {
                const data = doc.data();
                if (data.type === 'region') fetchedLocations.regions.push(data.name);
                if (data.type === 'zone') fetchedLocations.zones.push(data.name);
                if (data.type === 'warehouse') fetchedLocations.warehouses.push(data.name);
            });
            setLocations(fetchedLocations);
        }, (error) => {
            console.error("Error fetching locations:", error);
        });

        return () => {
            unsubscribeRequests();
            unsubscribeLocations();
        };
    }, [authReady, userId]);

    const handleLogin = (email, password) => {
        const user = users.find(u => u.email === email && u.password === password);
        if (user) {
            // Mock sign-in. In a real app, this would be a Firebase call.
            setCurrentUser({ uid: `mock-${user.email}`, email: user.email, role: user.role });
            setShowModal(true);
            setModalContent(`Welcome, ${user.role}! You have successfully logged in.`);
        } else {
            setShowModal(true);
            setModalContent('Invalid credentials. Please try again.');
        }
    };

    const handleLogout = async() => {
        try {
            // In a real app, you would sign out from Firebase.
            // For our mock users, we just clear the state.
            setCurrentUser(null);
            setShowModal(true);
            setModalContent('You have been logged out.');
        } catch (error) {
            console.error('Error during logout:', error);
        }
    };

    const Header = ({ userRole, onLogout }) => ( <
            header className = "bg-gray-800 text-white p-4 shadow-md flex justify-between items-center" >
            <
            div className = "flex items-center space-x-4" >
            <
            h1 className = "text-xl font-bold" > Warehouse Portal < /h1> <
            span className = "bg-indigo-600 text-xs font-semibold px-2 py-1 rounded-full" > { userRole ? userRole.toUpperCase() : 'GUEST' } < /span> <
            /div> <
            div className = "flex items-center space-x-4" > {
                userId && < span className = "text-sm" > User: { currentUser.email } < /span>} {
                    userRole && ( <
                        button onClick = { onLogout }
                        className = "text-gray-300 hover:text-white transition duration-150 ease-in-out" >
                        <
                        FaSignOutAlt className = "inline-block mr-2" / >
                        Logout <
                        /button>
                    )
                } <
                /div> <
                /header>
            );

            const NewRequestForm = () => {
                    const { register, handleSubmit, reset, watch, setValue, formState: { errors } } = useForm();
                    const [isSubmitting, setIsSubmitting] = useState(false);
                    const [isGeminiLoading, setIsGeminiLoading] = useState(false);

                    const onSubmit = async(data) => {
                        setIsSubmitting(true);
                        try {
                            if (!userId) {
                                setShowModal(true);
                                setModalContent('Please log in to submit a request.');
                                setIsSubmitting(false);
                                return;
                            }
                            const privateRequestsPath = `artifacts/${appId}/users/${userId}/requests`;
                            await addDoc(collection(db, privateRequestsPath), {
                                ...data,
                                status: 'pending',
                                createdAt: serverTimestamp(),
                                requesterId: userId,
                            });
                            setShowModal(true);
                            setModalContent('Request submitted successfully!');
                            reset();
                        } catch (e) {
                            console.error("Error adding document: ", e);
                            setShowModal(true);
                            setModalContent('Failed to submit request. Please try again.');
                        } finally {
                            setIsSubmitting(false);
                        }
                    };

                    const generateDescription = async() => {
                        const subject = watch('subject');
                        const currentDescription = watch('description');
                        if (!subject) {
                            setShowModal(true);
                            setModalContent('Please enter a subject before using this feature.');
                            return;
                        }

                        setIsGeminiLoading(true);
                        try {
                            // Construct the prompt for the Gemini API
                            const prompt = `Elaborate on the following warehouse request subject and description to create a more detailed and professional description. Be specific and include details that would be helpful for an approver.
                Subject: ${subject}
                Description: ${currentDescription || ''}
                New Description:`;

                            const chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
                            const payload = { contents: chatHistory };
                            const apiKey = "";
                            const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

                            const response = await fetch(apiUrl, {
                                method: 'POST',
                                headers: { 'Content-Type': 'application/json' },
                                body: JSON.stringify(payload)
                            });
                            const result = await response.json();

                            if (result.candidates && result.candidates.length > 0 &&
                                result.candidates[0].content && result.candidates[0].content.parts &&
                                result.candidates[0].content.parts.length > 0) {
                                const text = result.candidates[0].content.parts[0].text;
                                setValue('description', text);
                            } else {
                                console.error("Gemini API response error:", result);
                                setShowModal(true);
                                setModalContent('Could not generate a description. Please try again.');
                            }
                        } catch (error) {
                            console.error("Error calling Gemini API:", error);
                            setShowModal(true);
                            setModalContent('An error occurred while communicating with the AI.');
                        } finally {
                            setIsGeminiLoading(false);
                        }
                    };

                    return ( <
                            div className = "max-w-2xl mx-auto p-8 bg-gray-800 rounded-xl shadow-lg my-8 text-white" >
                            <
                            h2 className = "text-2xl font-bold mb-6 text-center" > New Request < /h2> <
                            form onSubmit = { handleSubmit(onSubmit) }
                            className = "space-y-6" >
                            <
                            div >
                            <
                            label htmlFor = "subject"
                            className = "block text-sm font-medium text-gray-300" >
                            Subject <
                            /label> <
                            input id = "subject" {...register('subject', { required: 'Subject is required' }) }
                            className = "mt-1 block w-full rounded-md bg-gray-700 border-transparent text-white focus:border-indigo-500 focus:ring-indigo-500" /
                            > {
                                errors.subject && < p className = "mt-2 text-sm text-red-500" > { errors.subject.message } < /p>} <
                                /div> <
                                div >
                                <
                                div className = "flex justify-between items-center mb-1" >
                                <
                                label htmlFor = "description"
                                className = "block text-sm font-medium text-gray-300" >
                                Description <
                                /label> <
                                button
                                type = "button"
                                onClick = { generateDescription }
                                disabled = { isGeminiLoading }
                                className = "px-3 py-1 bg-indigo-600 text-white font-medium text-xs rounded-lg hover:bg-indigo-700 disabled:bg-indigo-400 disabled:cursor-not-allowed transition duration-150 ease-in-out" >
                                { isGeminiLoading ? 'Generating...' : '✨ Elaborate on Description' } <
                                /button> <
                                /div> <
                                textarea
                                id = "description" {...register('description', { required: 'Description is required' }) }
                                rows = "4"
                                className = "mt-1 block w-full rounded-md bg-gray-700 border-transparent text-white focus:border-indigo-500 focus:ring-indigo-500" /
                                > {
                                    errors.description && < p className = "mt-2 text-sm text-red-500" > { errors.description.message } < /p>} <
                                    /div>

                                    <
                                    div >
                                    <
                                    label htmlFor = "region"
                                    className = "block text-sm font-medium text-gray-300" >
                                    Region <
                                    /label> <
                                    select
                                    id = "region" {...register('region', { required: 'Region is required' }) }
                                    className = "mt-1 block w-full rounded-md bg-gray-700 border-transparent text-white focus:border-indigo-500 focus:ring-indigo-500" >
                                    <
                                    option value = "" > Select Region < /option> {
                                        locations.regions.map(region => ( <
                                            option key = { region }
                                            value = { region } > { region } < /option>
                                        ))
                                    } <
                                    /select> {
                                        errors.region && < p className = "mt-2 text-sm text-red-500" > { errors.region.message } < /p>} <
                                            /div> <
                                            div >
                                            <
                                            label htmlFor = "zone"
                                        className = "block text-sm font-medium text-gray-300" >
                                            Zone <
                                            /label> <
                                            select
                                        id = "zone" {...register('zone', { required: 'Zone is required' }) }
                                        className = "mt-1 block w-full rounded-md bg-gray-700 border-transparent text-white focus:border-indigo-500 focus:ring-indigo-500" >
                                            <
                                            option value = "" > Select Zone < /option> {
                                                locations.zones.map(zone => ( <
                                                    option key = { zone }
                                                    value = { zone } > { zone } < /option>
                                                ))
                                            } <
                                            /select> {
                                                errors.zone && < p className = "mt-2 text-sm text-red-500" > { errors.zone.message } < /p>} <
                                                    /div> <
                                                    div >
                                                    <
                                                    label htmlFor = "warehouse"
                                                className = "block text-sm font-medium text-gray-300" >
                                                    Warehouse <
                                                    /label> <
                                                    select
                                                id = "warehouse" {...register('warehouse', { required: 'Warehouse is required' }) }
                                                className = "mt-1 block w-full rounded-md bg-gray-700 border-transparent text-white focus:border-indigo-500 focus:ring-indigo-500" >
                                                    <
                                                    option value = "" > Select Warehouse < /option> {
                                                        locations.warehouses.map(warehouse => ( <
                                                            option key = { warehouse }
                                                            value = { warehouse } > { warehouse } < /option>
                                                        ))
                                                    } <
                                                    /select> {
                                                        errors.warehouse && < p className = "mt-2 text-sm text-red-500" > { errors.warehouse.message } < /p>} <
                                                            /div>

                                                        <
                                                        div >
                                                            <
                                                            button
                                                        type = "submit"
                                                        disabled = { isSubmitting }
                                                        className = "w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:bg-indigo-400" >
                                                            { isSubmitting ? 'Submitting...' : 'Submit Request' } <
                                                            /button> <
                                                            /div> <
                                                            /form> <
                                                            /div>
                                                    );
                                            };

                                        const AdminManagerDashboard = () => {
                                            const [selectedRequest, setSelectedRequest] = useState(null);
                                            const [isProcessing, setIsProcessing] = useState(false);

                                            const handleStatusChange = async(status) => {
                                                if (!selectedRequest) return;
                                                setIsProcessing(true);
                                                try {
                                                    if (!userId) {
                                                        setShowModal(true);
                                                        setModalContent('Authentication failed. Please log in again.');
                                                        setIsProcessing(false);
                                                        return;
                                                    }
                                                    const docRef = doc(db, `artifacts/${appId}/users/${userId}/requests`, selectedRequest.id);
                                                    await updateDoc(docRef, { status, approvedBy: userId, approvedAt: serverTimestamp() });
                                                    setShowModal(true);
                                                    setModalContent(`Request '${selectedRequest.subject}' has been ${status}.`);
                                                    setSelectedRequest(null);
                                                } catch (e) {
                                                    console.error(`Error updating request status:`, e);
                                                    setShowModal(true);
                                                    setModalContent('Failed to update request status. Please try again.');
                                                } finally {
                                                    setIsProcessing(false);
                                                }
                                            };

                                            const SummaryCard = ({ title, count, color, icon }) => ( <
                                                div className = { `flex flex-col items-center justify-center p-6 rounded-xl shadow-lg ${color} text-white` } >
                                                <
                                                span className = "text-3xl mb-2" > { icon } < /span> <
                                                h3 className = "text-2xl font-bold" > { count } < /h3> <
                                                p className = "mt-2 text-sm font-medium" > { title } < /p> <
                                                /div>
                                            );

                                            const pendingRequests = requests.filter(r => r.status === 'pending');
                                            const approvedRequests = requests.filter(r => r.status === 'approved');
                                            const rejectedRequests = requests.filter(r => r.status === 'rejected');

                                            return ( <
                                                div className = "max-w-4xl mx-auto p-8 my-8 text-white" >
                                                <
                                                h2 className = "text-2xl font-bold text-gray-200 mb-6 text-center" > Admin Dashboard < /h2> <
                                                div className = "grid grid-cols-1 md:grid-cols-3 gap-6 mb-8" >
                                                <
                                                SummaryCard title = "Total Pending"
                                                count = { pendingRequests.length }
                                                color = "bg-yellow-600"
                                                icon = { < FaClipboardList / > }
                                                /> <
                                                SummaryCard title = "Total Approved"
                                                count = { approvedRequests.length }
                                                color = "bg-green-600"
                                                icon = { < FaFileSignature / > }
                                                /> <
                                                SummaryCard title = "Total Rejected"
                                                count = { rejectedRequests.length }
                                                color = "bg-red-600"
                                                icon = { < FaTruck / > }
                                                /> <
                                                /div> <
                                                div className = "bg-gray-800 p-6 rounded-xl shadow-lg" >
                                                <
                                                h3 className = "text-xl font-bold text-gray-200 mb-4" > Pending Requests({ pendingRequests.length }) < /h3> <
                                                div className = "space-y-4 max-h-96 overflow-y-auto pr-2" > {
                                                    pendingRequests.length === 0 ? ( <
                                                        p className = "text-gray-400 text-center" > No pending requests to approve. < /p>
                                                    ) : (
                                                        pendingRequests.map(req => ( <
                                                            div key = { req.id }
                                                            className = { `bg-gray-700 p-4 rounded-lg shadow-md flex justify-between items-center cursor-pointer transition-colors duration-150 ease-in-out ${selectedRequest && selectedRequest.id === req.id ? 'border-2 border-indigo-500' : 'hover:bg-gray-600'}` }
                                                            onClick = {
                                                                () => setSelectedRequest(req) } >
                                                            <
                                                            div >
                                                            <
                                                            p className = "text-sm font-bold text-indigo-300" > { req.subject } < /p> <
                                                            p className = "text-xs text-gray-400 mt-1" > Requester: { req.requesterId.substring(0, 8) }... < /p> <
                                                            p className = "text-xs text-gray-400" > Created: { req.createdAt ? new Date(req.createdAt.toDate()).toLocaleString() : 'N/A' } < /p> <
                                                            /div> <
                                                            span className = "text-xs text-gray-500" > { req.region }
                                                            / {req.zone}</span >
                                                            <
                                                            /div>
                                                        ))
                                                    )
                                                } <
                                                /div> <
                                                /div>

                                                {
                                                    selectedRequest && ( <
                                                        div className = "bg-gray-800 p-6 rounded-xl shadow-lg mt-8" >
                                                        <
                                                        h3 className = "text-xl font-bold mb-4" > Request Details < /h3> <
                                                        div className = "bg-gray-700 p-6 rounded-lg space-y-4" >
                                                        <
                                                        h4 className = "text-xl font-bold text-indigo-300" > { selectedRequest.subject } < /h4> <
                                                        p className = "text-gray-300 text-sm" > { selectedRequest.description } < /p> <
                                                        div className = "flex flex-col sm:flex-row sm:space-x-4 space-y-2 sm:space-y-0 text-sm" >
                                                        <
                                                        span className = "text-gray-400" > Region: < span className = "font-semibold text-white" > { selectedRequest.region } < /span></span >
                                                        <
                                                        span className = "text-gray-400" > Zone: < span className = "font-semibold text-white" > { selectedRequest.zone } < /span></span >
                                                        <
                                                        span className = "text-gray-400" > Warehouse: < span className = "font-semibold text-white" > { selectedRequest.warehouse } < /span></span >
                                                        <
                                                        /div> <
                                                        div className = "flex space-x-4 mt-4" >
                                                        <
                                                        button onClick = {
                                                            () => handleStatusChange('approved') }
                                                        disabled = { isProcessing }
                                                        className = "flex-1 py-2 px-4 rounded-md shadow-sm text-sm font-medium text-white bg-green-600 hover:bg-green-700 disabled:bg-green-400" >
                                                        { isProcessing ? 'Processing...' : 'Approve' } <
                                                        /button> <
                                                        button onClick = {
                                                            () => handleStatusChange('rejected') }
                                                        disabled = { isProcessing }
                                                        className = "flex-1 py-2 px-4 rounded-md shadow-sm text-sm font-medium text-white bg-red-600 hover:bg-red-700 disabled:bg-red-400" >
                                                        { isProcessing ? 'Processing...' : 'Reject' } <
                                                        /button> <
                                                        /div> <
                                                        /div> <
                                                        /div>
                                                    )
                                                } <
                                                /div>
                                            );
                                        };

                                        const FarmerDashboard = () => ( <
                                            >
                                            <
                                            h2 className = "text-2xl font-bold text-gray-200 text-center my-8" > Welcome, Farmer < /h2> <
                                            NewRequestForm / >
                                            <
                                            />
                                        );

                                        const Login = ({ onLogin }) => {
                                                const { register, handleSubmit, formState: { errors } } = useForm();

                                                const onSubmit = (data) => {
                                                    onLogin(data.email, data.password);
                                                };

                                                return ( <
                                                    div className = "flex items-center justify-center min-h-screen bg-gray-900" >
                                                    <
                                                    div className = "w-full max-w-md p-8 space-y-8 bg-gray-800 rounded-xl shadow-lg text-white" >
                                                    <
                                                    div className = "text-center" >
                                                    <
                                                    FaSeedling className = "mx-auto h-12 w-auto text-indigo-500" / >
                                                    <
                                                    h2 className = "mt-6 text-3xl font-extrabold text-white" >
                                                    Sign in to your account <
                                                    /h2> <
                                                    p className = "mt-2 text-sm text-gray-400" >
                                                    Use the hardcoded credentials to test. <
                                                    /p> <
                                                    /div> <
                                                    form className = "mt-8 space-y-6"
                                                    onSubmit = { handleSubmit(onSubmit) } >
                                                    <
                                                    div >
                                                    <
                                                    label htmlFor = "email"
                                                    className = "sr-only" > Email address < /label> <
                                                    input id = "email"
                                                    name = "email"
                                                    type = "email" {...register('email', { required: 'Email is required' }) }
                                                    className = "appearance-none rounded-md relative block w-full px-3 py-2 border border-gray-700 bg-gray-700 placeholder-gray-400 text-white focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                                                    placeholder = "Email address" /
                                                    > {
                                                        errors.email && < p className = "mt-2 text-sm text-red-500" > { errors.email.message } < /p>} <
                                                        /div> <
                                                        div >
                                                        <
                                                        label htmlFor = "password"
                                                        className = "sr-only" > Password < /label> <
                                                        input
                                                        id = "password"
                                                        name = "password"
                                                        type = "password" {...register('password', { required: 'Password is required' }) }
                                                        className = "appearance-none rounded-md relative block w-full px-3 py-2 border border-gray-700 bg-gray-700 placeholder-gray-400 text-white focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                                                        placeholder = "Password" /
                                                        > {
                                                            errors.password && < p className = "mt-2 text-sm text-red-500" > { errors.password.message } < /p>} <
                                                            /div> <
                                                            div >
                                                            <
                                                            button
                                                            type = "submit"
                                                            className = "group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500" >
                                                            <
                                                            span className = "absolute left-0 inset-y-0 flex items-center pl-3" >
                                                            <
                                                            FaUser className = "h-5 w-5 text-indigo-500 group-hover:text-indigo-400" / >
                                                            <
                                                            /span>
                                                            Sign in
                                                            <
                                                            /button> <
                                                            /div> <
                                                            /form> <
                                                            /div> <
                                                            /div>
                                                        );
                                                    };

                                                    const renderDashboard = () => {
                                                        if (!currentUser) {
                                                            return <Login onLogin = { handleLogin }
                                                            />;
                                                        }

                                                        switch (userRole) {
                                                            case 'admin':
                                                            case 'manager':
                                                                return <AdminManagerDashboard / > ;
                                                            case 'farmer':
                                                                return <FarmerDashboard / > ;
                                                            default:
                                                                return <p className = "text-center text-white" > Something went wrong. < /p>;
                                                        }
                                                    };

                                                    if (!authReady) {
                                                        return ( <
                                                            div className = "flex items-center justify-center h-screen bg-gray-900 text-white" >
                                                            <
                                                            div className = "animate-spin rounded-full h-32 w-32 border-t-2 border-b-2 border-indigo-500" > < /div> <
                                                            /div>
                                                        );
                                                    }

                                                    return ( <
                                                        div className = "bg-gray-900 min-h-screen font-[Inter]" >
                                                        <
                                                        Header userRole = { userRole }
                                                        onLogout = { handleLogout }
                                                        /> <
                                                        main className = "container mx-auto p-4" > { renderDashboard() } <
                                                        /main> <
                                                        Modal show = { showModal }
                                                        onClose = {
                                                            () => setShowModal(false) }
                                                        title = "Notification"
                                                        message = { modalContent }
                                                        /> <
                                                        /div>
                                                    );
                                                }
