import React, { useEffect, useState } from 'react';
import { api } from '../api';

export default function WoredaDashboard() {
    const [rows, setRows] = useState([]);
    const [loading, setLoading] = useState(true);
    const reload = async() => {
        setLoading(true);
        try { setRows(await api('/api/requests')); } catch (e) { console.error(e); }
        setLoading(false);
    };
    useEffect(() => { reload(); }, []);

    const act = async(id, action) => { await api(`/api/requests/${id}/status`, { method: 'PUT', body: { action } });
        reload(); };
    const del = async(id) => { await api(`/api/requests/${id}`, { method: 'DELETE' });
        reload(); };

    return ( <
        div >
        <
        h2 > Woreda Dashboard < /h2> {
            loading ? 'Loading...' : ( <
                    table >
                    <
                    thead > < tr >
                    <
                    th > # < /th><th>Farmer</th > < th > Product < /th><th>Qty</th > < th > Kebele < /th><th>Status</th > < th > Action < /th> <
                    /tr></thead >
                    <
                    tbody > {
                        rows.map(r => ( <
                                tr key = { r.id } >
                                <
                                td > { r.id } < /td><td>{r.farmer_name}</td > < td > { r.product_name } < /td><td>{r.quantity}</td >
                                <
                                td > { r.kebele_name } < /td><td>{r.status}</td >
                                <
                                td >
                                <
                                label > < input type = "radio"
                                name = { `act-${r.id}` }
                                onChange = {
                                    () => act(r.id, 'Accept') }
                                /> Accept</label >
                                <
                                label > < input type = "radio"
                                name = { `act-${r.id}` }
                                onChange = {
                                    () => act(r.id, 'Approve') }
                                /> Approve</label >
                                <
                                label > < input type = "radio"
                                name = { `act-${r.id}` }
                                onChange = {
                                    () => act(r.id, 'Reject') }
                                /> Reject</label > {
                                    r.status === 'Rejected' && < button onClick = {
                                        () => del(r.id) } > Delete < /button>} <
                                    /td> <
                                    /tr>
                                ))
                        } <
                        /tbody> <
                        /table>
                    )
                } <
                /div>
        );
    }
