import React, { useState } from 'react';
import { adminRoles } from '../constants';

const LoginPage = ({ onLogin }) => {
    const [selectedRole, setSelectedRole] = useState(adminRoles.FEDERAL);

    const handleSubmit = (e) => {
        e.preventDefault();
        onLogin(selectedRole);
    };

    return ( <
        div className = "flex items-center justify-center min-h-screen bg-gray-100" >
        <
        div className = "w-full max-w-md p-8 bg-white rounded-xl shadow-lg" >
        <
        h2 className = "text-3xl font-bold text-center text-gray-900 mb-6" > Log In < /h2> <
        form onSubmit = { handleSubmit }
        className = "space-y-6" >
        <
        div >
        <
        label htmlFor = "role"
        className = "block text-sm font-medium text-gray-700" > Select Role < /label> <
        select id = "role"
        name = "role"
        value = { selectedRole }
        onChange = {
            (e) => setSelectedRole(e.target.value) }
        className = "mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md" >
        {
            Object.values(adminRoles).map(role => ( <
                option key = { role }
                value = { role } > { role } < /option>
            ))
        } <
        /select> <
        /div> <
        button type = "submit"
        className = "w-full bg-indigo-600 text-white py-2 px-4 rounded-lg font-semibold hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors" >
        Sign In <
        /button> <
        /form> <
        /div> <
        /div>
    );
};

export default LoginPage;
