import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const FarmerDashboard = () => {
        const [activeTab, setActiveTab] = useState('requests');
        const [user, setUser] = useState(null);
        const [darkMode, setDarkMode] = useState(false);
        const navigate = useNavigate();

        // Request Management State
        const [requests, setRequests] = useState([]);
        const [showRequestModal, setShowRequestModal] = useState(false);
        const [showStatusModal, setShowStatusModal] = useState(false);
        const [selectedRequest, setSelectedRequest] = useState(null);
        const [editingRequest, setEditingRequest] = useState(null);
        const [requestForm, setRequestForm] = useState({
            product_id: '',
            quantity: '',
            note: ''
        });

        // Products State
        const [products, setProducts] = useState([]);
        const [filteredProducts, setFilteredProducts] = useState([]);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState('');
        const [filters, setFilters] = useState({
            adminLevel: ''
        });

        // Request Filter State
        const [requestStatusFilter, setRequestStatusFilter] = useState('all');

        useEffect(() => {
            // Check authentication
            const token = localStorage.getItem('token');
            const userData = JSON.parse(localStorage.getItem('user') || 'null');
            const userType = localStorage.getItem('userType');

            if (!token || userType !== 'Farmer' || !userData) {
                navigate('/login');
                return;
            }

            setUser(userData);
            fetchDashboardData();

            // Check for saved theme preference
            const savedDarkMode = localStorage.getItem('darkMode') === 'true';
            setDarkMode(savedDarkMode);
        }, [navigate]);

        useEffect(() => {
            // Apply dark mode class to body
            if (darkMode) {
                document.documentElement.classList.add('dark');
            } else {
                document.documentElement.classList.remove('dark');
            }
            localStorage.setItem('darkMode', darkMode);
        }, [darkMode]);

        const fetchDashboardData = async() => {
            try {
                setLoading(true);
                const token = localStorage.getItem('token');

                // Fetch ALL products available in the country
                const productsResponse = await fetch('http://localhost:5000/api/farmers/products', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (productsResponse.ok) {
                    const productsData = await productsResponse.json();
                    setProducts(productsData);
                    setFilteredProducts(productsData);
                } else {
                    console.error('Failed to fetch products');
                    setError('Failed to load products');
                }

                // Fetch farmer's requests
                const requestsResponse = await fetch('http://localhost:5000/api/farmers/requests', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (requestsResponse.ok) {
                    const requestsData = await requestsResponse.json();
                    setRequests(requestsData);
                } else {
                    console.error('Failed to fetch requests');
                }

            } catch (error) {
                console.error('Error fetching dashboard data:', error);
                setError('Error loading dashboard data');
            } finally {
                setLoading(false);
            }
        };

        const applyFilters = () => {
            let result = [...products];

            if (filters.adminLevel) {
                result = result.filter(product => {
                    if (filters.adminLevel === 'kebele') return product.added_by_level === 'Kebele';
                    if (filters.adminLevel === 'woreda') return product.added_by_level === 'Woreda';
                    if (filters.adminLevel === 'zone') return product.added_by_level === 'Zone';
                    if (filters.adminLevel === 'region') return product.added_by_level === 'Region';
                    if (filters.adminLevel === 'federal') return product.added_by_level === 'Federal';
                    if (filters.adminLevel === 'other') return !['Kebele', 'Woreda', 'Zone', 'Region', 'Federal'].includes(product.added_by_level);
                    return true;
                });
            }

            setFilteredProducts(result);
        };

        useEffect(() => {
            applyFilters();
        }, [filters, products]);

        const handleFilterChange = (e) => {
            const { name, value } = e.target;
            setFilters(prev => ({
                ...prev,
                [name]: value
            }));
        };

        const clearFilters = () => {
            setFilters({
                adminLevel: ''
            });
        };

        const fetchRequestStatusDetail = async(requestId) => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/farmers/request/${requestId}/status`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    const statusData = await response.json();
                    setSelectedRequest(statusData);
                    setShowStatusModal(true);
                } else {
                    alert('Failed to fetch request status details');
                }
            } catch (error) {
                console.error('Error fetching status details:', error);
                alert('Error fetching status details');
            }
        };

        const handleLogout = () => {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            localStorage.removeItem('userType');
            navigate('/login');
        };

        // Request Management Functions
        const handleRequestSubmit = async(e) => {
            e.preventDefault();
            try {
                const token = localStorage.getItem('token');

                if (!requestForm.product_id || !requestForm.quantity) {
                    alert('Product and quantity are required');
                    return;
                }

                if (requestForm.quantity <= 0) {
                    alert('Quantity must be greater than 0');
                    return;
                }

                const url = editingRequest ?
                    `http://localhost:5000/api/farmers/request/${editingRequest.id}` :
                    'http://localhost:5000/api/farmers/request';

                const method = editingRequest ? 'PUT' : 'POST';

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        product_id: parseInt(requestForm.product_id),
                        quantity: parseInt(requestForm.quantity),
                        note: requestForm.note
                    })
                });

                const responseData = await response.json();

                if (response.ok) {
                    setShowRequestModal(false);
                    setEditingRequest(null);
                    setRequestForm({ product_id: '', quantity: '', note: '' });
                    fetchDashboardData();
                    alert(editingRequest ? 'Request updated successfully' : 'Request created successfully');
                } else {
                    alert(responseData.message || 'Error processing request');
                }
            } catch (error) {
                console.error('Error processing request:', error);
                alert('Error processing request');
            }
        };

        const handleEditRequest = (request) => {
            // Only allow editing if request is pending
            if (request.status !== 'Pending') {
                alert('Only pending requests can be edited');
                return;
            }

            setEditingRequest(request);
            setRequestForm({
                product_id: request.product_id.toString(),
                quantity: request.quantity.toString(),
                note: request.note || ''
            });
            setShowRequestModal(true);
        };

        const handleDeleteRequest = async(requestId) => {
            if (window.confirm('Are you sure you want to delete this request?')) {
                try {
                    const token = localStorage.getItem('token');
                    const response = await fetch(`http://localhost:5000/api/farmers/request/${requestId}`, {
                        method: 'DELETE',
                        headers: {
                            'Authorization': `Bearer ${token}`
                        }
                    });

                    if (response.ok) {
                        fetchDashboardData();
                        alert('Request deleted successfully');
                    } else {
                        const errorData = await response.json();
                        alert(errorData.message || 'Error deleting request');
                    }
                } catch (error) {
                    console.error('Error deleting request:', error);
                    alert('Error deleting request');
                }
            }
        };

        const getStatusBadge = (status) => {
            const statusClasses = {
                'Pending': 'bg-yellow-100 text-yellow-800',
                'Accepted': 'bg-green-100 text-green-800',
                'Rejected': 'bg-red-100 text-red-800',
                'Approved': 'bg-blue-100 text-blue-800',
                'Kebele_Approved': 'bg-purple-100 text-purple-800',
                'Woreda_Approved': 'bg-indigo-100 text-indigo-800',
                'Zone_Approved': 'bg-teal-100 text-teal-800',
                'Region_Approved': 'bg-cyan-100 text-cyan-800'
            };

            // Format status for display
            const formatStatus = (status) => {
                return status.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
            };

            return ( <
                span className = { `px-2 py-1 rounded-full text-xs font-medium ${statusClasses[status] || 'bg-gray-100 text-gray-800'}` } > { formatStatus(status) } <
                /span>
            );
        };

        const getLevelStatus = (level, request) => {
            const status = request[`${level}_status`];
            const admin = request[`${level}_admin_name`];
            const feedback = request[`${level}_feedback`];
            const date = request[`${level}_approved_at`];

            const formatLevel = level.charAt(0).toUpperCase() + level.slice(1);

            return ( <
                div className = "mb-4 p-3 bg-gray-50 rounded-lg dark:bg-gray-700" >
                <
                h4 className = "font-semibold text-lg mb-2 dark:text-white" > { formatLevel }
                Level < /h4> <
                p className = "dark:text-gray-300" > < strong > Status: < /strong> {status || 'Pending'}</p > { admin && < p className = "dark:text-gray-300" > < strong > Admin: < /strong> {admin}</p > } { feedback && < p className = "dark:text-gray-300" > < strong > Feedback: < /strong> {feedback}</p > } { date && < p className = "dark:text-gray-300" > < strong > Date: < /strong> {new Date(date).toLocaleString()}</p > } <
                /div>
            );
        };

        // Filter requests by status
        const filteredRequests = requestStatusFilter === 'all' ?
            requests :
            requests.filter(request => request.status === requestStatusFilter);

        if (loading) {
            return <div className = "flex justify-center items-center h-screen dark:bg-gray-900 dark:text-white" > Loading... < /div>;
        }

        if (error) {
            return <div className = "flex justify-center items-center h-screen dark:bg-gray-900 text-red-500" > { error } < /div>;
        }

        return ( <
            div className = { `flex h-screen ${darkMode ? 'dark' : ''}` } > { /* Sidebar */ } <
            div className = "w-64 bg-gray-800 text-white dark:bg-gray-900" >
            <
            div className = "p-4" >
            <
            h2 className = "text-xl font-bold" > Farmer Dashboard < /h2> <
            /div> <
            nav className = "mt-6" >
            <
            button className = { `w-full text-left px-4 py-3 flex items-center ${activeTab === 'requests' ? 'bg-gray-700 dark:bg-gray-800' : 'hover:bg-gray-700 dark:hover:bg-gray-800'}` }
            onClick = {
                () => setActiveTab('requests') } >
            <
            span className = "mr-2" > ðŸ“‹ < /span> My Requests <
            /button> <
            button className = { `w-full text-left px-4 py-3 flex items-center ${activeTab === 'products' ? 'bg-gray-700 dark:bg-gray-800' : 'hover:bg-gray-700 dark:hover:bg-gray-800'}` }
            onClick = {
                () => setActiveTab('products') } >
            <
            span className = "mr-2" > ðŸ›’ < /span> Available Products <
            /button> <
            /nav> <
            div className = "absolute bottom-0 w-full p-4" >
            <
            button onClick = {
                () => setDarkMode(!darkMode) }
            className = "w-full py-2 px-4 bg-gray-700 rounded-md hover:bg-gray-600 flex items-center justify-center" >
            { darkMode ? 'â˜€ï¸ Light Mode' : 'ðŸŒ™ Dark Mode' } <
            /button> <
            /div> <
            /div>

            { /* Main Content */ } <
            div className = "flex-1 overflow-auto bg-gray-100 dark:bg-gray-800" >
            <
            header className = "bg-white shadow-sm dark:bg-gray-900 p-4 flex justify-between items-center" >
            <
            h1 className = "text-xl font-semibold dark:text-white" > Welcome, { user ? user.fullName : '' }(Farmer) < /h1> <
            button onClick = { handleLogout }
            className = "bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-md" >
            Logout <
            /button> <
            /header>

            <
            main className = "p-6" > { /* Requests Tab */ } {
                activeTab === 'requests' && ( <
                    div className = "requests-tab" >
                    <
                    div className = "mb-6" >
                    <
                    h2 className = "text-2xl font-bold dark:text-white" > My Requests < /h2> <
                    p className = "text-gray-600 dark:text-gray-400" > Manage your product requests < /p> <
                    button onClick = {
                        () => setShowRequestModal(true) }
                    className = "mt-4 bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-md flex items-center" >
                    <
                    span className = "mr-2" > âž• < /span> New Request <
                    /button> <
                    /div>

                    { /* Request Filter */ } <
                    div className = "mb-4 bg-white p-4 rounded-lg shadow dark:bg-gray-900" >
                    <
                    h3 className = "text-lg font-semibold mb-3 dark:text-white" > Filter Requests < /h3> <
                    div className = "flex items-center space-x-4" >
                    <
                    label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Status: < /label> <
                    select value = { requestStatusFilter }
                    onChange = {
                        (e) => setRequestStatusFilter(e.target.value) }
                    className = "p-2 border border-gray-300 rounded-md dark:bg-gray-800 dark:text-white dark:border-gray-600" >
                    <
                    option value = "all" > All Statuses < /option> <
                    option value = "Pending" > Pending < /option> <
                    option value = "Approved" > Approved < /option> <
                    option value = "Rejected" > Rejected < /option> <
                    option value = "Accepted" > Accepted < /option> <
                    /select> <
                    /div> <
                    /div>

                    <
                    div className = "grid gap-4" > {
                        filteredRequests.map((request) => ( <
                                div key = { request.id }
                                className = "bg-white rounded-lg shadow p-4 dark:bg-gray-900" >
                                <
                                div className = "flex justify-between" >
                                <
                                div className = "flex-1" >
                                <
                                h3 className = "text-lg font-semibold dark:text-white" > { request.product_name } < /h3> <
                                p className = "text-gray-600 dark:text-gray-400" > ðŸ“¦Category: { request.category } < /p> <
                                p className = "text-gray-600 dark:text-gray-400" > ðŸ”¢Quantity: { request.quantity } < /p> <
                                p className = "text-gray-600 dark:text-gray-400" > ðŸ’°Price: { request.price } < /p> <
                                p className = "text-gray-600 dark:text-gray-400" > ðŸ“Location: { request.location } < /p> {
                                    request.note && < p className = "text-gray-600 dark:text-gray-400" > ðŸ“Note: { request.note } < /p>} <
                                        p className = "text-gray-600 dark:text-gray-400" > ðŸ“…Date: { new Date(request.created_at).toLocaleDateString() } < /p> <
                                        div className = "mt-2" > { getStatusBadge(request.status) } <
                                        /div> {
                                            request.handled_by_admin && ( <
                                                p className = "text-gray-600 dark:text-gray-400 mt-2" > ðŸ‘¤Handled by: { request.handled_by_admin } < /p>
                                            )
                                        } <
                                        /div> <
                                        div className = "flex flex-col space-y-2" >
                                        <
                                        button
                                    onClick = {
                                        () => fetchRequestStatusDetail(request.id) }
                                    className = "bg-gray-200 hover:bg-gray-300 text-gray-800 px-3 py-1 rounded text-sm flex items-center dark:bg-gray-700 dark:text-white dark:hover:bg-gray-600" >
                                        <
                                        span className = "mr-1" > ðŸ“Š < /span> View Status Details <
                                        /button> {
                                            request.status === 'Pending' && ( <
                                                >
                                                <
                                                button onClick = {
                                                    () => handleEditRequest(request) }
                                                className = "bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded text-sm flex items-center" >
                                                <
                                                span className = "mr-1" > âœï¸ < /span> Edit <
                                                /button> <
                                                button onClick = {
                                                    () => handleDeleteRequest(request.id) }
                                                className = "bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded text-sm flex items-center" >
                                                <
                                                span className = "mr-1" > ðŸ—‘ï¸ < /span> Delete <
                                                /button> <
                                                />
                                            )
                                        } {
                                            (request.status === 'Accepted' || request.status === 'Rejected') && ( <
                                                p className = "text-sm text-gray-500 dark:text-gray-400" > Processed < /p>
                                            )
                                        } <
                                        /div> <
                                        /div> <
                                        /div>
                                ))
                        } {
                            requests.length === 0 && ( <
                                p className = "text-gray-600 dark:text-gray-400" > No requests found.Create your first request! < /p>
                            )
                        } <
                        /div> <
                        /div>
                    )
                }

                { /* Products Tab */ } {
                    activeTab === 'products' && ( <
                        div className = "products-tab" >
                        <
                        div className = "mb-6" >
                        <
                        h2 className = "text-2xl font-bold dark:text-white" > Available Products < /h2> <
                        p className = "text-gray-600 dark:text-gray-400" > Products available across the country < /p> <
                        /div>

                        { /* Filter Section */ } <
                        div className = "bg-white p-4 rounded-lg shadow mb-6 dark:bg-gray-900" >
                        <
                        h3 className = "text-lg font-semibold mb-3 dark:text-white" > Filter Products by Admin Level < /h3> <
                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" >
                        <
                        div >
                        <
                        label className = "block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1" > Admin Level < /label> <
                        select name = "adminLevel"
                        value = { filters.adminLevel }
                        onChange = { handleFilterChange }
                        className = "w-full p-2 border border-gray-300 rounded-md dark:bg-gray-800 dark:text-white dark:border-gray-600" >
                        <
                        option value = "" > All Levels < /option> <
                        option value = "kebele" > At Your Kebele < /option> <
                        option value = "woreda" > At Your Woreda < /option> <
                        option value = "zone" > At Your Zone < /option> <
                        option value = "region" > At Your Region < /option> <
                        option value = "federal" > At Federal Level < /option> <
                        option value = "other" > By Other Admins < /option> <
                        /select> <
                        /div> <
                        /div> <
                        button onClick = { clearFilters }
                        className = "mt-4 bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded-md" >
                        Clear Filters <
                        /button> <
                        /div>

                        <
                        div className = "grid gap-4" > {
                            filteredProducts.map((product) => ( <
                                div key = { product.id }
                                className = "bg-white rounded-lg shadow p-4 dark:bg-gray-900" >
                                <
                                div className = "flex justify-between" >
                                <
                                div className = "flex-1" >
                                <
                                h3 className = "text-lg font-semibold dark:text-white" > { product.name } < /h3> <
                                p className = "text-gray-600 dark:text-gray-400" > { product.description } < /p> <
                                p className = "text-gray-600 dark:text-gray-400" > ðŸ“¦Category: { product.category } < /p> <
                                p className = "text-gray-600 dark:text-gray-400" > ðŸ’°Amount: { product.amount } < /p> <
                                p className = "text-gray-600 dark:text-gray-400" > ðŸ’µPrice: { product.price } < /p> <
                                p className = "text-gray-600 dark:text-gray-400" > ðŸ“Location: { product.location } < /p> {
                                    product.added_by && ( <
                                        p className = "text-gray-600 dark:text-gray-400" > ðŸ‘¤Added by: { product.added_by }({ product.added_by_level }) < /p>
                                    )
                                } <
                                p className = "text-gray-600 dark:text-gray-400" > ðŸ“…Added: { new Date(product.created_at).toLocaleDateString() } < /p> <
                                /div> <
                                div className = "flex items-center" >
                                <
                                button onClick = {
                                    () => {
                                        setRequestForm({
                                            product_id: product.id.toString(),
                                            quantity: '',
                                            note: ''
                                        });
                                        setEditingRequest(null);
                                        setShowRequestModal(true);
                                    }
                                }
                                className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-md flex items-center" >
                                <
                                span className = "mr-2" > ðŸ“‹ < /span> Request This Product <
                                /button> <
                                /div> <
                                /div> <
                                /div>
                            ))
                        } {
                            filteredProducts.length === 0 && ( <
                                p className = "text-gray-600 dark:text-gray-400" > No products available at the moment. < /p>
                            )
                        } <
                        /div> <
                        /div>
                    )
                }

                { /* Request Modal */ } {
                    showRequestModal && ( <
                        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                        <
                        div className = "bg-white rounded-lg shadow-lg w-full max-w-md dark:bg-gray-900" >
                        <
                        div className = "p-6" >
                        <
                        h3 className = "text-xl font-semibold mb-4 dark:text-white" > { editingRequest ? 'Edit Request' : 'Create New Request' } < /h3> <
                        form onSubmit = { handleRequestSubmit } >
                        <
                        div className = "mb-4" >
                        <
                        label className = "block text-gray-700 dark:text-gray-300 mb-2" > Product: < /label> <
                        select value = { requestForm.product_id }
                        onChange = {
                            (e) => setRequestForm({...requestForm, product_id: e.target.value }) }
                        required disabled = { editingRequest }
                        className = "w-full p-2 border border-gray-300 rounded-md dark:bg-gray-800 dark:text-white dark:border-gray-600" >
                        <
                        option value = "" > Select a product < /option> {
                            products.map((product) => ( <
                                option key = { product.id }
                                value = { product.id } > { product.name } - { product.category }(Price: { product.price }, Location: { product.location }) <
                                /option>
                            ))
                        } <
                        /select> <
                        /div> <
                        div className = "mb-4" >
                        <
                        label className = "block text-gray-700 dark:text-gray-300 mb-2" > Quantity: < /label> <
                        input type = "number"
                        min = "1"
                        value = { requestForm.quantity }
                        onChange = {
                            (e) => setRequestForm({...requestForm, quantity: e.target.value }) }
                        required className = "w-full p-2 border border-gray-300 rounded-md dark:bg-gray-800 dark:text-white dark:border-gray-600" /
                        >
                        <
                        /div> <
                        div className = "mb-4" >
                        <
                        label className = "block text-gray-700 dark:text-gray-300 mb-2" > Note(Optional): < /label> <
                        textarea value = { requestForm.note }
                        onChange = {
                            (e) => setRequestForm({...requestForm, note: e.target.value }) }
                        rows = "3"
                        placeholder = "Any additional information about your request..."
                        className = "w-full p-2 border border-gray-300 rounded-md dark:bg-gray-800 dark:text-white dark:border-gray-600" /
                        >
                        <
                        /div> <
                        div className = "flex justify-end space-x-2" >
                        <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowRequestModal(false);
                                setEditingRequest(null);
                                setRequestForm({ product_id: '', quantity: '', note: '' });
                            }
                        }
                        className = "bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded-md dark:bg-gray-700 dark:text-white dark:hover:bg-gray-600" >
                        Cancel <
                        /button> <
                        button type = "submit"
                        className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-md" >
                        { editingRequest ? 'Update Request' : 'Create Request' } <
                        /button> <
                        /div> <
                        /form> <
                        /div> <
                        /div> <
                        /div>
                    )
                }

                { /* Status Detail Modal */ } {
                    showStatusModal && selectedRequest && ( <
                        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                        <
                        div className = "bg-white rounded-lg shadow-lg w-full max-w-2xl max-h-screen overflow-y-auto dark:bg-gray-900" >
                        <
                        div className = "p-6" >
                        <
                        h3 className = "text-xl font-semibold mb-4 dark:text-white" > Request Status Details < /h3> <
                        div className = "mb-6 p-4 bg-gray-50 rounded-lg dark:bg-gray-800" >
                        <
                        h4 className = "text-lg font-semibold mb-2 dark:text-white" > Overall Status: { getStatusBadge(selectedRequest.status) } < /h4> <
                        p className = "dark:text-gray-300" > < strong > Product: < /strong> {selectedRequest.product_name}</p >
                        <
                        p className = "dark:text-gray-300" > < strong > Quantity: < /strong> {selectedRequest.quantity}</p >
                        <
                        p className = "dark:text-gray-300" > < strong > Price: < /strong> {selectedRequest.price}</p >
                        <
                        p className = "dark:text-gray-300" > < strong > Location: < /strong> {selectedRequest.location}</p > { selectedRequest.note && < p className = "dark:text-gray-300" > < strong > Note: < /strong> {selectedRequest.note}</p > } <
                        /div>

                        <
                        div className = "approval-levels" > { getLevelStatus('kebele', selectedRequest) } { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) } { getLevelStatus('federal', selectedRequest) } <
                        /div>

                        <
                        div className = "flex justify-end mt-6" >
                        <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowStatusModal(false);
                                setSelectedRequest(null);
                            }
                        }
                        className = "bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded-md dark:bg-gray-700 dark:text-white dark:hover:bg-gray-600" >
                        Close <
                        /button> <
                        /div> <
                        /div> <
                        /div> <
                        /div>
                    )
                } <
                /main> <
                /div> <
                /div>
            );
        };

        export default FarmerDashboard;
