// App.jsx

import React, { useState } from 'react';

// This is a simple login component that communicates with the backend.
// It assumes Tailwind CSS is configured.

const App = () => {
    const [phoneNumber, setPhoneNumber] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    // Function to handle the form submission
    const handleSubmit = async(event) => {
        event.preventDefault();
        setIsLoading(true);
        setErrorMessage('');
        setSuccessMessage('');

        try {
            // Make a POST request to the backend's login endpoint
            const response = await fetch('http://localhost:5000/api/admin/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ phoneNumber, password }),
            });

            const data = await response.json();

            // Check if the response was successful (status code 200)
            if (response.ok) {
                setSuccessMessage(data.message);
                setErrorMessage('');
                console.log('Login successful:', data);
            } else {
                // If there was an error, set the error message from the backend
                setErrorMessage(data.message || 'An error occurred. Please try again.');
                setSuccessMessage('');
                console.error('Login failed:', data);
            }
        } catch (error) {
            // Handle network errors or other exceptions
            setErrorMessage('Network error. Could not connect to the server.');
            setSuccessMessage('');
            console.error('Fetch error:', error);
        } finally {
            setIsLoading(false);
        }
    };

    return ( <
        div className = "flex items-center justify-center min-h-screen bg-gray-100 p-4" >
        <
        div className = "bg-white rounded-xl shadow-2xl p-8 max-w-sm w-full space-y-6" >
        <
        h2 className = "text-3xl font-bold text-center text-gray-800" > Log In < /h2> <
        p className = "text-center text-gray-600" >
        Enter your credentials to access the admin system. <
        /p> <
        form onSubmit = { handleSubmit }
        className = "space-y-5" > { /* Phone Number Input */ } <
        div >
        <
        label htmlFor = "phoneNumber"
        className = "text-sm font-medium text-gray-700" > Phone Number < /label> <
        div className = "mt-1 relative rounded-md shadow-sm" >
        <
        div className = "absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none" >
        <
        svg xmlns = "http://www.w3.org/2000/svg"
        className = "h-5 w-5 text-gray-400"
        fill = "none"
        viewBox = "0 0 24 24"
        stroke = "currentColor"
        strokeWidth = { 2 } >
        <
        path strokeLinecap = "round"
        strokeLinejoin = "round"
        d = "M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.293 1.147a1 1 0 00-.487.942v1a1 1 0 001 1h1a1 1 0 001-1v-1a1 1 0 00-.487-.942l-2.293-1.147a1 1 0 01-.502-1.21l1.498-4.493a1 1 0 01.948-.684H19a2 2 0 012 2v10a2 2 0 01-2 2H5a2 2 0 01-2-2V5z" / >
        <
        /svg> < /
        div > <
        input id = "phoneNumber"
        name = "phoneNumber"
        type = "tel"
        value = { phoneNumber }
        onChange = {
            (e) => setPhoneNumber(e.target.value)
        }
        required className = "focus:ring-indigo-500 focus:border-indigo-500 block w-full pl-10 sm:text-sm border-gray-300 rounded-md p-3"
        placeholder = "Your Phone Number" /
        >
        <
        /div> < /
        div >

        { /* Password Input */ } <
        div >
        <
        label htmlFor = "password"
        className = "text-sm font-medium text-gray-700" > Password < /label> <
        div className = "mt-1 relative rounded-md shadow-sm" >
        <
        div className = "absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none" >
        <
        svg xmlns = "http://www.w3.org/2000/svg"
        className = "h-5 w-5 text-gray-400"
        fill = "none"
        viewBox = "0 0 24 24"
        stroke = "currentColor"
        strokeWidth = { 2 } >
        <
        path strokeLinecap = "round"
        strokeLinejoin = "round"
        d = "M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2z" / >
        <
        path strokeLinecap = "round"
        strokeLinejoin = "round"
        d = "M11 7h2v4h-2z" / >
        <
        /svg> < /
        div > <
        input id = "password"
        name = "password"
        type = "password"
        value = { password }
        onChange = {
            (e) => setPassword(e.target.value)
        }
        required className = "focus:ring-indigo-500 focus:border-indigo-500 block w-full pl-10 sm:text-sm border-gray-300 rounded-md p-3"
        placeholder = "********" /
        >
        <
        /div> < /
        div >

        { /* Error and Success Messages */ } {
            errorMessage && ( <
                p className = "text-sm font-medium text-red-600 text-center" > { errorMessage } < /p>
            )
        } {
            successMessage && ( <
                p className = "text-sm font-medium text-green-600 text-center" > { successMessage } < /p>
            )
        }

        { /* Sign In Button */ } <
        div >
        <
        button type = "submit"
        className = "w-full flex justify-center py-3 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-150 ease-in-out"
        disabled = { isLoading } > { isLoading ? 'Signing In...' : 'Sign In' } <
        /button> < /
        div > <
        /form> < /
        div > <
        /div>
    );
};

export default App;
