    // Global variables provided by the Canvas environment
    const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
    const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
    const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

    // State variables for the application
    const [db, setDb] = useState(null);
    const [auth, setAuth] = useState(null);
    const [user, setUser] = useState(null);
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [showAddForm, setShowAddForm] = useState(false);
    const [newRequestTitle, setNewRequestTitle] = useState('');
    const [newRequestDepartment, setNewRequestDepartment] = useState('Biological Input Supply');

    // Custom modal state for deletion confirmation
    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [requestToDelete, setRequestToDelete] = useState(null);

    // 1. Initialize Firebase and set up auth listener
    useEffect(() => {
        try {
            const app = initializeApp(firebaseConfig);
            const firestoreDb = getFirestore(app);
            const firestoreAuth = getAuth(app);
            setDb(firestoreDb);
            setAuth(firestoreAuth);

            const unsubscribe = onAuthStateChanged(firestoreAuth, async(currentUser) => {
                if (currentUser) {
                    setUser(currentUser);
                } else {
                    try {
                        if (initialAuthToken) {
                            await signInWithCustomToken(firestoreAuth, initialAuthToken);
                        } else {
                            await signInAnonymously(firestoreAuth);
                        }
                    } catch (e) {
                        console.error("Authentication failed:", e);
                        setError("Failed to authenticate. Please try again.");
                    }
                }
            });

            return () => unsubscribe();
        } catch (e) {
            console.error("Firebase initialization failed:", e);
            setError("Failed to initialize the application.");
        }
    }, [firebaseConfig, initialAuthToken]);

    // 2. Fetch requests from Firestore in real-time
    useEffect(() => {
        // Only proceed if db and auth are initialized and a user is authenticated
        if (!db || !auth || !auth.currentUser) {
            return;
        }

        setLoading(true);
        setError(null);

        // The data is public for all authenticated users
        const requestsCollectionRef = collection(db, `artifacts/${appId}/public/data/requests`);
        const q = query(requestsCollectionRef);

        const unsubscribe = onSnapshot(q, (querySnapshot) => {
            const fetchedRequests = querySnapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data(),
            }));
            setRequests(fetchedRequests);
            setLoading(false);
        }, (e) => {
            console.error("Failed to fetch requests:", e);
            setError("Failed to load requests. Please check your connection.");
            setLoading(false);
        });

        // Clean up the listener when the component unmounts
        return () => unsubscribe();
    }, [db, auth, appId]);

    // Function to handle adding a new request
    const handleAddRequest = async(e) => {
        e.preventDefault();
        if (!newRequestTitle.trim()) {
            return;
        }
        setLoading(true);
        try {
            const requestsCollectionRef = collection(db, `artifacts/${appId}/public/data/requests`);
            await addDoc(requestsCollectionRef, {
                title: newRequestTitle,
                department: newRequestDepartment,
                status: 'pending', // Initial status
                createdAt: serverTimestamp(),
                userId: user.uid,
            });
            setNewRequestTitle('');
            setShowAddForm(false);
        } catch (e) {
            console.error("Error adding document: ", e);
            setError("Failed to add request. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    // Function to handle updating a request's status
    const handleUpdateStatus = async(id, currentStatus) => {
        setLoading(true);
        try {
            const requestDocRef = doc(db, `artifacts/${appId}/public/data/requests`, id);
            const newStatus = currentStatus === 'pending' ? 'completed' : 'pending';
            await updateDoc(requestDocRef, {
                status: newStatus,
            });
        } catch (e) {
            console.error("Error updating document: ", e);
            setError("Failed to update status. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    // Function to confirm and then delete a request
    const confirmDelete = async() => {
        if (!requestToDelete) return;

        setLoading(true);
        try {
            const requestDocRef = doc(db, `artifacts/${appId}/public/data/requests`, requestToDelete.id);
            await deleteDoc(requestDocRef);
            // Close the modal and reset state
            setShowDeleteModal(false);
            setRequestToDelete(null);
        } catch (e) {
            console.error("Error deleting document: ", e);
            setError("Failed to delete request. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    // UI for the delete confirmation modal
    const renderDeleteModal = () => ( <
        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
        <
        div className = "bg-white p-6 rounded-xl shadow-2xl max-w-sm w-full space-y-4" >
        <
        h3 className = "text-xl font-bold text-gray-800" > Confirm Deletion < /h3> <
        p className = "text-gray-600" >
        Are you sure you want to delete the request titled: < span className = "font-semibold italic" > "{requestToDelete?.title}" < /span>? This action cannot be undone. < /
        p > <
        div className = "flex justify-end gap-2" >
        <
        button onClick = {
            () => {
                setShowDeleteModal(false);
                setRequestToDelete(null);
            }
        }
        className = "px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition-colors" >
        Cancel <
        /button> <
        button onClick = { confirmDelete }
        className = "px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors" >
        Delete <
        /button> < /
        div > <
        /div> < /
        div >
    );

    return ( <
        div className = "min-h-screen bg-gray-50 p-4 sm:p-6 md:p-8 font-['Inter']" > { showDeleteModal && renderDeleteModal() } <
        div className = "max-w-4xl mx-auto space-y-8" > { /* Header */ } <
        div className = "flex flex-col sm:flex-row justify-between items-start sm:items-center" >
        <
        h1 className = "text-3xl font-extrabold text-gray-900 mb-2 sm:mb-0" > Request Dashboard < /h1> <
        div className = "flex items-center gap-2 text-sm text-gray-600" > {
            user && ( <
                >
                <
                span className = "font-semibold" > User ID: < /span> <
                span className = "truncate max-w-[150px] sm:max-w-none" > { user.uid } < /span> < /
                >
            )
        } <
        /div> < /
        div >

        { /* Action Button */ } <
        div className = "flex justify-end" >
        <
        button onClick = {
            () => setShowAddForm(!showAddForm)
        }
        className = "flex items-center gap-2 px-4 py-2 text-white bg-blue-600 rounded-full shadow-lg hover:bg-blue-700 transition-colors transform hover:scale-105" > { showAddForm ? < X size = { 20 } /> : <Plus size={20} / > } <
        span > { showAddForm ? 'Close Form' : 'Add New Request' } < /span> < /
        button > <
        /div>

        { /* Add Request Form */ } {
            showAddForm && ( <
                div className = "bg-white p-6 rounded-xl shadow-lg border border-gray-200" >
                <
                h2 className = "text-xl font-bold text-gray-800 mb-4" > Create a New Request < /h2> <
                form onSubmit = { handleAddRequest }
                className = "space-y-4" >
                <
                div className = "flex flex-col sm:flex-row sm:gap-4" >
                <
                div className = "flex-1" >
                <
                label htmlFor = "title"
                className = "block text-sm font-medium text-gray-700" > Request Title < /label> <
                input id = "title"
                type = "text"
                value = { newRequestTitle }
                onChange = {
                    (e) => setNewRequestTitle(e.target.value)
                }
                placeholder = "e.g., 'Order more fertilizer'"
                className = "mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 p-2" /
                >
                <
                /div> <
                div className = "flex-1 mt-4 sm:mt-0" >
                <
                label htmlFor = "department"
                className = "block text-sm font-medium text-gray-700" > Department < /label> <
                select id = "department"
                value = { newRequestDepartment }
                onChange = {
                    (e) => setNewRequestDepartment(e.target.value)
                }
                className = "mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 p-2" >
                <
                option value = "Biological Input Supply" > Biological Input Supply < /option> <
                option value = "Chemical Input Supply" > Chemical Input Supply < /option> < /
                select > <
                /div> < /
                div > <
                div className = "flex justify-end" >
                <
                button type = "submit"
                disabled = {!newRequestTitle || loading }
                className = "px-6 py-2 bg-green-600 text-white font-semibold rounded-full shadow-lg hover:bg-green-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed" > { loading ? 'Adding...' : 'Add Request' } <
                /button> < /
                div > <
                /form> < /
                div >
            )
        }

        { /* Status and Error Messages */ } {
            loading && ( <
                div className = "flex items-center justify-center p-4 text-blue-600" >
                <
                Loader2 className = "animate-spin mr-2"
                size = { 24 }
                /> <
                span > Loading requests... < /span> < /
                div >
            )
        } {
            error && ( <
                div className = "p-4 text-red-700 bg-red-100 rounded-md" >
                <
                p > { error } < /p> < /
                div >
            )
        }

        { /* Requests List */ } {
            !loading && requests.length === 0 && !error && ( <
                div className = "p-8 text-center text-gray-500 bg-white rounded-xl shadow-lg border border-gray-200" >
                <
                p > No requests found.Add a new one to get started! < /p> < /
                div >
            )
        }

        {
            !loading && requests.length > 0 && ( <
                div className = "bg-white rounded-xl shadow-lg border border-gray-200 overflow-hidden" >
                <
                div className = "overflow-x-auto" >
                <
                table className = "min-w-full divide-y divide-gray-200" >
                <
                thead className = "bg-gray-50" >
                <
                tr >
                <
                th scope = "col"
                className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" >
                Title <
                /th> <
                th scope = "col"
                className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" >
                Department <
                /th> <
                th scope = "col"
                className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" >
                Status <
                /th> <
                th scope = "col"
                className = "relative px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider" >
                Actions <
                /th> < /
                tr > <
                /thead> <
                tbody className = "bg-white divide-y divide-gray-200" > {
                    requests.map((request) => ( <
                        tr key = { request.id }
                        className = "hover:bg-gray-50 transition-colors" >
                        <
                        td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900" > { request.title } <
                        /td> <
                        td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { request.department } <
                        /td> <
                        td className = "px-6 py-4 whitespace-nowrap text-sm" >
                        <
                        span className = { `inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-semibold ${
                          request.status === 'completed'
                            ? 'bg-green-100 text-green-800'
                            : 'bg-yellow-100 text-yellow-800'
                        }` } > {
                            request.status === 'completed' ?
                            <
                            > < CheckCircle2 size = { 14 }
                            className = "mr-1" / > Completed < /> : <
                            > < CircleDashed size = { 14 }
                            className = "mr-1" / > Pending < />
                        } <
                        /span> < /
                        td > <
                        td className = "px-6 py-4 whitespace-nowrap text-right text-sm font-medium space-x-2" >
                        <
                        button onClick = {
                            () => handleUpdateStatus(request.id, request.status)
                        }
                        className = { `text-white px-3 py-1 rounded-full text-xs font-medium transition-colors ${
                            request.status === 'completed' ? 'bg-yellow-500 hover:bg-yellow-600' : 'bg-green-600 hover:bg-green-700'
                          }` } > { request.status === 'completed' ? 'Mark Pending' : 'Mark Complete' } <
                        /button> <
                        button onClick = {
                            () => {
                                setShowDeleteModal(true);
                                setRequestToDelete(request);
                            }
                        }
                        className = "text-red-600 hover:text-red-900" >
                        <
                        Trash2 size = { 18 }
                        /> < /
                        button > <
                        /td> < /
                        tr >
                    ))
                } <
                /tbody> < /
                table > <
                /div> < /
                div >
            )
        } <
        /div> < /
        div >
    );
};

export default App;
