import React, { useState, useEffect } from 'react';

// Header Component
function Header() {
    const [user, setUser] = useState(null);
    const [isDarkMode, setIsDarkMode] = useState(false);

    useEffect(function() {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const payload = JSON.parse(atob(token.split('.')[1]));
                setUser(payload);
            } catch (error) {
                console.error('Error parsing token:', error);
            }
        }
    }, []);

    function toggleDarkMode() {
        setIsDarkMode(!isDarkMode);
        document.documentElement.classList.toggle('dark');
    }

    function handleLogout() {
        localStorage.removeItem('token');
        window.location.href = '/login';
    }

    return (
        <header className="bg-white dark:bg-gray-900 border-b border-gray-200 dark:border-gray-800">
            <div className="mx-auto max-w-7xl px-4 py-4 flex items-center justify-between">
                <div>
                    <h1 className="text-xl font-semibold">Region Admin Dashboard</h1>
                    {user && <p className="text-sm text-gray-500">{user.region_name} Region</p>}
                </div>
                <div className="flex items-center gap-3">
                    <button onClick={toggleDarkMode} className="p-2 rounded-lg border border-gray-200 dark:border-gray-800">
                        {isDarkMode ? 'â˜€ï¸' : 'ðŸŒ™'}
                    </button>
                    <button onClick={handleLogout} className="px-3 py-1.5 text-sm rounded-lg border border-gray-200 dark:border-gray-800">
                        Logout
                    </button>
                </div>
            </div>
        </header>
    );
}

// StatCard Component
function StatCard({ label, value, sub }) {
    return (
        <div className="rounded-2xl border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900 p-4">
            <div className="text-2xl font-bold">{value || 0}</div>
            <div className="text-sm text-gray-500">{label}</div>
            {sub && <div className="text-xs text-gray-400 mt-1">{sub}</div>}
        </div>
    );
}

// Main RegionDashboard Component
function RegionDashboard() {
    const [user, setUser] = useState(null);
    const [activeTab, setActiveTab] = useState('overview');
    const [requests, setRequests] = useState([]);
    const [farmers, setFarmers] = useState([]);
    const [products, setProducts] = useState([]);
    const [zones, setZones] = useState([]);
    const [statusFilter, setStatusFilter] = useState('all');
    const [searchQuery, setSearchQuery] = useState('');
    const [farmerSearchQuery, setFarmerSearchQuery] = useState('');
    const [productView, setProductView] = useState('all');
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [selectedFarmer, setSelectedFarmer] = useState(null);
    const [showRequestModal, setShowRequestModal] = useState(false);
    const [showFarmerModal, setShowFarmerModal] = useState(false);

    useEffect(function() {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const payload = JSON.parse(atob(token.split('.')[1]));
                setUser(payload);
            } catch (error) {
                console.error('Error parsing token:', error);
            }
        }
        fetchRequests();
        fetchFarmers();
        fetchProducts();
        fetchZones();
    }, []);

    function fetchRequests() {
        const token = localStorage.getItem('token');
        fetch('http://localhost:5000/api/admins/requests', {
            headers: { 'Authorization': 'Bearer ' + token }
        })
        .then(response => response.json())
        .then(data => setRequests(data.requests || []))
        .catch(error => console.error('Error fetching requests:', error));
    }

    function fetchFarmers() {
        const token = localStorage.getItem('token');
        fetch('http://localhost:5000/api/admins/admins?role=Farmer', {
            headers: { 'Authorization': 'Bearer ' + token }
        })
        .then(response => response.json())
        .then(data => setFarmers(data.admins || []))
        .catch(error => console.error('Error fetching farmers:', error));
    }

    function fetchProducts() {
        const token = localStorage.getItem('token');
        fetch('http://localhost:5000/api/admins/products/' + productView, {
            headers: { 'Authorization': 'Bearer ' + token }
        })
        .then(response => response.json())
        .then(data => setProducts(data.products || []))
        .catch(error => console.error('Error fetching products:', error));
    }

    function fetchZones() {
        const token = localStorage.getItem('token');
        fetch('http://localhost:5000/api/admins/admins?role=Zone', {
            headers: { 'Authorization': 'Bearer ' + token }
        })
        .then(response => response.json())
        .then(data => setZones(data.admins || []))
        .catch(error => console.error('Error fetching zones:', error));
    }

    // Filter functions
    const filteredRequests = requests.filter(function(req) {
        const matchesSearch = !searchQuery || 
            req.farmer_name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
            req.product_name?.toLowerCase().includes(searchQuery.toLowerCase());

        if (statusFilter === 'all') return matchesSearch;
        if (statusFilter === 'pending') return matchesSearch && !req.region_status;
        if (statusFilter === 'accepted') return matchesSearch && req.region_status === 'Accepted';
        if (statusFilter === 'approved') return matchesSearch && req.region_status === 'Approved';
        if (statusFilter === 'rejected') return matchesSearch && req.region_status === 'Rejected';
        return matchesSearch;
    });

    const filteredFarmers = farmers.filter(function(farmer) {
        return !farmerSearchQuery || 
            farmer.full_name?.toLowerCase().includes(farmerSearchQuery.toLowerCase()) ||
            farmer.phone_number?.includes(farmerSearchQuery) ||
            farmer.zone_name?.toLowerCase().includes(farmerSearchQuery.toLowerCase()) ||
            farmer.woreda_name?.toLowerCase().includes(farmerSearchQuery.toLowerCase()) ||
            farmer.kebele_name?.toLowerCase().includes(farmerSearchQuery.toLowerCase());
    });

    // Stats calculations
    const stats = {
        totalRequests: requests.length,
        pendingRequests: requests.filter(r => !r.region_status).length,
        acceptedRequests: requests.filter(r => r.region_status === 'Accepted').length,
        rejectedRequests: requests.filter(r => r.region_status === 'Rejected').length,
        regionApproved: requests.filter(r => r.region_status === 'Approved').length,
        regionAccepted: requests.filter(r => r.region_status === 'Accepted').length,
        totalProducts: products.length,
        totalFarmers: farmers.length,
        totalZones: zones.length
    };

    function updateRequestStatus(requestId, newStatus) {
        const token = localStorage.getItem('token');
        fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + token
            },
            body: JSON.stringify({ status: newStatus })
        })
        .then(response => response.json())
        .then(data => {
            if (data.success) {
                fetchRequests();
                setShowRequestModal(false);
            }
        })
        .catch(error => console.error('Error updating request status:', error));
    }

    return (
        <div className="min-h-screen bg-gray-50 dark:bg-gray-950 text-gray-900 dark:text-white">
            <Header />

            <main className="mx-auto max-w-7xl px-4 py-6">
                {/* Tabs */}
                <div className="mb-6 flex flex-wrap gap-2">
                    {['overview', 'farmers', 'requests', 'products', 'zones'].map(function(tab) {
                        return (
                            <button 
                                key={tab}
                                onClick={function() { setActiveTab(tab); }}
                                className={'rounded-xl px-4 py-2 text-sm capitalize border ' + (activeTab === tab ? 'bg-gray-900 text-white border-gray-900 dark:bg-white dark:text-gray-900' : 'bg-white dark:bg-gray-900 text-gray-700 dark:text-gray-200 border-gray-200 dark:border-gray-800 hover:bg-gray-100 dark:hover:bg-gray-800')}
                            >
                                {tab}
                            </button>
                        );
                    })}
                </div>

                {/* OVERVIEW */}
                {activeTab === 'overview' ? (
                    <section className="grid gap-4 sm:grid-cols-2 lg:grid-cols-4">
                        <StatCard 
                            label="Total Requests"
                            value={stats.totalRequests}
                        />
                        <StatCard 
                            label="Pending (overall)"
                            value={stats.pendingRequests}
                        />
                        <StatCard 
                            label="Accepted (overall)"
                            value={stats.acceptedRequests}
                        />
                        <StatCard 
                            label="Rejected (overall)"
                            value={stats.rejectedRequests}
                        />
                        <StatCard 
                            label="Region Approved"
                            value={stats.regionApproved}
                            sub="region_status === 'Approved'"
                        />
                        <StatCard 
                            label="Region Accepted"
                            value={stats.regionAccepted}
                            sub="region_status === 'Accepted'"
                        />
                        <StatCard 
                            label="Products"
                            value={stats.totalProducts}
                            sub={productView.toUpperCase()}
                        />
                        <StatCard 
                            label="Total Farmers"
                            value={stats.totalFarmers}
                            sub="Within Region Scope"
                        />
                        <StatCard 
                            label="Zone Admins"
                            value={stats.totalZones}
                        />
                    </section>
                ) : null}

                {/* FARMERS */}
                {activeTab === 'farmers' ? (
                    <section className="space-y-4">
                        <div className="flex flex-col gap-3 sm:flex-row sm:items-center sm:justify-between">
                            <div>
                                <div className="text-lg font-semibold">Farmers in {user?.region_name} Region</div>
                                <div className="text-sm text-gray-500">View all farmer profiles within your region scope</div>
                            </div>
                            <div className="flex items-center gap-2">
                                <input 
                                    value={farmerSearchQuery}
                                    onChange={function(e) { setFarmerSearchQuery(e.target.value); }}
                                    placeholder="Search farmers... (name, phone, location)"
                                    className="rounded-xl border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900 px-3 py-1.5 text-sm w-72"
                                />
                                <button 
                                    onClick={fetchFarmers}
                                    className="rounded-xl px-3 py-1.5 text-sm border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900"
                                >
                                    Refresh
                                </button>
                            </div>
                        </div>

                        <div className="overflow-x-auto rounded-2xl border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900">
                            <table className="min-w-full text-sm">
                                <thead className="bg-gray-50 dark:bg-gray-950/40">
                                    <tr>
                                        <th className="text-left px-4 py-2">#</th>
                                        <th className="text-left px-4 py-2">Name</th>
                                        <th className="text-left px-4 py-2">Phone</th>
                                        <th className="text-left px-4 py-2">Location</th>
                                        <th className="text-left px-4 py-2">Registered</th>
                                        <th className="text-left px-4 py-2">Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {filteredFarmers.length === 0 ? (
                                        <tr>
                                            <td colSpan={6} className="text-center px-4 py-6 text-gray-500">No farmers found</td>
                                        </tr>
                                    ) : filteredFarmers.map(function(f) {
                                        return (
                                            <tr key={f.id} className="border-t border-gray-100 dark:border-gray-800">
                                                <td className="px-4 py-2">{f.id}</td>
                                                <td className="px-4 py-2">
                                                    <div className="font-medium">{f.full_name}</div>
                                                </td>
                                                <td className="px-4 py-2">{f.phone_number}</td>
                                                <td className="px-4 py-2">
                                                    <div className="text-xs text-gray-500">{f.zone_name} / {f.woreda_name} / {f.kebele_name}</div>
                                                </td>
                                                <td className="px-4 py-2">{f.created_at ? new Date(f.created_at).toLocaleDateString() : '-'}</td>
                                                <td className="px-4 py-2">
                                                    <button 
                                                        onClick={function() {
                                                            setSelectedFarmer(f);
                                                            setShowFarmerModal(true);
                                                        }}
                                                        className="px-2 py-1 rounded-lg border border-gray-200 dark:border-gray-800"
                                                    >
                                                        View Profile
                                                    </button>
                                                </td>
                                            </tr>
                                        );
                                    })}
                                </tbody>
                            </table>
                        </div>
                    </section>
                ) : null}

                {/* REQUESTS */}
                {activeTab === 'requests' ? (
                    <section className="space-y-4">
                        <div className="flex flex-col gap-3 sm:flex-row sm:items-center sm:justify-between">
                            <div className="flex items-center gap-2">
                                <button 
                                    onClick={function() { setStatusFilter('all'); }}
                                    className={'rounded-xl px-3 py-1.5 text-sm border ' + (statusFilter === 'all' ? 'bg-gray-900 text-white border-gray-900 dark:bg-white dark:text-gray-900' : 'bg-white dark:bg-gray-900 text-gray-700 dark:text-gray-200 border-gray-200 dark:border-gray-800')}
                                >
                                    All
                                </button>
                                <button 
                                    onClick={function() { setStatusFilter('pending'); }}
                                    className={'rounded-xl px-3 py-1.5 text-sm border ' + (statusFilter === 'pending' ? 'bg-gray-900 text-white border-gray-900 dark:bg-white dark:text-gray-900' : 'bg-white dark:bg-gray-900 text-gray-700 dark:text-gray-200 border-gray-200 dark:border-gray-800')}
                                >
                                    Pending
                                </button>
                                <button 
                                    onClick={function() { setStatusFilter('accepted'); }}
                                    className={'rounded-xl px-3 py-1.5 text-sm border ' + (statusFilter === 'accepted' ? 'bg-gray-900 text-white border-gray-900 dark:bg-white dark:text-gray-900' : 'bg-white dark:bg-gray-900 text-gray-700 dark:text-gray-200 border-gray-200 dark:border-gray-800')}
                                >
                                    Accepted
                                </button>
                                <button 
                                    onClick={function() { setStatusFilter('approved'); }}
                                    className={'rounded-xl px-3 py-1.5 text-sm border ' + (statusFilter === 'approved' ? 'bg-gray-900 text-white border-gray-900 dark:bg-white dark:text-gray-900' : 'bg-white dark:bg-gray-900 text-gray-700 dark:text-gray-200 border-gray-200 dark:border-gray-800')}
                                >
                                    Approved
                                </button>
                                <button 
                                    onClick={function() { setStatusFilter('rejected'); }}
                                    className={'rounded-xl px-3 py-1.5 text-sm border ' + (statusFilter === 'rejected' ? 'bg-gray-900 text-white border-gray-900 dark:bg-white dark:text-gray-900' : 'bg-white dark:bg-gray-900 text-gray-700 dark:text-gray-200 border-gray-200 dark:border-gray-800')}
                                >
                                    Rejected
                                </button>
                            </div>

                            <div className="flex items-center gap-2">
                                <input 
                                    value={searchQuery}
                                    onChange={function(e) { setSearchQuery(e.target.value); }}
                                    placeholder="Search requests..."
                                    className="rounded-xl border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900 px-3 py-1.5 text-sm w-72"
                                />
                                <button 
                                    onClick={fetchRequests}
                                    className="rounded-xl px-3 py-1.5 text-sm border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900"
                                >
                                    Refresh
                                </button>
                            </div>
                        </div>

                        <div className="overflow-x-auto rounded-2xl border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900">
                            <table className="min-w-full text-sm">
                                <thead className="bg-gray-50 dark:bg-gray-950/40">
                                    <tr>
                                        <th className="text-left px-4 py-2">#</th>
                                        <th className="text-left px-4 py-2">Farmer</th>
                                        <th className="text-left px-4 py-2">Product</th>
                                        <th className="text-left px-4 py-2">Quantity</th>
                                        <th className="text-left px-4 py-2">Region Status</th>
                                        <th className="text-left px-4 py-2">Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {filteredRequests.length === 0 ? (
                                        <tr>
                                            <td colSpan={6} className="text-center px-4 py-6 text-gray-500">No requests found</td>
                                        </tr>
                                    ) : filteredRequests.map(function(req) {
                                        return (
                                            <tr key={req.id} className="border-t border-gray-100 dark:border-gray-800">
                                                <td className="px-4 py-2">{req.id}</td>
                                                <td className="px-4 py-2">{req.farmer_name}</td>
                                                <td className="px-4 py-2">{req.product_name}</td>
                                                <td className="px-4 py-2">{req.quantity} {req.unit}</td>
                                                <td className="px-4 py-2">
                                                    <span className={'px-2 py-1 rounded-full text-xs ' + 
                                                        (req.region_status === 'Approved' ? 'bg-green-100 text-green-800' :
                                                         req.region_status === 'Accepted' ? 'bg-blue-100 text-blue-800' :
                                                         req.region_status === 'Rejected' ? 'bg-red-100 text-red-800' :
                                                         'bg-yellow-100 text-yellow-800')}>
                                                        {req.region_status || 'Pending'}
                                                    </span>
                                                </td>
                                                <td className="px-4 py-2">
                                                    <div className="flex gap-1">
                                                        {!req.region_status && (
                                                            <>
                                                                <button 
                                                                    onClick={function() { updateRequestStatus(req.id, 'Accepted'); }}
                                                                    className="px-2 py-1 text-xs rounded bg-blue-500 text-white"
                                                                >
                                                                    Accept
                                                                </button>
                                                                <button 
                                                                    onClick={function() { updateRequestStatus(req.id, 'Rejected'); }}
                                                                    className="px-2 py-1 text-xs rounded bg-red-500 text-white"
                                                                >
                                                                    Reject
                                                                </button>
                                                            </>
                                                        )}
                                                        {req.region_status === 'Accepted' && (
                                                            <button 
                                                                onClick={function() { updateRequestStatus(req.id, 'Approved'); }}
                                                                className="px-2 py-1 text-xs rounded bg-green-500 text-white"
                                                            >
                                                                Approve
                                                            </button>
                                                        )}
                                                        <button 
                                                            onClick={function() {
                                                                setSelectedRequest(req);
                                                                setShowRequestModal(true);
                                                            }}
                                                            className="px-2 py-1 text-xs rounded border border-gray-200 dark:border-gray-800"
                                                        >
                                                            View
                                                        </button>
                                                    </div>
                                                </td>
                                            </tr>
                                        );
                                    })}
                                </tbody>
                            </table>
                        </div>
                    </section>
                ) : null}

                {/* PRODUCTS */}
                {activeTab === 'products' ? (
                    <section className="space-y-4">
                        <div className="flex flex-col gap-3 sm:flex-row sm:items-center sm:justify-between">
                            <div className="flex items-center gap-2">
                                <button 
                                    onClick={function() { setProductView('all'); fetchProducts(); }}
                                    className={'rounded-xl px-3 py-1.5 text-sm border ' + (productView === 'all' ? 'bg-gray-900 text-white border-gray-900' : 'bg-white dark:bg-gray-900 border-gray-200 dark:border-gray-800')}
                                >
                                    All Products
                                </button>
                                <button 
                                    onClick={function() { setProductView('mine'); fetchProducts(); }}
                                    className={'rounded-xl px-3 py-1.5 text-sm border ' + (productView === 'mine' ? 'bg-gray-900 text-white border-gray-900' : 'bg-white dark:bg-gray-900 border-gray-200 dark:border-gray-800')}
                                >
                                    My Products
                                </button>
                                <button 
                                    onClick={function() { setProductView('others'); fetchProducts(); }}
                                    className={'rounded-xl px-3 py-1.5 text-sm border ' + (productView === 'others' ? 'bg-gray-900 text-white border-gray-900' : 'bg-white dark:bg-gray-900 border-gray-200 dark:border-gray-800')}
                                >
                                    Others' Products
                                </button>
                            </div>
                        </div>

                        <div className="overflow-x-auto rounded-2xl border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900">
                            <table className="min-w-full text-sm">
                                <thead className="bg-gray-50 dark:bg-gray-950/40">
                                    <tr>
                                        <th className="text-left px-4 py-2">Product</th>
                                        <th className="text-left px-4 py-2">Category</th>
                                        <th className="text-left px-4 py-2">Created By</th>
                                        <th className="text-left px-4 py-2">Created</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {products.length === 0 ? (
                                        <tr>
                                            <td colSpan={4} className="text-center px-4 py-6 text-gray-500">No products found</td>
                                        </tr>
                                    ) : products.map(function(product) {
                                        return (
                                            <tr key={product.id} className="border-t border-gray-100 dark:border-gray-800">
                                                <td className="px-4 py-2">{product.name}</td>
                                                <td className="px-4 py-2">{product.category}</td>
                                                <td className="px-4 py-2">{product.created_by_name}</td>
                                                <td className="px-4 py-2">{product.created_at ? new Date(product.created_at).toLocaleDateString() : '-'}</td>
                                            </tr>
                                        );
                                    })}
                                </tbody>
                            </table>
                        </div>
                    </section>
                ) : null}

                {/* ZONES */}
                {activeTab === 'zones' ? (
                    <section className="space-y-4">
                        <div className="flex flex-col gap-3 sm:flex-row sm:items-center sm:justify-between">
                            <div>
                                <div className="text-lg font-semibold">Zone Admins in {user?.region_name} Region</div>
                                <div className="text-sm text-gray-500">Manage zone administrators within your region</div>
                            </div>
                        </div>

                        <div className="overflow-x-auto rounded-2xl border border-gray-200 dark:border-gray-800 bg-white dark:bg-gray-900">
                            <table className="min-w-full text-sm">
                                <thead className="bg-gray-50 dark:bg-gray-950/40">
                                    <tr>
                                        <th className="text-left px-4 py-2">#</th>
                                        <th className="text-left px-4 py-2">Name</th>
                                        <th className="text-left px-4 py-2">Zone</th>
                                        <th className="text-left px-4 py-2">Phone</th>
                                        <th className="text-left px-4 py-2">Created</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {zones.length === 0 ? (
                                        <tr>
                                            <td colSpan={5} className="text-center px-4 py-6 text-gray-500">No zone admins found</td>
                                        </tr>
                                    ) : zones.map(function(zone) {
                                        return (
                                            <tr key={zone.id} className="border-t border-gray-100 dark:border-gray-800">
                                                <td className="px-4 py-2">{zone.id}</td>
                                                <td className="px-4 py-2">{zone.full_name}</td>
                                                <td className="px-4 py-2">{zone.zone_name}</td>
                                                <td className="px-4 py-2">{zone.phone_number}</td>
                                                <td className="px-4 py-2">{zone.created_at ? new Date(zone.created_at).toLocaleDateString() : '-'}</td>
                                            </tr>
                                        );
                                    })}
                                </tbody>
                            </table>
                        </div>
                    </section>
                ) : null}
            </main>
        </div>
    );
}

export default RegionDashboard;
