import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
    Moon, 
    Sun, 
    Package, 
    Truck, 
    ShoppingCart, 
    LogOut, 
    Plus, 
    Edit, 
    Trash2, 
    Eye, 
    Check,
    MapPin,
    Calendar,
    User,
    Building
} from 'lucide-react';

const FarmerDashboard = () => {
    const [activeTab, setActiveTab] = useState('requests');
    const [user, setUser] = useState(null);
    const [darkMode, setDarkMode] = useState(false);
    const [language, setLanguage] = useState('en'); // 'en' for English, 'am' for Amharic
    const [statusFilter, setStatusFilter] = useState('all'); // Filter for request status
    const navigate = useNavigate();

    // Request Management State
    const [requests, setRequests] = useState([]);
    const [showRequestModal, setShowRequestModal] = useState(false);
    const [showStatusModal, setShowStatusModal] = useState(false);
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [editingRequest, setEditingRequest] = useState(null);
    const [requestForm, setRequestForm] = useState({
        product_id: '',
        quantity: '',
        note: ''
    });

    // Products State
    const [products, setProducts] = useState([]);

    // Deliveries State
    const [deliveries, setDeliveries] = useState([]);
    const [showDeliveryModal, setShowDeliveryModal] = useState(false);
    const [confirmingDelivery, setConfirmingDelivery] = useState(null);
    const [deliveryNote, setDeliveryNote] = useState('');

    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Farmer' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
        fetchDashboardData();

        // Load preferences
        const savedDarkMode = localStorage.getItem('darkMode') === 'true';
        const savedLanguage = localStorage.getItem('language') || 'en';
        setDarkMode(savedDarkMode);
        setLanguage(savedLanguage);
    }, [navigate]);

    useEffect(() => {
        // Apply dark mode to the entire document
        if (darkMode) {
            document.documentElement.classList.add('dark');
        } else {
            document.documentElement.classList.remove('dark');
        }
    }, [darkMode]);

    const fetchDashboardData = async() => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');

            // Fetch ALL products available in the country
            const productsResponse = await fetch('http://localhost:5000/api/farmers/products', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
            } else {
                console.error('Failed to fetch products');
                setError('Failed to load products');
            }

            // Fetch farmer's requests
            const requestsResponse = await fetch('http://localhost:5000/api/farmers/requests', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                setRequests(requestsData);
            } else {
                console.error('Failed to fetch requests');
            }

            // Fetch farmer's deliveries
            const deliveriesResponse = await fetch('http://localhost:5000/api/farmers/deliveries', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (deliveriesResponse.ok) {
                const deliveriesData = await deliveriesResponse.json();
                setDeliveries(deliveriesData);
            } else {
                console.error('Failed to fetch deliveries');
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
            setError('Error loading dashboard data');
        } finally {
            setLoading(false);
        }
    };

    const fetchRequestStatusDetail = async(requestId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/farmers/request/${requestId}/status`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                const statusData = await response.json();
                setSelectedRequest(statusData);
                setShowStatusModal(true);
            } else {
                alert('Failed to fetch request status details');
            }
        } catch (error) {
            console.error('Error fetching status details:', error);
            alert('Error fetching status details');
        }
    };

    const toggleDarkMode = () => {
        const newDarkMode = !darkMode;
        setDarkMode(newDarkMode);
        localStorage.setItem('darkMode', newDarkMode.toString());
    };

    const toggleLanguage = () => {
        const newLanguage = language === 'en' ? 'am' : 'en';
        setLanguage(newLanguage);
        localStorage.setItem('language', newLanguage);
    };

    // Translation object
    const translations = {
        en: {
            farmerPortal: 'Farmer Portal',
            myRequests: 'My Requests',
            myDeliveries: 'My Deliveries',
            availableProducts: 'Available Products',
            logout: 'Logout',
            welcome: 'Welcome',
            farmerDashboard: 'Farmer Dashboard',
            manageRequests: 'Manage your product requests',
            trackDeliveries: 'Track your confirmed deliveries',
            productsAvailable: 'Products available across the country',
            newRequest: 'New Request',
            viewDetails: 'View Details',
            confirmDelivery: 'Confirm Delivery',
            edit: 'Edit',
            delete: 'Delete',
            requestProduct: 'Request Product',
            noRequests: 'No requests found. Create your first request!',
            noDeliveries: 'No confirmed deliveries yet.',
            noProducts: 'No products available at the moment.',
            category: 'Category',
            price: 'Price',
            quantity: 'Quantity',
            date: 'Date',
            location: 'Location',
            subCategory: 'Sub-category',
            amount: 'Amount',
            manufacturer: 'Manufacturer',
            expires: 'Expires',
            added: 'Added',
            confirmed: 'Confirmed',
            delivered: 'Delivered',
            all: 'All',
            pending: 'Pending',
            approved: 'Approved',
            accepted: 'Accepted',
            rejected: 'Rejected',
            filterBy: 'Filter by Status',
            birr: 'Birr',
            units: 'units'
        },
        am: {
            farmerPortal: 'á‹¨áŒˆá‰ áˆ¬ áˆ˜áŒá‰¢á‹«',
            myRequests: 'á‹¨áŠ¥áŠ” áŒ¥á‹«á‰„á‹Žá‰½',
            myDeliveries: 'á‹¨áŠ¥áŠ” áˆ˜áˆ‹áŠªá‹«á‹Žá‰½',
            availableProducts: 'á‹«áˆ‰ áˆáˆ­á‰¶á‰½',
            logout: 'á‹áŒ£',
            welcome: 'áŠ¥áŠ•áŠ³áŠ• á‹°áˆ…áŠ“ áˆ˜áŒ£áˆ…',
            farmerDashboard: 'á‹¨áŒˆá‰ áˆ¬ á‹³áˆ½á‰¦áˆ­á‹µ',
            manageRequests: 'á‹¨áˆáˆ­á‰µ áŒ¥á‹«á‰„á‹Žá‰½á‹ŽáŠ• á‹«áˆµá‰°á‹³á‹µáˆ©',
            trackDeliveries: 'á‹¨á‰°áˆ¨áŒ‹áŒˆáŒ¡ áˆ˜áˆ‹áŠªá‹«á‹Žá‰½á‹ŽáŠ• á‹­áŠ¨á‰³á‰°áˆ‰',
            productsAvailable: 'á‰ áˆ€áŒˆáˆªá‰± á‹áˆµáŒ¥ á‹«áˆ‰ áˆáˆ­á‰¶á‰½',
            newRequest: 'áŠ á‹²áˆµ áŒ¥á‹«á‰„',
            viewDetails: 'á‹áˆ­á‹áˆ­ á‹­áˆ˜áˆáŠ¨á‰±',
            confirmDelivery: 'áˆ˜áˆ‹áŠªá‹« á‹«áˆ¨áŒ‹áŒáŒ¡',
            edit: 'áŠ áˆ­á‰µá‹•',
            delete: 'áˆ°áˆ­á‹',
            requestProduct: 'áˆáˆ­á‰µ áŒ á‹­á‰…',
            noRequests: 'áˆáŠ•áˆ áŒ¥á‹«á‰„á‹Žá‰½ áŠ áˆá‰°áŒˆáŠ™áˆá¢ á‹¨áˆ˜áŒ€áˆ˜áˆªá‹« áŒ¥á‹«á‰„á‹ŽáŠ• á‹­ááŒ áˆ©!',
            noDeliveries: 'áŒˆáŠ“ á‹¨á‰°áˆ¨áŒ‹áŒˆáŒ  áˆ˜áˆ‹áŠªá‹« á‹¨áˆˆáˆá¢',
            noProducts: 'á‰ áŠ áˆáŠ‘ áŒŠá‹œ áˆáŠ•áˆ áˆáˆ­á‰¶á‰½ áŠ á‹­áŒˆáŠ™áˆá¢',
            category: 'áˆá‹µá‰¥',
            price: 'á‹‹áŒ‹',
            quantity: 'áˆ˜áŒ áŠ•',
            date: 'á‰€áŠ•',
            location: 'áŠ áŠ«á‰£á‰¢',
            subCategory: 'áŠ•á‹‘áˆµ áˆá‹µá‰¥',
            amount: 'áˆ˜áŒ áŠ•',
            manufacturer: 'áŠ áˆáˆ«á‰½',
            expires: 'á‹«áˆˆá‰€á‰ á‰µ',
            added: 'á‹¨á‰°áŒ¨áˆ˜áˆ¨',
            confirmed: 'á‹¨á‰°áˆ¨áŒ‹áŒˆáŒ ',
            delivered: 'á‹¨á‰°áˆ‹áŠ¨',
            all: 'áˆáˆ‰áˆ',
            pending: 'á‰ áˆ˜áŒ á‰£á‰ á‰… áˆ‹á‹­',
            approved: 'á‹¨á‰°áˆá‰€á‹°',
            accepted: 'á‹¨á‰°á‰€á‰ áˆˆ',
            rejected: 'á‹¨á‰°áŠ¨áˆˆáŠ¨áˆˆ',
            filterBy: 'á‰ áˆáŠ”á‰³ áŠ áŒ£áˆ«',
