                                onChange = {
                                    (e) => setProductForm({...productForm, category: e.target.value }) }
                                className = "w-full border border-gray-300 rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600" / > {
                                    productErrors.category && < p className = "text-red-500 text-sm mt-1" > { productErrors.category } < /p>} <
                                    /div> <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700 dark:text-gray-300 mb-2" > Amount < /label> <
                                    input type = "number"
                                    required
                                    value = { productForm.amount }
                                    onChange = {
                                        (e) => setProductForm({...productForm, amount: e.target.value }) }
                                    className = "w-full border border-gray-300 rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600" / > {
                                        productErrors.amount && < p className = "text-red-500 text-sm mt-1" > { productErrors.amount } < /p>} <
                                        /div> <
                                        div className = "mb-4" >
                                        <
                                        label className = "block text-gray-700 dark:text-gray-300 mb-2" > Price < /label> <
                                        input type = "number"
                                        required
                                        value = { productForm.price }
                                        onChange = {
                                            (e) => setProductForm({...productForm, price: e.target.value }) }
                                        className = "w-full border border-gray-300 rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600" / > {
                                            productErrors.price && < p className = "text-red-500 text-sm mt-1" > { productErrors.price } < /p>} <
                                            /div> <
                                            div className = "flex justify-end space-x-2" >
                                            <
                                            button type = "button"
                                            onClick = {
                                                () => {
                                                    setShowProductModal(false);
                                                    setEditingProduct(null);
                                                    setProductForm({ name: '', description: '', category: '', amount: '', price: '' });
                                                    setProductErrors({});
                                                }
                                            }
                                            className = "bg-gray-300 hover:bg-gray-400 text-gray-800 py-2 px-4 rounded dark:bg-gray-600 dark:hover:bg-gray-700 dark:text-white" >
                                            Cancel <
                                            /button> <
                                            button type = "submit"
                                            className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" > { editingProduct ? 'Update' : 'Add' } <
                                            /button> <
                                            /div> <
                                            /form> <
                                            /div> <
                                            /div>
                                        )
                                    }

                                    { /* Request Status Update Modal */ } {
                                        showRequestModal && selectedRequest && ( <
                                            div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                                            <
                                            div className = "bg-white dark:bg-gray-800 rounded-lg p-6 w-full max-w-2xl" >
                                            <
                                            h2 className = "text-xl font-semibold mb-4 dark:text-white" > { requestStatus ? `Update Request Status to ${requestStatus}` : 'Request Details' } <
                                            /h2>

                                            <
                                            div className = "request-details mb-6" >
                                            <
                                            h3 className = "font-semibold dark:text-white" > Request Information < /h3> <
                                            div className = "grid grid-cols-1 md:grid-cols-2 gap-4 mt-2" >
                                            <
                                            div >
                                            <
                                            p className = "text-gray-600 dark:text-gray-400" > < strong > Farmer: < /strong> {selectedRequest.farmer_name}</p >
                                            <
                                            p className = "text-gray-600 dark:text-gray-400" > < strong > Product: < /strong> {selectedRequest.product_name}</p >
                                            <
                                            p className = "text-gray-600 dark:text-gray-400" > < strong > Kebele: < /strong> {selectedRequest.kebele_name}</p >
                                            <
                                            /div> <
                                            div >
                                            <
                                            p className = "text-gray-600 dark:text-gray-400" > < strong > Phone: < /strong> {selectedRequest.farmer_phone}</p >
                                            <
                                            p className = "text-gray-600 dark:text-gray-400" > < strong > Request Date: < /strong> {new Date(selectedRequest.created_at).toLocaleString()}</p >
                                            <
                                            p className = "text-gray-600 dark:text-gray-400" > < strong > Current Status: < /strong> {selectedRequest.status}</p >
                                            <
                                            /div> <
                                            /div> <
                                            /div>

                                            <
                                            div className = "approval-status mb-6" >
                                            <
                                            h3 className = "font-semibold dark:text-white" > Approval Status by Level < /h3> { getLevelStatus('kebele', selectedRequest) } { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) } <
                                            /div>

                                            {
                                                requestStatus && ( <
                                                    div className = "status-update mb-6" >
                                                    <
                                                    h3 className = "font-semibold dark:text-white" > Update Status < /h3> <
                                                    div className = "mt-2" >
                                                    <
                                                    label className = "block text-gray-700 dark:text-gray-300 mb-2" > Decision Reason(Optional): < /label> <
                                                    textarea value = { decisionReason }
                                                    onChange = {
                                                        (e) => setDecisionReason(e.target.value) }
                                                    className = "w-full border border-gray-300 rounded p-2 dark:bg-gray-700 dark:text-white dark:border-gray-600"
                                                    placeholder = "Enter the reason for your decision" > < /textarea> <
                                                    /div> <
                                                    /div>
                                                )
                                            }

                                            <
                                            div className = "flex justify-end space-x-2" >
                                            <
                                            button onClick = {
                                                () => {
                                                    setShowRequestModal(false);
                                                    setSelectedRequest(null);
                                                    setRequestStatus('');
                                                    setDecisionReason('');
                                                }
                                            }
                                            className = "bg-gray-300 hover:bg-gray-400 text-gray-800 py-2 px-4 rounded dark:bg-gray-600 dark:hover:bg-gray-700 dark:text-white" >
                                            Close <
                                            /button> {
                                                requestStatus && ( <
                                                    button onClick = { confirmStatusUpdate }
                                                    className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" >
                                                    Confirm { requestStatus } <
                                                    /button>
                                                )
                                            } <
                                            /div> <
                                            /div> <
                                            /div>
                                        )
                                    } <
                                    /div>
                                );
                            };

                            export default WoredaDashboard;
