import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './WoredaDashboard.css';

const WoredaDashboard = () => {
        const [activeTab, setActiveTab] = useState('overview');
        const [user, setUser] = useState(null);
        const navigate = useNavigate();

        // Product Management State
        const [products, setProducts] = useState([]);
        const [showProductForm, setShowProductForm] = useState(false);
        const [editingProduct, setEditingProduct] = useState(null);
        const [productForm, setProductForm] = useState({
            name: '',
            category: '',
            amount: '',
            description: ''
        });

        // Admin/Farmer Management State
        const [admins, setAdmins] = useState([]);
        const [showAdminForm, setShowAdminForm] = useState(false);
        const [editingAdmin, setEditingAdmin] = useState(null);
        const [adminForm, setAdminForm] = useState({
            fullName: '',
            phoneNumber: '',
            password: '',
            confirmPassword: '',
            role: 'Kebele',
            region_name: '',
            zone_name: '',
            woreda_name: '',
            kebele_name: ''
        });
        const [adminError, setAdminError] = useState('');

        // Requests Management State
        const [requests, setRequests] = useState([]);
        const [selectedRequest, setSelectedRequest] = useState(null);
        const [requestStatus, setRequestStatus] = useState('pending');

        // Stats State
        const [stats, setStats] = useState({
            totalRequests: 0,
            pendingRequests: 0,
            approvedRequests: 0,
            rejectedRequests: 0,
            totalAdmins: 0,
            totalFarmers: 0,
            totalProducts: 0
        });

        useEffect(() => {
            // Check authentication
            const token = localStorage.getItem('token');
            const userData = JSON.parse(localStorage.getItem('user') || 'null');
            const userType = localStorage.getItem('userType');

            if (!token || userType !== 'Woreda' || !userData) {
                navigate('/login');
                return;
            }

            setUser(userData);
            fetchDashboardData();
        }, [navigate]);

        const fetchDashboardData = async() => {
            try {
                const token = localStorage.getItem('token');

                // Fetch products
                const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (productsResponse.ok) {
                    const productsData = await productsResponse.json();
                    setProducts(productsData);
                    setStats(prev => ({...prev, totalProducts: productsData.length }));
                }

                // Fetch admins and farmers in scope
                const adminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (adminsResponse.ok) {
                    const adminsData = await adminsResponse.json();
                    // Filter to only show Kebele admins and farmers (Woreda admin can create Kebele admins and farmers)
                    const kebeleAdmins = adminsData.filter(admin => admin.role === 'Kebele');
                    const farmers = adminsData.filter(admin => admin.type === 'farmer');

                    setAdmins([...kebeleAdmins, ...farmers]);
                    setStats(prev => ({
                        ...prev,
                        totalAdmins: kebeleAdmins.length,
                        totalFarmers: farmers.length
                    }));
                }

                // Fetch requests
                const requestsResponse = await fetch('http://localhost:5000/api/admins/getrequests', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (requestsResponse.ok) {
                    const requestsData = await requestsResponse.json();
                    setRequests(requestsData);

                    // Calculate stats from requests data
                    const totalRequests = requestsData.length;
                    const pendingRequests = requestsData.filter(req => req.status === 'pending').length;
                    const approvedRequests = requestsData.filter(req => req.status === 'accepted').length;
                    const rejectedRequests = requestsData.filter(req => req.status === 'rejected').length;

                    setStats(prev => ({
                        ...prev,
                        totalRequests,
                        pendingRequests,
                        approvedRequests,
                        rejectedRequests
                    }));
                }

            } catch (error) {
                console.error('Error fetching dashboard data:', error);
            }
        };

        const handleLogout = () => {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            localStorage.removeItem('userType');
            navigate('/login');
        };

        // Product Management Functions
        const handleProductSubmit = async(e) => {
            e.preventDefault();
            try {
                const token = localStorage.getItem('token');
                const url = editingProduct ?
                    `http://localhost:5000/api/admins/editproduct/${editingProduct.id}` :
                    'http://localhost:5000/api/admins/addproduct';

                const method = editingProduct ? 'PUT' : 'POST';

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(productForm)
                });

                if (response.ok) {
                    setShowProductForm(false);
                    setEditingProduct(null);
                    setProductForm({ name: '', category: '', amount: '', description: '' });
                    fetchDashboardData();
                } else {
                    const errorData = await response.json();
                    console.error('Error saving product:', errorData.message);
                    alert(errorData.message || 'Error saving product');
                }
            } catch (error) {
                console.error('Error saving product:', error);
                alert('Error saving product');
            }
        };

        const deleteProduct = async(productId) => {
            if (!window.confirm('Are you sure you want to delete this product?')) {
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/deleteproduct/${productId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                } else {
                    const errorData = await response.json();
                    console.error('Error deleting product:', errorData.message);
                    alert(errorData.message || 'Error deleting product');
                }
            } catch (error) {
                console.error('Error deleting product:', error);
                alert('Error deleting product');
            }
        };

        // Admin/Farmer Management Functions
        const handleAdminSubmit = async(e) => {
            e.preventDefault();
            setAdminError('');

            if (adminForm.password !== adminForm.confirmPassword && !editingAdmin) {
                setAdminError('Passwords do not match');
                return;
            }

            try {
                const token = localStorage.getItem('token');
                let url, method;

                if (editingAdmin) {
                    // Update existing admin
                    url = `http://localhost:5000/api/admins/edit/${editingAdmin.id}`;
                    method = 'PUT';
                } else {
                    // Create new admin or farmer
                    if (adminForm.role === 'Farmer') {
                        url = 'http://localhost:5000/api/admins/createfarmer';
                    } else {
                        url = 'http://localhost:5000/api/admins/register';
                    }
                    method = 'POST';
                }

                // Prepare data for submission (don't send password if editing and password is empty)
                const submitData = {...adminForm };
                if (editingAdmin && !submitData.password) {
                    delete submitData.password;
                    delete submitData.confirmPassword;
                }

                // Set location data based on user's scope
                submitData.region_name = user.region_name;
                submitData.zone_name = user.zone_name;
                submitData.woreda_name = user.woreda_name;

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(submitData)
                });

                const data = await response.json();

                if (response.ok) {
                    setShowAdminForm(false);
                    setEditingAdmin(null);
                    setAdminForm({
                        fullName: '',
                        phoneNumber: '',
                        password: '',
                        confirmPassword: '',
                        role: 'Kebele',
                        kebele_name: ''
                    });
                    fetchDashboardData();
                } else {
                    setAdminError(data.message || 'Error processing ' + (adminForm.role === 'Farmer' ? 'farmer' : 'admin'));
                }
            } catch (error) {
                console.error('Error processing admin/farmer:', error);
                setAdminError('Server error occurred');
            }
        };

        const deleteAdmin = async(adminId, isFarmer = false) => {
            if (!window.confirm(`Are you sure you want to delete this ${isFarmer ? 'farmer' : 'admin'}?`)) {
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const url = isFarmer ?
                    `http://localhost:5000/api/admins/deletefarmer/${adminId}` : // Note: This endpoint would need to be implemented
                    `http://localhost:5000/api/admins/delete/${adminId}`;

                const response = await fetch(url, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                } else {
                    const errorData = await response.json();
                    console.error(`Error deleting ${isFarmer ? 'farmer' : 'admin'}:`, errorData.message);
                    alert(errorData.message || `Error deleting ${isFarmer ? 'farmer' : 'admin'}`);
                }
            } catch (error) {
                console.error(`Error deleting ${isFarmer ? 'farmer' : 'admin'}:`, error);
                alert(`Error deleting ${isFarmer ? 'farmer' : 'admin'}`);
            }
        };

        const editAdmin = (admin) => {
            setEditingAdmin(admin);
            setAdminForm({
                fullName: admin.full_name || '',
                phoneNumber: admin.phone_number || '',
                password: '',
                confirmPassword: '',
                role: admin.role || 'Kebele',
                kebele_name: admin.kebele_name || ''
            });
            setShowAdminForm(true);
        };

        const editProduct = (product) => {
            setEditingProduct(product);
            setProductForm({
                name: product.name || '',
                category: product.category || '',
                amount: product.amount || '',
                description: product.description || ''
            });
            setShowProductForm(true);
        };

        // Request Management Functions
        const updateRequestStatus = async(requestId, status) => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/editrequest/${requestId}/status`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({ status })
                });

                if (response.ok) {
                    fetchDashboardData();
                    setSelectedRequest(null);
                } else {
                    const errorData = await response.json();
                    console.error('Error updating request:', errorData.message);
                    alert(errorData.message || 'Error updating request');
                }
            } catch (error) {
                console.error('Error updating request:', error);
                alert('Error updating request');
            }
        };

        if (!user) {
            return <div className = "loading" > Loading... < /div>;
        }

        return ( <
                div className = "woreda-dashboard" > { /* Sidebar */ } <
                div className = "sidebar" >
                <
                h2 > Woreda Dashboard < /h2> <
                nav >
                <
                button className = { activeTab === 'overview' ? 'active' : '' }
                onClick = {
                    () => setActiveTab('overview') } > 📊Overview < /button> <
                button className = { activeTab === 'products' ? 'active' : '' }
                onClick = {
                    () => setActiveTab('products') } > 📦Products < /button> <
                button className = { activeTab === 'admins' ? 'active' : '' }
                onClick = {
                    () => setActiveTab('admins') } > 👥Kebele Admins & Farmers < /button> <
                button className = { activeTab === 'requests' ? 'active' : '' }
                onClick = {
                    () => setActiveTab('requests') } > 📋Requests < /button> <
                /nav> <
                /div>

                { /* Main Content */ } <
                div className = "main-content" >
                <
                header >
                <
                h1 > Welcome, { user.fullName }(Woreda Admin) < /h1> <
                p > Region: { user.region_name } | Zone: { user.zone_name } | Woreda: { user.woreda_name } < /p> <
                button onClick = { handleLogout }
                className = "logout-btn" >
                Logout <
                /button> <
                /header>

                <
                main > { /* Overview Tab */ } {
                    activeTab === 'overview' && ( <
                        div className = "overview-tab" >
                        <
                        h2 > System Overview < /h2> <
                        div className = "stats-grid" >
                        <
                        div className = "stat-card" >
                        <
                        h3 > { stats.totalRequests } < /h3> <
                        p > Total Requests < /p> <
                        /div> <
                        div className = "stat-card" >
                        <
                        h3 > { stats.pendingRequests } < /h3> <
                        p > Pending Requests < /p> <
                        /div> <
                        div className = "stat-card" >
                        <
                        h3 > { stats.approvedRequests } < /h3> <
                        p > Approved Requests < /p> <
                        /div> <
                        div className = "stat-card" >
                        <
                        h3 > { stats.rejectedRequests } < /h3> <
                        p > Rejected Requests < /p> <
                        /div> <
                        div className = "stat-card" >
                        <
                        h3 > { stats.totalAdmins } < /h3> <
                        p > Kebele Admins < /p> <
                        /div> <
                        div className = "stat-card" >
                        <
                        h3 > { stats.totalFarmers } < /h3> <
                        p > Farmers < /p> <
                        /div> <
                        div className = "stat-card" >
                        <
                        h3 > { stats.totalProducts } < /h3> <
                        p > Products < /p> <
                        /div> <
                        /div>

                        <
                        div className = "quick-actions" >
                        <
                        h2 > Quick Actions < /h2> <
                        div className = "actions-grid" >
                        <
                        button onClick = {
                            () => setActiveTab('admins') }
                        className = "action-btn" > 👥Create Kebele Admin <
                        /button> <
                        button onClick = {
                            () => {
                                setEditingAdmin(null);
                                setAdminForm({
                                    fullName: '',
                                    phoneNumber: '',
                                    password: '',
                                    confirmPassword: '',
                                    role: 'Farmer',
                                    kebele_name: ''
                                });
                                setShowAdminForm(true);
                            }
                        }
                        className = "action-btn" > 👨‍🌾Register Farmer <
                        /button> <
                        button onClick = {
                            () => setActiveTab('products') }
                        className = "action-btn" > 📦Add Product <
                        /button> <
                        button onClick = {
                            () => setActiveTab('requests') }
                        className = "action-btn" > 📋Manage Requests <
                        /button> <
                        /div> <
                        /div> <
                        /div>
                    )
                }

                { /* Products Tab */ } {
                    activeTab === 'products' && ( <
                        div className = "products-tab" >
                        <
                        div className = "tab-header" >
                        <
                        h2 > Product Management < /h2> <
                        button onClick = {
                            () => setShowProductForm(true) }
                        className = "add-btn" >
                        Add Product <
                        /button> <
                        /div>

                        { /* Product Form Modal */ } {
                            showProductForm && ( <
                                div className = "modal" >
                                <
                                div className = "modal-content" >
                                <
                                h3 > { editingProduct ? 'Edit Product' : 'Add Product' } < /h3> <
                                form onSubmit = { handleProductSubmit } >
                                <
                                input type = "text"
                                placeholder = "Product Name"
                                value = { productForm.name }
                                onChange = {
                                    (e) => setProductForm({...productForm, name: e.target.value }) }
                                required /
                                >
                                <
                                input type = "text"
                                placeholder = "Category"
                                value = { productForm.category }
                                onChange = {
                                    (e) => setProductForm({...productForm, category: e.target.value }) }
                                required /
                                >
                                <
                                input type = "number"
                                placeholder = "Amount"
                                min = "1"
                                value = { productForm.amount }
                                onChange = {
                                    (e) => setProductForm({...productForm, amount: e.target.value }) }
                                required /
                                >
                                <
                                textarea placeholder = "Description"
                                value = { productForm.description }
                                onChange = {
                                    (e) => setProductForm({...productForm, description: e.target.value }) }
                                rows = "3"
                                required /
                                >
                                <
                                div className = "modal-actions" >
                                <
                                button type = "submit" > Save < /button> <
                                button type = "button"
                                onClick = {
                                    () => {
                                        setShowProductForm(false);
                                        setEditingProduct(null);
                                        setProductForm({ name: '', category: '', amount: '', description: '' });
                                    }
                                } >
                                Cancel <
                                /button> <
                                /div> <
                                /form> <
                                /div> <
                                /div>
                            )
                        }

                        { /* Products List */ } <
                        div className = "products-grid" > {
                            products.map((product) => ( <
                                div key = { product.id }
                                className = "product-card" >
                                <
                                h3 > { product.name } < /h3> <
                                p className = "category" > Category: { product.category } < /p> <
                                p className = "amount" > Amount: { product.amount } < /p> <
                                p className = "description" > { product.description } < /p> <
                                div className = "product-actions" >
                                <
                                button onClick = {
                                    () => editProduct(product) }
                                className = "edit-btn" >
                                Edit <
                                /button> <
                                button onClick = {
                                    () => deleteProduct(product.id) }
                                className = "delete-btn" >
                                Delete <
                                /button> <
                                /div> <
                                /div>
                            ))
                        } <
                        /div> <
                        /div>
                    )
                }

                { /* Admins Tab */ } {
                    activeTab === 'admins' && ( <
                            div className = "admins-tab" >
                            <
                            div className = "tab-header" >
                            <
                            h2 > Kebele Admin & Farmer Management < /h2> <
                            div >
                            <
                            button onClick = {
                                () => {
                                    setEditingAdmin(null);
                                    setAdminForm({
                                        fullName: '',
                                        phoneNumber: '',
                                        password: '',
                                        confirmPassword: '',
                                        role: 'Kebele',
                                        kebele_name: ''
                                    });
                                    setShowAdminForm(true);
                                }
                            }
                            className = "add-btn" >
                            Register Kebele Admin <
                            /button> <
                            button onClick = {
                                () => {
                                    setEditingAdmin(null);
                                    setAdminForm({
                                        fullName: '',
                                        phoneNumber: '',
                                        password: '',
                                        confirmPassword: '',
                                        role: 'Farmer',
                                        kebele_name: ''
                                    });
                                    setShowAdminForm(true);
                                }
                            }
                            className = "add-btn"
                            style = {
                                { marginLeft: '10px' } } >
                            Register Farmer <
                            /button> <
                            /div> <
                            /div>

                            { /* Admin/Farmer Form Modal */ } {
                                showAdminForm && ( <
                                    div className = "modal" >
                                    <
                                    div className = "modal-content" >
                                    <
                                    h3 > { editingAdmin ? `Edit ${adminForm.role}` : `Register ${adminForm.role}` } < /h3> {
                                        adminError && ( <
                                            div className = "error-message" > { adminError } < /div>
                                        )
                                    } <
                                    form onSubmit = { handleAdminSubmit } >
                                    <
                                    input type = "text"
                                    placeholder = "Full Name"
                                    value = { adminForm.fullName }
                                    onChange = {
                                        (e) => setAdminForm({...adminForm, fullName: e.target.value }) }
                                    required /
                                    >
                                    <
                                    input type = "tel"
                                    placeholder = "Phone Number"
                                    value = { adminForm.phoneNumber }
                                    onChange = {
                                        (e) => setAdminForm({...adminForm, phoneNumber: e.target.value }) }
                                    required /
                                    > {!editingAdmin && ( <
                                            >
                                            <
                                            input type = "password"
                                            placeholder = "Password"
                                            value = { adminForm.password }
                                            onChange = {
                                                (e) => setAdminForm({...adminForm, password: e.target.value }) }
                                            required /
                                            >
                                            <
                                            input type = "password"
                                            placeholder = "Confirm Password"
                                            value = { adminForm.confirmPassword }
                                            onChange = {
                                                (e) => setAdminForm({...adminForm, confirmPassword: e.target.value }) }
                                            required /
                                            >
                                            <
                                            />
                                        )
                                    } {
                                        editingAdmin && ( <
                                            >
                                            <
                                            input type = "password"
                                            placeholder = "New Password (leave empty to keep current)"
                                            value = { adminForm.password }
                                            onChange = {
                                                (e) => setAdminForm({...adminForm, password: e.target.value }) }
                                            /> <
                                            input type = "password"
                                            placeholder = "Confirm New Password"
                                            value = { adminForm.confirmPassword }
                                            onChange = {
                                                (e) => setAdminForm({...adminForm, confirmPassword: e.target.value }) }
                                            /> <
                                            />
                                        )
                                    } {
                                        adminForm.role === 'Kebele' && ( <
                                            input type = "text"
                                            placeholder = "Kebele Name (Must be unique)"
                                            value = { adminForm.kebele_name }
                                            onChange = {
                                                (e) => setAdminForm({...adminForm, kebele_name: e.target.value }) }
                                            required /
                                            >
                                        )
                                    } <
                                    div className = "modal-actions" >
                                    <
                                    button type = "submit" > { editingAdmin ? 'Update' : 'Register' } < /button> <
                                    button type = "button"
                                    onClick = {
                                        () => {
                                            setShowAdminForm(false);
                                            setEditingAdmin(null);
                                            setAdminError('');
                                            setAdminForm({
                                                fullName: '',
                                                phoneNumber: '',
                                                password: '',
                                                confirmPassword: '',
                                                role: 'Kebele',
                                                kebele_name: ''
                                            });
                                        }
                                    } >
                                    Cancel <
                                    /button> <
                                    /div> <
                                    /form> <
                                    /div> <
                                    /div>
                                )
                            }

                            { /* Admins List */ } <
                            div className = "admins-list" > {
                                admins.map((admin) => ( <
                                        div key = { admin.id }
                                        className = "admin-card" >
                                        <
                                        h3 > { admin.full_name } < /h3> <
                                        p > Phone: { admin.phone_number } < /p> <
                                        p > Type: { admin.role || 'Farmer' } < /p> {
                                            admin.region_name && < p > Region: { admin.region_name } < /p>} {
                                                    admin.zone_name && < p > Zone: { admin.zone_name } < /p>} {
                                                        admin.woreda_name && < p > Woreda: { admin.woreda_name } < /p>} {
                                                            admin.kebele_name && < p > Kebele: { admin.kebele_name } < /p>} <
                                                                div className = "admin-actions" >
                                                                <
                                                                button onClick = {
                                                                    () => editAdmin(admin) }
                                                            className = "edit-btn" >
                                                                Edit <
                                                                /button> <
                                                                button onClick = {
                                                                    () => deleteAdmin(admin.id, !admin.role) }
                                                            className = "delete-btn" >
                                                                Delete <
                                                                /button> <
                                                                /div> <
                                                                /div>
                                                        ))
                                                } <
                                                /div> <
                                                /div>
                                        )
                                    }

                                    { /* Requests Tab */ } {
                                        activeTab === 'requests' && ( <
                                            div className = "requests-tab" >
                                            <
                                            h2 > Request Management < /h2>

                                            <
                                            div className = "requests-list" > {
                                                requests.map((request) => ( <
                                                    div key = { request.id }
                                                    className = "request-card" >
                                                    <
                                                    h3 > { request.product_name } < /h3> <
                                                    p > Farmer: { request.farmer_name } < /p> <
                                                    p > Quantity: { request.quantity } < /p> <
                                                    p > Status:
                                                    <
                                                    span className = { `status-${request.status}` } > { request.status } <
                                                    /span> <
                                                    /p> <
                                                    p > Date: { new Date(request.created_at).toLocaleDateString() } < /p> <
                                                    div className = "request-actions" > {
                                                        request.status === 'pending' && ( <
                                                            >
                                                            <
                                                            button onClick = {
                                                                () => updateRequestStatus(request.id, 'accepted') }
                                                            className = "approve-btn" >
                                                            Approve <
                                                            /button> <
                                                            button onClick = {
                                                                () => updateRequestStatus(request.id, 'rejected') }
                                                            className = "reject-btn" >
                                                            Reject <
                                                            /button> <
                                                            />
                                                        )
                                                    } {
                                                        request.status !== 'pending' && ( <
                                                            span > Processed < /span>
                                                        )
                                                    } <
                                                    /div> <
                                                    /div>
                                                ))
                                            } <
                                            /div> <
                                            /div>
                                        )
                                    } <
                                    /main> <
                                    /div> <
                                    /div>
                                );
                            };

                            export default WoredaDashboard;
