import React from 'react';
import { LayoutDashboard, PlusCircle, FolderOpen, CheckCircle, List } from 'lucide-react';

const Sidebar = ({ onNavigate, user }) => ( <
    nav className = "fixed top-16 left-0 h-full w-64 bg-gray-50 border-r border-gray-200 p-4 space-y-2 transform -translate-x-full md:translate-x-0 transition-transform duration-300 z-20" >
    <
    button onClick = {
        () => onNavigate('dashboard') }
    className = "w-full flex items-center space-x-3 p-3 rounded-lg text-gray-700 hover:bg-gray-200 transition-colors" >
    <
    LayoutDashboard className = "w-5 h-5" / >
    <
    span > Dashboard < /span> <
    /button> {
        user && (user.role === 'Regional Admin' || user.role === 'Zone Admin') && ( <
            button onClick = {
                () => onNavigate('new-request') }
            className = "w-full flex items-center space-x-3 p-3 rounded-lg text-gray-700 hover:bg-gray-200 transition-colors" >
            <
            PlusCircle className = "w-5 h-5" / >
            <
            span > New Request < /span> <
            /button>
        )
    } {
        user && (user.role === 'Woreda Admin' || user.role === 'Regional Admin' || user.role === 'Zone Admin') && ( <
            button onClick = {
                () => onNavigate('pending-requests') }
            className = "w-full flex items-center space-x-3 p-3 rounded-lg text-gray-700 hover:bg-gray-200 transition-colors" >
            <
            FolderOpen className = "w-5 h-5" / >
            <
            span > Pending Requests < /span> <
            /button>
        )
    } {
        user && (user.role === 'Woreda Admin' || user.role === 'Regional Admin' || user.role === 'Zone Admin') && ( <
            button onClick = {
                () => onNavigate('approved-requests') }
            className = "w-full flex items-center space-x-3 p-3 rounded-lg text-gray-700 hover:bg-gray-200 transition-colors" >
            <
            CheckCircle className = "w-5 h-5" / >
            <
            span > Approved Requests < /span> <
            /button>
        )
    } {
        user && (user.role === 'Head of Department' || user.role === 'Data Encoder') && ( <
            button onClick = {
                () => onNavigate('reports') }
            className = "w-full flex items-center space-x-3 p-3 rounded-lg text-gray-700 hover:bg-gray-200 transition-colors" >
            <
            List className = "w-5 h-5" / >
            <
            span > Reports < /span> <
            /button>
        )
    } <
    /nav>
);

export default Sidebar;
