import React, { useEffect, useMemo, useState } from 'react';
import { CheckCircle, XCircle, Eye } from 'lucide-react';

const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const RegionDashboard = () => {
    const [darkMode, setDarkMode] = useState(false);
    const [activeTab, setActiveTab] = useState('requests');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const [requests, setRequests] = useState([]);
    const [requestFilter, setRequestFilter] = useState('all');
    const [reports, setReports] = useState([]);
    const [statistics, setStatistics] = useState({});
    const [showRequestDetailModal, setShowRequestDetailModal] = useState(false);
    const [requestDetail, setRequestDetail] = useState(null);

    const token = useMemo(() => localStorage.getItem('token') || '', []);
    const regionName = useMemo(() => {
        try {
            const user = JSON.parse(localStorage.getItem('user') || '{}');
            return user.region_name || localStorage.getItem('userRegion') || '';
        } catch (_) {
            return localStorage.getItem('userRegion') || '';
        }
    }, []);

    const apiCall = async(endpoint, options = {}) => {
        const cfg = {
            method: options.method || 'GET',
            headers: {
                'Content-Type': 'application/json',
                Authorization: token ? `Bearer ${token}` : '',
                ...(options.headers || {}),
            },
            body: options.body || undefined,
        };
        const res = await fetch(`${API_BASE}${endpoint}`, cfg);
        if (!res.ok) {
            let msg = `API error ${res.status}`;
            try {
                const j = await res.json();
                if (j && j.message) msg = j.message;
            } catch (_) {}
            throw new Error(msg);
        }
        return res.json();
    };

    const fetchRequests = async() => {
        setError('');
        try {
            setLoading(true);
            const q = requestFilter !== 'all' ? `?status=${encodeURIComponent(requestFilter)}` : '';
            const data = await apiCall(`/api/admins/requests/filter${q}`);
            const list = Array.isArray(data) ? data : (data.requests || []);
            const scoped = regionName ? list.filter(r => r.region_name === regionName) : list;
            setRequests(scoped);
        } catch (e) {
            setError('Failed to fetch requests: ' + e.message);
        } finally {
            setLoading(false);
        }
    };

    const fetchReports = async() => {
        try {
            const data = await apiCall('/api/admins/reports');
            setReports(data.reports || data || []);
        } catch (e) {
            setError('Failed to fetch reports: ' + e.message);
        }
    };

    const fetchStatistics = async() => {
        try {
            const data = await apiCall('/api/admins/reports/statistics');
            setStatistics(data.statistics || data || {});
        } catch (e) {
            setError('Failed to fetch statistics: ' + e.message);
        }
    };

    const updateRequestStatus = async(id, status, feedback) => {
        setError('');
        try {
            setLoading(true);
            await apiCall(`/api/admins/requests/${id}/status`, {
                method: 'PUT',
                body: JSON.stringify({ status: status, feedback: feedback || '' }),
            });
            setSuccess(`Request ${status.toLowerCase()} successfully`);
            fetchRequests();
        } catch (e) {
            setError('Failed to update request status: ' + e.message);
        } finally {
            setLoading(false);
        }
    };

    const deleteRejectedRequest = async(id) => {
        if (!window.confirm('Delete this rejected request?')) return;
        try {
            setLoading(true);
            await apiCall(`/api/admins/deleterequest/${id}`, { method: 'DELETE' });
            setSuccess('Rejected request deleted');
            fetchRequests();
        } catch (e) {
            setError('Failed to delete request: ' + e.message);
        } finally {
            setLoading(false);
        }
    };

    const openRequestDetail = async(id) => {
        setError('');
        try {
            setLoading(true);
            const data = await apiCall(`/api/admins/requests/${id}/status`);
            setRequestDetail(data);
            setShowRequestDetailModal(true);
        } catch (e) {
            setError('Failed to load request detail: ' + e.message);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchRequests();
        fetchReports();
        fetchStatistics();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [requestFilter]);

    return ( <
        div className = { darkMode ? 'bg-gray-900 min-h-screen' : 'bg-gray-50 min-h-screen' } >
        <
        div className = "max-w-6xl mx-auto p-6" >
        <
        div className = "flex items-center justify-between mb-6" >
        <
        h2 className = { darkMode ? 'text-white text-2xl font-semibold' : 'text-gray-900 text-2xl font-semibold' } >
        Region Dashboard <
        /h2> <
        div className = "flex items-center gap-3" >
        <
        select value = { activeTab }
        onChange = {
            (e) => setActiveTab(e.target.value)
        }
        className = { 'px-3 py-2 rounded-lg border ' + (darkMode ? 'bg-gray-800 border-gray-700 text-white' : 'bg-white border-gray-300 text-gray-900') } >
        <
        option value = "requests" > Requests < /option> <
        option value = "reports" > Reports < /option> <
        option value = "statistics" > Statistics < /option> < /
        select > <
        button onClick = {
            () => setDarkMode(!darkMode)
        }
        className = { 'px-3 py-2 rounded-lg ' + (darkMode ? 'bg-gray-700 text-white hover:bg-gray-600' : 'bg-gray-200 text-gray-700 hover:bg-gray-300') } > { darkMode ? 'Light' : 'Dark' } <
        /button> < /
        div > <
        /div>

        {
            error ? < div className = "mb-4 text-sm text-red-700 bg-red-100 border border-red-200 rounded px-3 py-2" > { error } < /div> : null} {
            success ? < div className = "mb-4 text-sm text-green-700 bg-green-100 border border-green-200 rounded px-3 py-2" > { success } < /div> : null}

            {
                activeTab === 'requests' ? ( <
                    div className = { darkMode ? 'bg-gray-800 p-6 rounded-lg shadow' : 'bg-white p-6 rounded-lg shadow' } >
                    <
                    div className = "flex items-center justify-between mb-4" >
                    <
                    h3 className = { darkMode ? 'text-white text-lg font-semibold' : 'text-gray-900 text-lg font-semibold' } > Farmer Requests < /h3> <
                    select value = { requestFilter }
                    onChange = {
                        (e) => setRequestFilter(e.target.value)
                    }
                    className = { 'px-3 py-2 rounded-lg border ' + (darkMode ? 'bg-gray-700 border-gray-600 text-white' : 'bg-white border-gray-300 text-gray-900') } >
                    <
                    option value = "all" > All Requests < /option> <
                    option value = "pending" > Pending < /option> <
                    option value = "approved" > Approved < /option> <
                    option value = "accepted" > Accepted < /option> <
                    option value = "rejected" > Rejected < /option> < /
                    select > <
                    /div>

                    <
                    div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" > {
                        requests.map((r) => {
                            const total = Number(r.quantity || 0) * Number(r.product_price || 0);
                            const badge = r.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' : r.status === 'Approved' ? 'bg-blue-100 text-blue-800' : r.status === 'Accepted' ? 'bg-green-100 text-green-800' : r.status === 'Rejected' ? 'bg-red-100 text-red-800' : 'bg-gray-100 text-gray-800';
                            return ( <
                                div key = { r.id }
                                className = { darkMode ? 'p-4 rounded-lg border bg-gray-700 border-gray-600' : 'p-4 rounded-lg border bg-white border-gray-200' } >
                                <
                                div className = "flex items-start justify-between mb-2" >
                                <
                                div >
                                <
                                div className = { darkMode ? 'font-semibold text-white' : 'font-semibold text-gray-900' } > { r.product_name || 'Product' } < /div> <
                                div className = { darkMode ? 'text-sm text-gray-300' : 'text-sm text-gray-600' } > By: { r.farmer_name || 'Farmer' } < /div> < /
                                div > <
                                span className = { `px-2 py-1 rounded-full text-xs font-medium ${badge}` } > { r.status } < /span> < /
                                div > <
                                div className = "space-y-1 mb-3" >
                                <
                                div className = { darkMode ? 'text-sm text-gray-300' : 'text-sm text-gray-600' } > Quantity: { r.quantity } < /div> <
                                div className = { darkMode ? 'text-sm text-gray-300' : 'text-sm text-gray-600' } > Price: $ { Number(r.product_price || 0).toFixed(2) } < /div> <
                                div className = { darkMode ? 'text-sm text-gray-300' : 'text-sm text-gray-600' } > Total: $ { total.toFixed(2) } < /div> <
                                div className = { darkMode ? 'text-sm text-gray-300' : 'text-sm text-gray-600' } > { r.kebele_name }, { r.woreda_name } < /div> < /
                                div > {
                                    r.status === 'Pending' ? ( <
                                        div className = "flex gap-2" >
                                        <
                                        button onClick = {
                                            () => updateRequestStatus(r.id, 'Approved')
                                        }
                                        className = "flex-1 bg-blue-500 hover:bg-blue-600 text-white px-3 py-2 rounded-lg text-sm flex items-center justify-center gap-1" > < CheckCircle className = "h-4 w-4" / > Approve < /button> <
                                        button onClick = {
                                            () => updateRequestStatus(r.id, 'Accepted')
                                        }
                                        className = "flex-1 bg-green-500 hover:bg-green-600 text-white px-3 py-2 rounded-lg text-sm flex items-center justify-center gap-1" > < CheckCircle className = "h-4 w-4" / > Accept < /button> <
                                        button onClick = {
                                            () => updateRequestStatus(r.id, 'Rejected')
                                        }
                                        className = "flex-1 bg-red-500 hover:bg-red-600 text-white px-3 py-2 rounded-lg text-sm flex items-center justify-center gap-1" > < XCircle className = "h-4 w-4" / > Reject < /button> < /
                                        div >
                                    ) : r.status === 'Rejected' ? ( <
                                        button onClick = {
                                            () => deleteRejectedRequest(r.id)
                                        }
                                        className = "w-full bg-red-500 hover:bg-red-600 text-white px-3 py-2 rounded-lg text-sm" > Delete Rejected Request < /button>
                                    ) : null
                                } <
                                div className = "flex justify-end mt-2" >
                                <
                                button onClick = {
                                    () => openRequestDetail(r.id)
                                }
                                className = "bg-gray-100 hover:bg-gray-200 text-gray-800 px-3 py-2 rounded-lg text-sm flex items-center gap-1" > < Eye className = "h-4 w-4" / > View < /button> < /
                                div > <
                                /div>
                            );
                        })
                    } <
                    /div> < /
                    div >
                ) : null
            }

            {
                activeTab === 'reports' ? ( <
                    div className = { darkMode ? 'bg-gray-800 p-6 rounded-lg shadow' : 'bg-white p-6 rounded-lg shadow' } >
                    <
                    h3 className = { darkMode ? 'text-white text-lg font-semibold mb-4' : 'text-gray-900 text-lg font-semibold mb-4' } > Admin Reports < /h3> <
                    div className = "space-y-2" >
                    <
                    div className = "flex justify-between" > < span className = { darkMode ? 'text-gray-300' : 'text-blue-700' } > Total Reports: < /span><span className="font-semibold">{reports.length}</span > < /div> <
                    div className = "flex justify-between" > < span className = { darkMode ? 'text-gray-300' : 'text-blue-700' } > Pending Reports: < /span><span className="font-semibold text-yellow-600">{reports.filter(r => r.status === 'Pending').length}</span > < /div> <
                    div className = "flex justify-between" > < span className = { darkMode ? 'text-gray-300' : 'text-blue-700' } > Resolved Reports: < /span><span className="font-semibold text-green-600">{reports.filter(r => r.status === 'Resolved').length}</span > < /div> <
                    div className = "flex justify-between" > < span className = { darkMode ? 'text-gray-300' : 'text-blue-700' } > Dismissed Reports: < /span><span className="font-semibold text-red-600">{reports.filter(r => r.status === 'Dismissed').length}</span > < /div> < /
                    div > <
                    /div>
                ) : null
            }

            {
                activeTab === 'statistics' ? ( <
                    div className = { darkMode ? 'bg-gray-800 p-6 rounded-lg shadow' : 'bg-white p-6 rounded-lg shadow' } >
                    <
                    h3 className = { darkMode ? 'text-white text-lg font-semibold mb-4' : 'text-gray-900 text-lg font-semibold mb-4' } > Statistics < /h3> <
                    pre className = { darkMode ? 'text-gray-300 text-sm' : 'text-gray-700 text-sm' } > { JSON.stringify(statistics, null, 2) } < /pre> < /
                    div >
                ) : null
            } <
            /div>

            {
                showRequestDetailModal ? ( <
                        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                        <
                        div className = { darkMode ? 'w-full max-w-lg p-6 rounded-lg shadow bg-gray-800' : 'w-full max-w-lg p-6 rounded-lg shadow bg-white' } >
                        <
                        h4 className = { darkMode ? 'text-white text-lg font-semibold mb-4' : 'text-gray-900 text-lg font-semibold mb-4' } > Request Detail < /h4> {
                        requestDetail ? ( <
                            div className = "grid grid-cols-2 gap-3 text-sm" >
                            <
                            div className = { darkMode ? 'text-gray-300' : 'text-gray-700' } > Kebele: { requestDetail.kebele_status } < /div> <
                            div className = { darkMode ? 'text-gray-300' : 'text-gray-700' } > Woreda: { requestDetail.woreda_status } < /div> <
                            div className = { darkMode ? 'text-gray-300' : 'text-gray-700' } > Zone: { requestDetail.zone_status } < /div> <
                            div className = { darkMode ? 'text-gray-300' : 'text-gray-700' } > Region: { requestDetail.region_status } < /div> <
                            div className = { darkMode ? 'text-gray-300' : 'text-gray-700' } > Federal: { requestDetail.federal_status } < /div> < /
                            div >
                        ) : ( <
                            div className = { darkMode ? 'text-gray-300' : 'text-gray-700' } > Loading... < /div>
                        )
                    } <
                    div className = "flex justify-end mt-6" >
                    <
                    button onClick = {
                        () => setShowRequestDetailModal(false)
                    }
                className = { darkMode ? 'px-4 py-2 rounded bg-gray-700 text-white hover:bg-gray-600' : 'px-4 py-2 rounded bg-gray-200 text-gray-700 hover:bg-gray-300' } >
                    Close <
                    /button> < /
                    div > <
                    /div> < /
                    div >
            ): null
        } <
        /div>
    );
};

export default RegionDashboard;
