import React, { useState, useEffect } from 'react';
import './assets/cssma'; // Assuming this file contains your CSS styles

// Note: In a real-world app, these would be in separate files.
// For this example, they are included here for simplicity.

// Mock data to simulate a database. This data will be lost on refresh.
const mockRequests = [{
    id: 'req1',
    type: 'Chemical Input Supply',
    item: 'Fertilizer',
    quantity: 10,
    status: 'pending',
    requestor: 'John Doe',
    submittedAt: new Date().toISOString()
}, {
    id: 'req2',
    type: 'Biological Input Supply',
    item: 'Seeds',
    quantity: 50,
    status: 'approved',
    requestor: 'Jane Smith',
    submittedAt: new Date(Date.now() - 86400000).toISOString() // 1 day ago
}, {
    id: 'req3',
    type: 'Chemical Input Supply',
    item: 'Pesticide',
    quantity: 5,
    status: 'rejected',
    requestor: 'Peter Jones',
    submittedAt: new Date(Date.now() - 172800000).toISOString() // 2 days ago
}, ];

const Sidebar = ({ currentPage, onPageChange, pendingRequestCount }) => {
    return ( <
            aside className = "w-64 min-h-screen bg-gray-800 text-gray-100 flex flex-col" >
            <
            div className = "p-4 text-center text-2xl font-bold border-b border-gray-700" >
            FMS <
            /div> <
            nav className = "flex-1" >
            <
            ul className = "space-y-2 p-4" >
            <
            li >
            <
            button onClick = {
                () => onPageChange('dashboard')
            }
            className = { `w-full text-left p-3 rounded-lg flex justify-between items-center transition-colors duration-200 ${
                                currentPage === 'dashboard' ? 'bg-gray-700 text-white' : 'hover:bg-gray-700'
                            }` } >
            <
            span > Dashboard < /span> {
            pendingRequestCount > 0 && ( <
                span className = "bg-red-500 text-white text-xs font-bold px-2 py-1 rounded-full" > { pendingRequestCount } <
                /span>
            )
        } <
        /button> < /
    li > <
        li >
        <
        button onClick = {
            () => onPageChange('master-data')
        }
    className = { `w-full text-left p-3 rounded-lg transition-colors duration-200 ${
                                currentPage === 'master-data' ? 'bg-gray-700 text-white' : 'hover:bg-gray-700'
                            }` } >
        Master Data <
        /button> < /
    li > <
        li >
        <
        button onClick = {
            () => onPageChange('reports')
        }
    className = { `w-full text-left p-3 rounded-lg transition-colors duration-200 ${
                                currentPage === 'reports' ? 'bg-gray-700 text-white' : 'hover:bg-gray-700'
                            }` } >
        Reports <
        /button> < /
    li > <
        /ul> < /
    nav > <
        div className = "p-4 text-sm text-center text-gray-500 border-t border-gray-700" >
        Logged in as: User <
        /div> < /
    aside >
);
};

const Modal = ({ show, onClose, title, message }) => {
    if (!show) return null;
    return ( <
        div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50" >
        <
        div className = "relative p-5 border w-96 shadow-lg rounded-md bg-white" >
        <
        div className = "mt-3 text-center" >
        <
        h3 className = "text-lg leading-6 font-medium text-gray-900" > { title } < /h3> <
        div className = "mt-2 px-7 py-3" >
        <
        p className = "text-sm text-gray-500" > { message } < /p> < /
        div > <
        div className = "items-center px-4 py-3" >
        <
        button onClick = { onClose }
        className = "px-4 py-2 bg-blue-500 text-white text-base font-medium rounded-md w-full shadow-sm hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500" >
        OK <
        /button> < /
        div > <
        /div> < /
        div > <
        /div>
    );
};

const MainDashboard = ({ requests, onApprove, onReject, onSelectRequest }) => {
    const pendingRequests = requests.filter(req => req.status === 'pending');
    const approvedRequests = requests.filter(req => req.status === 'approved');
    const rejectedRequests = requests.filter(req => req.status === 'rejected');

    return ( <
        div className = "p-8 space-y-8" >
        <
        h1 className = "text-3xl font-bold text-gray-800" > Dashboard < /h1> <
        section >
        <
        h2 className = "text-2xl font-semibold mb-4" > Pending Requests({ pendingRequests.length }) < /h2> <
        div className = "bg-white p-6 rounded-lg shadow-md overflow-x-auto" >
        <
        table className = "min-w-full divide-y divide-gray-200" >
        <
        thead className = "bg-gray-50" >
        <
        tr >
        <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Requestor < /th> <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Item < /th> <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Quantity < /th> <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Type < /th> <
        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Actions < /th> < /
        tr > <
        /thead> <
        tbody className = "bg-white divide-y divide-gray-200" > {
            pendingRequests.map(req => ( <
                tr key = { req.id } >
                <
                td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900" > { req.requestor } < /td> <
                td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { req.item } < /td> <
                td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { req.quantity } < /td> <
                td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { req.type } < /td> <
                td className = "px-6 py-4 whitespace-nowrap text-right text-sm font-medium" >
                <
                button onClick = {
                    () => onSelectRequest(req)
                }
                className = "text-blue-600 hover:text-blue-900 mr-4" > View < /button> <
                button onClick = {
                    () => onApprove(req.id)
                }
                className = "text-green-600 hover:text-green-900 mr-4" > Approve < /button> <
                button onClick = {
                    () => onReject(req.id)
                }
                className = "text-red-600 hover:text-red-900" > Reject < /button> < /
                td > <
                /tr>
            ))
        } <
        /tbody> < /
        table > <
        /div> < /
        section > { /* Approved and Rejected sections can be added here following a similar pattern */ } <
        /div>
    );
};

const MasterData = () => {
    return ( <
        div className = "p-8" >
        <
        h1 className = "text-3xl font-bold text-gray-800" > Master Data < /h1> <
        p className = "mt-4 text-gray-600" > This section would manage lists of items, departments, and other core data. < /p> < /
        div >
    );
};

const Reports = ({ requests }) => {
    const totalRequests = requests.length;
    const approved = requests.filter(req => req.status === 'approved').length;
    const rejected = requests.filter(req => req.status === 'rejected').length;
    const pending = requests.filter(req => req.status === 'pending').length;

    return ( <
        div className = "p-8" >
        <
        h1 className = "text-3xl font-bold text-gray-800" > Reports < /h1> <
        div className = "mt-8 grid grid-cols-1 md:grid-cols-3 gap-6" >
        <
        div className = "bg-white p-6 rounded-lg shadow-md" >
        <
        h2 className = "text-xl font-semibold" > Total Requests < /h2> <
        p className = "text-3xl font-bold text-gray-800 mt-2" > { totalRequests } < /p> < /
        div > <
        div className = "bg-white p-6 rounded-lg shadow-md" >
        <
        h2 className = "text-xl font-semibold text-green-600" > Approved < /h2> <
        p className = "text-3xl font-bold text-green-600 mt-2" > { approved } < /p> < /
        div > <
        div className = "bg-white p-6 rounded-lg shadow-md" >
        <
        h2 className = "text-xl font-semibold text-red-600" > Rejected < /h2> <
        p className = "text-3xl font-bold text-red-600 mt-2" > { rejected } < /p> < /
        div > <
        /div> < /
        div >
    );
};

const RequestApprovalForm = ({ request, onClose }) => {
    if (!request) return null;
    return ( <
        div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50" >
        <
        div className = "relative p-8 border w-3/4 max-w-lg shadow-lg rounded-md bg-white" >
        <
        h3 className = "text-xl font-bold mb-4" > Request Details: { request.id } < /h3> <
        div className = "grid grid-cols-2 gap-4 text-sm" >
        <
        div >
        <
        p className = "font-semibold" > Requestor: < /p> <
        p className = "text-gray-600" > { request.requestor } < /p> < /
        div > <
        div >
        <
        p className = "font-semibold" > Department: < /p> <
        p className = "text-gray-600" > { request.type } < /p> < /
        div > <
        div >
        <
        p className = "font-semibold" > Item: < /p> <
        p className = "text-gray-600" > { request.item } < /p> < /
        div > <
        div >
        <
        p className = "font-semibold" > Quantity: < /p> <
        p className = "text-gray-600" > { request.quantity } < /p> < /
        div > <
        div >
        <
        p className = "font-semibold" > Status: < /p> <
        p className = { `text-gray-600 font-bold ${
                            request.status === 'approved' ? 'text-green-500' :
                            request.status === 'rejected' ? 'text-red-500' : 'text-yellow-500'
                        }` } > { request.status.toUpperCase() } < /p> < /
        div > <
        /div> <
        div className = "mt-6 flex justify-end space-x-4" >
        <
        button onClick = { onClose }
        className = "px-4 py-2 bg-gray-300 text-gray-800 rounded-md hover:bg-gray-400" > Close < /button> < /
        div > <
        /div> < /
        div >
    );
};

// Main App Component
const App = () => {
    const [currentPage, setCurrentPage] = useState('dashboard');
    const [showModal, setShowModal] = useState(false);
    const [modalContent, setModalContent] = useState('');
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [requests, setRequests] = useState(mockRequests);

    const pendingRequestCount = requests.filter(req => req.status === 'pending').length;

    const showNotification = (message) => {
        setModalContent(message);
        setShowModal(true);
    };

    const handleApproveRequest = (id) => {
        setRequests(prevRequests =>
            prevRequests.map(req =>
                req.id === id ? {...req, status: 'approved' } : req
            )
        );
        showNotification(`Request ${id} has been approved.`);
    };

    const handleRejectRequest = (id) => {
        setRequests(prevRequests =>
            prevRequests.map(req =>
                req.id === id ? {...req, status: 'rejected' } : req
            )
        );
        showNotification(`Request ${id} has been rejected.`);
    };

    return ( <
        div className = "bg-gray-100 min-h-screen font-sans antialiased flex" >
        <
        Sidebar currentPage = { currentPage }
        onPageChange = { setCurrentPage }
        pendingRequestCount = { pendingRequestCount }
        /> <
        main className = "flex-1 overflow-x-hidden overflow-y-auto" > {
            currentPage === 'dashboard' && ( <
                MainDashboard requests = { requests }
                onApprove = { handleApproveRequest }
                onReject = { handleRejectRequest }
                onSelectRequest = { setSelectedRequest }
                />
            )
        } { currentPage === 'master-data' && < MasterData / > } {
            currentPage === 'reports' && < Reports requests = { requests }
            />} {
            selectedRequest && ( <
                RequestApprovalForm request = { selectedRequest }
                onClose = {
                    () => setSelectedRequest(null)
                }
                />
            )
        } <
        Modal show = { showModal }
        onClose = {
            () => setShowModal(false)
        }
        title = "Notification"
        message = { modalContent }
        /> < /
        main > <
        /div>
    );
};

export default App;
