// File: RegionDashboard.js (updated)
import React, { useState, useEffect } from 'react';
import {
    Users,
    Package,
    FileText,
    CheckCircle,
    XCircle,
    Clock,
    Plus,
    Edit,
    Trash2,
    Eye,
    Search,
    Moon,
    Sun,
    Bell,
    Settings,
    LogOut,
    Building,
    Home,
    Wheat,
    AlertTriangle,
    BarChart3,
    Phone,
    MapPin,
    Target,
    Truck,
    DollarSign
} from 'lucide-react';

const RegionDashboard = () => {
        // State management (keep your existing states)
        const [darkMode, setDarkMode] = useState(false);
        const [activeTab, setActiveTab] = useState('overview');
        const [loading, setLoading] = useState(false);
        const [error, setError] = useState('');
        const [success, setSuccess] = useState('');

        // Data states (keep your existing states)
        const [admins, setAdmins] = useState([]);
        const [farmers, setFarmers] = useState([]);
        const [products, setProducts] = useState([]);
        const [requests, setRequests] = useState([]);
        const [reports, setReports] = useState([]);
        const [statistics, setStatistics] = useState({});
        const [myReports, setMyReports] = useState([]);
        const [myProducts, setMyProducts] = useState([]);
        const [otherProducts, setOtherProducts] = useState([]);

        // Filter states (keep your existing states)
        const [requestFilter, setRequestFilter] = useState('all');
        const [reportFilter, setReportFilter] = useState('all');
        const [searchTerm, setSearchTerm] = useState('');
        const [farmerFilter, setFarmerFilter] = useState('all');
        const [productFilter, setProductFilter] = useState('all');

        // Modal states (keep your existing states)
        const [showAdminModal, setShowAdminModal] = useState(false);
        const [showProductModal, setShowProductModal] = useState(false);
        const [showReportModal, setShowReportModal] = useState(false);
        const [showFarmerModal, setShowFarmerModal] = useState(false);
        const [selectedItem, setSelectedItem] = useState(null);
        const [modalMode, setModalMode] = useState('create');

        // Form states (keep your existing states)
        const [adminForm, setAdminForm] = useState({
            full_name: '',
            phone_number: '',
            password: '',
            confirmPassword: '',
            role: 'Zone',
            region_name: '',
            zone_name: '',
            woreda_name: '',
            kebele_name: ''
        });

        const [productForm, setProductForm] = useState({
            name: '',
            category: '',
            sub_category: '',
            price: '',
            amount: '',
            unit: '',
            description: '',
            manufacturer: '',
            expiry_date: ''
        });

        const [reportForm, setReportForm] = useState({
            reported_admin_id: '',
            report_type: 'Misconduct',
            title: '',
            description: '',
            evidence: '',
            priority: 'Medium'
        });

        const [farmerForm, setFarmerForm] = useState({
            full_name: '',
            phone_number: '',
            password: '',
            region_name: '',
            zone_name: '',
            woreda_name: '',
            kebele_name: '',
            land_size_hectares: '',
            crop_types: '',
            crops_season: '',
            land_type: '',
            farming_experience: '',
            irrigation_type: '',
            farming_method: '',
            primary_crops: '',
            secondary_crops: '',
            soil_type: '',
            has_livestock: false,
            livestock_types: '',
            annual_income: '',
            education_level: ''
        });

        // API base URL - FIXED: point to server root (endpoints include /api/...)
        const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000';

        // Utility function for API calls - FIXED: Added proper error handling
        const apiCall = async(endpoint, options = {}) => {
            const token = localStorage.getItem('token');
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                    ...options.headers
                },
                ...options
            };

            try {
                const response = await fetch(`${API_BASE}${endpoint}`, config);

                if (!response.ok) {
                    if (response.status === 401) {
                        handleLogout();
                        throw new Error('Authentication failed. Please login again.');
                    }

                    const errorData = await response.json().catch(() => ({}));
                    throw new Error(errorData.message || `API call failed with status ${response.status}`);
                }

                return await response.json();
            } catch (err) {
                console.error('API call error:', err);
                throw new Error(err.message || 'API call failed');
            }
        };

        // Payload mappers and validations
        const mapAdminFormToPayload = (form, isUpdate = false) => {
            const payload = {};

            // For updates, only include fields that have actual values (not empty strings or undefined)
            if (isUpdate) {
                if (form.full_name && form.full_name.trim() !== '') payload.fullName = form.full_name;
                if (form.phone_number && form.phone_number.trim() !== '') payload.phoneNumber = form.phone_number;
                if (form.password && form.password.trim() !== '') {
                    payload.password = form.password;
                    payload.confirmPassword = form.confirmPassword;
                }
                // Only include location fields if they have actual values - this prevents triggering uniqueness checks
                if (form.zone_name && form.zone_name.trim() !== '') payload.zone_name = form.zone_name;
                if (form.woreda_name && form.woreda_name.trim() !== '') payload.woreda_name = form.woreda_name;
                if (form.kebele_name && form.kebele_name.trim() !== '') payload.kebele_name = form.kebele_name;
                if (form.region_name && form.region_name.trim() !== '') payload.region_name = form.region_name;
            } else {
                // For creation, include all required fields
                payload.fullName = form.full_name;
                payload.phoneNumber = form.phone_number;
                payload.password = form.password;
                payload.confirmPassword = form.confirmPassword;
                payload.role = form.role;
                payload.zone_name = form.zone_name;
                payload.woreda_name = form.woreda_name;
                payload.kebele_name = form.kebele_name;

                // Only include region_name if it's provided (for Federal admin creating Region)
                if (form.region_name) {
                    payload.region_name = form.region_name;
                }
            }

            return payload;
        };

        const validateAdminForm = (form, requireConfirm = false, isUpdate = false) => {
            // For updates, only validate password if provided
            if (!isUpdate) {
                // For creation, all required fields must be present
                if (!form.full_name || !form.phone_number || !form.role) return 'Full name, phone number and role are required';
                if (form.role === 'Zone' && !form.zone_name) return 'Zone name is required for Zone admin';
            }

            // Password validation (for both create and update)
            if (form.password) {
                if (requireConfirm && !form.confirmPassword) return 'Password confirmation is required';
                if (requireConfirm && form.password !== form.confirmPassword) return 'Password and confirmation do not match';
                if (form.password.length < 6) return 'Password must be at least 6 characters';
            }
            return null;
        };

        const mapProductFormToPayload = (form) => ({
            name: form.name,
            category: form.category,
            sub_category: form.sub_category,
            price: form.price !== '' ? Number(form.price) : form.price,
            amount: form.amount !== '' ? Number(form.amount) : form.amount,
            unit: form.unit,
            description: form.description,
            manufacturer: form.manufacturer,
            expiry_date: form.expiry_date
        });

        const validateProductForm = (form) => {
            if (!form.name || !form.category || !form.sub_category || !form.unit || !form.expiry_date) {
                return 'Product name, category, sub_category, unit, and expiry_date are required';
            }
            if (form.amount === '' || isNaN(Number(form.amount))) return 'Valid amount is required';
            if (form.price === '' || isNaN(Number(form.price))) return 'Valid price is required';
            return null;
        };

        const mapReportFormToPayload = (form) => ({
            reported_admin_id: form.reported_admin_id ? Number(form.reported_admin_id) : form.reported_admin_id,
            report_type: form.report_type,
            title: form.title,
            description: form.description,
            evidence: form.evidence,
            priority: form.priority
        });

        const validateReportForm = (form) => {
            if (!form.reported_admin_id || !form.report_type || !form.title || !form.description) {
                return 'Reported admin ID, type, title, and description are required';
            }
            return null;
        };

        // Logout function
        const handleLogout = () => {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            window.location.href = '/login';
        };

        // Data fetching functions - FIXED: Added proper error handling and scope filtering
        const fetchAdmins = async() => {
            try {
                const data = await apiCall('/api/admins/admins');
                // Filter to only show Zone admins in the region
                const regionAdmins = data.filter(admin =>
                    admin.role === 'Zone' && admin.region_name === localStorage.getItem('userRegion')
                );
                setAdmins(regionAdmins);
            } catch (err) {
                setError('Failed to fetch admins: ' + err.message);
            }
        };

        const fetchFarmers = async() => {
            try {
                const data = await apiCall('/api/admins/farmers');
                // Filter farmers by region
                const regionFarmers = data.farmers ?
                    data.farmers.filter(farmer => farmer.region_name === localStorage.getItem('userRegion')) :
                    data.filter(farmer => farmer.region_name === localStorage.getItem('userRegion'));
                setFarmers(regionFarmers);
            } catch (err) {
                setError('Failed to fetch farmers: ' + err.message);
            }
        };

        const fetchProducts = async() => {
            try {
                const data = await apiCall('/api/admins/getproducts');
                setProducts(data);
            } catch (err) {
                setError('Failed to fetch products: ' + err.message);
            }
        };

        const fetchMyProducts = async() => {
            try {
                const data = await apiCall('/api/admins/myproducts');
                setMyProducts(data);
            } catch (err) {
                setError('Failed to fetch my products: ' + err.message);
            }
        };

        const fetchOtherProducts = async() => {
            try {
                const data = await apiCall('/api/admins/otherproducts');
                setOtherProducts(data);
            } catch (err) {
                setError('Failed to fetch other products: ' + err.message);
            }
        };

        const fetchRequests = async() => {
            try {
                const data = await apiCall('/api/admins/getrequests');
                // Filter requests by region
                const regionRequests = data.requests ?
                    data.requests.filter(request => request.region_name === localStorage.getItem('userRegion')) :
                    data.filter(request => request.region_name === localStorage.getItem('userRegion'));
                setRequests(regionRequests);
            } catch (err) {
                setError('Failed to fetch requests: ' + err.message);
            }
        };

        // Fetch requests by backend status filter (pending | approved | accepted | rejected)
        const fetchRequestsByStatus = async(statusKey) => {
            try {
                const q = statusKey && statusKey !== 'all' ? `?status=${encodeURIComponent(statusKey)}` : '';
                const data = await apiCall(`/api/admins/requests/filter${q}`);
                const region = localStorage.getItem('userRegion');
                const list = Array.isArray(data) ? data : (data.requests || []);
                const regionRequests = region ? list.filter(r => r.region_name === region) : list;
                setRequests(regionRequests);
            } catch (err) {
                setError('Failed to fetch requests: ' + err.message);
            }
        };

        const fetchReports = async() => {
            try {
                const data = await apiCall('/api/admins/reports');
                setReports(data.reports || data);
            } catch (err) {
                setError('Failed to fetch reports: ' + err.message);
            }
        };

        const fetchMyReports = async() => {
            try {
                const data = await apiCall('/api/admins/reports/my');
                setMyReports(data.reports || data);
            } catch (err) {
                setError('Failed to fetch my reports: ' + err.message);
            }
        };

        const fetchStatistics = async() => {
            try {
                const data = await apiCall('/api/admins/reports/statistics');
                setStatistics(data.statistics || data);
            } catch (err) {
                setError('Failed to fetch statistics: ' + err.message);
            }
        };

        // CRUD operations - FIXED: Added proper API endpoints
        const createAdmin = async() => {
            try {
                setLoading(true);
                const validationError = validateAdminForm(adminForm, true);
                if (validationError) throw new Error(validationError);
                await apiCall('/api/admins/register', {
                    method: 'POST',
                    body: JSON.stringify(mapAdminFormToPayload(adminForm, false))
                });
                setSuccess('Zone admin created successfully');
                setShowAdminModal(false);
                resetAdminForm();
                fetchAdmins();
            } catch (err) {
                setError('Failed to create admin: ' + err.message);
            } finally {
                setLoading(false);
            }
        };

        const updateAdmin = async() => {
            try {
                setLoading(true);
                const validationError = validateAdminForm(adminForm, !!adminForm.password, true);
                if (validationError) throw new Error(validationError);
                await apiCall(`/api/admins/edit/${selectedItem.id}`, {
                    method: 'PUT',
                    body: JSON.stringify(mapAdminFormToPayload(adminForm, true))
                });
                setSuccess('Admin updated successfully');
                setShowAdminModal(false);
                resetAdminForm();
                fetchAdmins();
            } catch (err) {
                setError('Failed to update admin: ' + err.message);
            } finally {
                setLoading(false);
            }
        };

        const createProduct = async() => {
            try {
                setLoading(true);
                const validationError = validateProductForm(productForm);
                if (validationError) throw new Error(validationError);
