        Secondary Crops <
        /label> <
        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { farmerForm.secondary_crops } < /p> < /
        div >

        <
        div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Soil Type <
        /label> <
        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { farmerForm.soil_type } < /p> < /
        div >

        <
        div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Has Livestock <
        /label> <
        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { farmerForm.has_livestock ? 'Yes' : 'No' } < /p> < /
        div >

        {
            farmerForm.has_livestock && ( <
                div >
                <
                label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
                Livestock Types <
                /label> <
                p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { farmerForm.livestock_types } < /p> < /
                div >
            )
        }

        <
        div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Annual Income <
        /label> <
        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } >
        $ { parseFloat(farmerForm.annual_income || 0).toLocaleString() } <
        /p> < /
        div >

        <
        div >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Education Level <
        /label> <
        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { farmerForm.education_level } < /p> < /
        div >

        <
        div className = "md:col-span-2" >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Crop Types <
        /label> <
        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { farmerForm.crop_types } < /p> < /
        div >

        <
        div className = "md:col-span-2" >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Crops Season <
        /label> <
        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { farmerForm.crops_season } < /p> < /
        div >

        <
        div className = "md:col-span-2" >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Land Type <
        /label> <
        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { farmerForm.land_type } < /p> < /
        div >

        <
        div className = "md:col-span-2" >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Irrigation Type <
        /label> <
        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { farmerForm.irrigation_type } < /p> < /
        div >

        <
        div className = "md:col-span-2" >
        <
        label className = { `block text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}` } >
        Farming Method <
        /label> <
        p className = { `${darkMode ? 'text-gray-300' : 'text-gray-600'}` } > { farmerForm.farming_method } < /p> < /
        div > <
        /div>

        <
        div className = "flex justify-end gap-3 mt-6" >
        <
        button onClick = {
            () => setShowFarmerModal(false)
        }
        className = { `px-4 py-2 rounded-lg ${
                                    darkMode 
                                        ? 'bg-gray-600 text-white hover:bg-gray-700' 
                                        : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                                }` } >
        Close <
        /button> < /
        div > <
        /div> < /
        div >
    )
} <
/div>
);
};

export default RegionDashboard;
