import React from 'react';
import { pageNames } from '../constants';

const Sidebar = ({ userRole, currentPage, onNavigate }) => {
    const menuItems = [
        { name: 'Dashboard', page: pageNames.DASHBOARD },
        { name: 'Product Requests', page: pageNames.REQUESTS },
        { name: 'Approvals', page: pageNames.APPROVALS },
        { name: 'Master Data', page: pageNames.MASTER_DATA },
        { name: 'Reports', page: pageNames.REPORTS },
    ];

    return ( <
        aside className = "w-64 bg-gray-800 text-white p-4 h-screen shadow-lg" >
        <
        ul className = "space-y-2 mt-4" > {
            menuItems.map((item) => ( <
                li key = { item.page } >
                <
                button onClick = {
                    () => onNavigate(item.page) }
                className = { `w-full text-left py-2 px-4 rounded-lg font-medium transition-colors ${
                currentPage === item.page
                  ? 'bg-indigo-600 text-white'
                  : 'hover:bg-gray-700'
              }` } >
                { item.name } <
                /button> <
                /li>
            ))
        } <
        /ul> <
        /aside>
    );
};

export default Sidebar;
