// components/ProductManagement.js
import React, { useState, useEffect } from 'react';
import './ProductManagement.css';

const ProductManagement = () => {
    const [products, setProducts] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        category: '',
        amount: ''
    });

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async() => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:5000/api/admins/products', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            const data = await response.json();
            if (response.ok) {
                setProducts(data);
            }
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const handleSubmit = async(e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const url = editingProduct ?
                `http://localhost:5000/api/admins/products/${editingProduct.id}` :
                'http://localhost:5000/api/admins/products';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(formData)
            });

            if (response.ok) {
                setShowForm(false);
                setEditingProduct(null);
                setFormData({ name: '', description: '', category: '', amount: '' });
                fetchProducts();
            }
        } catch (error) {
            console.error('Error saving product:', error);
        }
    };

    const handleEdit = (product) => {
        setEditingProduct(product);
        setFormData({
            name: product.name,
            description: product.description,
            category: product.category,
            amount: product.amount || ''
        });
        setShowForm(true);
    };

    const handleDelete = async(productId) => {
        if (window.confirm('Are you sure you want to delete this product?')) {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/products/${productId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchProducts();
                }
            } catch (error) {
                console.error('Error deleting product:', error);
            }
        }
    };

    return ( <
        div className = "product-management" >
        <
        div className = "section-header" >
        <
        h2 > Product Management < /h2> <
        button onClick = {
            () => setShowForm(true) } > +Add Product < /button> <
        /div>

        {
            showForm && ( <
                div className = "modal-overlay" >
                <
                div className = "modal" >
                <
                h3 > { editingProduct ? 'Edit' : 'Add' }
                Product < /h3> <
                form onSubmit = { handleSubmit } >
                <
                input type = "text"
                placeholder = "Product Name"
                value = { formData.name }
                onChange = {
                    (e) => setFormData({...formData, name: e.target.value }) }
                required /
                >
                <
                textarea placeholder = "Description"
                value = { formData.description }
                onChange = {
                    (e) => setFormData({...formData, description: e.target.value }) }
                required /
                >
                <
                input type = "text"
                placeholder = "Category"
                value = { formData.category }
                onChange = {
                    (e) => setFormData({...formData, category: e.target.value }) }
                required /
                >
                <
                input type = "number"
                placeholder = "Amount"
                value = { formData.amount }
                onChange = {
                    (e) => setFormData({...formData, amount: e.target.value }) }
                required /
                >

                <
                div className = "modal-actions" >
                <
                button type = "submit" > { editingProduct ? 'Update' : 'Create' } < /button> <
                button type = "button"
                onClick = {
                    () => {
                        setShowForm(false);
                        setEditingProduct(null);
                        setFormData({ name: '', description: '', category: '', amount: '' });
                    }
                } > Cancel < /button> <
                /div> <
                /form> <
                /div> <
                /div>
            )
        }

        <
        div className = "products-list" >
        <
        table >
        <
        thead >
        <
        tr >
        <
        th > Name < /th> <
        th > Description < /th> <
        th > Category < /th> <
        th > Amount < /th> <
        th > Actions < /th> <
        /tr> <
        /thead> <
        tbody > {
            products.map((product) => ( <
                tr key = { product.id } >
                <
                td > { product.name } < /td> <
                td > { product.description } < /td> <
                td > { product.category } < /td> <
                td > { product.amount } < /td> <
                td >
                <
                button onClick = {
                    () => handleEdit(product) } > Edit < /button> <
                button onClick = {
                    () => handleDelete(product.id) } > Delete < /button> <
                /td> <
                /tr>
            ))
        } <
        /tbody> <
        /table> <
        /div> <
        /div>
    );
};

export default ProductManagement;
