import React, { useState, useEffect } from 'react';
import './index.css';

// ----------------------
// Mock Data and Components
// ----------------------

// Mock user data for authentication.
// In a real application, this would come from a database.
const mockUsers = [{
    phoneNumber: '111-222-3333',
    password: 'password',
    role: 'Federal',
    fullName: 'Federal Admin',
}, {
    phoneNumber: '444-555-6666',
    password: 'password',
    role: 'Region',
    fullName: 'Region Admin',
}, {
    phoneNumber: '777-888-9999',
    password: 'password',
    role: 'Zone',
    fullName: 'Zone Admin',
}, {
    phoneNumber: '987-654-3210',
    password: 'password',
    role: 'Woreda',
    fullName: 'Woreda Admin',
}, {
    phoneNumber: '987-654-3211',
    password: 'password',
    role: 'Kebele',
    fullName: 'Kebele Rep',
}, {
    phoneNumber: '123-456-7890',
    password: 'password',
    role: 'Farmer',
    fullName: 'Jane Doe',
}, ];

const Modal = ({ show, onClose, title, message }) => {
    if (!show) return null;
    return ( <
        div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50" >
        <
        div className = "relative p-5 border w-96 shadow-lg rounded-md bg-white" >
        <
        div className = "mt-3 text-center" >
        <
        h3 className = "text-lg leading-6 font-medium text-gray-900" > { title } < /h3> <
        div className = "mt-2 px-7 py-3" >
        <
        p className = "text-sm text-gray-500" > { message } < /p> <
        /div> <
        div className = "items-center px-4 py-3" >
        <
        button onClick = { onClose }
        className = "px-4 py-2 bg-blue-500 text-white text-base font-medium rounded-md w-full shadow-sm hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500" >
        OK <
        /button> <
        /div> <
        /div> <
        /div> <
        /div>
    );
};

// ----------------------
// LoginPage Component
// ----------------------
const LoginPage = ({ onLogin }) => {
    const [phoneNumber, setPhoneNumber] = useState('');
    const [password, setPassword] = useState('');
    const [showModal, setShowModal] = useState(false);
    const [modalMessage, setModalMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const user = mockUsers.find(
            (u) => u.phoneNumber === phoneNumber && u.password === password
        );
        if (user) {
            onLogin(user);
        } else {
            setModalMessage('Invalid phone number or password.');
            setShowModal(true);
        }
    };

    return ( <
        div className = "flex items-center justify-center min-h-screen bg-gray-200" >
        <
        div className = "bg-white p-8 rounded-xl shadow-2xl w-full max-w-md transform transition-all duration-300" >
        <
        h2 className = "text-4xl font-extrabold text-center text-gray-800 mb-6" >
        FMS Login <
        /h2> <
        form onSubmit = { handleSubmit }
        className = "space-y-6" >
        <
        div >
        <
        label htmlFor = "phoneNumber"
        className = "text-sm font-medium text-gray-700 block mb-1" >
        Phone Number <
        /label> <
        input type = "text"
        id = "phoneNumber"
        value = { phoneNumber }
        onChange = {
            (e) => setPhoneNumber(e.target.value) }
        className = "w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition"
        placeholder = "e.g., 987-654-3210" /
        >
        <
        /div> <
        div >
        <
        label htmlFor = "password"
        className = "text-sm font-medium text-gray-700 block mb-1" >
        Password <
        /label> <
        input type = "password"
        id = "password"
        value = { password }
        onChange = {
            (e) => setPassword(e.target.value) }
        className = "w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition"
        placeholder = "Enter your password" /
        >
        <
        /div> <
        button type = "submit"
        className = "w-full p-3 text-white bg-blue-600 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 font-semibold transition-all duration-200 shadow-lg" >
        Log In <
        /button> <
        /form> <
        /div> <
        Modal show = { showModal }
        onClose = {
            () => setShowModal(false) }
        title = "Login Failed"
        message = { modalMessage }
        /> <
        /div>
    );
};

// ----------------------
// Dashboard Components
// ----------------------

// These are placeholder components that would contain the actual UI for each role.
const FederalDashboard = () => < div className = "p-8" >
    <
    h1 className = "text-3xl font-bold text-gray-800" > Federal Admin Dashboard < /h1> <
    p className = "mt-4 text-gray-600" > You can view all requests,
    manage products globally, and create regional admins. < /p> <
