// src/dashboards/FederalDashboard.js
import React, { useState } from 'react';

export default function FederalDashboard() {
    const [activeMenu, setActiveMenu] = useState('requests');

    return ( <
            div className = "flex h-screen bg-gray-100" > { /* Sidebar */ } <
            div className = "w-64 bg-white shadow-md" >
            <
            div className = "p-6 text-xl font-bold border-b" > üè†Dashboards < /div> <
            nav className = "mt-6" >
            <
            button onClick = {
                () => setActiveMenu('requests') }
            className = { `w-full text-left px-6 py-3 hover:bg-gray-200 ${
              activeMenu === 'requests' ? 'bg-gray-200 font-semibold' : ''
            }` } >
            üì¶Product Requests <
            /button> <
            button onClick = {
                () => setActiveMenu('approvals') }
            className = { `w-full text-left px-6 py-3 hover:bg-gray-200 ${
              activeMenu === 'approvals' ? 'bg-gray-200 font-semibold' : ''
            }` } >
            ‚úÖApprovals <
            /button> <
            button onClick = {
                () => setActiveMenu('master') }
            className = { `w-full text-left px-6 py-3 hover:bg-gray-200 ${
              activeMenu === 'master' ? 'bg-gray-200 font-semibold' : ''
            }` } >
            üõ†Master Data <
            /button> <
            button onClick = {
                () => setActiveMenu('reports') }
            className = { `w-full text-left px-6 py-3 hover:bg-gray-200 ${
              activeMenu === 'reports' ? 'bg-gray-200 font-semibold' : ''
            }` } >
            üìäReports <
            /button> <
            /nav> <
            /div>

            { /* Main Content */ } <
            div className = "flex-1 p-6 overflow-auto" > {
                activeMenu === 'requests' && < div > üì¶Here are all product requests < /div>} {
                    activeMenu === 'approvals' && < div > ‚úÖHere you can approve / reject requests < /div>} {
                        activeMenu === 'master' && < div > üõ†Manage admins and products < /div>} {
                            activeMenu === 'reports' && < div > üìäReports content goes here < /div>} <
                                /div> <
                                /div>
                        );
                    }
