import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ZoneDashboard.css';

const ZoneDashboard = () => {
    const [activeTab, setActiveTab] = useState('overview');
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    // Woreda Admin Management State
    const [woredaAdmins, setWoredaAdmins] = useState([]);
    const [showWoredaForm, setShowWoredaForm] = useState(false);
    const [editingWoreda, setEditingWoreda] = useState(null);
    const [woredaForm, setWoredaForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        woreda_name: ''
    });
    const [woredaError, setWoredaError] = useState('');

    // Farmer Management State
    const [farmers, setFarmers] = useState([]);
    const [showFarmerForm, setShowFarmerForm] = useState(false);
    const [editingFarmer, setEditingFarmer] = useState(null);
    const [farmerForm, setFarmerForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        woreda_name: '',
        kebele_name: ''
    });
    const [farmerError, setFarmerError] = useState('');

    // Product Management State
    const [products, setProducts] = useState([]);
    const [showProductForm, setShowProductForm] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        amount: '',
        description: ''
    });

    // Request Management State
    const [requests, setRequests] = useState([]);
    const [selectedRequest, setSelectedRequest] = useState(null);

    // Stats State
    const [stats, setStats] = useState({
        totalRequests: 0,
        pendingRequests: 0,
        approvedRequests: 0,
        rejectedRequests: 0,
        totalWoredaAdmins: 0,
        totalFarmers: 0,
        totalProducts: 0
    });

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Zone' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
        fetchDashboardData();
    }, [navigate]);

    const fetchDashboardData = async() => {
        try {
            const token = localStorage.getItem('token');

            // Fetch woreda admins
            const adminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (adminsResponse.ok) {
                const adminsData = await adminsResponse.json();
                // Filter to only show Woreda admins (Zone admin can only create Woreda admins)
                const woredaAdminsData = adminsData.filter(admin => admin.role === 'Woreda');
                setWoredaAdmins(woredaAdminsData);
                setStats(prev => ({...prev, totalWoredaAdmins: woredaAdminsData.length }));
            }

            // Fetch farmers
            const farmersResponse = await fetch('http://localhost:5000/api/admins/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (farmersResponse.ok) {
                const allData = await farmersResponse.json();
                const farmersData = allData.filter(item => item.type === 'farmer');
                setFarmers(farmersData);
                setStats(prev => ({...prev, totalFarmers: farmersData.length }));
            }

            // Fetch products
            const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
                setStats(prev => ({...prev, totalProducts: productsData.length }));
            }

            // Fetch requests
            const requestsResponse = await fetch('http://localhost:5000/api/admins/getrequests', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                setRequests(requestsData);

                // Calculate stats from requests data
                const totalRequests = requestsData.length;
                const pendingRequests = requestsData.filter(req => req.status === 'pending').length;
                const approvedRequests = requestsData.filter(req => req.status === 'accepted').length;
                const rejectedRequests = requestsData.filter(req => req.status === 'rejected').length;

                setStats(prev => ({
                    ...prev,
                    totalRequests,
                    pendingRequests,
                    approvedRequests,
                    rejectedRequests
                }));
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('userType');
        navigate('/login');
    };

    // Woreda Admin Management Functions
    const handleWoredaSubmit = async(e) => {
        e.preventDefault();
        setWoredaError('');

        if (woredaForm.password !== woredaForm.confirmPassword && !editingWoreda) {
            setWoredaError('Passwords do not match');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            let url, method;

            if (editingWoreda) {
                // Update existing woreda admin
                url = `http://localhost:5000/api/admins/edit/${editingWoreda.id}`;
                method = 'PUT';
            } else {
                // Create new woreda admin
                url = 'http://localhost:5000/api/admins/register';
                method = 'POST';
            }

            // Prepare data for submission
            const submitData = {
                fullName: woredaForm.fullName,
                phoneNumber: woredaForm.phoneNumber,
                password: woredaForm.password,
                role: 'Woreda',
                region_name: user.region_name,
                zone_name: user.zone_name,
                woreda_name: woredaForm.woreda_name
            };

            // Don't send password if editing and password is empty
            if (editingWoreda && !submitData.password) {
                delete submitData.password;
            }

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(submitData)
            });

            const data = await response.json();

            if (response.ok) {
                setShowWoredaForm(false);
                setEditingWoreda(null);
                setWoredaForm({
                    fullName: '',
                    phoneNumber: '',
                    password: '',
                    confirmPassword: '',
                    woreda_name: ''
                });
                fetchDashboardData();
            } else {
                setWoredaError(data.message || 'Error processing woreda admin');
            }
        } catch (error) {
            console.error('Error processing woreda admin:', error);
            setWoredaError('Server error occurred');
        }
    };

    const deleteWoredaAdmin = async(adminId) => {
        if (!window.confirm('Are you sure you want to delete this woreda admin?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/delete/${adminId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
            } else {
                const errorData = await response.json();
                console.error('Error deleting woreda admin:', errorData.message);
                alert(errorData.message || 'Error deleting woreda admin');
            }
        } catch (error) {
            console.error('Error deleting woreda admin:', error);
            alert('Error deleting woreda admin');
        }
    };

    const editWoredaAdmin = (admin) => {
        setEditingWoreda(admin);
        setWoredaForm({
            fullName: admin.full_name || '',
            phoneNumber: admin.phone_number || '',
            password: '',
            confirmPassword: '',
            woreda_name: admin.woreda_name || ''
        });
        setShowWoredaForm(true);
    };

    // Farmer Management Functions
    const handleFarmerSubmit = async(e) => {
        e.preventDefault();
        setFarmerError('');

        if (farmerForm.password !== farmerForm.confirmPassword && !editingFarmer) {
            setFarmerError('Passwords do not match');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const url = 'http://localhost:5000/api/admins/createfarmer';
            const method = 'POST';

            // Prepare data for submission
            const submitData = {
                fullName: farmerForm.fullName,
                phoneNumber: farmerForm.phoneNumber,
                password: farmerForm.password,
                role: 'Farmer',
                region_name: user.region_name,
                zone_name: user.zone_name,
                woreda_name: farmerForm.woreda_name,
                kebele_name: farmerForm.kebele_name
            };

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(submitData)
            });

            const data = await response.json();

            if (response.ok) {
                setShowFarmerForm(false);
                setEditingFarmer(null);
                setFarmerForm({
                    fullName: '',
                    phoneNumber: '',
                    password: '',
                    confirmPassword: '',
                    woreda_name: '',
                    kebele_name: ''
                });
                fetchDashboardData();
            } else {
                setFarmerError(data.message || 'Error processing farmer');
            }
        } catch (error) {
            console.error('Error processing farmer:', error);
            setFarmerError('Server error occurred');
        }
    };

    // Product Management Functions
    const handleProductSubmit = async(e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const url = editingProduct ?
                `http://localhost:5000/api/admins/editproduct/${editingProduct.id}` :
                'http://localhost:5000/api/admins/addproduct';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(productForm)
            });

            if (response.ok) {
                setShowProductForm(false);
                setEditingProduct(null);
                setProductForm({ name: '', category: '', amount: '', description: '' });
                fetchDashboardData();
            } else {
                const errorData = await response.json();
                console.error('Error saving product:', errorData.message);
                alert(errorData.message || 'Error saving product');
            }
        } catch (error) {
            console.error('Error saving product:', error);
            alert('Error saving product');
        }
    };

    const deleteProduct = async(productId) => {
        if (!window.confirm('Are you sure you want to delete this product?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/deleteproduct/${productId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
            } else {
                const errorData = await response.json();
                console.error('Error deleting product:', errorData.message);
                alert(errorData.message || 'Error deleting product');
            }
        } catch (error) {
            console.error('Error deleting product:', error);
            alert('Error deleting product');
        }
    };

    const editProduct = (product) => {
        setEditingProduct(product);
        setProductForm({
            name: product.name || '',
            category: product.category || '',
            amount: product.amount || '',
            description: product.description || ''
        });
        setShowProductForm(true);
    };

    // Request Management Functions
    const updateRequestStatus = async(requestId, status) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/editrequest/${requestId}/status`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ status })
            });

            if (response.ok) {
                setSelectedRequest(null);
                fetchDashboardData();
            } else {
                const errorData = await response.json();
                console.error('Error updating request:', errorData.message);
                alert(errorData.message || 'Error updating request');
            }
        } catch (error) {
            console.error('Error updating request:', error);
            alert('Error updating request');
        }
    };

    if (!user) {
        return <div className = "loading" > Loading... < /div>;
    }

    return ( <
        div className = "zone-dashboard" > { /* Sidebar */ } <
        div className = "sidebar" >
        <
        h2 > Zone Dashboard < /h2> <
        nav >
        <
        button className = { activeTab === 'overview' ? 'active' : '' }
        onClick = {
            () => setActiveTab('overview')
        } > 📊Overview <
        /button> <
        button className = { activeTab === 'woredas' ? 'active' : '' }
        onClick = {
            () => setActiveTab('woredas')
        } > 👥Woreda Admins <
        /button> <
        button className = { activeTab === 'farmers' ? 'active' : '' }
        onClick = {
            () => setActiveTab('farmers')
        } > 👨‍🌾Farmers <
        /button> <
        button className = { activeTab === 'products' ? 'active' : '' }
        onClick = {
            () => setActiveTab('products')
        } > 📦Products <
        /button> <
        button className = { activeTab === 'requests' ? 'active' : '' }
        onClick = {
            () => setActiveTab('requests')
        } > 📋Requests <
        /button> < /
        nav > <
        /div>

        { /* Main Content */ } <
        div className = "main-content" >
        <
        header >
        <
        h1 > Welcome, { user.fullName }(Zone Admin) < /h1> <
        p > Zone: { user.zone_name } | Region: { user.region_name } < /p> <
        button onClick = { handleLogout }
        className = "logout-btn" >
        Logout <
        /button> < /
        header >

        <
        main > { /* Overview Tab */ } {
            activeTab === 'overview' && ( <
                div className = "overview-tab" >
                <
                h2 > System Overview < /h2> <
                div className = "stats-grid" >
                <
                div className = "stat-card" >
                <
                h3 > { stats.totalRequests } < /h3> <
                p > Total Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.pendingRequests } < /h3> <
                p > Pending Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.approvedRequests } < /h3> <
                p > Approved Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.rejectedRequests } < /h3> <
                p > Rejected Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.totalWoredaAdmins } < /h3> <
                p > Woreda Admins < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.totalFarmers } < /h3> <
                p > Farmers < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.totalProducts } < /h3> <
                p > Products < /p> < /
                div > <
                /div>

                <
                div className = "quick-actions" >
                <
                h2 > Quick Actions < /h2> <
                div className = "actions-grid" >
                <
                button onClick = {
                    () => setActiveTab('woredas')
                }
                className = "action-btn" > 👥Create Woreda Admin <
                /button> <
                button onClick = {
                    () => setActiveTab('farmers')
                }
                className = "action-btn" > 👨‍🌾Register Farmer <
                /button> <
                button onClick = {
                    () => setActiveTab('products')
                }
                className = "action-btn" > 📦Add Product <
                /button> <
                button onClick = {
                    () => setActiveTab('requests')
                }
                className = "action-btn" > 📋Review Requests <
                /button> < /
                div > <
                /div> < /
                div >
            )
        }

        { /* Woreda Admins Tab */ } {
            activeTab === 'woredas' && ( <
                div className = "woredas-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Woreda Admin Management < /h2> <
                button onClick = {
                    () => {
                        setEditingWoreda(null);
                        setWoredaForm({
                            fullName: '',
                            phoneNumber: '',
                            password: '',
                            confirmPassword: '',
                            woreda_name: ''
                        });
                        setShowWoredaForm(true);
                    }
                }
                className = "add-btn" >
                Register Woreda Admin <
                /button> < /
                div >

                { /* Woreda Form Modal */ } {
                    showWoredaForm && ( <
                            div className = "modal" >
                            <
                            div className = "modal-content" >
                            <
                            h3 > { editingWoreda ? 'Edit Woreda Admin' : 'Register Woreda Admin' } < /h3> {
                            woredaError && ( <
                                div className = "error-message" > { woredaError } < /div>
                            )
                        } <
                        form onSubmit = { handleWoredaSubmit } >
                        <
                        input type = "text"
                    placeholder = "Full Name"
                    value = { woredaForm.fullName }
                    onChange = {
                        (e) => setWoredaForm({...woredaForm, fullName: e.target.value })
                    }
                    required /
                        >
                        <
                        input type = "tel"
                    placeholder = "Phone Number"
                    value = { woredaForm.phoneNumber }
                    onChange = {
                        (e) => setWoredaForm({...woredaForm, phoneNumber: e.target.value })
                    }
                    required /
                        >
                        {!editingWoreda && ( <
                                >
                                <
                                input type = "password"
                                placeholder = "Password"
                                value = { woredaForm.password }
                                onChange = {
                                    (e) => setWoredaForm({...woredaForm, password: e.target.value })
                                }
                                required /
                                >
                                <
                                input type = "password"
                                placeholder = "Confirm Password"
                                value = { woredaForm.confirmPassword }
                                onChange = {
                                    (e) => setWoredaForm({...woredaForm, confirmPassword: e.target.value })
                                }
                                required /
                                >
                                <
                                />
                            )
                        } {
                            editingWoreda && ( <
                                >
                                <
                                input type = "password"
                                placeholder = "New Password (leave empty to keep current)"
                                value = { woredaForm.password }
                                onChange = {
                                    (e) => setWoredaForm({...woredaForm, password: e.target.value })
                                }
                                /> <
                                input type = "password"
                                placeholder = "Confirm New Password"
                                value = { woredaForm.confirmPassword }
                                onChange = {
                                    (e) => setWoredaForm({...woredaForm, confirmPassword: e.target.value })
                                }
                                /> < /
                                >
                            )
                        } <
                        input type = "text"
                    placeholder = "Woreda Name"
                    value = { woredaForm.woreda_name }
                    onChange = {
                        (e) => setWoredaForm({...woredaForm, woreda_name: e.target.value })
                    }
                    required /
                        >
                        <
                        div className = "modal-actions" >
                        <
                        button type = "submit" > { editingWoreda ? 'Update' : 'Register' } < /button> <
                    button type = "button"
                    onClick = {
                            () => {
                                setShowWoredaForm(false);
                                setEditingWoreda(null);
                                setWoredaError('');
                                setWoredaForm({
                                    fullName: '',
                                    phoneNumber: '',
                                    password: '',
                                    confirmPassword: '',
                                    woreda_name: ''
                                });
                            }
                        } >
                        Cancel <
                        /button> < /
                        div > <
                        /form> < /
                        div > <
                        /div>
                )
            }

            { /* Woreda Admins List */ } <
            div className = "admins-list" > {
                    woredaAdmins.map((admin) => ( <
                        div key = { admin.id }
                        className = "admin-card" >
                        <
                        h3 > { admin.full_name } < /h3> <
                        p > Phone: { admin.phone_number } < /p> <
                        p > Role: { admin.role } < /p> <
                        p > Woreda: { admin.woreda_name } < /p> <
                        div className = "admin-actions" >
                        <
                        button onClick = {
                            () => editWoredaAdmin(admin)
                        }
                        className = "edit-btn" >
                        Edit <
                        /button> <
                        button onClick = {
                            () => deleteWoredaAdmin(admin.id)
                        }
                        className = "delete-btn" >
                        Delete <
                        /button> < /
                        div > <
                        /div>
                    ))
                } <
                /div> < /
                div >
        )
    }

    { /* Farmers Tab */ } {
        activeTab === 'farmers' && ( <
            div className = "farmers-tab" >
            <
            div className = "tab-header" >
            <
            h2 > Farmer Management < /h2> <
            button onClick = {
                () => {
                    setEditingFarmer(null);
                    setFarmerForm({
                        fullName: '',
                        phoneNumber: '',
                        password: '',
                        confirmPassword: '',
                        woreda_name: '',
                        kebele_name: ''
                    });
                    setShowFarmerForm(true);
                }
            }
            className = "add-btn" >
            Register Farmer <
            /button> < /
            div >

            { /* Farmer Form Modal */ } {
                showFarmerForm && ( <
                        div className = "modal" >
                        <
                        div className = "modal-content" >
                        <
                        h3 > Register Farmer < /h3> {
                        farmerError && ( <
                            div className = "error-message" > { farmerError } < /div>
                        )
                    } <
                    form onSubmit = { handleFarmerSubmit } >
                    <
                    input type = "text"
                placeholder = "Full Name"
                value = { farmerForm.fullName }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, fullName: e.target.value })
                }
                required /
                    >
                    <
                    input type = "tel"
                placeholder = "Phone Number"
                value = { farmerForm.phoneNumber }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, phoneNumber: e.target.value })
                }
                required /
                    >
                    <
                    input type = "password"
                placeholder = "Password"
                value = { farmerForm.password }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, password: e.target.value })
                }
                required /
                    >
                    <
                    input type = "password"
                placeholder = "Confirm Password"
                value = { farmerForm.confirmPassword }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, confirmPassword: e.target.value })
                }
                required /
                    >
                    <
                    input type = "text"
                placeholder = "Woreda Name"
                value = { farmerForm.woreda_name }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, woreda_name: e.target.value })
                }
                required /
                    >
                    <
                    input type = "text"
                placeholder = "Kebele Name"
                value = { farmerForm.kebele_name }
                onChange = {
                    (e) => setFarmerForm({...farmerForm, kebele_name: e.target.value })
                }
                required /
                    >
                    <
                    div className = "modal-actions" >
                    <
                    button type = "submit" > Register < /button> <
                button type = "button"
                onClick = {
                        () => {
                            setShowFarmerForm(false);
                            setEditingFarmer(null);
                            setFarmerError('');
                            setFarmerForm({
                                fullName: '',
                                phoneNumber: '',
                                password: '',
                                confirmPassword: '',
                                woreda_name: '',

                            });
                        }
                    } >
                    Cancel <
                    /button> < /
                    div > <
                    /form> < /
                    div > <
                    /div>
            )
        }

        { /* Farmers List */ } <
        div className = "farmers-list" > {
                farmers.map((farmer) => ( <
                    div key = { farmer.id }
                    className = "farmer-card" >
                    <
                    h3 > { farmer.full_name } < /h3> <
                    p > Phone: { farmer.phone_number } < /p> <
                    p > Woreda: { farmer.woreda_name } < /p> <
                    p > Kebele: { farmer.kebele_name } < /p> < /
                    div >
                ))
            } <
            /div> < /
            div >
    )
}

{ /* Products Tab */ } {
    activeTab === 'products' && ( <
        div className = "products-tab" >
        <
        div className = "tab-header" >
        <
        h2 > Product Management < /h2> <
        button onClick = {
            () => setShowProductForm(true)
        }
        className = "add-btn" >
        Add Product <
        /button> < /
        div >

        { /* Product Form Modal */ } {
            showProductForm && ( <
                div className = "modal" >
                <
                div className = "modal-content" >
                <
                h3 > { editingProduct ? 'Edit Product' : 'Add Product' } < /h3> <
                form onSubmit = { handleProductSubmit } >
                <
                input type = "text"
                placeholder = "Product Name"
                value = { productForm.name }
                onChange = {
                    (e) => setProductForm({...productForm, name: e.target.value })
                }
                required /
                >
                <
                input type = "text"
                placeholder = "Category"
                value = { productForm.category }
                onChange = {
                    (e) => setProductForm({...productForm, category: e.target.value })
                }
                required /
                >
                <
                input type = "number"
                placeholder = "Amount"
                min = "1"
                value = { productForm.amount }
                onChange = {
                    (e) => setProductForm({...productForm, amount: e.target.value })
                }
                required /
                >
                <
                textarea placeholder = "Description"
                value = { productForm.description }
                onChange = {
                    (e) => setProductForm({...productForm, description: e.target.value })
                }
                rows = "3"
                required /
                >
                <
                div className = "modal-actions" >
                <
                button type = "submit" > Save < /button> <
                button type = "button"
                onClick = {
                    () => {
                        setShowProductForm(false);
                        setEditingProduct(null);
                        setProductForm({ name: '', category: '', amount: '', description: '' });
                    }
                } >
                Cancel <
                /button> < /
                div > <
                /form> < /
                div > <
                /div>
            )
        }

        { /* Products List */ } <
        div className = "products-grid" > {
            products.map((product) => ( <
                div key = { product.id }
                className = "product-card" >
                <
                h3 > { product.name } < /h3> <
                p className = "category" > Category: { product.category } < /p> <
                p className = "amount" > Amount: { product.amount } < /p> <
                p className = "description" > { product.description } < /p> <
                div className = "product-actions" >
                <
                button onClick = {
                    () => editProduct(product)
                }
                className = "edit-btn" >
                Edit <
                /button> <
                button onClick = {
                    () => deleteProduct(product.id)
                }
                className = "delete-btn" >
                Delete <
                /button> < /
                div > <
                /div>
            ))
        } <
        /div> < /
        div >
    )
}

{ /* Requests Tab */ } {
    activeTab === 'requests' && ( <
        div className = "requests-tab" >
        <
        h2 > Request Management < /h2>

        { /* Requests List */ } <
        div className = "requests-list" > {
            requests.map((request) => ( <
                div key = { request.id }
                className = "request-card" >
                <
                div className = "request-info" >
                <
                h3 > { request.product_name } < /h3> <
                p > Farmer: { request.farmer_name } < /p> <
                p > Quantity: { request.quantity } < /p> <
                p > Status: < span className = { `status ${request.status}` } > { request.status } < /span></p >
                <
                p > Date: { new Date(request.created_at).toLocaleDateString() } < /p> < /
                div > <
                div className = "request-actions" > {
                    request.status === 'pending' && ( <
                        >
                        <
                        button onClick = {
                            () => updateRequestStatus(request.id, 'accepted')
                        }
                        className = "approve-btn" >
                        Approve <
                        /button> <
                        button onClick = {
                            () => updateRequestStatus(request.id, 'rejected')
                        }
                        className = "reject-btn" >
                        Reject <
                        /button> < /
                        >
                    )
                } {
                    request.status !== 'pending' && ( <
                        span className = "processed-text" > { request.status === 'accepted' ? 'Approved' : 'Rejected' } <
                        /span>
                    )
                } <
                /div> < /
                div >
            ))
        } <
        /div> < /
        div >
    )
} <
/main> < /
div > <
    /div>
);
};

export default ZoneDashboard;
