// src/components/MasterData.js
import React, { useState, useEffect } from 'react';
import api from '../api'; // make sure your api.js has default export

export default function MasterData() {
    const [admins, setAdmins] = useState([]);
    const [products, setProducts] = useState([]);
    const [newAdmin, setNewAdmin] = useState({ name: '', phone: '', password: '' });
    const [newProduct, setNewProduct] = useState({ name: '', category: '', description: '', amount: '' });

    useEffect(() => {
        fetchAdmins();
        fetchProducts();
    }, []);

    const fetchAdmins = async() => {
        try {
            const res = await api.get('/admins');
            setAdmins(res.data);
        } catch (err) {
            alert('Failed to fetch admins: ' + err.message);
        }
    };

    const fetchProducts = async() => {
        try {
            const res = await api.get('/products');
            setProducts(res.data);
        } catch (err) {
            alert('Failed to fetch products: ' + err.message);
        }
    };

    const addAdmin = async() => {
        if (!newAdmin.name || !newAdmin.phone || !newAdmin.password) return alert('Fill all fields');
        try {
            await api.post('/admins', newAdmin);
            setNewAdmin({ name: '', phone: '', password: '' });
            fetchAdmins();
        } catch (err) {
            alert('Failed to add admin: ' + err.message);
        }
    };

    const deleteAdmin = async(id) => {
        if (!window.confirm('Delete this admin?')) return;
        try {
            await api.delete(`/admins/${id}`);
            fetchAdmins();
        } catch (err) {
            alert('Failed to delete admin: ' + err.message);
        }
    };

    const addProduct = async() => {
        if (!newProduct.name || !newProduct.category || !newProduct.description || !newProduct.amount)
            return alert('Fill all product fields');
        try {
            await api.post('/products', newProduct);
            setNewProduct({ name: '', category: '', description: '', amount: '' });
            fetchProducts();
        } catch (err) {
            alert('Failed to add product: ' + err.message);
        }
    };

    const deleteProduct = async(id) => {
        if (!window.confirm('Delete this product?')) return;
        try {
            await api.delete(`/products/${id}`);
            fetchProducts();
        } catch (err) {
            alert('Failed to delete product: ' + err.message);
        }
    };

    return ( <
        div className = "grid grid-cols-2 gap-8" > { /* Admins */ } <
        div className = "bg-white p-4 shadow rounded" >
        <
        h2 className = "font-bold text-lg mb-4" > Regional Admins < /h2> <
        div className = "flex gap-2 mb-2" >
        <
        input className = "border p-1 flex-1"
        placeholder = "Name"
        value = { newAdmin.name }
        onChange = { e => setNewAdmin({...newAdmin, name: e.target.value }) }
        /> <
        input className = "border p-1 flex-1"
        placeholder = "Phone"
        value = { newAdmin.phone }
        onChange = { e => setNewAdmin({...newAdmin, phone: e.target.value }) }
        /> <
        input className = "border p-1 flex-1"
        placeholder = "Password"
        value = { newAdmin.password }
        onChange = { e => setNewAdmin({...newAdmin, password: e.target.value }) }
        type = "password" / >
        <
        button className = "bg-blue-500 text-white px-3 rounded"
        onClick = { addAdmin } > Add < /button> <
        /div> <
        ul > {
            admins.map(a => ( <
                li key = { a.id }
                className = "flex justify-between border-b py-1" > { a.name }({ a.phone }) <
                button className = "text-red-500"
                onClick = {
                    () => deleteAdmin(a.id) } > Delete < /button> <
                /li>
            ))
        } <
        /ul> <
        /div>

        { /* Products */ } <
        div className = "bg-white p-4 shadow rounded" >
        <
        h2 className = "font-bold text-lg mb-4" > Products < /h2> <
        div className = "flex gap-2 mb-2" >
        <
        input className = "border p-1 flex-1"
        placeholder = "Name"
        value = { newProduct.name }
        onChange = { e => setNewProduct({...newProduct, name: e.target.value }) }
        /> <
        input className = "border p-1 flex-1"
        placeholder = "Category"
        value = { newProduct.category }
        onChange = { e => setNewProduct({...newProduct, category: e.target.value }) }
        /> <
        input className = "border p-1 flex-1"
        placeholder = "Description"
        value = { newProduct.description }
        onChange = { e => setNewProduct({...newProduct, description: e.target.value }) }
        /> <
        input className = "border p-1 flex-1"
        placeholder = "Amount"
        value = { newProduct.amount }
        onChange = { e => setNewProduct({...newProduct, amount: e.target.value }) }
        /> <
        button className = "bg-green-500 text-white px-3 rounded"
        onClick = { addProduct } > Add < /button> <
        /div> <
        ul > {
            products.map(p => ( <
                li key = { p.id }
                className = "flex justify-between border-b py-1" > { p.name }({ p.category }) - { p.amount } <
                button className = "text-red-500"
                onClick = {
                    () => deleteProduct(p.id) } > Delete < /button> <
                /li>
            ))
        } <
        /ul> <
        /div> <
        /div>
    );
}
