import React, { useState } from 'react';
import './index.css';

// ----------------------
// Modal Component
// ----------------------
const Modal = ({ show, onClose, title, message }) => {
    if (!show) return null;
    return ( <
        div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50" >
        <
        div className = "relative p-5 border w-96 shadow-lg rounded-xl bg-white" >
        <
        div className = "mt-3 text-center" >
        <
        h3 className = "text-xl leading-6 font-bold text-gray-900" > { title } < /h3> <
        div className = "mt-2 px-7 py-3" >
        <
        p className = "text-sm text-gray-500" > { message } < /p> <
        /div> <
        div className = "items-center px-4 py-3" >
        <
        button onClick = { onClose }
        className = "px-4 py-2 bg-blue-600 text-white text-base font-medium rounded-lg w-full shadow-md hover:bg-blue-700 transition" >
        OK <
        /button> <
        /div> <
        /div> <
        /div> <
        /div>
    );
};

// ----------------------
// User Data, Products, and Main App
// ----------------------

// Mock data for initial state
const initialUsers = [{
    phoneNumber: '111-222-3333',
    password: 'password',
    role: 'Federal',
    fullName: 'Federal Admin',
    id: 'fed-1'
}, {
    phoneNumber: '444-555-6666',
    password: 'password',
    role: 'Region',
    fullName: 'Region Admin',
    regionName: 'Amhara',
    id: 'reg-1'
}, {
    phoneNumber: '777-888-9999',
    password: 'password',
    role: 'Zone',
    fullName: 'Zone Admin',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    id: 'zone-1'
}, {
    phoneNumber: '987-654-3210',
    password: 'password',
    role: 'Woreda',
    fullName: 'Woreda Admin',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    id: 'wor-1'
}, {
    phoneNumber: '987-654-3211',
    password: 'password',
    role: 'Kebele',
    fullName: 'Kebele Rep',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    id: 'keb-1'
}, {
    phoneNumber: '123-456-7890',
    password: 'password',
    role: 'Farmer',
    fullName: 'Jane Doe',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    id: 'farm-1'
}];

const initialProducts = [{
    id: 'prod-1',
    name: 'Fertilizer A',
    description: 'High-yield fertilizer for corn',
    regionName: 'Amhara'
}, {
    id: 'prod-2',
    name: 'Tractor',
    description: 'A large farming tractor',
    regionName: 'Amhara',
    zoneName: 'North Shewa'
}, {
    id: 'prod-3',
    name: 'Plow',
    description: 'A basic plowing tool',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet'
}];

// Main App Component
const App = () => {
    const [currentUser, setCurrentUser] = useState(null);
    const [users, setUsers] = useState(initialUsers);
    const [products, setProducts] = useState(initialProducts);
    const [requests, setRequests] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [modalContent, setModalContent] = useState({ title: '', message: '' });

    const handleLogin = (user) => {
        setCurrentUser(user);
    };

    const handleLogout = () => {
        setCurrentUser(null);
    };

    const handleRegistration = (newUser) => {
        if (users.some(u => u.phoneNumber === newUser.phoneNumber)) {
            setModalContent({ title: 'Error', message: 'User with this phone number already exists.' });
            setShowModal(true);
            return;
        }

        const newUserWithId = {
            ...newUser,
            id: `${newUser.role.toLowerCase()}-${users.length + 1}`
        };
        setUsers([...users, newUserWithId]);
        setModalContent({
            title: 'Success',
            message: `${newUser.fullName} has been registered as a ${newUser.role}. Their login details are Phone: ${newUser.phoneNumber}, Password: ${newUser.password}`
        });
        setShowModal(true);
    };

    const handleDeleteUser = (userId, role) => {
        setUsers(users.filter(u => u.id !== userId));
        setModalContent({ title: 'Success', message: `A ${role} account has been deleted.` });
        setShowModal(true);
    };

    const handleAddProduct = (newProduct) => {
        const newProductWithId = {
            ...newProduct,
            id: `prod-${products.length + 1}`
        };
        setProducts([...products, newProductWithId]);
        setModalContent({ title: 'Success', message: 'New product added successfully.' });
        setShowModal(true);
    };

    const handleUpdateProduct = (updatedProduct) => {
        setProducts(products.map(p => p.id === updatedProduct.id ? updatedProduct : p));
        setModalContent({ title: 'Success', message: 'Product updated successfully.' });
        setShowModal(true);
    };

    const handleDeleteProduct = (productId) => {
        setProducts(products.filter(p => p.id !== productId));
        setModalContent({ title: 'Success', message: 'Product deleted successfully.' });
        setShowModal(true);
    };

    const handleRequestProduct = (request) => {
        const newRequest = {
            ...request,
            id: `req-${requests.length + 1}`,
            status: 'Pending',
            createdAt: new Date().toISOString()
        };
        setRequests([...requests, newRequest]);
        setModalContent({ title: 'Request Sent', message: 'Your product request has been submitted and is pending approval.' });
        setShowModal(true);
    };

    // Conditional rendering for login page or dashboard
    if (!currentUser) {
        return <LoginPage onLogin = { handleLogin }
        users = { users }
        />;
    }

    const renderDashboard = () => {
        switch (currentUser.role) {
            case 'Federal':
                return <FederalDashboard
                onRegister = { handleRegistration }
                onDeleteUser = { handleDeleteUser }
                onAddProduct = { handleAddProduct }
                onUpdateProduct = { handleUpdateProduct }
                onDeleteProduct = { handleDeleteProduct }
                currentUser = { currentUser }
                users = { users }
                products = { products }
                />;
            case 'Region':
                return <RegionDashboard
                onRegister = { handleRegistration }
                onDeleteUser = { handleDeleteUser }
                onAddProduct = { handleAddProduct }
                onUpdateProduct = { handleUpdateProduct }
                onDeleteProduct = { handleDeleteProduct }
                currentUser = { currentUser }
                users = { users }
                products = { products }
                />;
            case 'Zone':
                return <ZoneDashboard
                onRegister = { handleRegistration }
                onDeleteUser = { handleDeleteUser }
                onAddProduct = { handleAddProduct }
                onUpdateProduct = { handleUpdateProduct }
                onDeleteProduct = { handleDeleteProduct }
                currentUser = { currentUser }
                users = { users }
                products = { products }
                />;
            case 'Woreda':
                return <WoredaDashboard
                onRegister = { handleRegistration }
                onDeleteUser = { handleDeleteUser }
                onAddProduct = { handleAddProduct }
                onUpdateProduct = { handleUpdateProduct }
                onDeleteProduct = { handleDeleteProduct }
                currentUser = { currentUser }
                users = { users }
                products = { products }
                />;
            case 'Kebele':
                return <KebeleDashboard
                onRegister = { handleRegistration }
                onAddProduct = { handleAddProduct }
                onUpdateProduct = { handleUpdateProduct }
                onDeleteProduct = { handleDeleteProduct }
                currentUser = { currentUser }
                users = { users }
                products = { products }
                />;
            case 'Farmer':
                return <FarmerDashboard
                currentUser = { currentUser }
                products = { products }
                requests = { requests }
                onRequest = { handleRequestProduct }
                />;
            default:
                return <div className = "p-8" > < h1 > Welcome, { currentUser.fullName }! < /h1></div > ;
        }
    };

    return ( <
        div className = "bg-gray-100 min-h-screen font-sans antialiased flex" >
        <
        aside className = "w-64 min-h-screen bg-gray-800 text-gray-100 flex flex-col" >
        <
        div className = "p-4 text-center text-2xl font-bold border-b border-gray-700" >
        FMS <
        /div> <
        nav className = "flex-1" >
        <
        ul className = "space-y-2 p-4" >
        <
        li >
        <
        button className = "w-full text-left p-3 rounded-lg hover:bg-gray-700 transition-colors duration-200" >
        Dashboard <
        /button> <
        /li> <
        li >
        <
        button onClick = { handleLogout }
        className = "w-full text-left p-3 rounded-lg hover:bg-gray-700 transition-colors duration-200" >
        Logout <
        /button> <
        /li> <
        /ul> <
        /nav> <
        div className = "p-4 text-sm text-center text-gray-500 border-t border-gray-700" >
        Logged in as: { currentUser.fullName }({ currentUser.role }) <
        /div> <
        /aside> <
        main className = "flex-1 overflow-x-hidden overflow-y-auto" > { renderDashboard() } <
        /main> <
        Modal show = { showModal }
        onClose = {
            () => setShowModal(false) }
        title = { modalContent.title }
        message = { modalContent.message }
        /> <
        /div>
    );
};

// ----------------------
// LoginPage Component
// ----------------------
const LoginPage = ({ onLogin, users }) => {
    const [phoneNumber, setPhoneNumber] = useState('');
    const [password, setPassword] = useState('');
    const [showModal, setShowModal] = useState(false);
    const [modalMessage, setModalMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const user = users.find(
            (u) => u.phoneNumber === phoneNumber && u.password === password
        );
        if (user) {
            onLogin(user);
        } else {
            setModalMessage('Invalid phone number or password.');
            setShowModal(true);
        }
    };

    return ( <
        div className = "flex items-center justify-center min-h-screen bg-gray-200" >
        <
        div className = "bg-white p-8 rounded-xl shadow-2xl w-full max-w-md transform transition-all duration-300" >
        <
        h2 className = "text-4xl font-extrabold text-center text-gray-800 mb-6" > FMS Login < /h2> <
        form onSubmit = { handleSubmit }
        className = "space-y-6" >
        <
        div >
        <
        label htmlFor = "phoneNumber"
        className = "text-sm font-medium text-gray-700 block mb-1" >
        Phone Number <
        /label> <
        input type = "text"
        id = "phoneNumber"
        value = { phoneNumber }
        onChange = {
            (e) => setPhoneNumber(e.target.value) }
        className = "w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition"
        placeholder = "e.g., 987-654-3210" /
        >
        <
        /div> <
        div >
        <
        label htmlFor = "password"
        className = "text-sm font-medium text-gray-700 block mb-1" >
        Password <
        /label> <
        input type = "password"
        id = "password"
        value = { password }
        onChange = {
            (e) => setPassword(e.target.value) }
        className = "w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition"
        placeholder = "Enter your password" /
        >
        <
        /div> <
        button type = "submit"
        className = "w-full p-3 text-white bg-blue-600 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 font-semibold transition-all duration-200 shadow-lg" >
        Log In <
        /button> <
        /form> <
        /div> <
        Modal show = { showModal }
        onClose = {
            () => setShowModal(false) }
        title = "Login Failed"
        message = { modalMessage }
        /> <
        /div>
    );
};

// ----------------------
// User Management Component
// ----------------------
const UserManagement = ({ users, role, onDeleteUser }) => {
    const nextLevelRole = {
        'Federal': 'Region',
        'Region': 'Zone',
        'Zone': 'Woreda',
        'Woreda': 'Kebele'
    }[role];

    const usersToManage = users.filter(user => user.role === nextLevelRole);

    return ( <
        div className = "bg-white p-6 rounded-xl shadow-md mt-8" >
        <
        h2 className = "text-2xl font-bold mb-4" > Manage { nextLevelRole }
        Admins < /h2> {
            usersToManage.length > 0 ? ( <
                ul className = "divide-y divide-gray-200" > {
                    usersToManage.map(user => ( <
                        li key = { user.id }
                        className = "py-4 flex justify-between items-center" >
                        <
                        div >
                        <
                        div className = "text-lg font-medium text-gray-900" > { user.fullName } < /div> <
                        div className = "text-sm text-gray-500" > Phone: { user.phoneNumber } < /div> <
                        /div> <
                        button onClick = {
                            () => onDeleteUser(user.id, user.role) }
                        className = "px-4 py-2 bg-red-600 text-white font-medium rounded-lg hover:bg-red-700 transition shadow-md" >
                        Delete <
                        /button> <
                        /li>
                    ))
                } <
                /ul>
            ) : ( <
                p className = "text-gray-500" > No { nextLevelRole }
                admins to display. < /p>
            )
        } <
        /div>
    );
};

// ----------------------
// Product Management Component
// ----------------------
const ProductManagement = ({ products, onAddProduct, onUpdateProduct, onDeleteProduct, currentUser, role }) => {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [editProduct, setEditProduct] = useState(null);

    const handleSubmit = (e) => {
        e.preventDefault();
        const newProduct = {
            name,
            description,
            // Add location details based on the current user's role and location
            ...(currentUser.regionName && { regionName: currentUser.regionName }),
            ...(currentUser.zoneName && { zoneName: currentUser.zoneName }),
            ...(currentUser.woredaName && { woredaName: currentUser.woredaName }),
            ...(currentUser.kebeleName && { kebeleName: currentUser.kebeleName }),
        };

        if (editProduct) {
            onUpdateProduct({...editProduct, ...newProduct });
            setEditProduct(null);
        } else {
            onAddProduct(newProduct);
        }

        // Clear form
        setName('');
        setDescription('');
    };

    const handleEditClick = (product) => {
        setEditProduct(product);
        setName(product.name);
        setDescription(product.description);
    };

    const productsToManage = products.filter(p => {
        if (role === 'Federal') return true;
        if (role === 'Region') return p.regionName === currentUser.regionName;
        if (role === 'Zone') return p.zoneName === currentUser.zoneName;
        if (role === 'Woreda') return p.woredaName === currentUser.woredaName;
        if (role === 'Kebele') return p.kebeleName === currentUser.kebeleName;
        return false;
    });

    return ( <
        div className = "bg-white p-6 rounded-xl shadow-md mt-8" >
        <
        h2 className = "text-2xl font-bold mb-4" > Manage Products < /h2> <
        form onSubmit = { handleSubmit }
        className = "space-y-4 mb-6" >
        <
        div >
        <
        label className = "block text-sm font-medium text-gray-700" > Product Name < /label> <
        input type = "text"
        value = { name }
        onChange = {
            (e) => setName(e.target.value) }
        className = "mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm"
        required / >
        <
        /div> <
        div >
        <
        label className = "block text-sm font-medium text-gray-700" > Description < /label> <
        textarea value = { description }
        onChange = {
            (e) => setDescription(e.target.value) }
        rows = "3"
        className = "mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm"
        required > < /textarea> <
        /div> <
        button type = "submit"
        className = "w-full px-4 py-2 bg-green-600 text-white font-medium rounded-lg hover:bg-green-700 transition shadow-md" > { editProduct ? 'Update Product' : 'Add Product' } <
        /button> <
        /form>

        <
        h3 className = "text-xl font-bold mb-2" > Available Products < /h3> {
            productsToManage.length > 0 ? ( <
                ul className = "divide-y divide-gray-200" > {
                    productsToManage.map(product => ( <
                        li key = { product.id }
                        className = "py-4 flex justify-between items-center" >
                        <
                        div >
                        <
                        div className = "text-lg font-medium text-gray-900" > { product.name } < /div> <
                        div className = "text-sm text-gray-500" > { product.description } < /div> <
                        /div> <
                        div className = "space-x-2" >
                        <
                        button onClick = {
                            () => handleEditClick(product) }
                        className = "px-3 py-1 bg-yellow-500 text-white text-sm font-medium rounded-lg hover:bg-yellow-600 transition" >
                        Edit <
                        /button> <
                        button onClick = {
                            () => onDeleteProduct(product.id) }
                        className = "px-3 py-1 bg-red-600 text-white text-sm font-medium rounded-lg hover:bg-red-700 transition" >
                        Delete <
                        /button> <
                        /div> <
                        /li>
                    ))
                } <
                /ul>
            ) : ( <
                p className = "text-gray-500" > No products to display. < /p>
            )
        } <
        /div>
    );
};

// ----------------------
// Dashboard Components
// ----------------------

const FederalDashboard = ({ onRegister, onDeleteUser, onAddProduct, onUpdateProduct, onDeleteProduct, currentUser, users, products }) => ( <
    div className = "p-8" >
    <
    h1 className = "text-3xl font-bold text-gray-800 mb-6" > Federal Admin Dashboard < /h1> <
    div className = "grid grid-cols-1 md:grid-cols-2 gap-8" >
    <
    div className = "bg-white p-6 rounded-xl shadow-md" >
    <
    h2 className = "text-2xl font-bold mb-4" > Create Regional Admin < /h2> <
    RegistrationForm onRegister = { onRegister }
    role = "Region"
    locationKeys = {
        ['regionName'] }
    /> <
    /div> <
    UserManagement users = { users }
    role = "Federal"
    onDeleteUser = { onDeleteUser }
    /> <
    ProductManagement products = { products }
