const FarmerDashboard = ({ currentUser, products, requests, onRequest }) => {
    const [selectedProduct, setSelectedProduct] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (selectedProduct) {
            const product = products.find(p => p.id === selectedProduct);
            onRequest({
                productId: product.id,
                productName: product.name,
                farmerId: currentUser.id,
                farmerName: currentUser.fullName,
                ...(currentUser.regionName && { regionName: currentUser.regionName }),
                ...(currentUser.zoneName && { zoneName: currentUser.zoneName }),
                ...(currentUser.woredaName && { woredaName: currentUser.woredaName }),
                ...(currentUser.kebeleName && { kebeleName: currentUser.kebeleName }),
            });
            setSelectedProduct('');
        }
    };

    const availableProducts = products.filter(p => p.kebeleName === currentUser.kebeleName);
    const myRequests = requests.filter(r => r.farmerId === currentUser.id);

    return ( <
        div className = "p-8" >
        <
        h1 className = "text-3xl font-bold text-gray-800 mb-6" > Farmer Dashboard < /h1> <
        div className = "grid grid-cols-1 md:grid-cols-2 gap-8" >
        <
        div className = "bg-white p-6 rounded-xl shadow-md" >
        <
        h2 className = "text-2xl font-bold mb-4" > Request a Product < /h2> <
        form onSubmit = { handleSubmit }
        className = "space-y-4" >
        <
        div >
        <
        label className = "block text-sm font-medium text-gray-700" > Select Product < /label> <
        select value = { selectedProduct }
        onChange = {
            (e) => setSelectedProduct(e.target.value) }
        className = "mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm"
        required >
        <
        option value = "" > --Select a product-- < /option> {
            availableProducts.map(product => ( <
                option key = { product.id }
                value = { product.id } > { product.name } < /option>
            ))
        } <
        /select> <
        /div> <
        button type = "submit"
        className = "w-full px-4 py-2 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition shadow-md" >
        Submit Request <
        /button> <
        /form> <
        /div> <
        div className = "bg-white p-6 rounded-xl shadow-md" >
        <
        h2 className = "text-2xl font-bold mb-4" > My Request Status < /h2> {
            myRequests.length > 0 ? ( <
                ul className = "divide-y divide-gray-200" > {
                    myRequests.map(request => ( <
                        li key = { request.id }
                        className = "py-4 flex justify-between items-center" >
                        <
                        div >
                        <
                        div className = "text-lg font-medium text-gray-900" > { request.productName } < /div> <
                        div className = "text-sm text-gray-500" > Status: { request.status } < /div> <
                        /div> <
                        span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${request.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' : 'bg-green-100 text-green-800'}` } > { request.status } <
                        /span> <
                        /li>
                    ))
                } <
                /ul>
            ) : ( <
                p className = "text-gray-500" > You have no pending requests. < /p>
            )
        } <
        /div> <
        /div> <
        /div>
    );
};

// Generic Registration Form for Admins and Farmers
const RegistrationForm = ({ onRegister, role, locationKeys = [], currentUserLocation = {} }) => {
    const [fullName, setFullName] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [password, setPassword] = useState('');
    const [locationValues, setLocationValues] = useState(
        locationKeys.reduce((acc, key) => ({...acc, [key]: '' }), {})
    );

    const handleSubmit = (e) => {
        e.preventDefault();
        const newUser = {
            fullName,
            phoneNumber,
            password,
            role,
            ...currentUserLocation,
            ...locationValues,
        };
        onRegister(newUser);
        setFullName('');
        setPhoneNumber('');
        setPassword('');
        setLocationValues(locationKeys.reduce((acc, key) => ({...acc, [key]: '' }), {}));
    };

    return ( <
        form onSubmit = { handleSubmit }
        className = "space-y-4" >
        <
        div >
        <
        label className = "block text-sm font-medium text-gray-700" > Full Name < /label> <
        input type = "text"
        value = { fullName }
        onChange = {
            (e) => setFullName(e.target.value) }
        className = "mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm"
        required / >
        <
        /div> <
        div >
        <
        label className = "block text-sm font-medium text-gray-700" > Phone Number < /label> <
        input type = "text"
        value = { phoneNumber }
        onChange = {
            (e) => setPhoneNumber(e.target.value) }
        className = "mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm"
        required / >
        <
        /div> <
        div >
        <
        label className = "block text-sm font-medium text-gray-700" > Password < /label> <
        input type = "password"
        value = { password }
        onChange = {
            (e) => setPassword(e.target.value) }
        className = "mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm"
        required / >
        <
        /div> {
            locationKeys.map(key => ( <
                div key = { key } >
                <
                label className = "block text-sm font-medium text-gray-700" > { key.replace('Name', '').replace(/([A-Z])/g, ' $1').trim() }
                Name < /label> <
                input type = "text"
                value = { locationValues[key] }
                onChange = {
                    (e) => setLocationValues({...locationValues, [key]: e.target.value }) }
                className = "mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm"
                required /
                >
                <
                /div>
            ))
        } <
        button type = "submit"
        className = "w-full px-4 py-2 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition shadow-md" >
        Create { role }
        Account <
        /button> <
        /form>
    );
};


export default App;
