import React from 'react';

function FarmerDashboard({ currentUser, products, requests, onRequest }) {
    return ( <
        div >
        <
        h2 > Farmer Dashboard - Welcome { currentUser.username } < /h2>

        <
        h3 > Products < /h3> <
        ul > {
            products.map(product => ( <
                li key = { product.id } > { product.name } - { product.description } <
                button onClick = {
                    () => onRequest(product.id) } > Request < /button> <
                /li>
            ))
        } <
        /ul>

        <
        h3 > Your Requests < /h3> <
        ul > {
            requests.map(req => ( <
                li key = { req.id } > { req.productName || 'Unknown' } - Status: { req.status || 'Pending' } <
                /li>
            ))
        } <
        /ul> <
        /div>
    );
}

export default FarmerDashboard;
