import React, { useEffect, useState } from 'react';
import api from '../api';

export default function ZoneDashboard() {
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);
    const reload = async() => {
        setLoading(true);
        try { setRequests(await api('/api/requests')); } catch (e) { console.error(e); }
        setLoading(false);
    };
    useEffect(() => { reload(); }, []);

    const setStatus = async(id, action) => {
        await api(`/api/requests/${id}/status`, { method: 'PUT', body: { action } });
        reload();
    };
    const del = async(id) => {
        await api(`/api/requests/${id}`, { method: 'DELETE' });
        reload();
    };

    return ( <
        div >
        <
        h2 > Zone Dashboard < /h2> {
        loading ? 'Loading...' : < Table rows = { requests }
        onAction = { setStatus }
        onDelete = { del }
        />} < /
        div >
    );
}

function Table({ rows, onAction, onDelete }) {
    return ( <
        table >
        <
        thead > < tr >
        <
        th > # < /th><th>Farmer</th > < th > Phone < /th><th>Product</th > < th > Qty < /th> <
        th > Woreda / Kebele < /th><th>Status</th > < th > Action < /th> < /
        tr > < /thead > <
        tbody > {
            rows.map(r => ( <
                    tr key = { r.id } >
                    <
                    td > { r.id } < /td><td>{r.farmer_name}</td > < td > { r.farmer_phone } < /td> <
                    td > { r.product_name } < /td><td>{r.quantity}</td >
                    <
                    td > {
                        [r.woreda_name, r.kebele_name].filter(Boolean).join(' / ')
                    } < /td> <
                    td > { r.status } < /td> <
                    td >
                    <
                    label > < input type = "radio"
                    name = { `act-${r.id}` }
                    onChange = {
                        () => onAction(r.id, 'Accept')
                    }
                    /> Accept</label >
                    <
                    label > < input type = "radio"
                    name = { `act-${r.id}` }
                    onChange = {
                        () => onAction(r.id, 'Approve')
                    }
                    /> Approve</label >
                    <
                    label > < input type = "radio"
                    name = { `act-${r.id}` }
                    onChange = {
                        () => onAction(r.id, 'Reject')
                    }
                    /> Reject</label > {
                        r.status === 'Rejected' && < button onClick = {
                            () => onDelete(r.id)
                        } > Delete < /button>} < /
                        td > <
                        /tr>
                    ))
            } <
            /tbody> < /
            table >
        );
    }
