import React, { useState, useEffect } from 'react';
import './index.css';
import { useForm } from 'react-hook-form';
import {
    UserPlus,
    LogOut,
    Package,
    CheckCircle,
    XCircle,
    ArrowUpCircle,
    Trash2,
    List,
    RefreshCcw,
    LayoutDashboard,
    Settings,
    Eye,
} from 'lucide-react';

// ----------------------
// Mock Data (Simulates a database)
// This data will be used to initialize the application state.
// ----------------------
const initialUsers = [{
    phoneNumber: '111-222-3333',
    password: 'password',
    role: 'Federal',
    fullName: 'Federal Admin',
    id: 'fed-1'
}, {
    phoneNumber: '444-555-6666',
    password: 'password',
    role: 'Region',
    fullName: 'Amhara Regional Admin',
    regionName: 'Amhara',
    id: 'reg-1'
}, {
    phoneNumber: '777-888-9999',
    password: 'password',
    role: 'Zone',
    fullName: 'North Shewa Zone Admin',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    id: 'zone-1'
}, {
    phoneNumber: '987-654-3210',
    password: 'password',
    role: 'Woreda',
    fullName: 'Kewet Woreda Admin',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    id: 'wor-1'
}, {
    phoneNumber: '987-654-3211',
    password: 'password',
    role: 'Kebele',
    fullName: 'Alem Ketema Kebele Rep',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    id: 'keb-1'
}, {
    phoneNumber: '123-456-7890',
    password: 'password',
    role: 'Farmer',
    fullName: 'Abebe Kebede',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    id: 'farm-1'
}];

const initialProducts = [{
    id: 'prod-1',
    name: 'Tractor',
    description: 'Used for plowing and land prep',
    category: 'Machinery',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet'
}, {
    id: 'prod-2',
    name: 'Fertilizer A',
    description: 'High-yield fertilizer for corn',
    category: 'Fertilizer',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet'
}, {
    id: 'prod-3',
    name: 'Harvester',
    description: 'Used for harvesting teff',
    category: 'Machinery',
    regionName: 'Amhara'
}];

const initialRequests = [{
    id: 'REQ12345',
    requesterId: 'farm-1',
    requesterName: 'Abebe Kebede',
    productName: 'Tractor',
    amount: 1,
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    statusHistory: [{
        status: 'Pending',
        level: 'Kebele',
        timestamp: new Date().toISOString()
    }]
}, {
    id: 'REQ12346',
    requesterId: 'farm-2',
    requesterName: 'Chala Debele',
    productName: 'Fertilizer A',
    amount: 5,
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    statusHistory: [{
        status: 'Approved',
        level: 'Kebele',
        timestamp: new Date().toISOString()
    }, {
        status: 'Approved',
        level: 'Woreda',
        timestamp: new Date().toISOString()
    }, {
        status: 'Accepted',
        level: 'Zone',
        timestamp: new Date().toISOString()
    }]
}];

// ----------------------
// Modal Component
// ----------------------
const Modal = ({ show, onClose, title, message, children }) => {
        if (!show) return null;
        return ( <
            div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50" >
            <
            div className = "relative p-5 border w-96 max-w-2xl shadow-lg rounded-xl bg-white dark:bg-gray-800 text-gray-900 dark:text-white" >
            <
            div className = "mt-3 text-center" >
            <
            h3 className = "text-xl leading-6 font-bold" > { title } < /h3> <
            div className = "mt-2 px-7 py-3" > {
                message && < p className = "text-sm text-gray-500 dark:text-gray-300" > { message } < /p>} { children } <
                /div> <
                div className = "items-center px-4 py-3" >
                <
                button onClick = { onClose }
                className = "px-4 py-2 bg-blue-600 text-white text-base font-medium rounded-lg w-full shadow-md hover:bg-blue-700 transition" >
                OK <
                /button> <
                /div> <
                /div> <
                /div> <
                /div>
            );
        };

        // ----------------------
        // Main App Component
        // ----------------------
        const App = () => {
                const [currentUser, setCurrentUser] = useState(null);
                const [users, setUsers] = useState(initialUsers);
                const [requests, setRequests] = useState(initialRequests);
                const [showModal, setShowModal] = useState(false);
                const [modalContent, setModalContent] = useState({ title: '', message: '', children: null });
                const [currentPage, setCurrentPage] = useState('dashboard');
                const [isDarkMode, setIsDarkMode] = useState(false);

                useEffect(() => {
                    if (isDarkMode) {
                        document.documentElement.classList.add('dark');
                    } else {
                        document.documentElement.classList.remove('dark');
                    }
                }, [isDarkMode]);

                const handleLogin = (user) => {
                    setCurrentUser(user);
                    setCurrentPage('dashboard');
                };

                const handleLogout = () => {
                    setCurrentUser(null);
                };

                const handleRegistration = (newUser) => {
                    if (users.some(u => u.phoneNumber === newUser.phoneNumber)) {
                        setModalContent({ title: 'Error', message: 'User with this phone number already exists.' });
                        setShowModal(true);
                        return;
                    }

                    const newUserWithId = {...newUser, id: `${newUser.role.toLowerCase()}-${users.length + 1}` };
                    setUsers([...users, newUserWithId]);
                    setModalContent({
                        title: 'Success',
                        message: `${newUser.fullName} has been registered as a ${newUser.role}.`
                    });
                    setShowModal(true);
                };

                const handleProductRequest = (request) => {
                    const newRequest = {
                        ...request,
                        id: `REQ${10000 + requests.length}`,
                        requesterId: currentUser.id,
                        requesterName: currentUser.fullName,
                        regionName: currentUser.regionName,
                        zoneName: currentUser.zoneName,
                        woredaName: currentUser.woredaName,
                        kebeleName: currentUser.kebeleName,
                        statusHistory: [{
                            status: 'Pending',
                            level: 'Kebele', // All new requests start at the Kebele level
                            timestamp: new Date().toISOString()
                        }]
                    };
                    setRequests([...requests, newRequest]);
                    setModalContent({ title: 'Request Sent', message: 'Your product request has been submitted.' });
                    setShowModal(true);
                };

                const handleRequestAction = (requestId, action) => {
                    setRequests(requests.map(req => {
                        if (req.id === requestId) {
                            const newStatusHistory = [...req.statusHistory, {
                                status: action,
                                level: currentUser.role,
                                timestamp: new Date().toISOString()
                            }];
                            return {...req,
                                statusHistory: newStatusHistory
                            };
                        }
                        return req;
                    }));
                    setModalContent({ title: 'Action Taken', message: `The request has been ${action.toLowerCase()}.` });
                    setShowModal(true);
                };

                const handleDeleteRejected = (requestId) => {
                    setRequests(requests.filter(req => req.id !== requestId));
                    setModalContent({ title: 'Deleted', message: 'The rejected request has been permanently deleted.' });
                    setShowModal(true);
                };

                // Helper function to get requests relevant to the current user's role and location
                const getFilteredRequests = (user, allRequests) => {
                    switch (user.role) {
                        case 'Federal':
                            // Federal admin sees all requests
                            return allRequests.filter(req => req.statusHistory[req.statusHistory.length - 1].status !== 'Accepted');
                        case 'Region':
                            // Region admin sees requests pending at their level
                            return allRequests.filter(req => req.regionName === user.regionName && req.statusHistory[req.statusHistory.length - 1].status === 'Approved' && req.statusHistory[req.statusHistory.length - 1].level === 'Woreda');
                        case 'Zone':
                            // Zone admin sees requests pending at their level
                            return allRequests.filter(req => req.zoneName === user.zoneName && req.statusHistory[req.statusHistory.length - 1].status === 'Approved' && req.statusHistory[req.statusHistory.length - 1].level === 'Kebele');
                        case 'Woreda':
                            // Woreda admin sees requests pending at their level
                            return allRequests.filter(req => req.woredaName === user.woredaName && req.statusHistory[req.statusHistory.length - 1].status === 'Pending' && req.statusHistory[req.statusHistory.length - 1].level === 'Kebele');
                        case 'Kebele':
                            // Kebele admin sees all farmer requests in their kebele
                            return allRequests.filter(req => req.kebeleName === user.kebeleName && req.statusHistory[req.statusHistory.length - 1].status === 'Pending');
                        case 'Farmer':
                            // Farmer sees only their own requests
                            return allRequests.filter(req => req.requesterId === user.id);
                        default:
                            return [];
                    }
                };

                const getDeletableRejectedRequests = (user, allRequests) => {
                    const filtered = getFilteredRequests(user, allRequests);
                    return filtered.filter(req => req.statusHistory[req.statusHistory.length - 1].status === 'Rejected');
                }

                const renderMainContent = () => {
                        if (!currentUser) {
                            return <LoginPage onLogin = { handleLogin }
                            users = { users }
                            />;
                        }

                        const isAdmin = currentUser.role !== 'Farmer';

                        return ( <
                                div className = "flex bg-gray-100 dark:bg-gray-900 min-h-screen text-gray-900 dark:text-gray-100 transition-colors duration-300" >
                                <
                                Sidebar currentUser = { currentUser }
                                currentPage = { currentPage }
                                setCurrentPage = { setCurrentPage }
                                /> <
                                div className = "flex-1 overflow-auto" >
                                <
                                TopBar currentUser = { currentUser }
                                onLogout = { handleLogout }
                                isDarkMode = { isDarkMode }
                                setIsDarkMode = { setIsDarkMode }
                                /> <
                                main className = "p-8" > {
                                    currentPage === 'dashboard' && isAdmin && < DashboardOverview requests = { getFilteredRequests(currentUser, requests) }
                                    currentUser = { currentUser }
                                    />} {
                                        currentPage === 'requests' && isAdmin && < RequestManagement requests = { getFilteredRequests(currentUser, requests) }
                                        currentUser = { currentUser }
                                        onRequestAction = { handleRequestAction }
                                        onDeleteRejected = { handleDeleteRejected }
                                        />} {
                                            currentPage === 'register' && isAdmin && < RegisterUser currentUser = { currentUser }
                                            onRegistration = { handleRegistration }
                                            />} {
                                                currentPage === 'my-requests' && !isAdmin && < MyRequests currentUser = { currentUser }
                                                requests = { getFilteredRequests(currentUser, requests) }
                                                onRequest = { handleProductRequest }
                                                />} <
                                                /main> <
                                                /div> <
                                                Modal show = { showModal }
                                                onClose = {
                                                    () => setShowModal(false) }
                                                title = { modalContent.title }
                                                message = { modalContent.message }
                                                children = { modalContent.children }
                                                /> <
                                                /div>
                                            );
                                        };

                                        return renderMainContent();
                                    };

                                    const LoginPage = ({ onLogin, users }) => {
                                            const { register, handleSubmit, formState: { errors } } = useForm();
                                            const [loginError, setLoginError] = useState('');

                                            const onSubmit = (data) => {
                                                const user = users.find(u => u.phoneNumber === data.phoneNumber && u.password === data.password);
                                                if (user) {
                                                    onLogin(user);
                                                } else {
                                                    setLoginError('Invalid phone number or password.');
                                                }
                                            };

                                            return ( <
                                                    div className = "min-h-screen flex items-center justify-center bg-gray-100 dark:bg-gray-900 px-4" >
                                                    <
                                                    div className = "max-w-md w-full bg-white dark:bg-gray-800 p-8 rounded-xl shadow-lg border-t-4 border-blue-500" >
                                                    <
                                                    div className = "text-center" >
                                                    <
                                                    h2 className = "text-3xl font-bold text-gray-900 dark:text-gray-100" > Login < /h2> <
                                                    p className = "mt-2 text-sm text-gray-600 dark:text-gray-400" > Sign in to your account < /p> <
                                                    /div> <
                                                    form className = "mt-8 space-y-6"
                                                    onSubmit = { handleSubmit(onSubmit) } >
                                                    <
                                                    div >
                                                    <
                                                    label htmlFor = "phoneNumber"
                                                    className = "sr-only" > Phone Number < /label> <
                                                    input id = "phoneNumber"
                                                    name = "phoneNumber"
                                                    type = "text"
                                                    autoComplete = "tel"
                                                    className = "w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg placeholder-gray-500 dark:placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500 bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100"
                                                    placeholder = "Phone Number" {...register("phoneNumber", { required: "Phone number is required" }) }
                                                    /> {
                                                        errors.phoneNumber && < p className = "mt-2 text-sm text-red-600" > { errors.phoneNumber.message } < /p>} <
                                                            /div> <
                                                            div >
                                                            <
                                                            label htmlFor = "password"
                                                        className = "sr-only" > Password < /label> <
                                                            input
                                                        id = "password"
                                                        name = "password"
                                                        type = "password"
                                                        autoComplete = "current-password"
                                                        className = "w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg placeholder-gray-500 dark:placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500 bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100"
                                                        placeholder = "Password" {...register("password", { required: "Password is required" }) }
                                                        /> {
                                                            errors.password && < p className = "mt-2 text-sm text-red-600" > { errors.password.message } < /p>} <
                                                                /div> {
                                                                    loginError && < p className = "mt-2 text-sm text-red-600 text-center" > { loginError } < /p>} <
                                                                        div >
                                                                        <
                                                                        button
                                                                    type = "submit"
                                                                    className = "w-full flex justify-center py-3 px-4 border border-transparent rounded-lg shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition" >
                                                                        Sign in
                                                                        <
                                                                        /button> <
                                                                        /div> <
                                                                        /form> <
                                                                        /div> <
                                                                        /div>
                                                                );
                                                        };

                                                        const TopBar = ({ currentUser, onLogout, isDarkMode, setIsDarkMode }) => {
                                                            return ( <
                                                                header className = "flex justify-between items-center p-4 bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700 sticky top-0 z-10" >
                                                                <
                                                                div >
                                                                <
                                                                h1 className = "text-2xl font-bold text-gray-900 dark:text-gray-100" > Supply Chain App < /h1> <
                                                                p className = "text-sm text-gray-500 dark:text-gray-400" > Welcome, { currentUser.fullName }({ currentUser.role }) < /p> <
                                                                /div> <
                                                                div className = "flex items-center space-x-4" >
                                                                <
                                                                button onClick = {
                                                                    () => setIsDarkMode(!isDarkMode) }
                                                                className = "p-2 rounded-full text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700 transition" >
                                                                <
                                                                Settings size = { 20 }
                                                                /> <
                                                                /button> <
                                                                button onClick = { onLogout }
                                                                className = "p-2 rounded-full text-red-600 dark:text-red-400 hover:bg-red-100 dark:hover:bg-red-900 transition" >
                                                                <
                                                                LogOut size = { 20 }
                                                                /> <
                                                                /button> <
                                                                /div> <
                                                                /header>
                                                            );
                                                        };

                                                        const Sidebar = ({ currentUser, currentPage, setCurrentPage }) => {
                                                                const isAdmin = currentUser.role !== 'Farmer';
                                                                return ( <
                                                                        aside className = "w-64 bg-white dark:bg-gray-800 shadow-md p-4 flex flex-col justify-between h-screen sticky top-0" >
                                                                        <
                                                                        nav className = "space-y-2" > {
                                                                            isAdmin ? ( <
                                                                                >
                                                                                <
                                                                                SidebarButton icon = { < LayoutDashboard size = { 20 }
                                                                                    />} text="Dashboard" page="dashboard" currentPage={currentPage} setCurrentPage={setCurrentPage} / >
                                                                                    <
                                                                                    SidebarButton icon = { < UserPlus size = { 20 }
                                                                                        />} text="Register User" page="register" currentPage={currentPage} setCurrentPage={setCurrentPage} / >
                                                                                        <
                                                                                        SidebarButton icon = { < Package size = { 20 }
                                                                                            />} text="Manage Requests" page="requests" currentPage={currentPage} setCurrentPage={setCurrentPage} / >
                                                                                            <
                                                                                            />
                                                                                        ): ( <
                                                                                                SidebarButton icon = { < List size = { 20 }
                                                                                                    />} text="My Requests" page="my-requests" currentPage={currentPage} setCurrentPage={setCurrentPage} / >
                                                                                                )
                                                                                            } <
                                                                                            /nav> <
                                                                                            /aside>
                                                                                    );
                                                                                };

                                                                                const SidebarButton = ({ icon, text, page, currentPage, setCurrentPage }) => ( <
                                                                                    button onClick = {
                                                                                        () => setCurrentPage(page) }
                                                                                    className = { `w-full flex items-center gap-2 px-4 py-3 rounded-lg text-left font-medium transition-colors duration-200 ${
            currentPage === page
                ? 'bg-blue-600 text-white shadow-md'
                : 'text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700'
        }` } >
                                                                                    { icon } { text } <
                                                                                    /button>
                                                                                );

                                                                                const DashboardOverview = ({ requests, currentUser }) => {
                                                                                    const pendingCount = requests.filter(req => req.statusHistory[req.statusHistory.length - 1].status === 'Pending').length;
                                                                                    const acceptedCount = requests.filter(req => req.statusHistory[req.statusHistory.length - 1].status === 'Accepted').length;
                                                                                    const approvedCount = requests.filter(req => req.statusHistory[req.statusHistory.length - 1].status === 'Approved').length;
                                                                                    const rejectedCount = requests.filter(req => req.statusHistory[req.statusHistory.length - 1].status === 'Rejected').length;
                                                                                    const totalCount = requests.length;

                                                                                    return ( <
                                                                                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6" >
                                                                                        <
                                                                                        StatCard icon = { < Package / > }
                                                                                        label = "Total Requests"
                                                                                        value = { totalCount }
                                                                                        color = "blue" / >
                                                                                        <
                                                                                        StatCard icon = { < RefreshCcw / > }
                                                                                        label = "Pending"
                                                                                        value = { pendingCount }
                                                                                        color = "yellow" / >
                                                                                        <
                                                                                        StatCard icon = { < CheckCircle / > }
                                                                                        label = "Approved/Accepted"
                                                                                        value = { approvedCount + acceptedCount }
                                                                                        color = "green" / >
                                                                                        <
                                                                                        StatCard icon = { < XCircle / > }
                                                                                        label = "Rejected"
                                                                                        value = { rejectedCount }
                                                                                        color = "red" / >
                                                                                        <
                                                                                        /div>
                                                                                    );
                                                                                };

                                                                                const StatCard = ({ icon, label, value, color }) => ( <
                                                                                    div className = { `flex items-center p-6 bg-white dark:bg-gray-800 rounded-xl shadow-md border-t-4 border-${color}-500` } >
                                                                                    <
                                                                                    div className = { `p-3 rounded-full bg-${color}-100 text-${color}-600 dark:bg-${color}-900 dark:text-${color}-300 mr-4` } > { icon } <
                                                                                    /div> <
                                                                                    div >
                                                                                    <
                                                                                    p className = "text-gray-500 dark:text-gray-400 text-sm font-medium" > { label } < /p> <
                                                                                    h3 className = "text-2xl font-bold mt-1" > { value } < /h3> <
                                                                                    /div> <
                                                                                    /div>
                                                                                );

                                                                                const RegisterUser = ({ currentUser, onRegistration }) => {
                                                                                        const { register, handleSubmit, formState: { errors }, watch, reset } = useForm();
                                                                                        const password = watch("password");

                                                                                        const registerableRoles = {
                                                                                            'Federal': ['Region'],
                                                                                            'Region': ['Zone'],
                                                                                            'Zone': ['Woreda'],
                                                                                            'Woreda': ['Kebele'],
                                                                                            'Kebele': ['Farmer'],
                                                                                        };
                                                                                        const rolesToRegister = registerableRoles[currentUser.role] || [];

                                                                                        const onSubmit = (data) => {
                                                                                            const newUser = {
                                                                                                fullName: data.fullName,
                                                                                                phoneNumber: data.phoneNumber,
                                                                                                password: data.password,
                                                                                                role: data.roleName,
                                                                                            };
                                                                                            // Add location details based on the registering admin's location
                                                                                            if (currentUser.regionName) newUser.regionName = currentUser.regionName;
                                                                                            if (currentUser.zoneName) newUser.zoneName = currentUser.zoneName;
                                                                                            if (currentUser.woredaName) newUser.woredaName = currentUser.woredaName;
                                                                                            if (currentUser.kebeleName) newUser.kebeleName = currentUser.kebeleName;

                                                                                            // Add the specific name for the new role's level
                                                                                            if (data.roleName === 'Region') newUser.regionName = data.levelName;
                                                                                            if (data.roleName === 'Zone') newUser.zoneName = data.levelName;
                                                                                            if (data.roleName === 'Woreda') newUser.woredaName = data.levelName;
                                                                                            if (data.roleName === 'Kebele') newUser.kebeleName = data.levelName;
                                                                                            if (data.roleName === 'Farmer') newUser.kebeleName = data.levelName;

                                                                                            onRegistration(newUser);
                                                                                            reset();
                                                                                        };

                                                                                        return ( <
                                                                                                div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                                                                                                <
                                                                                                h2 className = "text-2xl font-bold mb-6" > Register a New User < /h2> <
                                                                                                form onSubmit = { handleSubmit(onSubmit) }
                                                                                                className = "space-y-4" > {
                                                                                                    rolesToRegister.length === 0 ? ( <
                                                                                                        p className = "text-red-500" > You are not authorized to register new users. < /p>
                                                                                                    ) : ( <
                                                                                                            >
                                                                                                            <
                                                                                                            div className = "grid grid-cols-1 md:grid-cols-2 gap-4" >
                                                                                                            <
                                                                                                            div >
                                                                                                            <
                                                                                                            label className = "block text-sm font-medium mb-1" > Full Name < /label> <
                                                                                                            input type = "text" {...register("fullName", { required: "Full name is required" }) }
                                                                                                            className = "w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-blue-500 focus:border-blue-500 bg-gray-50 dark:bg-gray-700" /
                                                                                                            > {
                                                                                                                errors.fullName && < p className = "text-red-500 text-xs mt-1" > { errors.fullName.message } < /p>} <
                                                                                                                /div> <
                                                                                                                div >
                                                                                                                <
                                                                                                                label className = "block text-sm font-medium mb-1" > Phone Number < /label> <
                                                                                                                input
                                                                                                                type = "text" {...register("phoneNumber", { required: "Phone number is required" }) }
                                                                                                                className = "w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-blue-500 focus:border-blue-500 bg-gray-50 dark:bg-gray-700" /
                                                                                                                > {
                                                                                                                    errors.phoneNumber && < p className = "text-red-500 text-xs mt-1" > { errors.phoneNumber.message } < /p>} <
                                                                                                                    /div> <
                                                                                                                    div >
                                                                                                                    <
                                                                                                                    label className = "block text-sm font-medium mb-1" > Password < /label> <
                                                                                                                    input
                                                                                                                    type = "password" {...register("password", { required: "Password is required" }) }
                                                                                                                    className = "w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-blue-500 focus:border-blue-500 bg-gray-50 dark:bg-gray-700" /
                                                                                                                    > {
                                                                                                                        errors.password && < p className = "text-red-500 text-xs mt-1" > { errors.password.message } < /p>} <
                                                                                                                        /div> <
                                                                                                                        div >
                                                                                                                        <
                                                                                                                        label className = "block text-sm font-medium mb-1" > Confirm Password < /label> <
                                                                                                                        input
                                                                                                                        type = "password" {...register("confirmPassword", {
                                                                                                                                required: "Please confirm your password",
                                                                                                                                validate: value => value === password || "Passwords do not match"
                                                                                                                            })
                                                                                                                        }
                                                                                                                        className = "w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-blue-500 focus:border-blue-500 bg-gray-50 dark:bg-gray-700" /
                                                                                                                        > {
                                                                                                                            errors.confirmPassword && < p className = "text-red-500 text-xs mt-1" > { errors.confirmPassword.message } < /p>} <
                                                                                                                            /div> <
                                                                                                                            /div> <
                                                                                                                            div className = "grid grid-cols-1 md:grid-cols-2 gap-4" >
                                                                                                                            <
                                                                                                                            div >
                                                                                                                            <
                                                                                                                            label className = "block text-sm font-medium mb-1" > Role < /label> <
                                                                                                                            select {...register("roleName", { required: "Role is required" }) }
                                                                                                                            className = "w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-blue-500 focus:border-blue-500 bg-gray-50 dark:bg-gray-700" >
                                                                                                                            <
                                                                                                                            option value = "" > Select a role < /option> {
                                                                                                                                rolesToRegister.map(role => ( <
                                                                                                                                    option key = { role }
                                                                                                                                    value = { role } > { role } < /option>
                                                                                                                                ))
                                                                                                                            } <
                                                                                                                            /select> {
                                                                                                                                errors.roleName && < p className = "text-red-500 text-xs mt-1" > { errors.roleName.message } < /p>} <
                                                                                                                                    /div> { /* Dynamically show the input for the new level name */ } {
                                                                                                                                        watch("roleName") && ( <
                                                                                                                                                div >
                                                                                                                                                <
                                                                                                                                                label className = "block text-sm font-medium mb-1" > { watch("roleName") }
                                                                                                                                                Name < /label> <
                                                                                                                                                input type = "text" {...register("levelName", { required: `${watch("roleName")} name is required` }) }
                                                                                                                                                className = "w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-blue-500 focus:border-blue-500 bg-gray-50 dark:bg-gray-700" /
                                                                                                                                                > {
                                                                                                                                                    errors.levelName && < p className = "text-red-500 text-xs mt-1" > { errors.levelName.message } < /p>} <
                                                                                                                                                    /div>
                                                                                                                                                )
                                                                                                                                            } <
                                                                                                                                            /div> <
                                                                                                                                            button
                                                                                                                                        type = "submit"
                                                                                                                                        className = "w-full px-6 py-3 mt-4 text-lg font-medium text-white bg-blue-600 rounded-lg shadow-md hover:bg-blue-700 transition-colors" >
                                                                                                                                            Register User <
                                                                                                                                            /button> <
                                                                                                                                            />
                                                                                                                                    )
                                                                                                                            } <
                                                                                                                            /form> <
                                                                                                                            /div>
                                                                                                                        );
                                                                                                                    };

                                                                                                                    const RequestManagement = ({ requests, currentUser, onRequestAction, onDeleteRejected }) => {
                                                                                                                        const pendingRequests = requests.filter(req => req.statusHistory[req.statusHistory.length - 1].status === 'Pending' && req.statusHistory[req.statusHistory.length - 1].level === currentUser.role);
                                                                                                                        const rejectedRequests = requests.filter(req => req.statusHistory[req.statusHistory.length - 1].status === 'Rejected' && req.statusHistory[req.statusHistory.length - 1].level === currentUser.role);

                                                                                                                        const getStatusColor = (status) => {
                                                                                                                            switch (status) {
                                                                                                                                case 'Approved':
                                                                                                                                case 'Accepted':
                                                                                                                                    return 'bg-green-100 text-green-800';
                                                                                                                                case 'Pending':
                                                                                                                                    return 'bg-yellow-100 text-yellow-800';
                                                                                                                                case 'Rejected':
                                                                                                                                    return 'bg-red-100 text-red-800';
                                                                                                                                default:
                                                                                                                                    return 'bg-gray-100 text-gray-800';
                                                                                                                            }
                                                                                                                        };

                                                                                                                        return ( <
                                                                                                                            div className = "space-y-8" >
                                                                                                                            <
                                                                                                                            div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                                                                                                                            <
                                                                                                                            h2 className = "text-2xl font-bold mb-6" > Pending Requests({ currentUser.role }
                                                                                                                                Level) < /h2> {
                                                                                                                                pendingRequests.length === 0 ? ( <
                                                                                                                                    div className = "text-center text-gray-500" > No new requests to manage. < /div>
                                                                                                                                ) : ( <
                                                                                                                                    div className = "overflow-x-auto" >
                                                                                                                                    <
                                                                                                                                    table className = "min-w-full divide-y divide-gray-200 dark:divide-gray-700" >
                                                                                                                                    <
                                                                                                                                    thead className = "bg-gray-50 dark:bg-gray-700" >
                                                                                                                                    <
                                                                                                                                    tr >
                                                                                                                                    <
                                                                                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Product Name < /th> <
                                                                                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Amount < /th> <
                                                                                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Status < /th> <
                                                                                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Actions < /th> <
                                                                                                                                    /tr> <
                                                                                                                                    /thead> <
                                                                                                                                    tbody className = "bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700" > {
                                                                                                                                        pendingRequests.map(req => ( <
                                                                                                                                            tr key = { req.id } >
                                                                                                                                            <
                                                                                                                                            td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-white" > { req.productName } < /td> <
                                                                                                                                            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.amount } < /td> <
                                                                                                                                            td className = "px-6 py-4 whitespace-nowrap" >
                                                                                                                                            <
                                                                                                                                            span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(req.statusHistory[req.statusHistory.length - 1].status)}` } > { req.statusHistory[req.statusHistory.length - 1].status } <
                                                                                                                                            /span> <
                                                                                                                                            /td> <
                                                                                                                                            td className = "px-6 py-4 whitespace-nowrap text-sm font-medium" >
                                                                                                                                            <
                                                                                                                                            div className = "flex space-x-2" >
                                                                                                                                            <
                                                                                                                                            button onClick = {
                                                                                                                                                () => onRequestAction(req.id, 'Approved') }
                                                                                                                                            className = "flex items-center gap-1 text-blue-600 hover:text-blue-900"
                                                                                                                                            title = "Approve (forward to next level)" >
                                                                                                                                            <
                                                                                                                                            ArrowUpCircle size = { 20 }
                                                                                                                                            /> <
                                                                                                                                            /button> <
                                                                                                                                            button onClick = {
                                                                                                                                                () => onRequestAction(req.id, 'Accepted') }
                                                                                                                                            className = "flex items-center gap-1 text-green-600 hover:text-green-900"
                                                                                                                                            title = "Accept (handle at this level)" >
                                                                                                                                            <
                                                                                                                                            CheckCircle size = { 20 }
                                                                                                                                            /> <
                                                                                                                                            /button> <
                                                                                                                                            button onClick = {
                                                                                                                                                () => onRequestAction(req.id, 'Rejected') }
                                                                                                                                            className = "flex items-center gap-1 text-red-600 hover:text-red-900"
                                                                                                                                            title = "Reject (deny request)" >
                                                                                                                                            <
                                                                                                                                            XCircle size = { 20 }
                                                                                                                                            /> <
                                                                                                                                            /button> <
                                                                                                                                            /div> <
                                                                                                                                            /td> <
                                                                                                                                            /tr>
                                                                                                                                        ))
                                                                                                                                    } <
                                                                                                                                    /tbody> <
                                                                                                                                    /table> <
                                                                                                                                    /div>
                                                                                                                                )
                                                                                                                            } <
                                                                                                                            /div>

                                                                                                                            <
                                                                                                                            div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                                                                                                                            <
                                                                                                                            h2 className = "text-2xl font-bold mb-6" > Rejected Requests < /h2> {
                                                                                                                                rejectedRequests.length === 0 ? ( <
                                                                                                                                    div className = "text-center text-gray-500" > No rejected requests at your level. < /div>
                                                                                                                                ) : ( <
                                                                                                                                    div className = "overflow-x-auto" >
                                                                                                                                    <
                                                                                                                                    table className = "min-w-full divide-y divide-gray-200 dark:divide-gray-700" >
                                                                                                                                    <
                                                                                                                                    thead className = "bg-gray-50 dark:bg-gray-700" >
                                                                                                                                    <
                                                                                                                                    tr >
                                                                                                                                    <
                                                                                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Product Name < /th> <
                                                                                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Amount < /th> <
                                                                                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Status < /th> <
                                                                                                                                    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Actions < /th> <
                                                                                                                                    /tr> <
                                                                                                                                    /thead> <
                                                                                                                                    tbody className = "bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700" > {
                                                                                                                                        rejectedRequests.map(req => ( <
                                                                                                                                            tr key = { req.id } >
                                                                                                                                            <
                                                                                                                                            td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-white" > { req.productName } < /td> <
                                                                                                                                            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.amount } < /td> <
                                                                                                                                            td className = "px-6 py-4 whitespace-nowrap" >
                                                                                                                                            <
                                                                                                                                            span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(req.statusHistory[req.statusHistory.length - 1].status)}` } > { req.statusHistory[req.statusHistory.length - 1].status } <
                                                                                                                                            /span> <
                                                                                                                                            /td> <
                                                                                                                                            td className = "px-6 py-4 whitespace-nowrap text-sm font-medium" >
                                                                                                                                            <
                                                                                                                                            button onClick = {
                                                                                                                                                () => onDeleteRejected(req.id) }
                                                                                                                                            className = "text-red-600 dark:text-red-400 hover:text-red-900 dark:hover:text-red-200"
                                                                                                                                            title = "Delete this rejected request" >
                                                                                                                                            <
                                                                                                                                            Trash2 size = { 20 }
                                                                                                                                            /> <
                                                                                                                                            /button> <
                                                                                                                                            /td> <
                                                                                                                                            /tr>
                                                                                                                                        ))
                                                                                                                                    } <
                                                                                                                                    /tbody> <
                                                                                                                                    /table> <
                                                                                                                                    /div>
                                                                                                                                )
                                                                                                                            } <
                                                                                                                            /div> <
                                                                                                                            /div>
                                                                                                                        );
                                                                                                                    };

                                                                                                                    const MyRequests = ({ requests, onRequest }) => {
                                                                                                                            const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
                                                                                                                            const [isDetailsModalOpen, setIsDetailsModalOpen] = useState(false);
                                                                                                                            const [selectedRequest, setSelectedRequest] = useState(null);
                                                                                                                            const { register, handleSubmit, formState: { errors }, reset } = useForm();

                                                                                                                            const onSubmit = (data) => {
                                                                                                                                const newRequest = {
                                                                                                                                    productName: data.productName,
                                                                                                                                    amount: parseInt(data.amount),
                                                                                                                                };
                                                                                                                                onRequest(newRequest);
                                                                                                                                setIsCreateModalOpen(false);
                                                                                                                                reset();
                                                                                                                            };

                                                                                                                            const getStatusColor = (status) => {
                                                                                                                                switch (status) {
                                                                                                                                    case 'Approved':
                                                                                                                                    case 'Accepted':
                                                                                                                                        return 'bg-green-100 text-green-800';
                                                                                                                                    case 'Pending':
                                                                                                                                        return 'bg-yellow-100 text-yellow-800';
                                                                                                                                    case 'Rejected':
                                                                                                                                        return 'bg-red-100 text-red-800';
                                                                                                                                    default:
                                                                                                                                        return 'bg-gray-100 text-gray-800';
                                                                                                                                }
                                                                                                                            };

                                                                                                                            return ( <
                                                                                                                                    div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                                                                                                                                    <
                                                                                                                                    div className = "flex justify-between items-center mb-6" >
                                                                                                                                    <
                                                                                                                                    h2 className = "text-2xl font-bold" > My Requests < /h2> <
                                                                                                                                    button onClick = {
                                                                                                                                        () => setIsCreateModalOpen(true) }
                                                                                                                                    className = "flex items-center gap-2 px-4 py-2 text-white bg-blue-600 rounded-lg shadow-md hover:bg-blue-700 transition-colors" >
                                                                                                                                    <
                                                                                                                                    Package size = { 20 }
                                                                                                                                    />
                                                                                                                                    New Request <
                                                                                                                                    /button> <
                                                                                                                                    /div> {
                                                                                                                                        requests.length === 0 ? ( <
                                                                                                                                            div className = "text-center text-gray-500" > You have not submitted any requests yet. < /div>
                                                                                                                                        ) : ( <
                                                                                                                                            div className = "overflow-x-auto" >
                                                                                                                                            <
                                                                                                                                            table className = "min-w-full divide-y divide-gray-200 dark:divide-gray-700" >
                                                                                                                                            <
                                                                                                                                            thead className = "bg-gray-50 dark:bg-gray-700" >
                                                                                                                                            <
                                                                                                                                            tr >
                                                                                                                                            <
                                                                                                                                            th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Product < /th> <
                                                                                                                                            th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Amount < /th> <
                                                                                                                                            th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Current Status < /th> <
                                                                                                                                            th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Actions < /th> <
                                                                                                                                            /tr> <
                                                                                                                                            /thead> <
                                                                                                                                            tbody className = "bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700" > {
                                                                                                                                                requests.map(req => ( <
                                                                                                                                                    tr key = { req.id } >
                                                                                                                                                    <
                                                                                                                                                    td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-white" > { req.productName } < /td> <
                                                                                                                                                    td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.amount } < /td> <
                                                                                                                                                    td className = "px-6 py-4 whitespace-nowrap" >
                                                                                                                                                    <
                                                                                                                                                    span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(req.statusHistory[req.statusHistory.length - 1].status)}` } > { req.statusHistory[req.statusHistory.length - 1].status } <
                                                                                                                                                    /span> <
                                                                                                                                                    /td> <
                                                                                                                                                    td className = "px-6 py-4 whitespace-nowrap text-sm font-medium" >
                                                                                                                                                    <
                                                                                                                                                    button onClick = {
                                                                                                                                                        () => { setSelectedRequest(req);
                                                                                                                                                            setIsDetailsModalOpen(true); } }
                                                                                                                                                    className = "text-blue-600 hover:text-blue-900"
                                                                                                                                                    title = "View History" >
                                                                                                                                                    <
                                                                                                                                                    Eye size = { 20 }
                                                                                                                                                    /> <
                                                                                                                                                    /button> <
                                                                                                                                                    /td> <
                                                                                                                                                    /tr>
                                                                                                                                                ))
                                                                                                                                            } <
                                                                                                                                            /tbody> <
                                                                                                                                            /table> <
                                                                                                                                            /div>
                                                                                                                                        )
                                                                                                                                    }

                                                                                                                                    <
                                                                                                                                    Modal show = { isCreateModalOpen }
                                                                                                                                    onClose = {
                                                                                                                                        () => setIsCreateModalOpen(false) }
                                                                                                                                    title = "Submit a New Request" >
                                                                                                                                    <
                                                                                                                                    form onSubmit = { handleSubmit(onSubmit) }
                                                                                                                                    className = "space-y-4" >
                                                                                                                                    <
                                                                                                                                    div >
                                                                                                                                    <
                                                                                                                                    label className = "block text-sm font-medium mb-1" > Product Name < /label> <
                                                                                                                                    input type = "text" {...register("productName", { required: "Product name is required" }) }
                                                                                                                                    className = "w-full px-4 py-2 border rounded-lg" /
                                                                                                                                    > {
                                                                                                                                        errors.productName && < p className = "text-red-500 text-xs mt-1" > { errors.productName.message } < /p>} <
                                                                                                                                        /div> <
                                                                                                                                        div >
                                                                                                                                        <
                                                                                                                                        label className = "block text-sm font-medium mb-1" > Amount < /label> <
                                                                                                                                        input
                                                                                                                                        type = "number" {...register("amount", { required: "Amount is required", min: 1 }) }
                                                                                                                                        className = "w-full px-4 py-2 border rounded-lg" /
                                                                                                                                        > {
                                                                                                                                            errors.amount && < p className = "text-red-500 text-xs mt-1" > { errors.amount.message } < /p>} <
                                                                                                                                            /div> <
                                                                                                                                            button
                                                                                                                                            type = "submit"
                                                                                                                                            className = "w-full px-6 py-3 text-lg font-medium text-white bg-blue-600 rounded-lg shadow-md hover:bg-blue-700" >
                                                                                                                                            Submit Request <
                                                                                                                                            /button> <
                                                                                                                                            /form> <
                                                                                                                                            /Modal>

                                                                                                                                            {
                                                                                                                                                selectedRequest && ( <
                                                                                                                                                    Modal show = { isDetailsModalOpen }
                                                                                                                                                    onClose = {
                                                                                                                                                        () => setIsDetailsModalOpen(false) }
                                                                                                                                                    title = "Request Status History" >
                                                                                                                                                    <
                                                                                                                                                    p className = "text-lg font-medium mb-2" > { selectedRequest.productName }({ selectedRequest.amount }) < /p> <
                                                                                                                                                    ul className = "space-y-2 text-left" > {
                                                                                                                                                        selectedRequest.statusHistory
                                                                                                                                                        .sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp))
                                                                                                                                                        .map((history, index) => ( <
                                                                                                                                                            li key = { index }
                                                                                                                                                            className = "flex items-center gap-2" >
                                                                                                                                                            <
                                                                                                                                                            div className = { `w-3 h-3 rounded-full ${getStatusColor(history.status).split(' ')[0] === 'bg-yellow-100' ? 'bg-yellow-500' : getStatusColor(history.status).split(' ')[0] === 'bg-green-100' ? 'bg-green-500' : 'bg-red-500'}` } > < /div> <
                                                                                                                                                            p >
                                                                                                                                                            <
                                                                                                                                                            span className = { `font-semibold ${getStatusColor(history.status).split(' ')[1]}` } > { history.status } < /span> <
                                                                                                                                                            span className = "text-sm text-gray-500 dark:text-gray-400" > by { history.level }
                                                                                                                                                            admin < /span> <
                                                                                                                                                            /p> <
                                                                                                                                                            /li>
                                                                                                                                                        ))
                                                                                                                                                    } <
                                                                                                                                                    /ul> <
                                                                                                                                                    /Modal>
                                                                                                                                                )
                                                                                                                                            } <
                                                                                                                                            /div>
                                                                                                                                        );
                                                                                                                                    };

                                                                                                                                    export default App;
