import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './RegionDashboard.css';

const RegionDashboard = () => {
    const [user, setUser] = useState(null);
    const [activeTab, setActiveTab] = useState('overview');
    const [admins, setAdmins] = useState([]);
    const [products, setProducts] = useState([]);
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);
    const [adminLoading, setAdminLoading] = useState(false);
    const [productLoading, setProductLoading] = useState(false);
    const navigate = useNavigate();

    // Product Management State
    const [showProductForm, setShowProductForm] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        description: ''
    });

    // Admin Management State
    const [showAdminForm, setShowAdminForm] = useState(false);
    const [editingAdmin, setEditingAdmin] = useState(null);
    const [adminForm, setAdminForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        role: 'Zone',
        region_name: '',
        zone_name: ''
    });
    const [adminError, setAdminError] = useState('');

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userDataString = localStorage.getItem('user');
        const userData = userDataString ? JSON.parse(userDataString) : null;
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Region' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
        fetchDashboardData();
    }, [navigate]);

    const fetchDashboardData = async() => {
        try {
            const token = localStorage.getItem('token');

            // Fetch user data
            const userResponse = await fetch('http://localhost:5000/api/admins/me', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            if (userResponse.ok) {
                const userData = await userResponse.json();
                setUser(userData);
            }

            // Fetch products
            const productsResponse = await fetch('http://localhost:5000/api/admins/products', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
            }

            // Fetch admins in scope
            const adminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (adminsResponse.ok) {
                const adminsData = await adminsResponse.json();
                // Filter to only show Zone admins (Region admin can only create Zone admins)
                const zoneAdmins = adminsData.filter(admin => admin.role === 'Zone');
                setAdmins(zoneAdmins);
            }

            // Fetch requests
            const requestsResponse = await fetch('http://localhost:5000/api/admins/requests', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                setRequests(requestsData);
            }

            setLoading(false);
        } catch (error) {
            console.error('Error fetching dashboard data:', error);
            setLoading(false);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('userType');
        navigate('/login');
    };

    const handleProductSubmit = async(e) => {
        e.preventDefault();
        setProductLoading(true);
        try {
            const token = localStorage.getItem('token');
            const url = editingProduct ?
                `http://localhost:5000/api/admins/products/${editingProduct.id}` :
                'http://localhost:5000/api/admins/products';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    name: productForm.name,
                    description: productForm.description,
                    category: productForm.category
                })
            });

            const data = await response.json();

            if (response.ok) {
                setShowProductForm(false);
                setEditingProduct(null);
                setProductForm({ name: '', category: '', description: '' });
                fetchDashboardData();
            } else {
                console.error('Error saving product:', data.message);
                setAdminError(data.message || 'Error saving product');
            }
        } catch (error) {
            console.error('Error saving product:', error);
            setAdminError('Server error occurred');
        } finally {
            setProductLoading(false);
        }
    };

    const deleteProduct = async(productId) => {
        if (!window.confirm('Are you sure you want to delete this product?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/products/${productId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
            } else {
                const errorData = await response.json();
                console.error('Error deleting product:', errorData.message);
            }
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    const handleAdminSubmit = async(e) => {
        e.preventDefault();
        setAdminError('');
        setAdminLoading(true);

        if (adminForm.password !== adminForm.confirmPassword && !editingAdmin) {
            setAdminError('Passwords do not match');
            setAdminLoading(false);
            return;
        }

        try {
            const token = localStorage.getItem('token');
            let url, method;

            if (editingAdmin) {
                url = `http://localhost:5000/api/admins/edit/${editingAdmin.id}`;
                method = 'PUT';
            } else {
                url = 'http://localhost:5000/api/admins/register';
                method = 'POST';
            }

            // Prepare data for submission
            const submitData = {
                fullName: adminForm.fullName,
                phoneNumber: adminForm.phoneNumber,
                password: adminForm.password,
                role: adminForm.role,
                region_name: adminForm.region_name,
                zone_name: adminForm.zone_name,
                woreda_name: '',
                kebele_name: ''
            };

            // Remove password if empty during edit
            if (editingAdmin && !adminForm.password) {
                delete submitData.password;
            }

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(submitData)
            });

            const data = await response.json();

            if (response.ok) {
                setShowAdminForm(false);
                setEditingAdmin(null);
                setAdminForm({
                    fullName: '',
                    phoneNumber: '',
                    password: '',
                    confirmPassword: '',
                    role: 'Zone',
                    region_name: user ? user.region_name : '',
                    zone_name: ''
                });
                fetchDashboardData();
            } else {
                setAdminError(data.message || 'Error processing admin');
            }
        } catch (error) {
            console.error('Error processing admin:', error);
            setAdminError('Server error occurred');
        } finally {
            setAdminLoading(false);
        }
    };

    const deleteAdmin = async(adminId) => {
        if (!window.confirm('Are you sure you want to delete this admin?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/delete/${adminId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
            } else {
                const errorData = await response.json();
                console.error('Error deleting admin:', errorData.message);
            }
        } catch (error) {
            console.error('Error deleting admin:', error);
        }
    };

    const editAdmin = (admin) => {
        setEditingAdmin(admin);
        setAdminForm({
            fullName: admin.full_name || '',
            phoneNumber: admin.phone_number || '',
            password: '',
            confirmPassword: '',
            role: admin.role || 'Zone',
            region_name: admin.region_name || (user ? user.region_name : ''),
            zone_name: admin.zone_name || ''
        });
        setShowAdminForm(true);
    };

    const handleUpdateRequestStatus = async(requestId, status) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ status })
            });

            if (response.ok) {
                fetchDashboardData();
            } else {
                const error = await response.json();
                console.error('Error updating request:', error.message);
            }
        } catch (error) {
            console.error('Error updating request:', error);
        }
    };

    if (loading) {
        return <div className = "loading" > Loading dashboard... < /div>;
    }

    if (!user) {
        return <div className = "loading" > Loading... < /div>;
    }

    return ( <
        div className = "region-dashboard" >
        <
        header className = "dashboard-header" >
        <
        h1 > Regional Administration Dashboard < /h1> <
        div className = "user-info" >
        <
        span > Welcome, { user ? user.fullName : 'Guest' } < /span> <
        span className = "user-role" > { user ? user.role : 'Unknown' } - { user ? user.region_name : 'Unknown Region' } < /span> <
        button onClick = { handleLogout }
        className = "logout-btn" >
        Logout <
        /button> <
        /div> <
        /header>

        <
        nav className = "dashboard-nav" >
        <
        button className = { activeTab === 'overview' ? 'active' : '' }
        onClick = {
            () => setActiveTab('overview') } >
        <
        i className = "fas fa-chart-bar" > < /i> Overview <
        /button> <
        button className = { activeTab === 'admins' ? 'active' : '' }
        onClick = {
            () => setActiveTab('admins') } >
        <
        i className = "fas fa-users" > < /i> Admin Management <
        /button> <
        button className = { activeTab === 'products' ? 'active' : '' }
        onClick = {
            () => setActiveTab('products') } >
        <
        i className = "fas fa-box" > < /i> Product Management <
        /button> <
        button className = { activeTab === 'requests' ? 'active' : '' }
        onClick = {
            () => setActiveTab('requests') } >
        <
        i className = "fas fa-clipboard-list" > < /i> Requests <
        /button> <
        /nav>

        <
        main className = "dashboard-content" > {
            activeTab === 'overview' && ( <
                div className = "overview-tab" >
                <
                div className = "stats-grid" >
                <
                div className = "stat-card" >
                <
                h3 > Zone Admins < /h3> <
                p className = "stat-number" > { admins.filter(a => a.role === 'Zone').length } < /p> <
                /div> <
                div className = "stat-card" >
                <
                h3 > Woreda Admins < /h3> <
                p className = "stat-number" > { admins.filter(a => a.role === 'Woreda').length } < /p> <
                /div> <
                div className = "stat-card" >
                <
                h3 > Products < /h3> <
                p className = "stat-number" > { products.length } < /p> <
                /div> <
                div className = "stat-card" >
                <
                h3 > Pending Requests < /h3> <
                p className = "stat-number" > { requests.filter(r => r.status === 'pending').length } < /p> <
                /div> <
                /div>

                <
                div className = "recent-activity" >
                <
                h2 > Recent Activity < /h2> <
                div className = "activity-list" > {
                    requests.slice(0, 5).map(request => ( <
                        div key = { request.id }
                        className = "activity-item" >
                        <
                        div className = "activity-content" >
                        <
                        p > < strong > { request.farmer_name } < /strong> requested <strong>{request.quantity}</strong > of { request.product_name } < /p> <
                        span className = "activity-time" > { new Date(request.created_at).toLocaleDateString() } < /span> <
                        /div> <
                        span className = { `status-badge status-${request.status}` } > { request.status } < /span> <
                        /div>
                    ))
                } <
                /div> <
                /div> <
                /div>
            )
        }

        {
            activeTab === 'admins' && ( <
                div className = "admins-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Admin Management < /h2> <
                button className = "btn-primary"
                onClick = {
                    () => {
                        setEditingAdmin(null);
                        setAdminForm({
                            fullName: '',
                            phoneNumber: '',
                            password: '',
                            confirmPassword: '',
                            role: 'Zone',
                            region_name: user ? user.region_name : '',
                            zone_name: ''
                        });
                        setShowAdminForm(true);
                    }
                } >
                <
                i className = "fas fa-plus" > < /i> Create Zone Admin <
                /button> <
                /div>

                {
                    showAdminForm && ( <
                        div className = "modal" >
                        <
                        div className = "modal-content" >
                        <
                        h3 > { editingAdmin ? 'Edit Zone Admin' : 'Create Zone Admin' } < /h3> {
                            adminError && ( <
                                div className = "error-message" > { adminError } < /div>
                            )
                        } <
                        form onSubmit = { handleAdminSubmit } >
                        <
                        input type = "text"
                        placeholder = "Full Name"
                        value = { adminForm.fullName }
                        onChange = {
                            (e) => setAdminForm({...adminForm, fullName: e.target.value }) }
                        required / >
                        <
                        input type = "tel"
                        placeholder = "Phone Number"
                        value = { adminForm.phoneNumber }
                        onChange = {
                            (e) => setAdminForm({...adminForm, phoneNumber: e.target.value }) }
                        required / > {!editingAdmin && ( <
                                >
                                <
                                input type = "password"
                                placeholder = "Password"
                                value = { adminForm.password }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, password: e.target.value }) }
                                required / >
                                <
                                input type = "password"
                                placeholder = "Confirm Password"
                                value = { adminForm.confirmPassword }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, confirmPassword: e.target.value }) }
                                required / >
                                <
                                />
                            )
                        } {
                            editingAdmin && ( <
                                >
                                <
                                input type = "password"
                                placeholder = "New Password (leave empty to keep current)"
                                value = { adminForm.password }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, password: e.target.value }) }
                                /> <
                                input type = "password"
                                placeholder = "Confirm New Password"
                                value = { adminForm.confirmPassword }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, confirmPassword: e.target.value }) }
                                /> <
                                />
                            )
                        } <
                        input type = "text"
                        placeholder = "Region Name"
                        value = { adminForm.region_name }
                        onChange = {
                            (e) => setAdminForm({...adminForm, region_name: e.target.value }) }
                        required / >
                        <
                        input type = "text"
                        placeholder = "Zone Name (Must be unique)"
                        value = { adminForm.zone_name }
                        onChange = {
                            (e) => setAdminForm({...adminForm, zone_name: e.target.value }) }
                        required / >
                        <
                        div className = "modal-actions" >
                        <
                        button type = "submit"
                        disabled = { adminLoading } > { adminLoading ? 'Processing...' : (editingAdmin ? 'Update' : 'Create') } <
                        /button> <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowAdminForm(false);
                                setEditingAdmin(null);
                                setAdminError('');
                            }
                        }
                        disabled = { adminLoading } >
                        Cancel <
                        /button> <
                        /div> <
                        /form> <
                        /div> <
                        /div>
                    )
                }

                <
                div className = "table-container" >
                <
                table className = "data-table" >
                <
                thead >
                <
                tr >
                <
                th > Name < /th> <
                th > Phone < /th> <
                th > Role < /th> <
                th > Zone < /th> <
                th > Actions < /th> <
                /tr> <
                /thead> <
                tbody > {
                    admins.map(admin => ( <
                        tr key = { admin.id } >
                        <
                        td > { admin.full_name } < /td> <
                        td > { admin.phone_number } < /td> <
                        td > < span className = "role-badge" > { admin.role } < /span></td >
                        <
                        td > { admin.zone_name || '-' } < /td> <
                        td >
                        <
                        button className = "btn-icon"
                        title = "Edit"
                        onClick = {
                            () => editAdmin(admin) } >
                        <
                        i className = "fas fa-edit" > < /i> <
                        /button> <
                        button className = "btn-icon btn-danger"
                        title = "Delete"
                        onClick = {
                            () => deleteAdmin(admin.id) } >
                        <
                        i className = "fas fa-trash" > < /i> <
                        /button> <
                        /td> <
                        /tr>
                    ))
                } <
                /tbody> <
                /table> <
                /div> <
                /div>
            )
        }

        {
            activeTab === 'products' && ( <
                div className = "products-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Product Management < /h2> <
                button className = "btn-primary"
                onClick = {
                    () => setShowProductForm(true) } >
                <
                i className = "fas fa-plus" > < /i> Add Product <
                /button> <
                /div>

                {
                    showProductForm && ( <
                        div className = "modal" >
                        <
                        div className = "modal-content" >
                        <
                        h3 > { editingProduct ? 'Edit Product' : 'Add Product' } < /h3> {
                            adminError && ( <
                                div className = "error-message" > { adminError } < /div>
                            )
                        } <
                        form onSubmit = { handleProductSubmit } >
                        <
                        input type = "text"
                        placeholder = "Product Name"
                        value = { productForm.name }
                        onChange = {
                            (e) => setProductForm({...productForm, name: e.target.value }) }
                        required / >
                        <
                        input type = "text"
                        placeholder = "Category"
                        value = { productForm.category }
                        onChange = {
                            (e) => setProductForm({...productForm, category: e.target.value }) }
                        required / >
                        <
                        textarea placeholder = "Description"
                        value = { productForm.description }
                        onChange = {
                            (e) => setProductForm({...productForm, description: e.target.value }) }
                        rows = "3"
                        required / >
                        <
                        div className = "modal-actions" >
                        <
                        button type = "submit"
                        disabled = { productLoading } > { productLoading ? 'Saving...' : 'Save' } <
                        /button> <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowProductForm(false);
                                setEditingProduct(null);
                                setProductForm({ name: '', category: '', description: '' });
                                setAdminError('');
                            }
                        }
                        disabled = { productLoading } >
                        Cancel <
                        /button> <
                        /div> <
                        /form> <
                        /div> <
                        /div>
                    )
                }

                <
                div className = "table-container" >
                <
                table className = "data-table" >
                <
                thead >
                <
                tr >
                <
                th > Name < /th> <
                th > Description < /th> <
                th > Category < /th> <
                th > Created Date < /th> <
                th > Actions < /th> <
                /tr> <
                /thead> <
                tbody > {
                    products.map(product => ( <
                        tr key = { product.id } >
                        <
                        td > { product.name } < /td> <
                        td > { product.description } < /td> <
                        td > { product.category } < /td> <
                        td > { new Date(product.created_at).toLocaleDateString() } < /td> <
                        td >
                        <
                        button className = "btn-icon"
                        title = "Edit"
                        onClick = {
                            () => {
                                setEditingProduct(product);
                                setProductForm({
                                    name: product.name,
                                    category: product.category,
                                    description: product.description
                                });
                                setShowProductForm(true);
                            }
                        } >
                        <
                        i className = "fas fa-edit" > < /i> <
                        /button> <
                        button className = "btn-icon btn-danger"
                        title = "Delete"
                        onClick = {
                            () => deleteProduct(product.id) } >
                        <
                        i className = "fas fa-trash" > < /i> <
                        /button> <
                        /td> <
                        /tr>
                    ))
                } <
                /tbody> <
                /table> <
                /div> <
                /div>
            )
        }

        {
            activeTab === 'requests' && ( <
                div className = "requests-tab" >
                <
                h2 > Farmer Requests < /h2>

                <
                div className = "filters" >
                <
                div className = "filter-group" >
                <
                label > Status: < /label> <
                select >
                <
                option value = "all" > All < /option> <
                option value = "pending" > Pending < /option> <
                option value = "accepted" > Accepted < /option> <
                option value = "rejected" > Rejected < /option> <
                /select> <
                /div> <
                /div>

                <
                div className = "table-container" >
                <
                table className = "data-table" >
                <
                thead >
                <
                tr >
                <
                th > Farmer Name < /th> <
                th > Product < /th> <
                th > Quantity < /th> <
                th > Zone < /th> <
                th > Woreda < /th> <
                th > Date < /th> <
                th > Status < /th> <
                th > Actions < /th> <
                /tr> <
                /thead> <
                tbody > {
                    requests.map(request => ( <
                        tr key = { request.id } >
                        <
                        td > { request.farmer_name } < /td> <
                        td > { request.product_name } < /td> <
                        td > { request.quantity } < /td> <
                        td > { request.zone_name || '-' } < /td> <
                        td > { request.woreda_name || '-' } < /td> <
                        td > { new Date(request.created_at).toLocaleDateString() } < /td> <
                        td >
                        <
                        span className = { `status-badge status-${request.status}` } > { request.status } < /span> <
                        /td> <
                        td > {
                            request.status === 'pending' && ( <
                                >
                                <
                                button className = "btn-icon btn-success"
                                onClick = {
                                    () => handleUpdateRequestStatus(request.id, 'accepted') }
                                title = "Accept" >
                                <
                                i className = "fas fa-check" > < /i> <
                                /button> <
                                button className = "btn-icon btn-danger"
                                onClick = {
                                    () => handleUpdateRequestStatus(request.id, 'rejected') }
                                title = "Reject" >
                                <
                                i className = "fas fa-times" > < /i> <
                                /button> <
                                />
                            )
                        } <
                        /td> <
                        /tr>
                    ))
                } <
                /tbody> <
                /table> <
                /div> <
                /div>
            )
        } <
        /main> <
        /div>
    );
};

export default RegionDashboard;
