import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const RegionDashboard = () => {
    const [activeTab, setActiveTab] = useState('overview');
    const [user, setUser] = useState(null);
    const [darkMode, setDarkMode] = useState(false);
    const navigate = useNavigate();

    // Product Management State
    const [products, setProducts] = useState([]);
    const [myProducts, setMyProducts] = useState([]);
    const [otherProducts, setOtherProducts] = useState([]);
    const [productFilter, setProductFilter] = useState('all');
    const [showProductModal, setShowProductModal] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        amount: '',
        description: '',
        price: '',
        subCategory: '',
        unit: '',
        manufacturer: '',
        expiryDate: ''
    });

    // Request Management State
    const [requests, setRequests] = useState([]);
    const [filteredRequests, setFilteredRequests] = useState([]);
    const [selectedRequests, setSelectedRequests] = useState([]);
    const [showRequestModal, setShowRequestModal] = useState(false);
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [requestStatus, setRequestStatus] = useState('');
    const [decisionReason, setDecisionReason] = useState('');
    const [bulkAction, setBulkAction] = useState('');
    const [statusFilter, setStatusFilter] = useState('all');

    // Zone Admin Management State
    const [zoneAdmins, setZoneAdmins] = useState([]);
    const [showZoneModal, setShowZoneModal] = useState(false);
    const [editingZone, setEditingZone] = useState(null);
    const [zoneForm, setZoneForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        zoneName: ''
    });

    // Stats State
    const [stats, setStats] = useState({
        totalRequests: 0,
        pendingRequests: 0,
        approvedRequests: 0,
        rejectedRequests: 0,
        acceptedRequests: 0,
        totalZoneAdmins: 0,
        totalProducts: 0
    });

    // Product categories with subcategories
    const productCategories = {
        'Chemicals': {
            subcategories: ['Fertilizers', 'Pesticides', 'Herbicides', 'Fungicides', 'Insecticides', 'Growth Regulators'],
            units: ['kg', 'liters', 'bottles', 'sachets']
        },
        'Machinery': {
            subcategories: ['Tractors', 'Plows', 'Harvesters', 'Seeders', 'Cultivators', 'Irrigation Equipment'],
            units: ['units', 'pieces']
        },
        'Seeds': {
            subcategories: ['Cereal Seeds', 'Vegetable Seeds', 'Fruit Seeds', 'Legume Seeds', 'Cash Crop Seeds'],
            units: ['kg', 'packets', 'bags']
        },
        'Tools': {
            subcategories: ['Hand Tools', 'Cutting Tools', 'Measuring Tools', 'Safety Equipment', 'Storage Equipment'],
            units: ['pieces', 'sets', 'units']
        },
        'Livestock': {
            subcategories: ['Feed', 'Vaccines', 'Supplements', 'Equipment', 'Medicine'],
            units: ['kg', 'liters', 'bottles', 'doses']
        },
        'Organic': {
            subcategories: ['Organic Fertilizers', 'Bio-pesticides', 'Compost', 'Organic Seeds'],
            units: ['kg', 'liters', 'bags']
        }
    };

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Region' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
    }, [navigate]);

    useEffect(() => {
        if (user) {
            fetchDashboardData();
        }
    }, [user]);

    useEffect(() => {
        if (requests.length > 0) {
            filterRequestsByStatus();
        }
    }, [statusFilter, requests]);

    useEffect(() => {
        // Apply dark mode to the entire document
        if (darkMode) {
            document.documentElement.classList.add('dark');
        } else {
            document.documentElement.classList.remove('dark');
        }
    }, [darkMode]);

    const filterRequestsByStatus = () => {
        if (statusFilter === 'all') {
            setFilteredRequests(requests);
            return;
        }

        let filtered = [];
        switch (statusFilter) {
            case 'pending':
                filtered = requests.filter(req => req.region_status === 'Pending' || !req.region_status);
                break;
            case 'approved':
                filtered = requests.filter(req => req.region_status === 'Approved');
                break;
            case 'accepted':
                filtered = requests.filter(req => req.region_status === 'Accepted');
                break;
            case 'rejected':
                filtered = requests.filter(req => req.region_status === 'Rejected');
                break;
            default:
                filtered = requests;
        }
        setFilteredRequests(filtered);
    };

    const fetchDashboardData = async () => {
        try {
            const token = localStorage.getItem('token');

            // Fetch zone admins in scope
            const zoneAdminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (zoneAdminsResponse.ok) {
                const data = await zoneAdminsResponse.json();
                const zoneAdminsData = data.filter(item => item.type === 'zone' && item.region_name === user.region_name);
                setZoneAdmins(zoneAdminsData);
                setStats(prev => ({ ...prev, totalZoneAdmins: zoneAdminsData.length }));
                console.log('Zone admins data:', zoneAdminsData);
            } else {
                console.error('Failed to fetch zone admins:', await zoneAdminsResponse.text());
            }

            // Fetch all products
            const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
                setStats(prev => ({ ...prev, totalProducts: productsData.length }));
                console.log('Products data:', productsData);
            } else {
                console.error('Failed to fetch products:', await productsResponse.text());
            }

            // Fetch my products
            const myProductsResponse = await fetch('http://localhost:5000/api/admins/myproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (myProductsResponse.ok) {
                const myProductsData = await myProductsResponse.json();
                setMyProducts(myProductsData);
                console.log('My products data:', myProductsData);
            } else {
                console.error('Failed to fetch my products:', await myProductsResponse.text());
            }

            // Fetch other admins' products
            const otherProductsResponse = await fetch('http://localhost:5000/api/admins/otherproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (otherProductsResponse.ok) {
                const otherProductsData = await otherProductsResponse.json();
                setOtherProducts(otherProductsData);
                console.log('Other products data:', otherProductsData);
            } else {
                console.error('Failed to fetch other products:', await otherProductsResponse.text());
            }

            // Fetch requests with detailed status
            const requestsResponse = await fetch('http://localhost:5000/api/admins/requests/status', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                console.log('All requests data:', requestsData);

                // Filter requests to only show those from the same region
                const regionRequests = requestsData.filter(req =>
                    req.region_name === user.region_name
                );
                console.log('User region_name:', user.region_name);
                console.log('User data:', user);
                setRequests(regionRequests);
                setFilteredRequests(regionRequests);
                console.log('Region requests:', regionRequests);

                // Calculate stats from requests data
                const totalRequests = regionRequests.length;
                const pendingRequests = regionRequests.filter(req => req.region_status === 'Pending' || !req.region_status).length;
                const approvedRequests = regionRequests.filter(req => req.region_status === 'Approved').length;
                const acceptedRequests = regionRequests.filter(req => req.region_status === 'Accepted').length;
                const rejectedRequests = regionRequests.filter(req => req.region_status === 'Rejected').length;

                setStats(prev => ({
                    ...prev,
                    totalRequests,
                    pendingRequests,
                    approvedRequests,
                    acceptedRequests,
                    rejectedRequests
                }));
            } else {
                console.error('Failed to fetch requests:', await requestsResponse.text());
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('userType');
        navigate('/login');
    };

    // Zone Admin Management Functions
    const handleZoneSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');

            if (zoneForm.password !== zoneForm.confirmPassword) {
                alert('Passwords do not match');
                return;
            }

            const zoneData = {
                full_name: zoneForm.fullName,
                phone_number: zoneForm.phoneNumber,
                password: zoneForm.password,
                zone_name: zoneForm.zoneName,
                region_name: user.region_name,
                type: 'zone'
            };

            const url = editingZone 
                ? `http://localhost:5000/api/admins/admins/${editingZone.id}`
                : 'http://localhost:5000/api/admins/register';

            const method = editingZone ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(zoneData)
            });

            if (response.ok) {
                alert(editingZone ? 'Zone admin updated successfully!' : 'Zone admin created successfully!');
                setShowZoneModal(false);
                setEditingZone(null);
                setZoneForm({
                    fullName: '',
                    phoneNumber: '',
                    password: '',
                    confirmPassword: '',
                    zoneName: ''
                });
                fetchDashboardData();
            } else {
                const errorData = await response.text();
                alert(`Error: ${errorData}`);
            }
        } catch (error) {
            console.error('Error submitting zone admin:', error);
            alert('Error submitting zone admin');
        }
    };

    // Product Management Functions
    const handleProductSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');

            const productData = {
                name: productForm.name,
                category: productForm.category,
                sub_category: productForm.subCategory,
                amount: parseInt(productForm.amount),
                unit: productForm.unit,
                price: parseFloat(productForm.price),
                description: productForm.description,
                manufacturer: productForm.manufacturer,
                expiry_date: productForm.expiryDate
            };

            const url = editingProduct 
                ? `http://localhost:5000/api/admins/products/${editingProduct.id}`
                : 'http://localhost:5000/api/admins/products';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(productData)
            });

            if (response.ok) {
                alert(editingProduct ? 'Product updated successfully!' : 'Product created successfully!');
                setShowProductModal(false);
                setEditingProduct(null);
                setProductForm({
                    name: '',
                    category: '',
                    amount: '',
                    description: '',
                    price: '',
                    subCategory: '',
                    unit: '',
                    manufacturer: '',
                    expiryDate: ''
                });
                fetchDashboardData();
            } else {
                const errorData = await response.text();
                alert(`Error: ${errorData}`);
            }
        } catch (error) {
            console.error('Error submitting product:', error);
            alert('Error submitting product');
        }
    };

    // Request Management Functions
    const handleRequestAction = async (requestId, action, reason = '') => {
        try {
            const token = localStorage.getItem('token');

            const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    status: action,
                    reason: reason
                })
            });

            if (response.ok) {
                alert(`Request ${action.toLowerCase()} successfully!`);
                fetchDashboardData();
                setShowRequestModal(false);
                setSelectedRequest(null);
                setDecisionReason('');
            } else {
                const errorData = await response.text();
                alert(`Error: ${errorData}`);
            }
        } catch (error) {
            console.error('Error updating request:', error);
            alert('Error updating request');
        }
    };

    const handleBulkAction = async () => {
        if (selectedRequests.length === 0 || !bulkAction) {
            alert('Please select requests and an action');
            return;
        }

        try {
            const token = localStorage.getItem('token');

            for (const requestId of selectedRequests) {
                await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        status: bulkAction,
                        reason: `Bulk ${bulkAction.toLowerCase()}`
                    })
                });
            }

            alert(`Bulk ${bulkAction.toLowerCase()} completed successfully!`);
            setSelectedRequests([]);
            setBulkAction('');
            fetchDashboardData();
        } catch (error) {
            console.error('Error with bulk action:', error);
            alert('Error with bulk action');
        }
    };

    return (
        <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
            {/* Header */}
            <header className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between items-center py-6">
                        <div className="flex items-center">
                            <div className="flex-shrink-0">
                                <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
                                    Region Admin Dashboard
                                </h1>
                                {user && (
                                    <p className="text-sm text-gray-500 dark:text-gray-400">
                                        {user.region_name} Region
                                    </p>
                                )}
                            </div>
                        </div>
                        <div className="flex items-center space-x-4">
                            <button
                                onClick={() => setDarkMode(!darkMode)}
                                className="p-2 rounded-md text-gray-400 hover:text-gray-500 hover:bg-gray-100 dark:hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-indigo-500"
                            >
                                {darkMode ? 'â˜€ï¸' : 'ðŸŒ™'}
                            </button>
                            <button
                                onClick={handleLogout}
                                className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-md text-sm font-medium"
                            >
                                Logout
                            </button>
                        </div>
                    </div>
                </div>
            </header>

            {/* Main Content */}
            <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
                {/* Tabs */}
                <div className="px-4 sm:px-0">
                    <div className="border-b border-gray-200 dark:border-gray-700">
                        <nav className="-mb-px flex space-x-8">
                            {['overview', 'requests', 'products', 'zones'].map((tab) => (
                                <button
                                    key={tab}
                                    onClick={() => setActiveTab(tab)}
                                    className={`${
                                        activeTab === tab
                                            ? 'border-indigo-500 text-indigo-600 dark:text-indigo-400'
                                            : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300 dark:text-gray-400 dark:hover:text-gray-300'
                                    } whitespace-nowrap py-2 px-1 border-b-2 font-medium text-sm capitalize`}
                                >
                                    {tab}
                                </button>
                            ))}
                        </nav>
                    </div>
                </div>

                {/* Tab Content */}
                <div className="mt-6">
                    {/* OVERVIEW */}
                    {activeTab === 'overview' && (
                        <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
                            <div className="bg-white dark:bg-gray-800 overflow-hidden shadow rounded-lg">
                                <div className="p-5">
                                    <div className="flex items-center">
                                        <div className="flex-shrink-0">
                                            <div className="text-2xl font-bold text-gray-900 dark:text-white">
                                                {stats.totalRequests}
                                            </div>
                                        </div>
                                    </div>
                                    <div className="mt-1">
                                        <p className="text-sm font-medium text-gray-500 dark:text-gray-400">
                                            Total Requests
                                        </p>
                                    </div>
                                </div>
                            </div>

                            <div className="bg-white dark:bg-gray-800 overflow-hidden shadow rounded-lg">
                                <div className="p-5">
                                    <div className="flex items-center">
                                        <div className="flex-shrink-0">
                                            <div className="text-2xl font-bold text-yellow-600">
                                                {stats.pendingRequests}
                                            </div>
                                        </div>
                                    </div>
                                    <div className="mt-1">
                                        <p className="text-sm font-medium text-gray-500 dark:text-gray-400">
                                            Pending Requests
                                        </p>
                                    </div>
                                </div>
                            </div>

                            <div className="bg-white dark:bg-gray-800 overflow-hidden shadow rounded-lg">
                                <div className="p-5">
                                    <div className="flex items-center">
                                        <div className="flex-shrink-0">
                                            <div className="text-2xl font-bold text-green-600">
                                                {stats.approvedRequests}
                                            </div>
                                        </div>
                                    </div>
                                    <div className="mt-1">
                                        <p className="text-sm font-medium text-gray-500 dark:text-gray-400">
                                            Approved Requests
                                        </p>
                                    </div>
                                </div>
                            </div>

                            <div className="bg-white dark:bg-gray-800 overflow-hidden shadow rounded-lg">
                                <div className="p-5">
                                    <div className="flex items-center">
                                        <div className="flex-shrink-0">
                                            <div className="text-2xl font-bold text-red-600">
                                                {stats.rejectedRequests}
                                            </div>
                                        </div>
                                    </div>
                                    <div className="mt-1">
                                        <p className="text-sm font-medium text-gray-500 dark:text-gray-400">
                                            Rejected Requests
                                        </p>
                                    </div>
                                </div>
                            </div>

                            <div className="bg-white dark:bg-gray-800 overflow-hidden shadow rounded-lg">
                                <div className="p-5">
                                    <div className="flex items-center">
                                        <div className="flex-shrink-0">
                                            <div className="text-2xl font-bold text-blue-600">
                                                {stats.acceptedRequests}
                                            </div>
                                        </div>
                                    </div>
                                    <div className="mt-1">
                                        <p className="text-sm font-medium text-gray-500 dark:text-gray-400">
                                            Accepted Requests
                                        </p>
                                    </div>
                                </div>
                            </div>

                            <div className="bg-white dark:bg-gray-800 overflow-hidden shadow rounded-lg">
                                <div className="p-5">
                                    <div className="flex items-center">
                                        <div className="flex-shrink-0">
                                            <div className="text-2xl font-bold text-purple-600">
                                                {stats.totalProducts}
                                            </div>
                                        </div>
                                    </div>
                                    <div className="mt-1">
                                        <p className="text-sm font-medium text-gray-500 dark:text-gray-400">
                                            Total Products
                                        </p>
                                    </div>
                                </div>
                            </div>

                            <div className="bg-white dark:bg-gray-800 overflow-hidden shadow rounded-lg">
                                <div className="p-5">
                                    <div className="flex items-center">
                                        <div className="flex-shrink-0">
                                            <div className="text-2xl font-bold text-indigo-600">
                                                {stats.totalZoneAdmins}
                                            </div>
                                        </div>
                                    </div>
                                    <div className="mt-1">
                                        <p className="text-sm font-medium text-gray-500 dark:text-gray-400">
                                            Zone Admins
                                        </p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}

                    {/* REQUESTS */}
                    {activeTab === 'requests' && (
                        <div className="space-y-6">
                            {/* Request Filters */}
                            <div className="bg-white dark:bg-gray-800 shadow rounded-lg p-6">
                                <div className="flex flex-wrap gap-4 items-center justify-between">
                                    <div className="flex flex-wrap gap-2">
                                        {['all', 'pending', 'approved', 'accepted', 'rejected'].map((status) => (
                                            <button
                                                key={status}
                                                onClick={() => setStatusFilter(status)}
                                                className={`px-4 py-2 rounded-md text-sm font-medium ${
                                                    statusFilter === status
                                                        ? 'bg-indigo-600 text-white'
                                                        : 'bg-gray-200 text-gray-700 hover:bg-gray-300 dark:bg-gray-700 dark:text-gray-300 dark:hover:bg-gray-600'
                                                }`}
                                            >
                                                {status.charAt(0).toUpperCase() + status.slice(1)}
                                            </button>
                                        ))}
                                    </div>
                                    <div className="flex gap-2">
                                        <select
                                            value={bulkAction}
                                            onChange={(e) => setBulkAction(e.target.value)}
                                            className="border border-gray-300 dark:border-gray-600 rounded-md px-3 py-2 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                                        >
                                            <option value="">Bulk Action</option>
                                            <option value="Approved">Approve</option>
                                            <option value="Accepted">Accept</option>
                                            <option value="Rejected">Reject</option>
                                        </select>
                                        <button
                                            onClick={handleBulkAction}
                                            disabled={selectedRequests.length === 0 || !bulkAction}
                                            className="bg-indigo-600 hover:bg-indigo-700 disabled:bg-gray-400 text-white px-4 py-2 rounded-md text-sm font-medium"
                                        >
                                            Apply
                                        </button>
                                    </div>
                                </div>
                            </div>

                            {/* Requests Table */}
                            <div className="bg-white dark:bg-gray-800 shadow rounded-lg overflow-hidden">
                                <div className="overflow-x-auto">
                                    <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
                                        <thead className="bg-gray-50 dark:bg-gray-700">
                                            <tr>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                                                    <input
                                                        type="checkbox"
                                                        onChange={(e) => {
                                                            if (e.target.checked) {
                                                                setSelectedRequests(filteredRequests.map(req => req.id));
                                                            } else {
                                                                setSelectedRequests([]);
                                                            }
                                                        }}
                                                        className="rounded"
                                                    />
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                                                    Farmer
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                                                    Product
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                                                    Quantity
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                                                    Status
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                                                    Actions
                                                </th>
                                            </tr>
                                        </thead>
                                        <tbody className="bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700">
                                            {filteredRequests.map((request) => (
                                                <tr key={request.id}>
                                                    <td className="px-6 py-4 whitespace-nowrap">
                                                        <input
                                                            type="checkbox"
                                                            checked={selectedRequests.includes(request.id)}
                                                            onChange={(e) => {
                                                                if (e.target.checked) {
                                                                    setSelectedRequests([...selectedRequests, request.id]);
                                                                } else {
                                                                    setSelectedRequests(selectedRequests.filter(id => id !== request.id));
                                                                }
                                                            }}
                                                            className="rounded"
                                                        />
                                                    </td>
                                                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                                                        {request.farmer_name}
                                                    </td>
                                                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                                                        {request.product_name}
                                                    </td>
                                                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                                                        {request.quantity} {request.unit}
                                                    </td>
                                                    <td className="px-6 py-4 whitespace-nowrap">
                                                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                                                            request.region_status === 'Approved' ? 'bg-green-100 text-green-800' :
                                                            request.region_status === 'Accepted' ? 'bg-blue-100 text-blue-800' :
                                                            request.region_status === 'Rejected' ? 'bg-red-100 text-red-800' :
                                                            'bg-yellow-100 text-yellow-800'
                                                        }`}>
                                                            {request.region_status || 'Pending'}
                                                        </span>
                                                    </td>
                                                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium space-x-2">
                                                        {(!request.region_status || request.region_status === 'Pending') && (
                                                            <>
                                                                <button
                                                                    onClick={() => handleRequestAction(request.id, 'Approved')}
                                                                    className="text-green-600 hover:text-green-900"
                                                                >
                                                                    Approve
                                                                </button>
                                                                <button
                                                                    onClick={() => handleRequestAction(request.id, 'Accepted')}
                                                                    className="text-blue-600 hover:text-blue-900"
                                                                >
                                                                    Accept
                                                                </button>
                                                                <button
                                                                    onClick={() => handleRequestAction(request.id, 'Rejected')}
                                                                    className="text-red-600 hover:text-red-900"
                                                                >
                                                                    Reject
                                                                </button>
                                                            </>
                                                        )}
                                                        <button
                                                            onClick={() => {
                                                                setSelectedRequest(request);
                                                                setShowRequestModal(true);
                                                            }}
                                                            className="text-indigo-600 hover:text-indigo-900"
                                                        >
                                                            View
                                                        </button>
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    )}

                    {/* PRODUCTS */}
                    {activeTab === 'products' && (
                        <div className="space-y-6">
                            {/* Product Filters */}
                            <div className="bg-white dark:bg-gray-800 shadow rounded-lg p-6">
                                <div className="flex flex-wrap gap-4 items-center justify-between">
                                    <div className="flex flex-wrap gap-2">
                                        {['all', 'my', 'others'].map((filter) => (
                                            <button
                                                key={filter}
                                                onClick={() => setProductFilter(filter)}
                                                className={`px-4 py-2 rounded-md text-sm font-medium ${
                                                    productFilter === filter
                                                        ? 'bg-indigo-600 text-white'
                                                        : 'bg-gray-200 text-gray-700 hover:bg-gray-300 dark:bg-gray-700 dark:text-gray-300 dark:hover:bg-gray-600'
                                                }`}
                                            >
                                                {filter === 'my' ? 'My Products' : filter === 'others' ? 'Other Products' : 'All Products'}
                                            </button>
                                        ))}
                                    </div>
                                    <button
                                        onClick={() => {
                                            setEditingProduct(null);
                                            setProductForm({
                                                name: '',
                                                category: '',
                                                amount: '',
                                                description: '',
                                                price: '',
                                                subCategory: '',
                                                unit: '',
                                                manufacturer: '',
                                                expiryDate: ''
                                            });
                                            setShowProductModal(true);
                                        }}
                                        className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-md text-sm font-medium"
                                    >
                                        Add Product
                                    </button>
                                </div>
                            </div>

                            {/* Products Grid */}
                            <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
                                {(productFilter === 'all' ? products : 
                                  productFilter === 'my' ? myProducts : otherProducts).map((product) => (
                                    <div key={product.id} className="bg-white dark:bg-gray-800 shadow rounded-lg p-6">
                                        <div className="flex items-center justify-between">
                                            <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                                                {product.name}
                                            </h3>
                                            <span className="text-sm text-gray-500 dark:text-gray-400">
                                                {product.category}
                                            </span>
                                        </div>
                                        <div className="mt-2">
                                            <p className="text-sm text-gray-600 dark:text-gray-300">
                                                {product.description}
                                            </p>
                                            <div className="mt-2 flex items-center justify-between">
                                                <span className="text-lg font-bold text-gray-900 dark:text-white">
                                                    ${product.price}
                                                </span>
                                                <span className="text-sm text-gray-500 dark:text-gray-400">
                                                    {product.amount} {product.unit}
                                                </span>
                                            </div>
                                        </div>
                                        <div className="mt-4 flex space-x-2">
                                            <button
                                                onClick={() => {
                                                    setEditingProduct(product);
                                                    setProductForm({
                                                        name: product.name,
                                                        category: product.category,
                                                        amount: product.amount.toString(),
                                                        description: product.description,
                                                        price: product.price.toString(),
                                                        subCategory: product.sub_category,
                                                        unit: product.unit,
                                                        manufacturer: product.manufacturer,
                                                        expiryDate: product.expiry_date
                                                    });
                                                    setShowProductModal(true);
                                                }}
                                                className="text-indigo-600 hover:text-indigo-900 text-sm font-medium"
                                            >
                                                Edit
                                            </button>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}

                    {/* ZONES */}
                    {activeTab === 'zones' && (
                        <div className="space-y-6">
                            {/* Zone Header */}
                            <div className="bg-white dark:bg-gray-800 shadow rounded-lg p-6">
                                <div className="flex items-center justify-between">
                                    <h2 className="text-lg font-medium text-gray-900 dark:text-white">
                                        Zone Administrators
                                    </h2>
                                    <button
                                        onClick={() => {
                                            setEditingZone(null);
                                            setZoneForm({
                                                fullName: '',
                                                phoneNumber: '',
                                                password: '',
                                                confirmPassword: '',
                                                zoneName: ''
                                            });
                                            setShowZoneModal(true);
                                        }}
                                        className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-md text-sm font-medium"
                                    >
                                        Add Zone Admin
                                    </button>
                                </div>
                            </div>

                            {/* Zone Admins Table */}
                            <div className="bg-white dark:bg-gray-800 shadow rounded-lg overflow-hidden">
                                <div className="overflow-x-auto">
                                    <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
                                        <thead className="bg-gray-50 dark:bg-gray-700">
                                            <tr>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                                                    Name
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                                                    Phone
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                                                    Zone
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                                                    Actions
                                                </th>
                                            </tr>
                                        </thead>
                                        <tbody className="bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700">
                                            {zoneAdmins.map((admin) => (
                                                <tr key={admin.id}>
                                                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                                                        {admin.full_name}
                                                    </td>
                                                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                                                        {admin.phone_number}
                                                    </td>
                                                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                                                        {admin.zone_name}
                                                    </td>
                                                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                                                        <button
                                                            onClick={() => {
                                                                setEditingZone(admin);
                                                                setZoneForm({
                                                                    fullName: admin.full_name,
                                                                    phoneNumber: admin.phone_number,
                                                                    password: '',
                                                                    confirmPassword: '',
                                                                    zoneName: admin.zone_name
                                                                });
                                                                setShowZoneModal(true);
                                                            }}
                                                            className="text-indigo-600 hover:text-indigo-900"
                                                        >
                                                            Edit
                                                        </button>
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </main>
        </div>
    );
};

export default RegionDashboard;
