import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import Sidebar from './components/Sidebar';
import './index.css';

// Import new utility functions and constants
import { formatTimestampToDate } from './utils/helpers';
import { DEPARTMENTS, REQUEST_STATUS } from './utils/constants';

// Import the UnionAdminDashboard component
import UnionAdminDashboard from './components/UnionAdminDashboard';

// Placeholder for a Modal component
const Modal = ({ show, onClose, title, message }) => {
    if (!show) return null;
    return ( <
        div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50" >
        <
        div className = "bg-white p-6 rounded-lg shadow-xl w-96" >
        <
        div className = "flex justify-between items-center mb-4" >
        <
        h3 className = "text-xl font-semibold text-gray-900" > { title } < /h3> <
        button onClick = { onClose }
        className = "text-gray-400 hover:text-gray-600 transition-colors" >
        <
        svg className = "w-6 h-6"
        fill = "none"
        stroke = "currentColor"
        viewBox = "0 0 24 24"
        xmlns = "http://www.w3.org/2000/svg" >
        <
        path strokeLinecap = "round"
        strokeLinejoin = "round"
        strokeWidth = "2"
        d = "M6 18L18 6M6 6l12 12" > < /path> <
        /svg> <
        /button> <
        /div> <
        div className = "text-sm text-gray-700 mb-4" >
        <
        p > { message } < /p> <
        /div> <
        div className = "text-right" >
        <
        button onClick = { onClose }
        className = "px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors" >
        OK <
        /button> <
        /div> <
        /div> <
        /div>
    );
};

// Placeholder components for other pages
const MasterData = () => ( <
    div className = "flex-1 p-6 bg-gray-100" >
    <
    h1 className = "text-2xl font-bold text-gray-800" > Master Data < /h1> <
    p className = "mt-4 text-gray-600" > Master data management features will be built here. < /p> <
    /div>
);

const Reports = () => ( <
    div className = "flex-1 p-6 bg-gray-100" >
    <
    h1 className = "text-2xl font-bold text-gray-800" > Reports < /h1> <
    p className = "mt-4 text-gray-600" > Reporting features will be built here. < /p> <
    /div>
);

// Placeholder component for RequestApprovalForm
const RequestApprovalForm = ({ request, onClose }) => {
    const { register, handleSubmit } = useForm();

    const onSubmit = (data) => {
        console.log("Form data:", data);
        // Here you would send data to your MySQL backend
        onClose();
    };

    return ( <
        div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50" >
        <
        div className = "bg-white p-6 rounded-lg shadow-xl w-1/2" >
        <
        div className = "flex justify-between items-center mb-4" >
        <
        h3 className = "text-xl font-semibold text-gray-900" > Request Details < /h3> <
        button onClick = { onClose }
        className = "text-gray-400 hover:text-gray-600 transition-colors" >
        <
        svg className = "w-6 h-6"
        fill = "none"
        stroke = "currentColor"
        viewBox = "0 0 24 24"
        xmlns = "http://www.w3.org/2000/svg" >
        <
        path strokeLinecap = "round"
        strokeLinejoin = "round"
        strokeWidth = "2"
        d = "M6 18L18 6M6 6l12 12" > < /path> <
        /svg> <
        /button> <
        /div> <
        div className = "text-sm text-gray-700 mb-4" >
        <
        pre > { JSON.stringify(request, null, 2) } < /pre> <
        /div> <
        form onSubmit = { handleSubmit(onSubmit) } >
        <
        div className = "mb-4" >
        <
        label className = "block text-gray-700" > Comments < /label> <
        textarea {...register("comments") }
        className = "mt-1 block w-full rounded-md border-gray-300 shadow-sm" > < /textarea> <
        /div> <
        div className = "text-right" >
        <
        button type = "submit"
        className = "px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 transition-colors" >
        Approve <
        /button> <
        button type = "button"
        onClick = { onClose }
        className = "ml-2 px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition-colors" >
        Reject <
        /button> <
        /div> <
        /form> <
        /div> <
        /div>
    );
};

function App() {
    const [currentPage, setCurrentPage] = useState('union-dashboard');
    const [showModal, setShowModal] = useState(false);
    const [modalContent, setModalContent] = useState('');
    const [selectedRequest, setSelectedRequest] = useState(null);

    return ( <
        div className = "flex h-screen bg-gray-100 font-sans antialiased" >
        <
        Sidebar currentPage = { currentPage }
        setCurrentPage = { setCurrentPage }
        /> <
        div className = "flex-1 flex flex-col overflow-hidden" >
        <
        main className = "flex-1 overflow-x-hidden overflow-y-auto bg-gray-200" > {
            currentPage === 'union-dashboard' && < UnionAdminDashboard setSelectedRequest = { setSelectedRequest }
            />} { currentPage === 'master-data' && < MasterData / > } { currentPage === 'reports' && < Reports / > } {
                selectedRequest && ( <
                    RequestApprovalForm request = { selectedRequest }
                    onClose = {
                        () => setSelectedRequest(null) }
                    />
                )
            } <
            /main> <
            /div> <
            Modal
            show = { showModal }
            onClose = {
                () => setShowModal(false) }
            title = "Notification"
            message = { modalContent }
            /> <
            /div>
        );
    }

    export default App;
