        button
        type = "submit"
        className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" > { editingWoreda ? 'Update' : 'Register' } <
            /button> < /
        div > <
            /form> < /
        div > <
            /div>
    )
}

{ /* Modal for Product Form */ } {
    showProductForm && ( <
        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
        <
        div className = "bg-white p-6 rounded shadow-lg w-full max-w-md" >
        <
        h2 className = "text-xl font-bold mb-4" > { editingProduct ? 'Edit Product' : 'Add Product' } <
        /h2> <
        form onSubmit = { handleProductSubmit } >
        <
        div className = "mb-4" >
        <
        label className = "block text-gray-700 mb-2" > Product Name < /label> <
        input type = "text"
        value = { productForm.name }
        onChange = {
            (e) => setProductForm({...productForm, name: e.target.value })
        }
        className = "w-full border rounded p-2"
        required /
        >
        <
        /div> <
        div className = "mb-4" >
        <
        label className = "block text-gray-700 mb-2" > Category < /label> <
        input type = "text"
        value = { productForm.category }
        onChange = {
            (e) => setProductForm({...productForm, category: e.target.value })
        }
        className = "w-full border rounded p-2"
        required /
        >
        <
        /div> <
        div className = "mb-4" >
        <
        label className = "block text-gray-700 mb-2" > Amount < /label> <
        input type = "text"
        value = { productForm.amount }
        onChange = {
            (e) => setProductForm({...productForm, amount: e.target.value })
        }
        className = "w-full border rounded p-2"
        required /
        >
        <
        /div> <
        div className = "mb-4" >
        <
        label className = "block text-gray-700 mb-2" > Price < /label> <
        input type = "text"
        value = { productForm.price }
        onChange = {
            (e) => setProductForm({...productForm, price: e.target.value })
        }
        className = "w-full border rounded p-2"
        required /
        >
        <
        /div> <
        div className = "mb-4" >
        <
        label className = "block text-gray-700 mb-2" > Description < /label> <
        textarea value = { productForm.description }
        onChange = {
            (e) => setProductForm({...productForm, description: e.target.value })
        }
        className = "w-full border rounded p-2"
        rows = "3" >
        <
        /textarea> < /
        div > <
        div className = "flex justify-end space-x-2" >
        <
        button type = "button"
        onClick = {
            () => {
                setShowProductForm(false);
                setEditingProduct(null);
            }
        }
        className = "bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded" >
        Cancel <
        /button> <
        button type = "submit"
        className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" > { editingProduct ? 'Update' : 'Add' } <
        /button> < /
        div > <
        /form> < /
        div > <
        /div>
    )
}

{ /* Modal for Request Details and Status Update */ } {
    showRequestModal && ( <
        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
        <
        div className = "bg-white p-6 rounded shadow-lg w-full max-w-2xl max-h-screen overflow-y-auto" >
        <
        h2 className = "text-xl font-bold mb-4" > Request Details < /h2>

        {
            selectedRequest && ( <
                div className = "request-details" >
                <
                div className = "mb-4 p-3 bg-gray-50 rounded" >
                <
                h3 className = "text-lg font-semibold" > { selectedRequest.product_name } < /h3> <
                p > < strong > Farmer: < /strong> {selectedRequest.farmer_name}</p >
                <
                p > < strong > Phone: < /strong> {selectedRequest.farmer_phone}</p >
                <
                p > < strong > Quantity: < /strong> {selectedRequest.quantity}</p >
                <
                p > < strong > Note: < /strong> {selectedRequest.note || 'None'}</p >
                <
                p > < strong > Product Amount: < /strong> {selectedRequest.product_amount}</p >
                <
                p > < strong > Woreda: < /strong> {selectedRequest.woreda_name}</p >
                <
                p > < strong > Kebele: < /strong> {selectedRequest.kebele_name}</p >
                <
                p > < strong > Date: < /strong> {new Date(selectedRequest.created_at).toLocaleString()}</p >
                <
                div className = "mt-2" >
                <
                strong > Status: < /strong> {getStatusBadge(selectedRequest.status)} < /
                div > <
                /div>

                { /* Show status at each level */ } { getLevelStatus('kebele', selectedRequest) } { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) }

                {
                    canZoneAdminAct(selectedRequest) && ( <
                        div className = "mt-4" >
                        <
                        h3 className = "font-semibold mb-2" > Update Status < /h3> <
                        div className = "mb-4" >
                        <
                        label className = "block text-gray-700 mb-2" > Decision Reason(Optional) < /label> <
                        textarea value = { decisionReason }
                        onChange = {
                            (e) => setDecisionReason(e.target.value)
                        }
                        className = "w-full border rounded p-2"
                        rows = "3"
                        placeholder = "Enter reason for your decision" >
                        <
                        /textarea> < /
                        div > <
                        div className = "flex space-x-2" >
                        <
                        button onClick = {
                            () => updateRequestStatus(selectedRequest.id, 'Approved', decisionReason)
                        }
                        className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" >
                        Approve <
                        /button> <
                        button onClick = {
                            () => updateRequestStatus(selectedRequest.id, 'Accepted', decisionReason)
                        }
                        className = "bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded" >
                        Accept <
                        /button> <
                        button onClick = {
                            () => updateRequestStatus(selectedRequest.id, 'Rejected', decisionReason)
                        }
                        className = "bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded" >
                        Reject <
                        /button> < /
                        div > <
                        /div>
                    )
                }

                {
                    canDeleteRequest(selectedRequest) && ( <
                        div className = "mt-4" >
                        <
                        button onClick = {
                            () => handleDeleteRequest(selectedRequest.id)
                        }
                        className = "bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded" >
                        Delete Request <
                        /button> < /
                        div >
                    )
                } <
                /div>
            )
        }

        <
        div className = "mt-6" >
        <
        button onClick = {
            () => {
                setShowRequestModal(false);
                setSelectedRequest(null);
                setRequestStatus('');
                setDecisionReason('');
            }
        }
        className = "bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded" >
        Close <
        /button> < /
        div > <
        /div> < /
        div >
    )
} <
/div>
);
};

export default ZoneDashboard;
