            }
        }
        className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
        Cancel <
        /button> <
        button type = "submit"
        className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" > { editingProduct ? 'Update' : 'Add' } <
        /button> < /
        div > <
        /form> < /
        div > <
        /div>
    )
}

{ /* Request Detail Modal */ } {
    showRequestModal && selectedRequest && ( <
        div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
        <
        div className = "bg-white dark:bg-gray-800 p-6 rounded-lg shadow-lg w-full max-w-2xl max-h-screen overflow-y-auto" >
        <
        h2 className = "text-xl font-semibold mb-4" > Request Details < /h2>

        <
        div className = "mb-4" >
        <
        h3 className = "font-semibold" > Farmer Information < /h3> <
        p > < strong > Name: < /strong> {selectedRequest.farmer_name}</p >
        <
        p > < strong > Phone: < /strong> {selectedRequest.farmer_phone}</p >
        <
        p > < strong > Location: < /strong> {selectedRequest.region_name} / { selectedRequest.zone_name }
        / {selectedRequest.woreda_name} / { selectedRequest.kebele_name } < /p> < /
        div >

        <
        div className = "mb-4" >
        <
        h3 className = "font-semibold" > Product Information < /h3> <
        p > < strong > Product: < /strong> {selectedRequest.product_name}</p >
        <
        p > < strong > Description: < /strong> {selectedRequest.product_description}</p >
        <
        p > < strong > Category: < /strong> {selectedRequest.product_category}</p >
        <
        p > < strong > Amount: < /strong> {selectedRequest.amount}</p >
        <
        p > < strong > Price: < /strong> {selectedRequest.price}</p >
        <
        /div>

        <
        div className = "mb-4" >
        <
        h3 className = "font-semibold" > Request Information < /h3> <
        p > < strong > Status: < /strong> {getStatusBadge(selectedRequest.status)}</p >
        <
        p > < strong > Request Date: < /strong> {new Date(selectedRequest.created_at).toLocaleString()}</p > { selectedRequest.feedback && < p > < strong > Feedback: < /strong> {selectedRequest.feedback}</p > } <
        /div>

        <
        div className = "mb-4" >
        <
        h3 className = "font-semibold" > Approval Process < /h3> { getLevelStatus('kebele', selectedRequest) } { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) } < /
        div >

        {
            requestStatus && ( <
                div className = "mb-4" >
                <
                h3 className = "font-semibold" > Update Status < /h3> <
                p > You are about to < strong > { requestStatus } < /strong> this request.</p >
                <
                div className = "mt-2" >
                <
                label className = "block mb-1" > Reason(Optional): < /label> <
                textarea value = { decisionReason }
                onChange = {
                    (e) => setDecisionReason(e.target.value)
                }
                className = "w-full border rounded p-2 dark:bg-gray-700 dark:text-white"
                placeholder = "Enter reason for your decision"
                rows = "3" /
                >
                <
                /div> < /
                div >
            )
        }

        <
        div className = "flex justify-end space-x-2" >
        <
        button onClick = {
            () => {
                setShowRequestModal(false);
                setSelectedRequest(null);
                setRequestStatus('');
                setDecisionReason('');
            }
        }
        className = "bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded" >
        Close <
        /button>

        {
            requestStatus && ( <
                button onClick = { confirmStatusUpdate }
                className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" >
                Confirm { requestStatus } <
                /button>
            )
        } <
        /div> < /
        div > <
        /div>
    )
} <
/div>
);
};

export default WoredaDashboard;
