import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Moon, Sun, Package, Truck, ShoppingCart, LogOut, Plus, Edit, Trash2, Eye, Check } from 'lucide-react';
import './FarmerDashboard.css';

const FarmerDashboard = () => {
    const [activeTab, setActiveTab] = useState('requests');
    const [user, setUser] = useState(null);
    const [darkMode, setDarkMode] = useState(false);
    const navigate = useNavigate();

    // Request Management State
    const [requests, setRequests] = useState([]);
    const [showRequestModal, setShowRequestModal] = useState(false);
    const [showStatusModal, setShowStatusModal] = useState(false);
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [editingRequest, setEditingRequest] = useState(null);
    const [requestForm, setRequestForm] = useState({
        product_id: '',
        quantity: '',
        note: ''
    });

    // Products State
    const [products, setProducts] = useState([]);
    
    // Deliveries State
    const [deliveries, setDeliveries] = useState([]);
    const [showDeliveryModal, setShowDeliveryModal] = useState(false);
    const [confirmingDelivery, setConfirmingDelivery] = useState(null);
    const [deliveryNote, setDeliveryNote] = useState('');

    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Farmer' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
        fetchDashboardData();
        
        // Load dark mode preference
        const savedDarkMode = localStorage.getItem('darkMode') === 'true';
        setDarkMode(savedDarkMode);
        document.body.classList.toggle('dark-mode', savedDarkMode);
    }, [navigate]);

    const fetchDashboardData = async() => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');

            // Fetch ALL products available in the country
            const productsResponse = await fetch('http://localhost:5000/api/farmers/products', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
            } else {
                console.error('Failed to fetch products');
                setError('Failed to load products');
            }

            // Fetch farmer's requests
            const requestsResponse = await fetch('http://localhost:5000/api/farmers/requests', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                setRequests(requestsData);
            } else {
                console.error('Failed to fetch requests');
            }

            // Fetch farmer's deliveries
            const deliveriesResponse = await fetch('http://localhost:5000/api/farmers/deliveries', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (deliveriesResponse.ok) {
                const deliveriesData = await deliveriesResponse.json();
                setDeliveries(deliveriesData);
            } else {
                console.error('Failed to fetch deliveries');
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
            setError('Error loading dashboard data');
        } finally {
            setLoading(false);
        }
    };

    const fetchRequestStatusDetail = async(requestId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/farmers/request/${requestId}/status`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                const statusData = await response.json();
                setSelectedRequest(statusData);
                setShowStatusModal(true);
            } else {
                alert('Failed to fetch request status details');
            }
        } catch (error) {
            console.error('Error fetching status details:', error);
            alert('Error fetching status details');
        }
    };

    const toggleDarkMode = () => {
        const newDarkMode = !darkMode;
        setDarkMode(newDarkMode);
        localStorage.setItem('darkMode', newDarkMode.toString());
        document.body.classList.toggle('dark-mode', newDarkMode);
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('userType');
        localStorage.removeItem('darkMode');
        document.body.classList.remove('dark-mode');
        navigate('/login');
    };

    // Request Management Functions
    const handleRequestSubmit = async(e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');

            if (!requestForm.product_id || !requestForm.quantity) {
                alert('Product and quantity are required');
                return;
            }

            if (requestForm.quantity <= 0) {
                alert('Quantity must be greater than 0');
                return;
            }

            const url = editingRequest ?
                `http://localhost:5000/api/farmers/request/${editingRequest.id}` :
                'http://localhost:5000/api/farmers/request';

            const method = editingRequest ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    product_id: parseInt(requestForm.product_id),
                    quantity: parseInt(requestForm.quantity),
                    note: requestForm.note
                })
            });

            const responseData = await response.json();

            if (response.ok) {
                setShowRequestModal(false);
                setEditingRequest(null);
                setRequestForm({ product_id: '', quantity: '', note: '' });
                fetchDashboardData();
                alert(editingRequest ? 'Request updated successfully' : 'Request created successfully');
            } else {
                alert(responseData.message || 'Error processing request');
            }
        } catch (error) {
            console.error('Error processing request:', error);
            alert('Error processing request');
        }
    };

    const canEditOrDelete = (request) => {
        // Allow edit/delete only if all kebele and above statuses are still Pending
        const higherLevelStatuses = [
            request.kebele_status,
            request.woreda_status,
            request.zone_status,
            request.region_status,
            request.federal_status
        ];

        return higherLevelStatuses.every(status => status === 'Pending');
    };

    const canConfirmDelivery = (request) => {
        // Check if request is accepted at any level
        const levelStatuses = [
            request.kebele_status,
            request.woreda_status,
            request.zone_status,
            request.region_status,
            request.federal_status
        ];

        return levelStatuses.some(status => status === 'Accepted');
    };

    const handleEditRequest = (request) => {
        if (!canEditOrDelete(request)) {
            alert('Cannot edit request: It has been processed beyond farmer level');
            return;
        }

        setEditingRequest(request);
        setRequestForm({
            product_id: request.product_id.toString(),
            quantity: request.quantity.toString(),
            note: request.note || ''
        });
        setShowRequestModal(true);
    };

    const handleDeleteRequest = async(requestId) => {
        const request = requests.find(r => r.id === requestId);
        if (!canEditOrDelete(request)) {
            alert('Cannot delete request: It has been processed beyond farmer level');
            return;
        }

        if (window.confirm('Are you sure you want to delete this request?')) {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/farmers/request/${requestId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                    alert('Request deleted successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error deleting request');
                }
            } catch (error) {
                console.error('Error deleting request:', error);
                alert('Error deleting request');
            }
        }
    };

    const handleConfirmDelivery = async(request) => {
        setConfirmingDelivery(request);
        setDeliveryNote('');
        setShowDeliveryModal(true);
    };

    const submitDeliveryConfirmation = async(e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/farmers/requests/${confirmingDelivery.id}/confirm-delivery`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    note: deliveryNote
                })
            });

            const responseData = await response.json();

            if (response.ok) {
                setShowDeliveryModal(false);
                setConfirmingDelivery(null);
                setDeliveryNote('');
                fetchDashboardData();
                alert('Delivery confirmed successfully');
            } else {
                alert(responseData.message || 'Error confirming delivery');
            }
        } catch (error) {
            console.error('Error confirming delivery:', error);
            alert('Error confirming delivery');
        }
    };

    const getStatusBadge = (status) => {
        const statusClasses = {
            'Pending': 'bg-yellow-100 text-yellow-800 border-yellow-300',
            'Accepted': 'bg-green-100 text-green-800 border-green-300',
            'Rejected': 'bg-red-100 text-red-800 border-red-300',
            'Approved': 'bg-blue-100 text-blue-800 border-blue-300'
        };

        return (
            <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium border ${statusClasses[status] || 'bg-gray-100 text-gray-800 border-gray-300'}`}>
                {status}
            </span>
        );
    };

    const getLevelStatus = (level, request) => {
        const status = request[`${level}_status`] || 'Pending';
        const admin = request[`${level}_admin_name`];
        const feedback = request[`${level}_feedback`];
        const date = request[`${level}_approved_at`];

        const formatLevel = level.charAt(0).toUpperCase() + level.slice(1);

        return (
            <div className="bg-white dark:bg-gray-800 p-4 rounded-lg border border-gray-200 dark:border-gray-700">
                <h4 className="font-semibold text-gray-900 dark:text-white mb-2">{formatLevel} Level</h4>
                <div className="space-y-1">
                    <p className="text-sm">
                        <span className="font-medium">Status:</span> {getStatusBadge(status)}
                    </p>
                    {admin && (
                        <p className="text-sm text-gray-600 dark:text-gray-300">
                            <span className="font-medium">Admin:</span> {admin}
                        </p>
                    )}
                    {feedback && (
                        <p className="text-sm text-gray-600 dark:text-gray-300">
                            <span className="font-medium">Feedback:</span> {feedback}
                        </p>
                    )}
                    {date && (
                        <p className="text-sm text-gray-600 dark:text-gray-300">
                            <span className="font-medium">Date:</span> {new Date(date).toLocaleString()}
                        </p>
                    )}
                </div>
            </div>
        );
    };

    if (loading) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <div className="text-center">
                    <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-500 mx-auto"></div>
                    <p className="mt-4 text-lg">Loading...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <div className="text-center text-red-600">
                    <p className="text-xl">{error}</p>
                </div>
            </div>
        );
    }

    return (
        <div className={`min-h-screen bg-gray-50 dark:bg-gray-900 ${darkMode ? 'dark' : ''}`}>
            {/* Sidebar */}
            <div className="fixed left-0 top-0 h-full w-64 bg-white dark:bg-gray-800 shadow-lg border-r border-gray-200 dark:border-gray-700 transition-colors duration-300">
                <div className="p-6">
                    <div className="flex items-center justify-between mb-8">
                        <h2 className="text-xl font-bold text-gray-900 dark:text-white">Farmer Portal</h2>
                        <button
                            onClick={toggleDarkMode}
                            className="p-2 rounded-lg bg-gray-100 dark:bg-gray-700 hover:bg-gray-200 dark:hover:bg-gray-600 transition-colors"
                        >
                            {darkMode ? <Sun size={20} className="text-yellow-500" /> : <Moon size={20} className="text-gray-600" />}
                        </button>
                    </div>

                    <nav className="space-y-2">
                        <button 
                            className={`w-full flex items-center px-4 py-3 rounded-lg text-left transition-colors ${
                                activeTab === 'requests' 
                                    ? 'bg-blue-50 dark:bg-blue-900 text-blue-700 dark:text-blue-300 border border-blue-200 dark:border-blue-700' 
                                    : 'text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700'
                            }`}
                            onClick={() => setActiveTab('requests')}
                        >
                            <Package size={20} className="mr-3" />
                            My Requests
                        </button>

                        <button 
                            className={`w-full flex items-center px-4 py-3 rounded-lg text-left transition-colors ${
                                activeTab === 'deliveries' 
                                    ? 'bg-blue-50 dark:bg-blue-900 text-blue-700 dark:text-blue-300 border border-blue-200 dark:border-blue-700' 
                                    : 'text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700'
                            }`}
                            onClick={() => setActiveTab('deliveries')}
                        >
                            <Truck size={20} className="mr-3" />
                            My Deliveries
                        </button>

                        <button 
                            className={`w-full flex items-center px-4 py-3 rounded-lg text-left transition-colors ${
                                activeTab === 'products' 
                                    ? 'bg-blue-50 dark:bg-blue-900 text-blue-700 dark:text-blue-300 border border-blue-200 dark:border-blue-700' 
                                    : 'text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700'
                            }`}
                            onClick={() => setActiveTab('products')}
                        >
                            <ShoppingCart size={20} className="mr-3" />
                            Available Products
                        </button>
                    </nav>

                    <div className="mt-auto pt-8">
                        <button
                            onClick={handleLogout}
                            className="w-full flex items-center px-4 py-3 text-red-600 dark:text-red-400 hover:bg-red-50 dark:hover:bg-red-900/50 rounded-lg transition-colors"
                        >
                            <LogOut size={20} className="mr-3" />
                            Logout
                        </button>
                    </div>
                </div>
            </div>

            {/* Main Content */}
            <div className="ml-64 min-h-screen">
                <header className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700 px-6 py-4 transition-colors duration-300">
                    <div className="flex justify-between items-center">
                        <div>
                            <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
                                Welcome, {user?.fullName}
                            </h1>
                            <p className="text-gray-600 dark:text-gray-400">Farmer Dashboard</p>
                        </div>
                    </div>
                </header>

                <main className="p-6">
                    {/* Requests Tab */}
                    {activeTab === 'requests' && (
                        <div className="space-y-6">
                            <div className="flex justify-between items-center">
                                <div>
                                    <h2 className="text-2xl font-bold text-gray-900 dark:text-white">My Requests</h2>
                                    <p className="text-gray-600 dark:text-gray-400">Manage your product requests</p>
                                </div>
                                <button 
                                    onClick={() => setShowRequestModal(true)}
                                    className="flex items-center px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors"
                                >
                                    <Plus size={20} className="mr-2" />
                                    New Request
                                </button>
                            </div>

                            <div className="grid gap-4">
                                {requests.map((request) => (
                                    <div key={request.id} className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6 transition-colors duration-300">
                                        <div className="flex justify-between items-start">
                                            <div className="flex-1">
                                                <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                                                    {request.product_name}
                                                </h3>
                                                <div className="grid grid-cols-2 gap-4 text-sm text-gray-600 dark:text-gray-400">
                                                    <p><span className="font-medium">Category:</span> {request.category}</p>
                                                    <p><span className="font-medium">Price:</span> ${request.price}</p>
                                                    <p><span className="font-medium">Quantity:</span> {request.quantity}</p>
                                                    <p><span className="font-medium">Date:</span> {new Date(request.created_at).toLocaleDateString()}</p>
                                                </div>
                                                {request.note && (
                                                    <p className="mt-2 text-sm text-gray-600 dark:text-gray-400">
                                                        <span className="font-medium">Note:</span> {request.note}
                                                    </p>
                                                )}
                                                <div className="mt-3 flex items-center space-x-3">
                                                    {getStatusBadge(request.status)}
                                                    {request.handled_by_admin && (
                                                        <span className="text-sm text-gray-600 dark:text-gray-400">
                                                            Handled by: {request.handled_by_admin}
                                                        </span>
                                                    )}
                                                </div>
                                            </div>

                                            <div className="flex flex-col space-y-2 ml-4">
                                                <button 
                                                    onClick={() => fetchRequestStatusDetail(request.id)}
                                                    className="flex items-center px-3 py-1.5 text-blue-600 dark:text-blue-400 hover:bg-blue-50 dark:hover:bg-blue-900/50 rounded-lg transition-colors text-sm"
                                                >
                                                    <Eye size={16} className="mr-1" />
                                                    View Details
                                                </button>

                                                {canConfirmDelivery(request) && (
                                                    <button 
                                                        onClick={() => handleConfirmDelivery(request)}
                                                        className="flex items-center px-3 py-1.5 text-green-600 dark:text-green-400 hover:bg-green-50 dark:hover:bg-green-900/50 rounded-lg transition-colors text-sm"
                                                    >
                                                        <Check size={16} className="mr-1" />
                                                        Confirm Delivery
                                                    </button>
                                                )}

                                                {canEditOrDelete(request) && (
                                                    <>
                                                        <button 
                                                            onClick={() => handleEditRequest(request)}
                                                            className="flex items-center px-3 py-1.5 text-amber-600 dark:text-amber-400 hover:bg-amber-50 dark:hover:bg-amber-900/50 rounded-lg transition-colors text-sm"
                                                        >
                                                            <Edit size={16} className="mr-1" />
                                                            Edit
                                                        </button>
                                                        <button 
                                                            onClick={() => handleDeleteRequest(request.id)}
                                                            className="flex items-center px-3 py-1.5 text-red-600 dark:text-red-400 hover:bg-red-50 dark:hover:bg-red-900/50 rounded-lg transition-colors text-sm"
                                                        >
                                                            <Trash2 size={16} className="mr-1" />
                                                            Delete
                                                        </button>
                                                    </>
                                                )}
                                            </div>
                                        </div>
                                    </div>
                                ))}

                                {requests.length === 0 && (
                                    <div className="text-center py-12 bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700">
                                        <Package size={48} className="mx-auto text-gray-400 mb-4" />
                                        <p className="text-gray-600 dark:text-gray-400">No requests found. Create your first request!</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    )}

                    {/* Deliveries Tab */}
                    {activeTab === 'deliveries' && (
                        <div className="space-y-6">
                            <div>
                                <h2 className="text-2xl font-bold text-gray-900 dark:text-white">My Deliveries</h2>
                                <p className="text-gray-600 dark:text-gray-400">Track your confirmed deliveries</p>
                            </div>

                            <div className="grid gap-4">
                                {deliveries.map((delivery) => (
                                    <div key={delivery.id} className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6 transition-colors duration-300">
                                        <div className="flex justify-between items-start">
                                            <div className="flex-1">
                                                <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                                                    {delivery.product_name}
                                                </h3>
                                                <div className="grid grid-cols-2 gap-4 text-sm text-gray-600 dark:text-gray-400">
                                                    <p><span className="font-medium">Category:</span> {delivery.category}</p>
                                                    <p><span className="font-medium">Price:</span> ${delivery.price}</p>
                                                    <p><span className="font-medium">Quantity:</span> {delivery.quantity}</p>
                                                    <p><span className="font-medium">Delivered:</span> {new Date(delivery.delivered_at).toLocaleDateString()}</p>
                                                </div>
                                                
                                                <div className="mt-3 p-3 bg-green-50 dark:bg-green-900/20 rounded-lg border border-green-200 dark:border-green-800">
                                                    <p className="text-sm font-medium text-green-800 dark:text-green-400 mb-1">
                                                        Accepted by {delivery.accepted_role} Level
                                                    </p>
                                                    {delivery.accepted_admin_name && (
                                                        <p className="text-sm text-green-700 dark:text-green-300">
                                                            Admin: {delivery.accepted_admin_name}
                                                        </p>
                                                    )}
                                                    {delivery.confirmation_date && (
                                                        <p className="text-sm text-green-700 dark:text-green-300">
                                                            Confirmed: {new Date(delivery.confirmation_date).toLocaleString()}
                                                        </p>
                                                    )}
                                                    {delivery.confirmation_note && (
                                                        <p className="text-sm text-green-700 dark:text-green-300 mt-2">
                                                            Note: {delivery.confirmation_note}
                                                        </p>
                                                    )}
                                                </div>
                                            </div>

                                            <div className="ml-4">
                                                <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-green-100 text-green-800 border border-green-300">
                                                    ✅ Confirmed
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                ))}

                                {deliveries.length === 0 && (
                                    <div className="text-center py-12 bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700">
                                        <Truck size={48} className="mx-auto text-gray-400 mb-4" />
                                        <p className="text-gray-600 dark:text-gray-400">No confirmed deliveries yet.</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    )}

                    {/* Products Tab */}
                    {activeTab === 'products' && (
                        <div className="space-y-6">
                            <div>
                                <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Available Products</h2>
                                <p className="text-gray-600 dark:text-gray-400">Products available across the country</p>
                            </div>

                            <div className="grid gap-4">
                                {products.map((product) => (
                                    <div key={product.id} className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6 transition-colors duration-300">
                                        <div className="flex justify-between items-start">
                                            <div className="flex-1">
                                                <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                                                    {product.name}
                                                </h3>
                                                <p className="text-gray-600 dark:text-gray-400 mb-4">{product.description}</p>
                                                <div className="grid grid-cols-2 gap-4 text-sm text-gray-600 dark:text-gray-400">
                                                    <p><span className="font-medium">Category:</span> {product.category}</p>
                                                    <p><span className="font-medium">Sub-category:</span> {product.sub_category || 'N/A'}</p>
                                                    <p><span className="font-medium">Amount:</span> {product.amount} {product.unit || 'units'}</p>
                                                    <p><span className="font-medium">Price:</span> ${product.price}</p>
                                                    {product.manufacturer && (
                                                        <p><span className="font-medium">Manufacturer:</span> {product.manufacturer}</p>
                                                    )}
                                                    <p><span className="font-medium">Added:</span> {new Date(product.created_at).toLocaleDateString()}</p>
                                                </div>
                                                {product.expiry_date && (
                                                    <p className="mt-2 text-sm text-amber-600 dark:text-amber-400">
                                                        <span className="font-medium">Expires:</span> {new Date(product.expiry_date).toLocaleDateString()}
                                                    </p>
                                                )}
                                            </div>

                                            <div className="ml-4">
                                                <button 
                                                    onClick={() => {
                                                        setRequestForm({
                                                            product_id: product.id.toString(),
                                                            quantity: '',
                                                            note: ''
                                                        });
                                                        setEditingRequest(null);
                                                        setShowRequestModal(true);
                                                    }}
                                                    className="flex items-center px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors"
                                                >
                                                    <Package size={16} className="mr-2" />
                                                    Request Product
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                ))}

                                {products.length === 0 && (
                                    <div className="text-center py-12 bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700">
                                        <ShoppingCart size={48} className="mx-auto text-gray-400 mb-4" />
                                        <p className="text-gray-600 dark:text-gray-400">No products available at the moment.</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    )}
                </main>
            </div>

            {/* Request Modal */}
            {showRequestModal && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
                    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-xl max-w-md w-full max-h-[80vh] overflow-y-auto">
                        <div className="p-6">
                            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">
                                {editingRequest ? 'Edit Request' : 'Create New Request'}
                            </h3>
                            <form onSubmit={handleRequestSubmit} className="space-y-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                                        Product:
                                    </label>
                                    <select 
                                        value={requestForm.product_id}
                                        onChange={(e) => setRequestForm({...requestForm, product_id: e.target.value})}
                                        disabled={editingRequest}
                                        className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                        required
                                    >
                                        <option value="">Select a product</option>
                                        {products.map((product) => (
                                            <option key={product.id} value={product.id}>
                                                {product.name} - {product.category}
                                            </option>
                                        ))}
                                    </select>
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                                        Quantity:
                                    </label>
                                    <input 
                                        type="number"
                                        min="1"
                                        value={requestForm.quantity}
                                        onChange={(e) => setRequestForm({...requestForm, quantity: e.target.value})}
                                        className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                        required
                                    />
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                                        Note (Optional):
                                    </label>
                                    <textarea 
                                        value={requestForm.note}
                                        onChange={(e) => setRequestForm({...requestForm, note: e.target.value})}
                                        rows="3"
                                        placeholder="Any additional information about your request..."
                                        className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                    />
                                </div>

                                <div className="flex space-x-3 pt-4">
                                    <button 
                                        type="submit"
                                        className="flex-1 px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors"
                                    >
                                        {editingRequest ? 'Update Request' : 'Create Request'}
                                    </button>
                                    <button 
                                        type="button"
                                        onClick={() => {
                                            setShowRequestModal(false);
                                            setEditingRequest(null);
                                            setRequestForm({ product_id: '', quantity: '', note: '' });
                                        }}
                                        className="flex-1 px-4 py-2 bg-gray-300 hover:bg-gray-400 dark:bg-gray-600 dark:hover:bg-gray-500 text-gray-700 dark:text-gray-300 rounded-lg transition-colors"
                                    >
                                        Cancel
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            )}

            {/* Status Detail Modal */}
            {showStatusModal && selectedRequest && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
                    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-xl max-w-4xl w-full max-h-[80vh] overflow-y-auto">
                        <div className="p-6">
                            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-6">Request Status Details</h3>
                            
                            <div className="mb-6 p-4 bg-gray-50 dark:bg-gray-700 rounded-lg">
                                <h4 className="font-semibold text-gray-900 dark:text-white mb-2">
                                    Overall Status: {getStatusBadge(selectedRequest.status)}
                                </h4>
                                <div className="grid grid-cols-2 gap-4 text-sm text-gray-600 dark:text-gray-400">
                                    <p><span className="font-medium">Product:</span> {selectedRequest.product_name}</p>
                                    <p><span className="font-medium">Quantity:</span> {selectedRequest.quantity}</p>
                                </div>
                                {selectedRequest.note && (
                                    <p className="mt-2 text-sm text-gray-600 dark:text-gray-400">
                                        <span className="font-medium">Note:</span> {selectedRequest.note}
                                    </p>
                                )}
                            </div>

                            <div className="space-y-4">
                                <h4 className="font-semibold text-gray-900 dark:text-white">Approval Levels:</h4>
                                <div className="grid gap-4">
                                    {getLevelStatus('kebele', selectedRequest)}
                                    {getLevelStatus('woreda', selectedRequest)}
                                    {getLevelStatus('zone', selectedRequest)}
                                    {getLevelStatus('region', selectedRequest)}
                                    {getLevelStatus('federal', selectedRequest)}
                                </div>
                            </div>

                            <div className="flex justify-end pt-6">
                                <button 
                                    onClick={() => {
                                        setShowStatusModal(false);
                                        setSelectedRequest(null);
                                    }}
                                    className="px-4 py-2 bg-gray-300 hover:bg-gray-400 dark:bg-gray-600 dark:hover:bg-gray-500 text-gray-700 dark:text-gray-300 rounded-lg transition-colors"
                                >
                                    Close
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Delivery Confirmation Modal */}
            {showDeliveryModal && confirmingDelivery && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
                    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-xl max-w-md w-full">
                        <div className="p-6">
                            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">Confirm Delivery</h3>
                            
                            <div className="mb-4 p-4 bg-green-50 dark:bg-green-900/20 rounded-lg border border-green-200 dark:border-green-800">
                                <p className="font-medium text-green-800 dark:text-green-400">
                                    Product: {confirmingDelivery.product_name}
                                </p>
                                <p className="text-sm text-green-700 dark:text-green-300">
                                    Quantity: {confirmingDelivery.quantity}
                                </p>
                            </div>

                            <form onSubmit={submitDeliveryConfirmation} className="space-y-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                                        Confirmation Note (Optional):
                                    </label>
                                    <textarea 
                                        value={deliveryNote}
                                        onChange={(e) => setDeliveryNote(e.target.value)}
                                        rows="3"
                                        placeholder="Any notes about the delivery..."
                                        className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                    />
                                </div>

                                <div className="flex space-x-3">
                                    <button 
                                        type="submit"
                                        className="flex-1 flex items-center justify-center px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg transition-colors"
                                    >
                                        <Check size={16} className="mr-2" />
                                        Confirm Delivery
                                    </button>
                                    <button 
                                        type="button"
                                        onClick={() => {
                                            setShowDeliveryModal(false);
                                            setConfirmingDelivery(null);
                                            setDeliveryNote('');
                                        }}
                                        className="flex-1 px-4 py-2 bg-gray-300 hover:bg-gray-400 dark:bg-gray-600 dark:hover:bg-gray-500 text-gray-700 dark:text-gray-300 rounded-lg transition-colors"
                                    >
                                        Cancel
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default FarmerDashboard;
