import React from 'react';

function RequestsPage({ requests, currentUser, onApprove }) {
    return ( <
        div >
        <
        h2 > Requests < /h2> <
        ul > {
            requests.map(request => ( <
                li key = { request.id } > { request.productName }â€” { request.status } {
                    currentUser && currentUser.role !== 'Farmer' && ( <
                        >
                        <
                        button onClick = {
                            () => onApprove(request.id, 'approved') } > Approve < /button> <
                        button onClick = {
                            () => onApprove(request.id, 'rejected') } > Reject < /button> <
                        />
                    )
                } <
                /li>
            ))
        } <
        /ul> <
        /div>
    );
}

export default RequestsPage;
