import React, { useState, useEffect } from 'react';
import './RegionDashboard.css';

const RegionDashboard = () => {
    const [user, setUser] = useState(null);
    const [activeTab, setActiveTab] = useState('overview');
    const [admins, setAdmins] = useState([]);
    const [products, setProducts] = useState([]);
    const [requests, setRequests] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showAdminModal, setShowAdminModal] = useState(false);

    useEffect(() => {
        // Fetch user data on component mount
        fetchUserData();
        fetchAdmins();
        fetchProducts();
        fetchRequests();
    }, []);

    const fetchUserData = async() => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('/api/admin/me', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            const userData = await response.json();
            setUser(userData);
        } catch (error) {
            console.error('Error fetching user data:', error);
        }
    };

    const fetchAdmins = async() => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('/api/admin/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            const adminsData = await response.json();
            setAdmins(adminsData);
        } catch (error) {
            console.error('Error fetching admins:', error);
        }
    };

    const fetchProducts = async() => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('/api/admin/products', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            const productsData = await response.json();
            setProducts(productsData);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const fetchRequests = async() => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('/api/admin/requests', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            const requestsData = await response.json();
            setRequests(requestsData);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching requests:', error);
            setLoading(false);
        }
    };

    const handleCreateAdmin = async(adminData) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('/api/admin/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(adminData)
            });

            if (response.ok) {
                const newAdmin = await response.json();
                setAdmins([...admins, newAdmin]);
                return { success: true, message: 'Admin created successfully' };
            } else {
                const error = await response.json();
                return { success: false, message: error.message };
            }
        } catch (error) {
            return { success: false, message: 'Error creating admin' };
        }
    };

    const handleUpdateRequestStatus = async(requestId, status) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`/api/admin/requests/${requestId}/status`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ status })
            });

            if (response.ok) {
                const updatedRequest = await response.json();
                setRequests(requests.map(req =>
                    req.id === requestId ? updatedRequest : req
                ));
                return { success: true, message: 'Request status updated' };
            } else {
                const error = await response.json();
                return { success: false, message: error.message };
            }
        } catch (error) {
            return { success: false, message: 'Error updating request status' };
        }
    };

    if (loading) {
        return <div className = "loading" > Loading dashboard... < /div>;
    }

    return ( <
        div className = "region-dashboard" >
        <
        header className = "dashboard-header" >
        <
        h1 > Regional Administration Dashboard < /h1> <
        div className = "user-info" >
        <
        span > Welcome, { user ? user.fullName : 'Guest' } < /span> <
        span className = "user-role" > { user ? user.role : 'Unknown' } - { user ? user.region_name : 'Unknown Region' } <
        /span> <
        /div> <
        /header>

        <
        nav className = "dashboard-nav" >
        <
        button className = { activeTab === 'overview' ? 'active' : '' }
        onClick = {
            () => setActiveTab('overview') } >
        <
        i className = "fas fa-chart-bar" > < /i> Overview <
        /button> <
        button className = { activeTab === 'admins' ? 'active' : '' }
        onClick = {
            () => setActiveTab('admins') } >
        <
        i className = "fas fa-users" > < /i> Admin Management <
        /button> <
        button className = { activeTab === 'products' ? 'active' : '' }
        onClick = {
            () => setActiveTab('products') } >
        <
        i className = "fas fa-box" > < /i> Product Management <
        /button> <
        button className = { activeTab === 'requests' ? 'active' : '' }
        onClick = {
            () => setActiveTab('requests') } >
        <
        i className = "fas fa-clipboard-list" > < /i> Requests <
        /button> <
        /nav>

        <
        main className = "dashboard-content" > {
            activeTab === 'overview' && ( <
                div className = "overview-tab" >
                <
                div className = "stats-grid" >
                <
                div className = "stat-card" >
                <
                h3 > Zone Admins < /h3> <
                p className = "stat-number" > { admins.filter(a => a.role === 'Zone').length } < /p> <
                /div> <
                div className = "stat-card" >
                <
                h3 > Woreda Admins < /h3> <
                p className = "stat-number" > { admins.filter(a => a.role === 'Woreda').length } < /p> <
                /div> <
                div className = "stat-card" >
                <
                h3 > Products < /h3> <
                p className = "stat-number" > { products.length } < /p> <
                /div> <
                div className = "stat-card" >
                <
                h3 > Pending Requests < /h3> <
                p className = "stat-number" > { requests.filter(r => r.status === 'pending').length } < /p> <
                /div> <
                /div>

                <
                div className = "recent-activity" >
                <
                h2 > Recent Activity < /h2> <
                div className = "activity-list" > {
                    requests.slice(0, 5).map(request => ( <
                        div key = { request.id }
                        className = "activity-item" >
                        <
                        div className = "activity-content" >
                        <
                        p > < strong > { request.farmer_name } < /strong> requested <strong>{request.quantity}</strong > of { request.product_name } < /p> <
                        span className = "activity-time" > { new Date(request.created_at).toLocaleDateString() } < /span> <
                        /div> <
                        span className = { `status-badge status-${request.status}` } > { request.status } <
                        /span> <
                        /div>
                    ))
                } <
                /div> <
                /div> <
                /div>
            )
        }

        {
            activeTab === 'admins' && ( <
                div className = "admins-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Admin Management < /h2> <
                button className = "btn-primary"
                onClick = {
                    () => setShowAdminModal(true) } >
                <
                i className = "fas fa-plus" > < /i> Create Zone Admin <
                /button> <
                /div>

                <
                div className = "table-container" >
                <
                table className = "data-table" >
                <
                thead >
                <
                tr >
                <
                th > Name < /th> <
                th > Phone < /th> <
                th > Role < /th> <
                th > Zone < /th> <
                th > Woreda < /th> <
                th > Kebele < /th> <
                th > Actions < /th> <
                /tr> <
                /thead> <
                tbody > {
                    admins.map(admin => ( <
                        tr key = { admin.id } >
                        <
                        td > { admin.full_name } < /td> <
                        td > { admin.phone_number } < /td> <
                        td > < span className = "role-badge" > { admin.role } < /span></td >
                        <
                        td > { admin.zone_name || '-' } < /td> <
                        td > { admin.woreda_name || '-' } < /td> <
                        td > { admin.kebele_name || '-' } < /td> <
                        td >
                        <
                        button className = "btn-icon"
                        title = "Edit" >
                        <
                        i className = "fas fa-edit" > < /i> <
                        /button> <
                        button className = "btn-icon btn-danger"
                        title = "Delete" >
                        <
                        i className = "fas fa-trash" > < /i> <
                        /button> <
                        /td> <
                        /tr>
                    ))
                } <
                /tbody> <
                /table> <
                /div> <
                /div>
            )
        }

        {
            activeTab === 'products' && ( <
                div className = "products-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Product Management < /h2> <
                button className = "btn-primary" >
                <
                i className = "fas fa-plus" > < /i> Add Product <
                /button> <
                /div>

                <
                div className = "table-container" >
                <
                table className = "data-table" >
                <
                thead >
                <
                tr >
                <
                th > Name < /th> <
                th > Description < /th> <
                th > Category < /th> <
                th > Created Date < /th> <
                th > Actions < /th> <
                /tr> <
                /thead> <
                tbody > {
                    products.map(product => ( <
                        tr key = { product.id } >
                        <
                        td > { product.name } < /td> <
                        td > { product.description } < /td> <
                        td > { product.category } < /td> <
                        td > { new Date(product.created_at).toLocaleDateString() } < /td> <
                        td >
                        <
                        button className = "btn-icon"
                        title = "Edit" >
                        <
                        i className = "fas fa-edit" > < /i> <
                        /button> <
                        button className = "btn-icon btn-danger"
                        title = "Delete" >
                        <
                        i className = "fas fa-trash" > < /i> <
                        /button> <
                        /td> <
                        /tr>
                    ))
                } <
                /tbody> <
                /table> <
                /div> <
                /div>
            )
        }

        {
            activeTab === 'requests' && ( <
                div className = "requests-tab" >
                <
                h2 > Farmer Requests < /h2>

                <
                div className = "filters" >
                <
                div className = "filter-group" >
                <
                label > Status: < /label> <
                select >
                <
                option value = "all" > All < /option> <
                option value = "pending" > Pending < /option> <
                option value = "accepted" > Accepted < /option> <
                option value = "rejected" > Rejected < /option> <
                /select> <
                /div> <
                /div>

                <
                div className = "table-container" >
                <
                table className = "data-table" >
                <
                thead >
                <
                tr >
                <
                th > Farmer Name < /th> <
                th > Product < /th> <
                th > Quantity < /th> <
                th > Zone < /th> <
                th > Woreda < /th> <
                th > Date < /th> <
                th > Status < /th> <
                th > Actions < /th> <
                /tr> <
                /thead> <
                tbody > {
                    requests.map(request => ( <
                        tr key = { request.id } >
                        <
                        td > { request.farmer_name } < /td> <
                        td > { request.product_name } < /td> <
                        td > { request.quantity } < /td> <
                        td > { request.zone_name || '-' } < /td> <
                        td > { request.woreda_name || '-' } < /td> <
                        td > { new Date(request.created_at).toLocaleDateString() } < /td> <
                        td >
                        <
                        span className = { `status-badge status-${request.status}` } > { request.status } <
                        /span> <
                        /td> <
                        td > {
                            request.status === 'pending' && ( <
                                >
                                <
                                button className = "btn-icon btn-success"
                                onClick = {
                                    () => handleUpdateRequestStatus(request.id, 'accepted') }
                                title = "Accept" >
                                <
                                i className = "fas fa-check" > < /i> <
                                /button> <
                                button className = "btn-icon btn-danger"
                                onClick = {
                                    () => handleUpdateRequestStatus(request.id, 'rejected') }
                                title = "Reject" >
                                <
                                i className = "fas fa-times" > < /i> <
                                /button> <
                                />
                            )
                        } <
                        /td> <
                        /tr>
                    ))
                } <
                /tbody> <
                /table> <
                /div> <
                /div>
            )
        } <
        /main> <
        /div>
    );
};

export default RegionDashboard;
