import React, { useState } from 'react';

// Import dashboard components
import FederalDashboard from './dashboards/FederalDashboard';
import RegionDashboard from './dashboards/RegionDashboard';
import ZoneDashboard from './dashboards/ZoneDashboard';
import WoredaDashboard from './dashboards/WoredaDashboard';
import KebeleDashboard from './dashboards/KebeleDashboard';
import FarmerDashboard from './dashboards/FarmerDashboard';

function App() {
    const [phone, setPhone] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [currentUser, setCurrentUser] = useState(null); // stores user info after login

    const handleLogin = async(e) => {
        e.preventDefault();
        try {
            const res = await api.post('/login', { phone, password });
            setCurrentUser(res.data); // save user info including role
            setMessage('');
        } catch (err) {
            console.log(err);
            setMessage('Login failed: ' + (err.response ? err.response.data.message : 'Network Error'));
        }
    };

    // Render dashboard based on role
    const renderDashboard = () => {
        if (!currentUser) return null;

        switch (currentUser.role) {
            case 'Federal':
                return <FederalDashboard currentUser = { currentUser }
                />;
            case 'Region':
                return <RegionDashboard currentUser = { currentUser }
                />;
            case 'Zone':
                return <ZoneDashboard currentUser = { currentUser }
                />;
            case 'Woreda':
                return <WoredaDashboard currentUser = { currentUser }
                />;
            case 'Kebele':
                return <KebeleDashboard currentUser = { currentUser }
                />;
            case 'Farmer':
                return <FarmerDashboard currentUser = { currentUser }
                />;
            default:
                return <div className = "text-red-500" > Unknown role < /div>;
        }
    };

    if (currentUser) {
        return renderDashboard();
    }

    return ( <
        div className = "min-h-screen flex items-center justify-center bg-gray-100" >
        <
        form onSubmit = { handleLogin }
        className = "bg-white p-8 rounded shadow-md w-80" >
        <
        h1 className = "text-2xl mb-4 font-bold text-center" > Login < /h1> {
        message && < div className = "text-red-500 mb-2" > { message } < /div>}

        <
        label className = "block mb-2" > Phone Number < /label> <
        input type = "text"
        value = { phone }
        onChange = {
            (e) => setPhone(e.target.value)
        }
        className = "border p-2 mb-4 w-full rounded"
        placeholder = "Enter phone number"
        required /
        >

        <
        label className = "block mb-2" > Password < /label> <
        input type = "password"
        value = { password }
        onChange = {
            (e) => setPassword(e.target.value)
        }
        className = "border p-2 mb-4 w-full rounded"
        placeholder = "Enter password"
        required /
        >

        <
        button type = "submit"
        className = "bg-blue-500 text-white p-2 rounded w-full hover:bg-blue-600" >
        Login <
        /button> < /
        form > <
        /div>
    );
}

export default App;
