import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const RegionDashboard = () => {
    const [activeTab, setActiveTab] = useState('overview');
    const [user, setUser] = useState(null);
    const [darkMode, setDarkMode] = useState(false);
    const navigate = useNavigate();

    // Product Management State
    const [products, setProducts] = useState([]);
    const [myProducts, setMyProducts] = useState([]);
    const [otherProducts, setOtherProducts] = useState([]);
    const [productFilter, setProductFilter] = useState('all');
    const [showProductModal, setShowProductModal] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        amount: '',
        description: '',
        price: '',
        subCategory: '',
        unit: '',
        manufacturer: '',
        expiryDate: ''
    });

    // Request Management State
    const [requests, setRequests] = useState([]);
    const [filteredRequests, setFilteredRequests] = useState([]);
    const [selectedRequests, setSelectedRequests] = useState([]);
    const [showRequestModal, setShowRequestModal] = useState(false);
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [decisionReason, setDecisionReason] = useState('');
    const [bulkAction, setBulkAction] = useState('');
    const [statusFilter, setStatusFilter] = useState('all');

    // Zone Admin Management State
    const [zoneAdmins, setZoneAdmins] = useState([]);
    const [showZoneModal, setShowZoneModal] = useState(false);
    const [editingZone, setEditingZone] = useState(null);
    const [zoneForm, setZoneForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        zoneName: ''
    });

    // Stats State
    const [stats, setStats] = useState({
        totalRequests: 0,
        pendingRequests: 0,
        approvedRequests: 0,
        rejectedRequests: 0,
        acceptedRequests: 0,
        totalZoneAdmins: 0,
        totalProducts: 0
    });

    // Product categories with subcategories
    const productCategories = {
        'Chemicals': {
            subcategories: ['Fertilizers', 'Pesticides', 'Herbicides', 'Fungicides', 'Insecticides', 'Growth Regulators', 'Soil Conditioners', 'Plant Nutrients', 'Micronutrients'],
            units: ['kg', 'liters', 'bottles', 'sachets', 'grams', 'ml', 'gallons', 'tons', 'bags', 'boxes']
        },
        'Machinery': {
            subcategories: ['Tractors', 'Plows', 'Harvesters', 'Seeders', 'Cultivators', 'Irrigation Equipment', 'Threshers', 'Tillers', 'Mowers', 'Sprayers', 'Pumps'],
            units: ['units', 'pieces', 'sets']
        },
        'Seeds': {
            subcategories: ['Cereal Seeds', 'Vegetable Seeds', 'Fruit Seeds', 'Legume Seeds', 'Cash Crop Seeds', 'Flower Seeds', 'Grass Seeds', 'Tree Seeds', 'Hybrid Seeds'],
            units: ['kg', 'packets', 'bags', 'grams', 'tons', 'quintal', 'pounds']
        },
        'Tools': {
            subcategories: ['Hand Tools', 'Cutting Tools', 'Measuring Tools', 'Safety Equipment', 'Storage Equipment', 'Digging Tools', 'Pruning Tools', 'Watering Tools'],
            units: ['pieces', 'sets', 'units', 'pairs', 'dozens']
        },
        'Livestock': {
            subcategories: ['Feed', 'Vaccines', 'Supplements', 'Equipment', 'Medicine', 'Vitamins', 'Antibiotics', 'Dewormers', 'Breeding Supplies'],
            units: ['kg', 'liters', 'bottles', 'doses', 'bags', 'tons', 'ml', 'tablets', 'injections']
        },
        'Organic': {
            subcategories: ['Organic Fertilizers', 'Bio-pesticides', 'Compost', 'Organic Seeds', 'Bio-fungicides', 'Natural Repellents', 'Organic Soil Amendments'],
            units: ['kg', 'liters', 'bags', 'tons', 'cubic meters', 'sacks']
        },
        'Irrigation': {
            subcategories: ['Drip Systems', 'Sprinkler Systems', 'Pipes', 'Fittings', 'Controllers', 'Valves', 'Filters', 'Emitters'],
            units: ['meters', 'pieces', 'sets', 'units', 'rolls', 'feet']
        },
        'Storage': {
            subcategories: ['Silos', 'Warehouses', 'Containers', 'Bags', 'Bins', 'Preservation Materials', 'Drying Equipment'],
            units: ['units', 'pieces', 'cubic meters', 'tons capacity', 'bags']
        }
    };

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Region' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
    }, [navigate]);

    useEffect(() => {
        if (user) {
            fetchDashboardData();
        }
    }, [user]);

    useEffect(() => {
        if (requests.length > 0) {
            filterRequestsByStatus();
        }
    }, [statusFilter, requests]);

    useEffect(() => {
        // Apply dark mode to the entire document
        if (darkMode) {
            document.documentElement.classList.add('dark');
        } else {
            document.documentElement.classList.remove('dark');
        }
    }, [darkMode]);

    const filterRequestsByStatus = () => {
        if (statusFilter === 'all') {
            setFilteredRequests(requests);
            return;
        }

        let filtered = [];
        switch (statusFilter) {
            case 'pending':
                // Show requests that are pending at region level and have been approved by lower levels
                filtered = requests.filter(req => 
                    (!req.region_status || req.region_status === 'Pending') && 
                    (req.kebele_status === 'Approved' || req.woreda_status === 'Approved' || req.zone_status === 'Approved')
                );
                break;
            case 'approved':
                filtered = requests.filter(req => req.region_status === 'Approved');
                break;
            case 'accepted':
                filtered = requests.filter(req => req.region_status === 'Accepted');
                break;
            case 'rejected':
                filtered = requests.filter(req => req.region_status === 'Rejected');
                break;
            default:
                filtered = requests;
        }
        setFilteredRequests(filtered);
    };

    const fetchDashboardData = async () => {
        try {
            const token = localStorage.getItem('token');

            // Fetch zone admins in scope
            const zoneAdminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (zoneAdminsResponse.ok) {
                const data = await zoneAdminsResponse.json();
                const zoneAdminsData = data.filter(item => item.type === 'zone' && item.region_name === user.region_name);
                setZoneAdmins(zoneAdminsData);
                setStats(prev => ({ ...prev, totalZoneAdmins: zoneAdminsData.length }));
                console.log('Zone admins data:', zoneAdminsData);
            } else {
                console.error('Failed to fetch zone admins:', await zoneAdminsResponse.text());
            }

            // Fetch all products
            const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
                setStats(prev => ({ ...prev, totalProducts: productsData.length }));
                console.log('Products data:', productsData);
            } else {
                console.error('Failed to fetch products:', await productsResponse.text());
            }

            // Fetch my products
            const myProductsResponse = await fetch('http://localhost:5000/api/admins/myproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (myProductsResponse.ok) {
                const myProductsData = await myProductsResponse.json();
                setMyProducts(myProductsData);
                console.log('My products data:', myProductsData);
            } else {
                console.error('Failed to fetch my products:', await myProductsResponse.text());
            }

            // Fetch other admins' products
            const otherProductsResponse = await fetch('http://localhost:5000/api/admins/otherproducts', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (otherProductsResponse.ok) {
                const otherProductsData = await otherProductsResponse.json();
                setOtherProducts(otherProductsData);
                console.log('Other products data:', otherProductsData);
            } else {
                console.error('Failed to fetch other products:', await otherProductsResponse.text());
            }

            // Fetch requests with detailed status
            const requestsResponse = await fetch('http://localhost:5000/api/admins/requests/status', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                console.log('All requests data:', requestsData);

                // Filter requests to only show those from the same region
                const regionRequests = requestsData.filter(req =>
                    req.region_name === user.region_name
                );
                console.log('User region_name:', user.region_name);
                console.log('User data:', user);
                setRequests(regionRequests);
                setFilteredRequests(regionRequests);
                console.log('Region requests:', regionRequests);

                // Calculate stats from requests data
                const totalRequests = regionRequests.length;
                const pendingRequests = regionRequests.filter(req => req.region_status === 'Pending' || !req.region_status).length;
                const approvedRequests = regionRequests.filter(req => req.region_status === 'Approved').length;
                const acceptedRequests = regionRequests.filter(req => req.region_status === 'Accepted').length;
                const rejectedRequests = regionRequests.filter(req => req.region_status === 'Rejected').length;

                setStats(prev => ({
                    ...prev,
                    totalRequests,
                    pendingRequests,
                    approvedRequests,
                    acceptedRequests,
                    rejectedRequests
                }));
            } else {
                console.error('Failed to fetch requests:', await requestsResponse.text());
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('userType');
        navigate('/login');
    };

    // Dark Mode Toggle
    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
        if (!darkMode) {
            document.documentElement.classList.add('dark');
        } else {
            document.documentElement.classList.remove('dark');
        }
    };

    // Zone Admin Management Functions
    const handleZoneSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');

            if (zoneForm.password !== zoneForm.confirmPassword) {
                alert('Passwords do not match');
                return;
            }

            const zoneData = {
                full_name: zoneForm.fullName,
                phone_number: zoneForm.phoneNumber,
                password: zoneForm.password,
                zone_name: zoneForm.zoneName,
                region_name: user.region_name,
                type: 'zone'
            };

            const url = editingZone 
                ? `http://localhost:5000/api/admins/admins/${editingZone.id}`
                : 'http://localhost:5000/api/admins/register';

            const method = editingZone ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(zoneData)
            });

            if (response.ok) {
                alert(editingZone ? 'Zone admin updated successfully!' : 'Zone admin created successfully!');
                setShowZoneModal(false);
                setEditingZone(null);
                setZoneForm({
                    fullName: '',
                    phoneNumber: '',
                    password: '',
                    confirmPassword: '',
                    zoneName: ''
                });
                fetchDashboardData();
            } else {
                const errorData = await response.text();
                alert(`Error: ${errorData}`);
            }
        } catch (error) {
            console.error('Error submitting zone admin:', error);
            alert('Error submitting zone admin');
        }
    };

    // Product Management Functions
    const handleProductSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');

            const productData = {
                name: productForm.name,
                category: productForm.category,
                sub_category: productForm.subCategory,
                amount: parseInt(productForm.amount),
                unit: productForm.unit,
                price: parseFloat(productForm.price),
                description: productForm.description,
                manufacturer: productForm.manufacturer,
                expiry_date: productForm.expiryDate
            };

            const url = editingProduct 
                ? `http://localhost:5000/api/admins/products/${editingProduct.id}`
                : 'http://localhost:5000/api/admins/products';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method: method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(productData)
            });

            if (response.ok) {
                alert(`Product ${editingProduct ? 'updated' : 'created'} successfully!`);
                setShowProductModal(false);
                setEditingProduct(null);
                setProductForm({
                    name: '',
                    category: '',
                    amount: '',
                    description: '',
                    price: '',
                    subCategory: '',
                    unit: '',
                    manufacturer: '',
                    expiryDate: ''
                });
                fetchDashboardData();
            } else {
                const errorData = await response.text();
                alert(`Error: ${errorData}`);
            }
        } catch (error) {
            console.error('Error submitting product:', error);
            alert('Error submitting product');
        }
    };

    const handleDeleteProduct = async (productId) => {
        if (!window.confirm('Are you sure you want to delete this product?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');

            const response = await fetch(`http://localhost:5000/api/admins/products/${productId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                alert('Product deleted successfully!');
                fetchDashboardData();
            } else {
                const errorData = await response.text();
                alert(`Error: ${errorData}`);
            }
        } catch (error) {
            console.error('Error deleting product:', error);
            alert('Error deleting product');
        }
    };

    // Helper function to check if region admin can act on request
    const canActOnRequest = (request) => {
        // Region admin can only act on requests that are pending at region level
        // and have been approved by at least one lower level
        return (!request.region_status || request.region_status === 'Pending') && 
               (request.kebele_status === 'Approved' || 
                request.woreda_status === 'Approved' || 
                request.zone_status === 'Approved');
    };

    // Request Management Functions
    const handleRequestAction = async (requestId, action, reason = '') => {
        try {
            const token = localStorage.getItem('token');

            const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    status: action,
                    reason: reason
                })
            });

            if (response.ok) {
                alert(`Request ${action.toLowerCase()} successfully!`);
                fetchDashboardData();
                setShowRequestModal(false);
                setSelectedRequest(null);
                setDecisionReason('');
            } else {
                const errorText = await response.text();
                let errorMessage = errorText;
                try {
                    const errorJson = JSON.parse(errorText);
                    errorMessage = errorJson.message || errorText;
                } catch (e) {
                    // Use text as is
                }
                alert(`Error: ${errorMessage}`);
            }
        } catch (error) {
            console.error('Error updating request:', error);
            alert('Network error. Please try again.');
        }
    };

    const handleBulkAction = async () => {
        if (selectedRequests.length === 0 || !bulkAction) {
            alert('Please select requests and an action');
            return;
        }

        try {
            const token = localStorage.getItem('token');

            for (const requestId of selectedRequests) {
                await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        status: bulkAction,
                        reason: `Bulk ${bulkAction.toLowerCase()}`
                    })
                });
            }

            alert(`Bulk ${bulkAction.toLowerCase()} completed successfully!`);
            setSelectedRequests([]);
            setBulkAction('');
            fetchDashboardData();
        } catch (error) {
            console.error('Error with bulk action:', error);
            alert('Error with bulk action');
        }
    };

    return (
        <div className="flex min-h-screen bg-gray-100 dark:bg-gray-900">
            {/* Sidebar */}
            <div className="w-64 bg-white dark:bg-gray-800 shadow-lg">
                <div className="p-6">
                    <h2 className="text-xl font-bold text-gray-900 dark:text-white">Region Admin</h2>
                    <p className="text-sm text-gray-600 dark:text-gray-400">{user?.name}</p>
                </div>

                <nav className="mt-6">
                    <div className="px-6 space-y-1">
                        <button
                            onClick={() => setActiveTab('overview')}
                            className={`w-full flex items-center px-3 py-2 text-sm font-medium rounded-md ${
                                activeTab === 'overview' 
                                    ? 'bg-blue-100 dark:bg-blue-900 text-blue-700 dark:text-blue-200' 
                                    : 'text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'
                            }`}
                        >
                            <svg className="mr-3 h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                            </svg>
                            Overview
                        </button>

                        <button
                            onClick={() => setActiveTab('requests')}
                            className={`w-full flex items-center px-3 py-2 text-sm font-medium rounded-md ${
                                activeTab === 'requests' 
                                    ? 'bg-blue-100 dark:bg-blue-900 text-blue-700 dark:text-blue-200' 
                                    : 'text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'
                            }`}
                        >
                            <svg className="mr-3 h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                            </svg>
                            Requests
                        </button>

                        <button
                            onClick={() => setActiveTab('products')}
                            className={`w-full flex items-center px-3 py-2 text-sm font-medium rounded-md ${
                                activeTab === 'products' 
                                    ? 'bg-blue-100 dark:bg-blue-900 text-blue-700 dark:text-blue-200' 
                                    : 'text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'
                            }`}
                        >
                            <svg className="mr-3 h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20 7l-8-4-8 4m16 0l-8 4m8-4v10l-8 4m0-10L4 7m8 4v10M4 7v10l8 4" />
                            </svg>
                            Products
                        </button>

                        <button
                            onClick={() => setActiveTab('zones')}
                            className={`w-full flex items-center px-3 py-2 text-sm font-medium rounded-md ${
                                activeTab === 'zones' 
                                    ? 'bg-blue-100 dark:bg-blue-900 text-blue-700 dark:text-blue-200' 
                                    : 'text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'
                            }`}
                        >
                            <svg className="mr-3 h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
                            </svg>
                            Zone Admins
                        </button>
                    </div>

                    <div className="mt-8 px-6">
                        <div className="border-t border-gray-200 dark:border-gray-700 pt-6">
                            <button
                                onClick={toggleDarkMode}
