import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, onAuthStateChanged, signInAnonymously } from 'firebase/auth';
import { getFirestore, doc, setDoc, getDoc, collection, query, where, onSnapshot, serverTimestamp, addDoc, updateDoc, getDocs, deleteDoc } from 'firebase/firestore';
import { useForm } from 'react-hook-form';
import Sidebar from './components/Sidebar'; // Corrected import path with a capital 'S'
import './index.css';

// ----------------------
// Firebase Initialization
// ----------------------
// The following global variables are provided by the Canvas environment.
// For local development, we provide fallback values to avoid compilation errors.
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {
    // You can replace these with your own Firebase project configuration for full functionality
    apiKey: "AIzaSyBh7ErxnKurtNkeK_0PW5XW4joDe_r9Ut4",
    authDomain: "farmers-management-syste-84643.firebaseapp.com",
    projectId: "farmers-management-syste-84643",
    storageBucket: "farmers-management-syste-84643.firebasestorage.app",
    messagingSenderId: "410779424956",
    appId: "1:410779424956:web:b1d26dab35719b1e9a18b9",
    measurementId: "G-RD879ML6B6"
};
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : '';

// Initialize Firebase app and services
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

// ----------------------
// Constants & Mock Data
// ----------------------
const ADMIN_ROLES = {
    FEDERAL: 'Federal',
    REGION: 'Region',
    ZONE: 'Zone',
    WOREDA: 'Woreda',
    KEBELE: 'Kebele',
};

// The user has requested to keep this constant, but it is not used in this implementation.
const ADMIN_ROLES_UNUSED = ['admin'];

const productTypes = [
    { id: 1, name: 'Tractor', department: 'Biological Input Supply', description: 'Used for plowing' },
    { id: 2, name: 'Harvester', department: 'Biological Input Supply', description: 'Used for harvesting' },
    { id: 3, name: 'Fertilizer (DAP)', department: 'Chemical Input Supply', description: 'Di-ammonium phosphate fertilizer' },
    { id: 4, name: 'Pesticide', department: 'Chemical Input Supply', description: 'Pesticide for crop protection' },
];

const mockUsers = [
    { phone: '0912345678', password: 'password123', role: ADMIN_ROLES.FEDERAL, fullName: 'Federal Admin', location: null, uid: 'federal-admin-uid' },
    { phone: '0987654321', password: 'password123', role: ADMIN_ROLES.REGION, fullName: 'Oromia Region Admin', location: 'Oromia', uid: 'oromia-admin-uid' },
    { phone: '0911223344', password: 'password123', role: ADMIN_ROLES.ZONE, fullName: 'Arsi Zone Admin', location: 'Arsi', uid: 'arsi-admin-uid' },
    { phone: '0933445566', password: 'password123', role: ADMIN_ROLES.WOREDA, fullName: 'Tiyo Woreda Admin', location: 'Tiyo', uid: 'tiyo-admin-uid' },
    { phone: '0955667788', password: 'password123', role: ADMIN_ROLES.KEBELE, fullName: 'Wendo Kebele Rep', location: 'Wendo', uid: 'wendo-admin-uid' },
];

// ----------------------
// Main App Component
// ----------------------
export default function App() {
    const [currentUser, setCurrentUser] = useState(null);
    const [authReady, setAuthReady] = useState(false);
    const [loading, setLoading] = useState(true);
    const [requests, setRequests] = useState([]);
    const [locations, setLocations] = useState({ regions: [], zones: [], woredas: [], kebeles: [] });
    const [allUsers, setAllUsers] = useState([]);
    const [currentPage, setCurrentPage] = useState('login');
    const [showModal, setShowModal] = useState(false);
    const [modalContent, setModalContent] = useState('');
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [selectedAdminToEdit, setSelectedAdminToEdit] = useState(null);

    // Initial Firebase setup and auth state listener
    useEffect(() => {
        const setupFirebase = async() => {
            try {
                // Check if the initialAuthToken exists and is not empty
                if (initialAuthToken) {
                    await signInWithCustomToken(auth, initialAuthToken);
                } else {
                    await signInAnonymously(auth);
                }
            } catch (error) {
                console.error("Firebase Auth Error:", error);
                // Fallback to anonymous sign-in if custom token fails
                try {
                    await signInAnonymously(auth);
                } catch (anonError) {
                    console.error("Anonymous sign-in failed:", anonError);
                }
            }
        };
        setupFirebase();

        const unsubscribeAuth = onAuthStateChanged(auth, (user) => {
            if (user) {
                const userRef = doc(db, 'users', user.uid);
                onSnapshot(userRef, (docSnap) => {
                    if (docSnap.exists()) {
                        const userData = { id: docSnap.id, ...docSnap.data() };
                        setCurrentUser(userData);
                    } else {
                        // For mock users, we set a default role based on the UID
                        const mockUser = mockUsers.find(u => u.uid === user.uid);
                        if (mockUser) {
                            setCurrentUser({...mockUser, id: user.uid });
                        } else {
                            // If a new anonymous user, set a default role or handle as needed
                            setCurrentUser({ id: user.uid, role: 'Farmer' });
                        }
                    }
                    setAuthReady(true);
                }, (error) => {
                    console.error("Error fetching user data:", error);
                });
            } else {
                setCurrentUser(null);
                setAuthReady(true);
            }
        });

        return () => unsubscribeAuth();
    }, []);

    // Fetch requests and locations once auth is ready
    useEffect(() => {
        if (!authReady || !currentUser) return;

        const fetchRequests = () => {
            const requestsCollectionRef = collection(db, 'requests');
            let requestsQuery;

            // Apply filters based on admin level
            if (currentUser.role === ADMIN_ROLES.FEDERAL) {
                requestsQuery = query(requestsCollectionRef);
            } else if (currentUser.role === ADMIN_ROLES.REGION) {
                requestsQuery = query(requestsCollectionRef, where('location.region', '==', currentUser.location));
            } else if (currentUser.role === ADMIN_ROLES.ZONE) {
                requestsQuery = query(requestsCollectionRef, where('location.zone', '==', currentUser.location));
            } else if (currentUser.role === ADMIN_ROLES.WOREDA) {
                requestsQuery = query(requestsCollectionRef, where('location.woreda', '==', currentUser.location));
            } else if (currentUser.role === ADMIN_ROLES.KEBELE) {
                requestsQuery = query(requestsCollectionRef, where('location.kebele', '==', currentUser.location));
            } else {
                requestsQuery = query(requestsCollectionRef, where('requesterId', '==', currentUser.id));
            }

            const unsubscribeRequests = onSnapshot(requestsQuery, (snapshot) => {
                const fetchedRequests = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
                // Corrected the sorting logic to avoid the syntax error
                const sortedRequests = fetchedRequests.sort((a, b) => {
                    const timeA = (a.timestamp && a.timestamp.seconds) || 0;
                    const timeB = (b.timestamp && b.timestamp.seconds) || 0;
                    return timeB - timeA;
                });
                setRequests(sortedRequests);
            }, (error) => {
                console.error("Error fetching requests:", error);
            });
            return unsubscribeRequests;
        };

        const fetchLocations = async() => {
            const fetchedRegions = await getDocs(collection(db, 'locations_regions')).then(s => s.docs.map(d => d.data().name));
            const fetchedZones = await getDocs(collection(db, 'locations_zones')).then(s => s.docs.map(d => d.data().name));
            const fetchedWoredas = await getDocs(collection(db, 'locations_woredas')).then(s => s.docs.map(d => d.data().name));
            const fetchedKebeles = await getDocs(collection(db, 'locations_kebeles')).then(s => s.docs.map(d => d.data().name));
            setLocations({
                regions: fetchedRegions,
                zones: fetchedZones,
                woredas: fetchedWoredas,
                kebeles: fetchedKebeles,
            });
        };

        const fetchUsers = async() => {
            const usersCollectionRef = collection(db, 'users');
            let usersQuery;
            if (currentUser.role === ADMIN_ROLES.FEDERAL) {
                usersQuery = query(usersCollectionRef, where('role', '!=', ADMIN_ROLES.FEDERAL));
            } else if (currentUser.role === ADMIN_ROLES.REGION) {
                usersQuery = query(usersCollectionRef, where('role', '==', ADMIN_ROLES.ZONE), where('location', '==', currentUser.location));
            } else if (currentUser.role === ADMIN_ROLES.ZONE) {
                usersQuery = query(usersCollectionRef, where('role', '==', ADMIN_ROLES.WOREDA), where('location', '==', currentUser.location));
            } else if (currentUser.role === ADMIN_ROLES.WOREDA) {
                usersQuery = query(usersCollectionRef, where('role', '==', ADMIN_ROLES.KEBELE), where('location', '==', currentUser.location));
            } else {
                usersQuery = query(usersCollectionRef, where('role', '==', 'Farmer'));
            }

            const unsubscribeUsers = onSnapshot(usersQuery, (snapshot) => {
                const fetchedUsers = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
                setAllUsers(fetchedUsers);
            }, (error) => {
                console.error("Error fetching users:", error);
            });
            return unsubscribeUsers;
        }

        const unsubscribeRequests = fetchRequests();
        fetchLocations();
        const unsubscribeUsers = fetchUsers();
        setLoading(false);

        return () => {
            unsubscribeRequests();
            unsubscribeUsers();
        };
    }, [authReady, currentUser]);


    // Helper functions for UI and state management
    const showMessage = (message) => {
        setModalContent(message);
        setShowModal(true);
    };

    const handleLogout = async() => {
        await auth.signOut();
        setCurrentUser(null);
        setCurrentPage('login');
    };

    const handleLogin = async(phone, password) => {
        const mockUser = mockUsers.find(u => u.phone === phone && u.password === password);
        if (mockUser) {
            // Simulate login by signing in with the user's custom UID
            try {
                const customToken = mockUser.uid; // In a real app, this would be a real custom token
                await signInWithCustomToken(auth, customToken);
                showMessage(`Welcome, ${mockUser.fullName}!`);
                setCurrentPage('dashboard');
            } catch (error) {
                console.error("Login failed:", error);
                showMessage("Login failed. Please check your credentials.");
            }
        } else {
            showMessage("Invalid phone number or password.");
        }
    };


    const handleRequestAction = async(requestId, newStatus, feedback) => {
        if (!currentUser) {
            showMessage("You must be logged in to perform this action.");
            return;
        }

        try {
            const requestRef = doc(db, 'requests', requestId);
            const requestSnapshot = await getDoc(requestRef);
            if (!requestSnapshot.exists()) {
                showMessage("Request not found.");
                return;
            }

            const existingApprovals = requestSnapshot.data().approvals || {};
            const newApprovals = {
                ...existingApprovals,
                [currentUser.role.toLowerCase()]: {
                    status: newStatus,
                    by: currentUser.fullName,
                    uid: currentUser.id,
                    feedback: feedback || null,
                    timestamp: serverTimestamp(),
                }
            };
            await updateDoc(requestRef, {
                status: newStatus, // Update overall status to the latest approval
                approvals: newApprovals
            });
            showMessage(`Request ${requestId} has been ${newStatus}.`);
            setSelectedRequest(null); // Close the form
        } catch (error) {
            console.error("Error updating request:", error);
            showMessage("Failed to update request status. Please try again.");
        }
    };

    // Components for different views
    const MainDashboard = () => {
        const statusCounts = requests.reduce((acc, req) => {
            acc[req.status] = (acc[req.status] || 0) + 1;
            return acc;
        }, {});
        return ( <
            div className = "p-8" >
            <
            div className = "grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-6 mb-8" >
            <
            DashboardCard title = "Total Requests"
            count = { requests.length }
            icon = "📦" / >
            <
            DashboardCard title = "Approved"
            count = { statusCounts['Approved'] || 0 }
            icon = "✅" / >
            <
            DashboardCard title = "Pending"
            count = { statusCounts['Pending'] || 0 }
            icon = "🕒" / >
            <
            DashboardCard title = "Rejected"
            count = { statusCounts['Rejected'] || 0 }
            icon = "❌" / >
            <
            DashboardCard title = "Delivered"
            count = { statusCounts['Delivered'] || 0 }
            icon = "🚚" / >
            <
            /div> <
            h2 className = "text-2xl font-bold mb-4" > Product Requests Table View < /h2> <
            RequestsTable / >
            <
            /div>
        );
    };

    const RequestsTable = () => {
        return ( <
            div className = "bg-white p-6 rounded-xl shadow-lg" >
            <
            div className = "overflow-x-auto" >
            <
            table className = "min-w-full text-left" >
            <
            thead >
            <
            tr className = "bg-gray-100 text-gray-600 uppercase text-sm leading-normal" >
            <
            th className = "py-3 px-6 text-left" > Request ID < /th> <
            th className = "py-3 px-6 text-left" > Requester < /th> <
            th className = "py-3 px-6 text-left" > Product < /th> <
            th className = "py-3 px-6 text-left" > Location < /th> <
            th className = "py-3 px-6 text-left" > Status < /th> <
            th className = "py-3 px-6 text-center" > Actions < /th> < /
            tr > <
            /thead> <
            tbody className = "text-gray-600 text-sm font-light" > {
                requests.length > 0 ? (
                    requests.map(req => ( <
                        tr key = { req.id }
                        className = "border-b border-gray-200 hover:bg-gray-50" >
                        <
                        td className = "py-3 px-6 text-left whitespace-nowrap" > { req.id } < /td> <
                        td className = "py-3 px-6 text-left" > { req.requesterName } < /td> <
                        td className = "py-3 px-6 text-left" > { req.productType } < /td> <
                        td className = "py-3 px-6 text-left" > { req.location && req.location.region } & gt; { req.location && req.location.zone } & gt; { req.location && req.location.woreda } & gt; { req.location && req.location.kebele } <
                        /td> <
                        td className = "py-3 px-6 text-left" >
                        <
                        span className = { `py-1 px-3 rounded-full text-xs font-semibold ${
                                                req.status === 'Approved' ? 'bg-green-200 text-green-600' :
                                                req.status === 'Pending' ? 'bg-yellow-200 text-yellow-600' :
                                                'bg-red-200 text-red-600'
                                            }` } > { req.status } <
                        /span> < /
                        td > <
                        td className = "py-3 px-6 text-center" >
                        <
                        button onClick = {
                            () => setSelectedRequest(req)
                        }
                        className = "bg-blue-500 text-white p-2 rounded-lg hover:bg-blue-600 transition-colors" > 👁View <
                        /button> < /
                        td > <
                        /tr>
                    ))
                ) : ( <
                    tr >
                    <
                    td colSpan = "6"
                    className = "text-center py-4 text-gray-500" > No requests found. < /td> < /
                    tr >
                )
            } <
            /tbody> < /
            table > <
            /div> < /
            div >
        );
    };

    const RequestApprovalForm = ({ request, onClose }) => {
            const { register, handleSubmit } = useForm();
            const onSubmit = data => {
                const newStatus = data.action === 'approve' ? 'Approved' : 'Rejected';
                handleRequestAction(request.id, newStatus, data.feedback);
                onClose();
            };

            const canApprove = () => {
                if (!currentUser) return false;
                const userRole = currentUser.role.toLowerCase();
                const approvals = request.approvals || {};

                // Check if the current user has already approved
                if (approvals[userRole] && (approvals[userRole].status === 'Approved' || approvals[userRole].status === 'Rejected')) {
                    return false;
                }

                // Allow approval if the previous level has approved (or if it's the lowest level)
                if (userRole === 'kebele') {
                    return true;
                }
                if (userRole === 'woreda' && approvals.kebele && approvals.kebele.status === 'Approved') {
                    return true;
                }
                if (userRole === 'zone' && approvals.woreda && approvals.woreda.status === 'Approved') {
                    return true;
                }
                if (userRole === 'region' && approvals.zone && approvals.zone.status === 'Approved') {
                    return true;
                }
                if (userRole === 'federal' && approvals.region && approvals.region.status === 'Approved') {
                    return true;
                }
                return false;
            };

            return ( <
                    Modal show = { true }
                    onClose = { onClose }
                    title = { `Request Approval for ${request.id}` } >
                    <
                    div className = "p-6" >
                    <
                    div className = "bg-gray-100 p-4 rounded-lg mb-4" >
                    <
                    h3 className = "font-bold text-lg mb-2" > Request Details < /h3> <
                    p > < strong > Requested By: < /strong> {request.requesterName}</p >
                    <
                    p > < strong > Product Type: < /strong> {request.productType}</p >
                    <
                    p > < strong > Amount: < /strong> {request.amount}</p >
                    <
                    p > < strong > Purpose: < /strong> {request.purpose}</p >
                    <
                    p > < strong > Location: < /strong> {request.location && request.location.region} &gt; {request.location && request.location.zone} &gt; {request.location && request.location.woreda} &gt; {request.location && request.location.kebele}</p >
                    <
                    p className = "mt-2 font-bold" > Previous Approvals: < /p> {
                    request.approvals && Object.keys(request.approvals).length > 0 ? (
                        Object.entries(request.approvals).map(([role, approval]) => ( <
                            p key = { role } >
                            -{ role }: < span className = { `font-semibold ${approval.status === 'Approved' ? 'text-green-600' : 'text-red-600'}` } > { approval.status } < /span> by {approval.by} < /
                            p >
                        ))
                    ) : ( <
                        p > -No previous approvals. < /p>
                    )
                } <
                /div> {
            canApprove() ? ( <
                form onSubmit = { handleSubmit(onSubmit) } >
                <
                div className = "mb-4" >
                <
                label className = "inline-flex items-center mr-4" >
                <
                input type = "radio" {...register("action", { required: true }) }
                value = "approve"
                className = "form-radio text-green-500" / >
                <
                span className = "ml-2" > Approve < /span> < /
                label > <
                label className = "inline-flex items-center" >
                <
                input type = "radio" {...register("action", { required: true }) }
                value = "reject"
                className = "form-radio text-red-500" / >
                <
                span className = "ml-2" > Reject < /span> < /
                label > <
                /div> <
                div className = "mb-4" >
                <
                label className = "block text-gray-700 font-bold mb-2" > Feedback < /label> <
                textarea {...register("feedback") }
                rows = "3"
                className = "w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder = "Add optional feedback..." >
                <
                /textarea> < /
                div > <
                button type = "submit"
                className = "w-full bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600 transition-colors" >
                Submit Decision <
                /button> < /
                form >
            ) : ( <
                div className = "bg-yellow-100 p-4 rounded-lg text-yellow-800" >
                You cannot approve or reject this request at this time. <
                /div>
            )
        } <
        /div> < /
    Modal >
);
};

const MasterData = () => {
        const [productData, setProductData] = useState([]);
        const [showAddUserModal, setShowAddUserModal] = useState(false);
        const [showAddProductModal, setShowAddProductModal] = useState(false);

        // Fetch products from Firestore
        useEffect(() => {
            const productsRef = collection(db, 'products');
            const unsubscribe = onSnapshot(productsRef, (snapshot) => {
                const fetchedProducts = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
                setProductData(fetchedProducts);
            });
            return unsubscribe;
        }, []);

        const isFederalAdmin = currentUser && currentUser.role === ADMIN_ROLES.FEDERAL;
        const canCreateAdmin = (adminRole) => {
            if (currentUser && currentUser.role === ADMIN_ROLES.FEDERAL && adminRole === ADMIN_ROLES.REGION) return true;
            if (currentUser && currentUser.role === ADMIN_ROLES.REGION && adminRole === ADMIN_ROLES.ZONE) return true;
            if (currentUser && currentUser.role === ADMIN_ROLES.ZONE && adminRole === ADMIN_ROLES.WOREDA) return true;
            if (currentUser && currentUser.role === ADMIN_ROLES.WOREDA && adminRole === ADMIN_ROLES.KEBELE) return true;
            return false;
        };

        const handleAddProduct = async(data) => {
            try {
                await addDoc(collection(db, 'products'), {...data, timestamp: serverTimestamp() });
                showMessage("Product added successfully!");
                setShowAddProductModal(false);
            } catch (error) {
                console.error("Error adding product:", error);
                showMessage("Failed to add product. Try again.");
            }
        };

        const handleEditProduct = async(productId, data) => {
            try {
                await updateDoc(doc(db, 'products', productId), {...data });
                showMessage("Product updated successfully!");
            } catch (error) {
                console.error("Error updating product:", error);
                showMessage("Failed to update product. Try again.");
            }
        };

        const handleDeleteProduct = async(productId) => {
            try {
                await deleteDoc(doc(db, 'products', productId));
                showMessage("Product deleted successfully!");
            } catch (error) {
                console.error("Error deleting product:", error);
                showMessage("Failed to delete product. Try again.");
            }
        };

        const handleAddUser = async(data) => {
            try {
                const newUserRef = doc(collection(db, 'users'));
                const newUserId = newUserRef.id;
                await setDoc(newUserRef, {...data, uid: newUserId, timestamp: serverTimestamp() });
                showMessage(`New ${data.role} admin created! Their ID is ${newUserId}`);
                setShowAddUserModal(false);
            } catch (error) {
                console.error("Error adding user:", error);
                showMessage("Failed to add user. Try again.");
            }
        };

        return ( <
                div className = "p-8" >
                <
                h2 className = "text-2xl font-bold mb-4" > Master Data Management < /h2>

                { /* Users Management Section */ } <
                div className = "bg-white p-6 rounded-xl shadow-lg mb-8" >
                <
                h3 className = "font-bold text-xl mb-4" > Admin Users < /h3> {
                canCreateAdmin(ADMIN_ROLES.REGION) && ( <
                    button onClick = {
                        () => setShowAddUserModal(true)
                    }
                    className = "bg-green-500 text-white p-3 rounded-lg hover:bg-green-600 transition-colors mb-4" >
                    +Add Regional Admin <
                    /button>
                )
            } {
                canCreateAdmin(ADMIN_ROLES.ZONE) && ( <
                    button onClick = {
                        () => setShowAddUserModal(true)
                    }
                    className = "bg-green-500 text-white p-3 rounded-lg hover:bg-green-600 transition-colors mb-4" >
                    +Add Zonal Admin <
                    /button>
                )
            } {
                canCreateAdmin(ADMIN_ROLES.WOREDA) && ( <
                    button onClick = {
                        () => setShowAddUserModal(true)
                    }
                    className = "bg-green-500 text-white p-3 rounded-lg hover:bg-green-600 transition-colors mb-4" >
                    +Add Woreda Admin <
                    /button>
                )
            } {
                canCreateAdmin(ADMIN_ROLES.KEBELE) && ( <
                    button onClick = {
                        () => setShowAddUserModal(true)
                    }
                    className = "bg-green-500 text-white p-3 rounded-lg hover:bg-green-600 transition-colors mb-4" >
                    +Add Kebele Rep <
                    /button>
                )
            } <
            div className = "overflow-x-auto" >
            <
            table className = "min-w-full text-left" >
            <
            thead >
            <
            tr className = "bg-gray-100 text-gray-600 uppercase text-sm leading-normal" >
            <
            th className = "py-3 px-6 text-left" > Full Name < /th> <
        th className = "py-3 px-6 text-left" > Phone < /th> <
        th className = "py-3 px-6 text-left" > Role < /th> <
        th className = "py-3 px-6 text-left" > Location < /th> <
        th className = "py-3 px-6 text-center" > Actions < /th> < /
        tr > <
            /thead> <
        tbody className = "text-gray-600 text-sm font-light" > {
                allUsers.map(user => ( <
                    tr key = { user.id }
                    className = "border-b border-gray-200 hover:bg-gray-50" >
                    <
                    td className = "py-3 px-6" > { user.fullName } < /td> <
                    td className = "py-3 px-6" > { user.phone } < /td> <
                    td className = "py-3 px-6" > { user.role } < /td> <
                    td className = "py-3 px-6" > { user.location } < /td> <
                    td className = "py-3 px-6 text-center" >
                    <
                    button onClick = {
                        () => setSelectedAdminToEdit(user)
                    }
                    className = "bg-yellow-500 text-white p-2 rounded-lg hover:bg-yellow-600 transition-colors mr-2" > ✏️Edit <
                    /button> <
                    button onClick = {
                        () => {}
                    }
                    className = "bg-red-500 text-white p-2 rounded-lg hover:bg-red-600 transition-colors" > 🗑Delete <
                    /button> < /
                    td > <
                    /tr>
                ))
            } <
            /tbody> < /
        table > <
            /div> < /
        div > {
                showAddUserModal && < AddUserModal onClose = {
                    () => setShowAddUserModal(false)
                }
                onSubmit = { handleAddUser }
                currentUser = { currentUser }
                />}

                { /* Product Types Management Section */ } <
                div className = "bg-white p-6 rounded-xl shadow-lg mb-8" >
                <
                h3 className = "font-bold text-xl mb-4" > Product Types < /h3> {
                isFederalAdmin && ( <
                    button onClick = {
                        () => setShowAddProductModal(true)
                    }
                    className = "bg-green-500 text-white p-3 rounded-lg hover:bg-green-600 transition-colors mb-4" >
                    +Add product Type <
                    /button>
                )
            } <
            div className = "overflow-x-auto" >
            <
            table className = "min-w-full text-left" >
            <
            thead >
            <
            tr className = "bg-gray-100 text-gray-600 uppercase text-sm leading-normal" >
            <
            th className = "py-3 px-6 text-left" > ID < /th> <
        th className = "py-3 px-6 text-left" > Name < /th> <
        th className = "py-3 px-6 text-left" > Department < /th> <
        th className = "py-3 px-6 text-left" > Description < /th> <
        th className = "py-3 px-6 text-center" > Actions < /th> < /
        tr > <
            /thead> <
        tbody className = "text-gray-600 text-sm font-light" > {
            productData.map(prod => ( <
                    tr key = { prod.id }
                    className = "border-b border-gray-200 hover:bg-gray-50" >
                    <
                    td className = "py-3 px-6" > { prod.id } < /td> <
                    td className = "py-3 px-6" > { prod.name } < /td> <
                    td className = "py-3 px-6" > { prod.department } < /td> <
                    td className = "py-3 px-6" > { prod.description } < /td> <
                    td className = "py-3 px-6 text-center" >
                    <
                    button onClick = {
                        () => {}
                    }
                    className = "bg-yellow-500 text-white p-2 rounded-lg hover:bg-yellow-600 transition-colors mr-2" > ✏️Edit <
                    /button> {
                    isFederalAdmin && ( <
                        button onClick = {
                            () => handleDeleteProduct(prod.id)
                        }
                        className = "bg-red-500 text-white p-2 rounded-lg hover:bg-red-600 transition-colors" > 🗑Delete <
                        /button>
                    )
                } <
                /td> < /
                tr >
            ))
    } <
    /tbody> < /
table > <
    /div> < /
div > {
    showAddProductModal && < AddProductModal onClose = {
        () => setShowAddProductModal(false)
    }
    onSubmit = { handleAddProduct }
    />} < /
    div >
);
};

const AddUserModal = ({ onClose, onSubmit, currentUser }) => {
        const { register, handleSubmit, formState: { errors } } = useForm();
        const getNextRole = () => {
            if (currentUser && currentUser.role === ADMIN_ROLES.FEDERAL) return ADMIN_ROLES.REGION;
            if (currentUser && currentUser.role === ADMIN_ROLES.REGION) return ADMIN_ROLES.ZONE;
            if (currentUser && currentUser.role === ADMIN_ROLES.ZONE) return ADMIN_ROLES.WOREDA;
            if (currentUser && currentUser.role === ADMIN_ROLES.WOREDA) return ADMIN_ROLES.KEBELE;
            return 'Farmer';
        };

        const getNextLocations = () => {
            if (currentUser && currentUser.role === ADMIN_ROLES.FEDERAL) return locations.regions;
            if (currentUser && currentUser.role === ADMIN_ROLES.REGION) return locations.zones.filter(z => z.parent === currentUser.location);
            if (currentUser && currentUser.role === ADMIN_ROLES.ZONE) return locations.woredas.filter(w => w.parent === currentUser.location);
            if (currentUser && currentUser.role === ADMIN_ROLES.WOREDA) return locations.kebeles.filter(k => k.parent === currentUser.location);
            return [];
        };
        const nextRole = getNextRole();
        const nextLocations = getNextLocations();

        return ( <
                Modal show = { true }
                onClose = { onClose }
                title = { `Add New ${nextRole} Admin` } >
                <
                form onSubmit = { handleSubmit(onSubmit) }
                className = "p-6" >
                <
                div className = "mb-4" >
                <
                label className = "block text-gray-700 font-bold mb-2" > Full Name < /label> <
                input {...register("fullName", { required: "Full name is required" }) }
                className = "w-full p-2 border rounded-lg"
                type = "text" /
                >
                {
                    errors.fullName && < p className = "text-red-500 text-sm mt-1" > { errors.fullName.message } < /p>} < /
                    div > <
                    div className = "mb-4" >
                    <
                    label className = "block text-gray-700 font-bold mb-2" > Phone Number < /label> <
                    input {...register("phone", { required: "Phone number is required" }) }
                    className = "w-full p-2 border rounded-lg"
                    type = "tel" /
                    >
                    {
                        errors.phone && < p className = "text-red-500 text-sm mt-1" > { errors.phone.message } < /p>} < /
                        div > <
                        div className = "mb-4" >
                        <
                        label className = "block text-gray-700 font-bold mb-2" > Password < /label> <
                        input {...register("password", { required: "Password is required" }) }
                        className = "w-full p-2 border rounded-lg"
                        type = "password" /
                        >
                        {
                            errors.password && < p className = "text-red-500 text-sm mt-1" > { errors.password.message } < /p>} < /
                            div > <
                            div className = "mb-4" >
                            <
                            label className = "block text-gray-700 font-bold mb-2" > Location < /label> <
                            select {...register("location", { required: "Location is required" }) }
                            className = "w-full p-2 border rounded-lg" >
                            <
                            option value = "" > Select a location < /option> {
                            nextLocations.map(loc => ( <
                                option key = { loc }
                                value = { loc } > { loc } < /option>
                            ))
                        } <
                        /select> {
                        errors.location && < p className = "text-red-500 text-sm mt-1" > { errors.location.message } < /p>} < /
                        div > <
                        input type = "hidden" {...register("role") }
                        value = { nextRole }
                        /> <
                        button type = "submit"
                        className = "w-full bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600 transition-colors" >
                        Add User <
                        /button> < /
                        form > <
                        /Modal>
                    );
                };

                const AddProductModal = ({ onClose, onSubmit }) => {
                        const { register, handleSubmit, formState: { errors } } = useForm();
                        return ( <
                                Modal show = { true }
                                onClose = { onClose }
                                title = "Add New Product" >
                                <
                                form onSubmit = { handleSubmit(onSubmit) }
                                className = "p-6" >
                                <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700 font-bold mb-2" > Product Name < /label> <
                                input {...register("name", { required: "Product name is required" }) }
                                className = "w-full p-2 border rounded-lg"
                                type = "text" /
                                >
                                {
                                    errors.name && < p className = "text-red-500 text-sm mt-1" > { errors.name.message } < /p>} < /
                                    div > <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700 font-bold mb-2" > Department < /label> <
                                    select {...register("department", { required: "Department is required" }) }
                                    className = "w-full p-2 border rounded-lg" >
                                    <
                                    option value = "" > Select Department < /option> <
                                    option value = "Biological Input Supply" > Biological Input Supply < /option> <
                                    option value = "Chemical Input Supply" > Chemical Input Supply < /option> < /
                                    select > {
                                        errors.department && < p className = "text-red-500 text-sm mt-1" > { errors.department.message } < /p>} < /
                                        div > <
                                        div className = "mb-4" >
                                        <
                                        label className = "block text-gray-700 font-bold mb-2" > Description < /label> <
                                        textarea {...register("description") }
                                        rows = "3"
                                        className = "w-full p-2 border rounded-lg" >
                                        <
                                        /textarea> < /
                                        div > <
                                        button type = "submit"
                                        className = "w-full bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600 transition-colors" >
                                        Add Product <
                                        /button> < /
                                        form > <
                                        /Modal>
                                    );
                                };


                                const Reports = () => {
                                    return ( <
                                        div className = "p-8" >
                                        <
                                        h2 className = "text-2xl font-bold mb-4" > Reports < /h2> <
                                        div className = "grid grid-cols-1 md:grid-cols-2 gap-8" >
                                        <
                                        div className = "bg-white p-6 rounded-xl shadow-lg" >
                                        <
                                        h3 className = "font-bold text-xl mb-4" > Requests by Region < /h3> <
                                        p className = "text-gray-500" > [Bar Chart: A placeholder
                                            for a bar chart showing requests by region
                                        ] <
                                        /p> < /
                                        div > <
                                        div className = "bg-white p-6 rounded-xl shadow-lg" >
                                        <
                                        h3 className = "font-bold text-xl mb-4" > Product Type Usage < /h3> <
                                        p className = "text-gray-500" > [Pie Chart: A placeholder
                                            for a pie chart showing product type usage
                                        ] <
                                        /p> < /
                                        div > <
                                        /div> < /
                                        div >
                                    );
                                };

                                const DashboardCard = ({ title, count, icon }) => ( <
                                    div className = "bg-white p-6 rounded-xl shadow-lg flex items-center justify-between" >
                                    <
                                    div >
                                    <
                                    h3 className = "text-xl font-semibold text-gray-500" > { title } < /h3> <
                                    p className = "text-4xl font-bold text-gray-800" > { count } < /p> < /
                                    div > <
                                    div className = "text-5xl text-blue-500" > { icon } < /div> < /
                                    div >
                                );

                                const Modal = ({ show, onClose, title, message, children }) => {
                                        if (!show) return null;
                                        return ( <
                                            div className = "fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4" >
                                            <
                                            div className = "bg-white rounded-xl shadow-2xl w-full max-w-md" >
                                            <
                                            div className = "flex justify-between items-center p-4 border-b" >
                                            <
                                            h2 className = "text-xl font-bold" > { title } < /h2> <
                                            button onClick = { onClose }
                                            className = "text-gray-500 hover:text-gray-800 text-2xl leading-none" > & times; < /button> < /
                                            div > <
                                            div className = "p-4" > {
                                                message && < p className = "mb-4 text-gray-700" > { message } < /p>} { children } < /
                                                div > <
                                                /div> < /
                                                div >
                                            );
                                        };

                                        const LoginScreen = () => {
                                                const { register, handleSubmit, formState: { errors } } = useForm();
                                                const onSubmit = data => {
                                                    handleLogin(data.phone, data.password);
                                                };
                                                return ( <
                                                        div className = "flex items-center justify-center min-h-screen bg-gray-100 p-4" >
                                                        <
                                                        div className = "bg-white p-8 rounded-xl shadow-2xl w-full max-w-sm" >
                                                        <
                                                        h2 className = "text-3xl font-bold text-center mb-6" > Admin Login < /h2> <
                                                        p className = "text-center text-gray-500 mb-6" > Use one of the mock credentials to log in . < /p> <
                                                        form onSubmit = { handleSubmit(onSubmit) } >
                                                        <
                                                        div className = "mb-4" >
                                                        <
                                                        label className = "block text-gray-700 font-bold mb-2" > Phone Number < /label> <
                                                        input {...register("phone", { required: "Phone number is required" }) }
                                                        className = "w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                                                        type = "tel"
                                                        placeholder = "e.g. 0912345678" /
                                                        >
                                                        {
                                                            errors.phone && < p className = "text-red-500 text-sm mt-1" > { errors.phone.message } < /p>} < /
                                                            div > <
                                                            div className = "mb-6" >
                                                            <
                                                            label className = "block text-gray-700 font-bold mb-2" > Password < /label> <
                                                            input {...register("password", { required: "Password is required" }) }
                                                            className = "w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                                                            type = "password"
                                                            placeholder = "password123" /
                                                            >
                                                            {
                                                                errors.password && < p className = "text-red-500 text-sm mt-1" > { errors.password.message } < /p>} < /
                                                                div > <
                                                                button
                                                                type = "submit"
                                                                className = "w-full bg-blue-500 text-white p-3 rounded-lg font-bold hover:bg-blue-600 transition-colors" >
                                                                Login <
                                                                /button> < /
                                                                form > <
                                                                /div> < /
                                                                div >
                                                            );
                                                        };

                                                        if (loading || !authReady) {
                                                            return <div className = "flex items-center justify-center min-h-screen text-2xl font-semibold" > Loading... < /div>;
                                                        }

                                                        if (!currentUser || currentPage === 'login') {
                                                            return <LoginScreen / > ;
                                                        }

                                                        return ( <
                                                            div className = "flex min-h-screen bg-gray-100 font-inter" >
                                                            <
                                                            Sidebar currentPage = { currentPage }
                                                            setCurrentPage = { setCurrentPage }
                                                            currentUser = { currentUser }
                                                            /> <
                                                            div className = "flex-1 flex flex-col" >
                                                            <
                                                            header className = "bg-white shadow-md p-4 flex justify-between items-center" >
                                                            <
                                                            h1 className = "text-2xl font-bold text-gray-800" > product Request Management System < /h1> <
                                                            div className = "flex items-center space-x-4" >
                                                            <
                                                            span className = "text-gray-700" > 👤{ currentUser.fullName }({ currentUser.role }) < /span> <
                                                            button onClick = { handleLogout }
                                                            className = "bg-red-500 text-white p-2 rounded-lg hover:bg-red-600 transition-colors" > Logout < /button> < /
                                                            div > <
                                                            /header> <
                                                            main className = "flex-1 overflow-auto" > { currentPage === 'dashboard' && < MainDashboard / > } { currentPage === 'master-data' && < MasterData / > } { currentPage === 'reports' && < Reports / > } {
                                                                selectedRequest && < RequestApprovalForm request = { selectedRequest }
                                                                onClose = {
                                                                    () => setSelectedRequest(null)
                                                                }
                                                                />} < /
                                                                main > <
                                                                    /div> <
                                                                Modal show = { showModal }
                                                                onClose = {
                                                                    () => setShowModal(false)
                                                                }
                                                                title = "Notification"
                                                                message = { modalContent }
                                                                /> < /
                                                                div >
                                                            );
                                                        }
