                        }

                        { /* Requests Tab */ } {
                            activeTab === 'requests' && ( <
                                div >
                                <
                                div className = "flex justify-between items-center mb-6" >
                                <
                                h2 className = "text-2xl font-bold text-gray-900 dark:text-white" > Request Management < /h2> <
                                div className = "flex items-center space-x-4" >
                                <
                                select value = { statusFilter }
                                onChange = {
                                    (e) => setStatusFilter(e.target.value) }
                                className = { `p-2 rounded border ${darkMode ? 'bg-gray-700 text-white border-gray-600' : 'bg-white border-gray-300 text-gray-900'}` } >
                                <
                                option value = "all" > All Status < /option> <
                                option value = "Pending" > Pending < /option> <
                                option value = "Approved" > Approved < /option> <
                                option value = "Accepted" > Accepted < /option> <
                                option value = "Rejected" > Rejected < /option> <
                                /select> <
                                /div> <
                                /div>

                                <
                                div className = "space-y-4" > {
                                    filteredRequests.map((request) => ( <
                                        div key = { request.id }
                                        className = { `p-4 rounded shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                                        <
                                        div className = "flex justify-between items-start mb-4" >
                                        <
                                        div >
                                        <
                                        h3 className = "text-lg font-semibold text-gray-900 dark:text-white" > { request.product_name } < /h3> <
                                        p className = "text-gray-600 dark:text-gray-300" > Farmer: { request.farmer_name } < /p> <
                                        p className = "text-gray-600 dark:text-gray-300" > Amount: { request.amount } { request.unit } < /p> <
                                        p className = "text-gray-600 dark:text-gray-300" > Requested: { new Date(request.created_at).toLocaleString() } < /p> <
                                        /div> <
                                        div className = "text-right" > { getStatusBadge(request.status) } <
                                        p className = "text-sm text-gray-600 dark:text-gray-300 mt-2" >
                                        Woreda: { request.woreda_status || 'Pending' } <
                                        /p> <
                                        p className = "text-sm text-gray-600 dark:text-gray-300" >
                                        Zone: { request.zone_status || 'Pending' } <
                                        /p> <
                                        /div> <
                                        /div>

                                        { /* Status Levels */ } <
                                        div className = "grid grid-cols-1 md:grid-cols-2 gap-4 mb-4" > { getLevelStatus('woreda', request) } { getLevelStatus('zone', request) } <
                                        /div>

                                        <
                                        div className = "flex justify-between items-center" >
                                        <
                                        button onClick = {
                                            () => viewRequestDetails(request.id) }
                                        className = "text-blue-600 hover:text-blue-800 dark:text-blue-400 dark:hover:text-blue-300" >
                                        View Full Details <
                                        /button> {
                                            canZoneAdminAct(request) && ( <
                                                div className = "flex space-x-2" >
                                                <
                                                button onClick = {
                                                    () => handleStatusUpdate(request, 'Approved') }
                                                className = "bg-green-500 hover:bg-green-600 text-white py-1 px-3 rounded text-sm" >
                                                Approve <
                                                /button> <
                                                button onClick = {
                                                    () => handleStatusUpdate(request, 'Rejected') }
                                                className = "bg-red-500 hover:bg-red-600 text-white py-1 px-3 rounded text-sm" >
                                                Reject <
                                                /button> <
                                                /div>
                                            )
                                        } <
                                        /div> <
                                        /div>
                                    ))
                                } {
                                    filteredRequests.length === 0 && ( <
                                        p className = "text-center py-8 text-gray-500 dark:text-gray-400" > No requests found. < /p>
                                    )
                                } <
                                /div> <
                                /div>
                            )
                        }

                        { /* Farmers Tab */ } { activeTab === 'farmers' && renderFarmers() }

                        { /* Reports Tab */ } { activeTab === 'reports' && renderReports() }

                        { /* Request Details Modal */ } {
                            showRequestModal && selectedRequest && ( <
                                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                                <
                                div className = { `p-6 rounded shadow w-full max-w-2xl ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                                <
                                h3 className = "text-xl font-semibold mb-4 text-gray-900 dark:text-white" > Request Details < /h3>

                                { /* Request Information */ } <
                                div className = "mb-6" >
                                <
                                h4 className = "font-semibold text-gray-900 dark:text-white mb-2" > Request Information < /h4> <
                                div className = "grid grid-cols-1 md:grid-cols-2 gap-4" >
                                <
                                div >
                                <
                                p className = "text-gray-700 dark:text-gray-300" > < strong > Product: < /strong> {selectedRequest.product_name}</p >
                                <
                                p className = "text-gray-700 dark:text-gray-300" > < strong > Farmer: < /strong> {selectedRequest.farmer_name}</p >
                                <
                                p className = "text-gray-700 dark:text-gray-300" > < strong > Amount: < /strong> {selectedRequest.amount} {selectedRequest.unit}</p >
                                <
                                /div> <
                                div >
                                <
                                p className = "text-gray-700 dark:text-gray-300" > < strong > Status: < /strong> {selectedRequest.status}</p >
                                <
                                p className = "text-gray-700 dark:text-gray-300" > < strong > Requested: < /strong> {new Date(selectedRequest.created_at).toLocaleString()}</p >
                                <
                                /div> <
                                /div> <
                                /div>

                                { /* Status Levels */ } <
                                div className = "mb-6" >
                                <
                                h4 className = "font-semibold text-gray-900 dark:text-white mb-2" > Approval Status < /h4> <
                                div className = "grid grid-cols-1 md:grid-cols-2 gap-4" > { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } <
                                /div> <
                                /div>

                                { /* Decision Form */ } {
                                    requestStatus && canZoneAdminAct(selectedRequest) && ( <
                                        div className = "mb-4" >
                                        <
                                        h4 className = "font-semibold text-gray-900 dark:text-white mb-2" > { requestStatus === 'Approved' ? 'Approve' : 'Reject' }
                                        Request <
                                        /h4> <
                                        textarea placeholder = { `Enter reason for ${requestStatus.toLowerCase()}...` }
                                        value = { decisionReason }
                                        onChange = {
                                            (e) => setDecisionReason(e.target.value) }
                                        className = { `w-full p-2 rounded border ${darkMode ? 'bg-gray-700 text-white border-gray-600' : 'bg-white border-gray-300 text-gray-900'}` }
                                        rows = "3" /
                                        >
                                        <
                                        /div>
                                    )
                                }

                                <
                                div className = "flex justify-end space-x-2" > {
                                    requestStatus && canZoneAdminAct(selectedRequest) ? ( <
                                        >
                                        <
                                        button onClick = {
                                            () => {
                                                setShowRequestModal(false);
                                                setRequestStatus('');
                                                setDecisionReason('');
                                            }
                                        }
                                        className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" >
                                        Cancel <
                                        /button> <
                                        button onClick = { confirmStatusUpdate }
                                        className = { `py-2 px-4 rounded text-white ${requestStatus === 'Approved'
                                                ? 'bg-green-500 hover:bg-green-600'
                                                : 'bg-red-500 hover:bg-red-600'
                                                }` } >
                                        Confirm { requestStatus } <
                                        /button> <
                                        />
                                    ) : ( <
                                        button onClick = {
                                            () => {
                                                setShowRequestModal(false);
                                                setSelectedRequest(null);
                                            }
                                        }
                                        className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" >
                                        Close <
                                        /button>
                                    )
                                } <
                                /div> <
                                /div> <
                                /div>
                            )
                        }

                        { /* Report Modal */ } {
                            showReportModal && ( <
                                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                                <
                                div className = { `p-6 rounded shadow w-full max-w-md ${darkMode ? 'bg-gray-800' : 'bg-white'}` } >
                                <
                                h3 className = "text-xl font-semibold mb-4 text-gray-900 dark:text-white" > Create New Report < /h3> <
                                form onSubmit = { handleReportSubmit } >
                                <
                                div className = "mb-4" >
                                <
                                label className = "block mb-1 text-gray-700 dark:text-gray-300" > Report Title < /label> <
                                input type = "text"
                                value = { reportForm.title }
                                onChange = {
                                    (e) => setReportForm({...reportForm, title: e.target.value }) }
                                className = { `w-full p-2 rounded border ${darkMode ? 'bg-gray-700 text-white border-gray-600' : 'bg-white border-gray-300 text-gray-900'}` }
                                required /
                                >
                                <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block mb-1 text-gray-700 dark:text-gray-300" > Report Type < /label> <
                                select value = { reportForm.reportType }
                                onChange = {
                                    (e) => setReportForm({...reportForm, reportType: e.target.value }) }
                                className = { `w-full p-2 rounded border ${darkMode ? 'bg-gray-700 text-white border-gray-600' : 'bg-white border-gray-300 text-gray-900'}` } >
                                <
                                option value = "Misconduct" > Misconduct < /option> <
                                option value = "Performance" > Performance Issue < /option> <
                                option value = "Complaint" > Complaint < /option> <
                                option value = "Other" > Other < /option> <
                                /select> <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block mb-1 text-gray-700 dark:text-gray-300" > Report Admin < /label> <
                                select value = { reportForm.reportedAdminId }
                                onChange = {
                                    (e) => setReportForm({...reportForm, reportedAdminId: e.target.value }) }
                                className = { `w-full p-2 rounded border ${darkMode ? 'bg-gray-700 text-white border-gray-600' : 'bg-white border-gray-300 text-gray-900'}` }
                                required >
                                <
                                option value = "" > Select Admin < /option> {
                                    woredaAdmins.map((admin) => ( <
                                        option key = { admin.id }
                                        value = { admin.id } > { admin.full_name }({ admin.woreda_name }) <
                                        /option>
                                    ))
                                } <
                                /select> <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block mb-1 text-gray-700 dark:text-gray-300" > Priority < /label> <
                                select value = { reportForm.priority }
                                onChange = {
                                    (e) => setReportForm({...reportForm, priority: e.target.value }) }
                                className = { `w-full p-2 rounded border ${darkMode ? 'bg-gray-700 text-white border-gray-600' : 'bg-white border-gray-300 text-gray-900'}` } >
                                <
                                option value = "Low" > Low < /option> <
                                option value = "Medium" > Medium < /option> <
                                option value = "High" > High < /option> <
                                option value = "Critical" > Critical < /option> <
                                /select> <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block mb-1 text-gray-700 dark:text-gray-300" > Description < /label> <
                                textarea value = { reportForm.description }
                                onChange = {
                                    (e) => setReportForm({...reportForm, description: e.target.value }) }
                                className = { `w-full p-2 rounded border ${darkMode ? 'bg-gray-700 text-white border-gray-600' : 'bg-white border-gray-300 text-gray-900'}` }
                                rows = "3"
                                required /
                                >
                                <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block mb-1 text-gray-700 dark:text-gray-300" > Evidence(Optional) < /label> <
                                input type = "text"
                                value = { reportForm.evidence }
                                onChange = {
                                    (e) => setReportForm({...reportForm, evidence: e.target.value }) }
                                className = { `w-full p-2 rounded border ${darkMode ? 'bg-gray-700 text-white border-gray-600' : 'bg-white border-gray-300 text-gray-900'}` }
                                placeholder = "Links, references, etc." /
                                >
                                <
                                /div> <
                                div className = "flex justify-end space-x-2" >
                                <
                                button type = "button"
                                onClick = {
                                    () => {
                                        setShowReportModal(false);
                                        resetReportForm();
                                    }
                                }
                                className = "bg-gray-500 hover:bg-gray-600 text-white py-2 px-4 rounded" >
                                Cancel <
                                /button> <
                                button type = "submit"
                                className = "bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded" >
                                Submit Report <
                                /button> <
                                /div> <
                                /form> <
                                /div> <
                                /div>
                            )
                        } <
                        /div> <
                        /div>
                    );
                };

                export default ZoneDashboard;
