import React, { useState } from 'react';
import { Eye, EyeOff, AlertCircle, LogIn, Shield } from 'lucide-react';

const AdminLogin = () => {
    const [phoneNumber, setPhoneNumber] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const handleSubmit = async(e) => {
        e.preventDefault();
        setIsLoading(true);
        setError('');

        // Validate inputs
        if (!phoneNumber || !password) {
            setError('Phone number and password are required');
            setIsLoading(false);
            return;
        }

        if (phoneNumber.length < 10) {
            setError('Please enter a valid phone number');
            setIsLoading(false);
            return;
        }

        try {
            // API call to your backend for authentication
            const response = await fetch('/api/admin/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ phoneNumber, password }),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Login failed');
            }

            // Store authentication token and user data
            localStorage.setItem('adminToken', data.token);
            localStorage.setItem('adminData', JSON.stringify(data.user));

            // Redirect to appropriate dashboard based on role
            window.location.href = '/dashboard';

        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    return ( <
        div style = { styles.container } >
        <
        div style = { styles.loginBox } >
        <
        div style = { styles.header } >
        <
        div style = { styles.logo } >
        <
        Shield size = { 32 }
        /> <
        /div> <
        h1 style = { styles.title } > Agricultural Portal < /h1> <
        p style = { styles.subtitle } > Admin System Login < /p> <
        /div>

        <
        form onSubmit = { handleSubmit }
        style = { styles.form } >
        <
        div style = { styles.inputGroup } >
        <
        label htmlFor = "phoneNumber"
        style = { styles.label } >
        Phone Number <
        /label> <
        input id = "phoneNumber"
        type = "tel"
        value = { phoneNumber }
        onChange = {
            (e) => setPhoneNumber(e.target.value) }
        style = { styles.input }
        placeholder = "Enter your phone number"
        required /
        >
        <
        /div>

        <
        div style = { styles.inputGroup } >
        <
        label htmlFor = "password"
        style = { styles.label } >
        Password <
        /label> <
        div style = { styles.passwordContainer } >
        <
        input id = "password"
        type = { showPassword ? 'text' : 'password' }
        value = { password }
        onChange = {
            (e) => setPassword(e.target.value) }
        style = { styles.input }
        placeholder = "Enter your password"
        required /
        >
        <
        button type = "button"
        onClick = {
            () => setShowPassword(!showPassword) }
        style = { styles.toggleButton } >
        { showPassword ? < EyeOff size = { 18 } /> : <Eye size={18} / > } <
        /button> <
        /div> <
        /div>

        <
        button type = "submit"
        disabled = { isLoading }
        style = {
            {
                ...styles.loginButton,
                    ...(isLoading ? styles.loginButtonLoading : {})
            }
        } >
        {
            isLoading ? ( <
                div style = { styles.spinner } > < /div>
            ) : ( <
                >
                <
                LogIn size = { 18 }
                style = {
                    { marginRight: '8px' } }
                />
                Login <
                />
            )
        } <
        /button>

        {
            error && ( <
                div style = { styles.errorBox } >
                <
                AlertCircle size = { 18 }
                style = {
                    { marginRight: '8px' } }
                /> <
                span > { error } < /span> <
                /div>
            )
        } <
        /form>

        <
        div style = { styles.debugInfo } >
        <
        h3 style = { styles.debugTitle } > API Information < /h3> <
        p > Endpoint: < span style = { styles.url } > POST / api / admin / login < /span></p >
        <
        p > Expected request body: phoneNumber, password < /p> <
        /div> <
        /div> <
        /div>
    );
};

const styles = {
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
        padding: '20px',
        fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif',
    },
    loginBox: {
        backgroundColor: 'white',
        borderRadius: '12px',
        boxShadow: '0 10px 30px rgba(0, 0, 0, 0.15)',
        width: '100%',
        maxWidth: '420px',
        overflow: 'hidden',
    },
    header: {
        background: 'linear-gradient(135deg, #2d5016 0%, #4a7b2c 100%)',
        color: 'white',
        padding: '25px 20px',
        textAlign: 'center',
    },
    logo: {
        width: '70px',
        height: '70px',
        backgroundColor: 'white',
        borderRadius: '50%',
        margin: '0 auto 15px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    title: {
        fontSize: '24px',
        fontWeight: '600',
        marginBottom: '8px',
    },
    subtitle: {
        fontSize: '14px',
        opacity: '0.9',
    },
    form: {
        padding: '25px',
    },
    inputGroup: {
        marginBottom: '20px',
    },
    label: {
        display: 'block',
        marginBottom: '8px',
        fontWeight: '500',
        color: '#333',
        fontSize: '14px',
    },
    input: {
        width: '100%',
        padding: '14px',
        border: '1px solid #ddd',
        borderRadius: '8px',
        fontSize: '16px',
        transition: 'border-color 0.3s',
    },
    passwordContainer: {
        position: 'relative',
    },
    toggleButton: {
        position: 'absolute',
        right: '15px',
        top: '50%',
        transform: 'translateY(-50%)',
        background: 'none',
        border: 'none',
        color: '#777',
        cursor: 'pointer',
    },
    loginButton: {
        width: '100%',
        padding: '14px',
        background: 'linear-gradient(135deg, #2d5016 0%, #4a7b2c 100%)',
        color: 'white',
        border: 'none',
        borderRadius: '8px',
        fontSize: '16px',
        fontWeight: '600',
        cursor: 'pointer',
        transition: 'all 0.3s',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    loginButtonLoading: {
        opacity: '0.8',
    },
    spinner: {
        border: '2px solid rgba(255, 255, 255, 0.3)',
        borderTop: '2px solid white',
        borderRadius: '50%',
        width: '20px',
        height: '20px',
        animation: 'spin 1s linear infinite',
    },
    errorBox: {
        backgroundColor: '#fee',
        color: '#c33',
        padding: '12px',
        borderRadius: '8px',
        marginTop: '20px',
        display: 'flex',
        alignItems: 'center',
        borderLeft: '4px solid #c33',
        fontSize: '14px',
    },
    debugInfo: {
        backgroundColor: '#f9f9f9',
        borderRadius: '8px',
        padding: '15px',
        margin: '20px 25px',
        fontSize: '12px',
        color: '#666',
        borderLeft: '4px solid #ddd',
    },
    debugTitle: {
        marginBottom: '10px',
        color: '#555',
        fontSize: '13px',
    },
    url: {
        color: '#c33',
        fontWeight: 'bold',
    },
};

// Add keyframes for spinner animation
const styleSheet = document.styleSheets[0];
styleSheet.insertRule(`
  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`, styleSheet.cssRules.length);

export default AdminLogin;
