// RegionDashboard.js with sidebar + TailwindCSS + dark mode
// Region admins cannot edit/delete products added by others

import React, { useCallback, useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const API = 'http://localhost:5000/api/admins';

export default function RegionDashboard() {
    const navigate = useNavigate();

    const [user, setUser] = useState(null);
    const [darkMode, setDarkMode] = useState(false);
    const [activeTab, setActiveTab] = useState('overview');

    // Requests
    const [requests, setRequests] = useState([]);
    const [filteredRequests, setFilteredRequests] = useState([]);
    const [statusFilter, setStatusFilter] = useState('all');
    const [searchQuery, setSearchQuery] = useState('');

    // Products
    const [productsAll, setProductsAll] = useState([]);
    const [productsMine, setProductsMine] = useState([]);
    const [productsOthers, setProductsOthers] = useState([]);
    const [productView, setProductView] = useState('all');
    const [showProductForm, setShowProductForm] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({ name: '', category: '', amount: '', price: '', description: '' });
    const [productError, setProductError] = useState('');

    // Zones
    const [zoneAdmins, setZoneAdmins] = useState([]);
    const [showZoneForm, setShowZoneForm] = useState(false);
    const [editingZone, setEditingZone] = useState(null);
    const [zoneForm, setZoneForm] = useState({ fullName: '', phoneNumber: '', password: '', confirmPassword: '', zoneName: '' });
    const [zoneError, setZoneError] = useState('');

    const authHeaders = useCallback(() => {
        const token = localStorage.getItem('token');
        return { 'Authorization': 'Bearer ' + token, 'Content-Type': 'application/json' };
    }, []);

    useEffect(() => {
        const token = localStorage.getItem('token');
        const userDataRaw = localStorage.getItem('user');
        const userType = localStorage.getItem('userType');

        if (!token || !userDataRaw || userType !== 'Region') {
            navigate('/login');
            return;
        }
        setUser(JSON.parse(userDataRaw));

        if (document.documentElement.classList.contains('dark')) setDarkMode(true);

        fetchAll();
    }, [navigate]);

    const fetchRequests = useCallback(async() => {
        const res = await fetch(API + '/requests/status', { headers: authHeaders() });
        if (res.ok) setRequests(await res.json());
    }, [authHeaders]);

    const fetchProducts = useCallback(async() => {
        const resAll = await fetch(API + '/getproducts', { headers: authHeaders() });
        if (resAll.ok) setProductsAll(await resAll.json());

        const resMine = await fetch(API + '/myproducts', { headers: authHeaders() });
        if (resMine.ok) setProductsMine(await resMine.json());

        const resOthers = await fetch(API + '/otherproducts', { headers: authHeaders() });
        if (resOthers.ok) setProductsOthers(await resOthers.json());
    }, [authHeaders]);

    const fetchZoneAdmins = useCallback(async() => {
        const res = await fetch(API + '/admins', { headers: authHeaders() });
        if (res.ok) setZoneAdmins(await res.json());
    }, [authHeaders]);

    const fetchAll = useCallback(async() => {
        await fetchRequests();
        await fetchProducts();
        await fetchZoneAdmins();
    }, [fetchRequests, fetchProducts, fetchZoneAdmins]);

    // Stats
    const stats = useMemo(() => {
        return {
            totalRequests: requests.length,
            totalProducts: productView === 'my' ? productsMine.length : productView === 'others' ? productsOthers.length : productsAll.length,
            totalZones: zoneAdmins.length
        };
    }, [requests, productsAll, productsMine, productsOthers, productView, zoneAdmins]);

    // Filtering
    useEffect(() => {
        let data = requests.slice();
        if (searchQuery) {
            const q = searchQuery.toLowerCase();
            data = data.filter(r => (r.farmer_name || '').toLowerCase().includes(q) || (r.product_name || '').toLowerCase().includes(q));
        }
        if (statusFilter === 'pending') data = data.filter(r => r.status === 'Pending');
        else if (statusFilter === 'regionAccepted') data = data.filter(r => r.region_status === 'Accepted');
        else if (statusFilter === 'regionApproved') data = data.filter(r => r.region_status === 'Approved');
        setFilteredRequests(data);
    }, [requests, searchQuery, statusFilter]);

    const toggleDark = () => {
        setDarkMode(!darkMode);
        document.documentElement.classList.toggle('dark');
    };

    const canEditOrDeleteProduct = (p) => user && p.created_by_admin_id === user.id;

    return ( <
        div className = "min-h-screen flex bg-gray-50 dark:bg-gray-950 text-gray-900 dark:text-white" > { /* Sidebar */ } <
        aside className = "w-64 bg-white dark:bg-gray-900 border-r border-gray-200 dark:border-gray-800 p-4" >
        <
        div className = "mb-6" >
        <
        h2 className = "text-lg font-bold" > Region Dashboard < /h2> <
        p className = "text-xs text-gray-500" > { user ? user.full_name : '' } < /p> <
        /div> <
        nav className = "space-y-2" > {
            ['overview', 'requests', 'products', 'zones'].map(tab => ( <
                button key = { tab }
                onClick = {
                    () => setActiveTab(tab) }
                className = { `w-full text-left px-3 py-2 rounded-lg ${activeTab === tab ? 'bg-gray-900 text-white dark:bg-white dark:text-gray-900' : 'hover:bg-gray-100 dark:hover:bg-gray-800'}` } > { tab.charAt(0).toUpperCase() + tab.slice(1) } < /button>
            ))
        } <
        /nav> <
        div className = "mt-6 space-y-2" >
        <
        button onClick = { toggleDark }
        className = "w-full px-3 py-2 rounded-lg border border-gray-200 dark:border-gray-800" > { darkMode ? 'Light' : 'Dark' }
        mode < /button> <
        button onClick = {
            () => { localStorage.clear();
                navigate('/login'); } }
        className = "w-full px-3 py-2 rounded-lg bg-red-600 text-white" > Logout < /button> <
        /div> <
        /aside>

        { /* Main Content */ } <
        main className = "flex-1 p-6" > {
            activeTab === 'overview' && ( <
                div className = "grid grid-cols-1 sm:grid-cols-3 gap-4" >
                <
                div className = "p-4 rounded-lg border bg-white dark:bg-gray-900" > Requests: { stats.totalRequests } < /div> <
                div className = "p-4 rounded-lg border bg-white dark:bg-gray-900" > Products: { stats.totalProducts } < /div> <
                div className = "p-4 rounded-lg border bg-white dark:bg-gray-900" > Zones: { stats.totalZones } < /div> <
                /div>
            )
        }

        {
            activeTab === 'products' && ( <
                div className = "grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4" > {
                    (productView === 'my' ? productsMine : productView === 'others' ? productsOthers : productsAll).map(p => ( <
                        div key = { p.id }
                        className = "p-4 rounded-lg border bg-white dark:bg-gray-900" >
                        <
                        h3 className = "font-semibold" > { p.name } < /h3> <
                        p > { p.description } < /p> <
                        p > Amount: { p.amount }
                        Price: { p.price } < /p> {
                            canEditOrDeleteProduct(p) && ( <
                                div className = "mt-2 flex gap-2" >
                                <
                                button onClick = {
                                    () => setEditingProduct(p) }
                                className = "px-2 py-1 rounded bg-blue-600 text-white" > Edit < /button> <
                                button onClick = {
                                    () => { /* delete logic */ } }
                                className = "px-2 py-1 rounded bg-red-600 text-white" > Delete < /button> <
                                /div>
                            )
                        } <
                        /div>
                    ))
                } <
                /div>
            )
        } <
        /main> <
        /div>
    );
}
