            administrator: 'Administrator',
            phoneNumber: 'Phone Number',
            password: 'Password',
            enterPassword: 'Enter your password',
            signingIn: 'Signing In...',
            signInAs: 'Sign In as',
            accessInfo: 'Access Information',
            farmersInfo: 'Farmers: Use your registered phone number and password',
            farmersDesc: 'Submit product requests, track status, and manage your applications',
            adminsInfo: 'Administrators: Select your level and use your credentials',
            adminsDesc: 'Approve/reject requests, manage users, and oversee your administrative area',
