            farmer: '·åà·â†·à¨',
            administrator: '·ä†·àµ·â∞·ã≥·ã≥·à™',
            phoneNumber: '·àµ·àç·ä≠ ·âÅ·å•·à≠',
            password: '·ã®·ã≠·àà·çç ·âÉ·àç',
            enterPassword: '·ã®·ã≠·àà·çç ·âÉ·àç·ãé·äï ·ã´·àµ·åà·â°',
            signingIn: '·ä•·ã®·åà·â£ ·äê·ãç...',
            signInAs: '·ä•·äï·ã∞',
            accessInfo: '·ã®·àò·ã≥·à®·àª ·àò·à®·åÉ',
            farmersInfo: '·åà·â†·à¨·ãé·âΩ: ·ã®·â∞·àò·ãò·åà·â† ·àµ·àç·ä≠ ·âÅ·å•·à≠·ãé·äï ·ä•·äì ·ã®·ã≠·àà·çç ·âÉ·àç·ãé·äï ·ã≠·å†·âÄ·àô',
            farmersDesc: '·ã®·àù·à≠·âµ ·å•·ã´·âÑ·ãé·âΩ·äï ·ã´·àµ·åà·â°·ç£ ·àÅ·äî·â≥·äï ·ã≠·ä®·â≥·â∞·àâ ·ä•·äì ·àõ·àò·àç·ä®·âª·ãé·âΩ·ãé·äï ·ã´·àµ·â∞·ã≥·ãµ·à©',
            adminsInfo: '·ä†·àµ·â∞·ã≥·ã≥·à™·ãé·âΩ: ·ã∞·à®·åÉ·ãé·äï ·ã≠·àù·à®·å° ·ä•·äì ·àù·àµ·ä≠·à≠·ãé·âΩ·ãé·äï ·ã≠·å†·âÄ·àô',
            adminsDesc: '·å•·ã´·âÑ·ãé·âΩ·äï ·ã´·åΩ·ãµ·âÅ/·ã≠·ä®·àç·ä≠·àâ·ç£ ·â∞·å†·âÉ·àö·ãé·âΩ·äï ·ã´·àµ·â∞·ã≥·ãµ·à© ·ä•·äì ·ã®·ä†·àµ·â∞·ã≥·ã∞·à≠ ·ä†·ä´·â£·â¢·ãé·äï ·ã≠·âÜ·å£·å†·à©',
            home: '·àò·äê·àª'
        }
    };

    const t = translations[language];

    const toggleLanguage = () => {
        setLanguage(language === 'en' ? 'am' : 'en');
    };

    const handleSubmit = async(e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            let endpoint = '';
            let requestBody = {};

            // Determine the correct API endpoint based on login type
            if (loginType === 'farmer') {
                endpoint = 'http://localhost:5000/api/farmers/login';
                requestBody = {
                    phoneNumber: formData.phoneNumber,
                    password: formData.password
                };
            } else {
                endpoint = 'http://localhost:5000/api/admins/login';
                requestBody = {
                    phoneNumber: formData.phoneNumber,
                    password: formData.password
                };
            }

            const response = await fetch(endpoint, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(requestBody)
            });

            const data = await response.json();
            console.log('API Response:', data);

            if (!response.ok) {
                throw new Error(data.message || 'Login failed');
            }

            // Handle response based on login type
            if (loginType === 'farmer') {
                const farmerData = data.farmer || data;
                const token = data.token;

                if (!token) {
                    throw new Error('Authentication error: No token received');
                }

                localStorage.setItem('token', token);
                localStorage.setItem('user', JSON.stringify(farmerData));
                localStorage.setItem('userType', 'Farmer');
                navigate('/farmer-dashboard');

            } else {
                const adminData = data.user || data;
                const token = data.token;
                const actualUserType = adminData.role;

                if (!token || !actualUserType) {
                    throw new Error('Authentication error: Invalid response format');
                }

                // Auto-set the correct admin type based on user's actual role
                setFormData(prev => ({ ...prev, adminType: actualUserType }));

                localStorage.setItem('token', token);
                localStorage.setItem('user', JSON.stringify(adminData));
                localStorage.setItem('userType', actualUserType);

                // Redirect to appropriate admin dashboard
                switch (actualUserType) {
                    case 'Federal':
                        navigate('/federal-dashboard');
                        break;
                    case 'Region':
                        navigate('/region-dashboard');
                        break;
                    case 'Zone':
                        navigate('/zone-dashboard');
                        break;
                    case 'Woreda':
                        navigate('/woreda-dashboard');
                        break;
                    case 'Kebele':
                        navigate('/kebele-dashboard');
                        break;
                    default:
                        navigate('/');
                }
            }

        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-green-50 via-blue-50 to-yellow-50">
            {/* Header */}
            <div className="bg-white shadow-sm border-b">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between items-center py-4">
                        <div className="flex items-center space-x-3">
                            <div className="text-2xl">üá™üáπ</div>
