                                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                        </svg>
                                        {t.signingIn}
                                    </span>
                                ) : (
                                    `${t.signInAs} ${loginType === 'farmer' ? t.farmer : t.administrator}`
                                )}
                            </button>
                        </form>

                        {/* Help Section */}
                        <div className="mt-6 p-4 bg-gray-50 rounded-lg">
                            <h3 className="font-semibold text-gray-700 mb-2">üîê {t.accessInfo}</h3>
                            <div className="text-sm text-gray-600 space-y-1">
                                {loginType === 'farmer' ? (
                                    <div>
                                        <p><strong>üöú {t.farmersInfo}</strong></p>
                                        <p className="text-xs text-gray-500 mt-1">{t.farmersDesc}</p>
                                    </div>
                                ) : (
                                    <div>
                                        <p><strong>üë®‚Äçüíº {t.adminsInfo}</strong></p>
                                        <p className="text-xs text-gray-500 mt-1">{t.adminsDesc}</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </div>
    );
};

export default Login;
