import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './RegionDashboard.css';

const RegionDashboard = () => {
    const [activeTab, setActiveTab] = useState('overview');
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    // Product Management State
    const [products, setProducts] = useState([]);
    const [showProductForm, setShowProductForm] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        description: ''
    });

    // Admin Management State (Zone Admins for Region Admin)
    const [admins, setAdmins] = useState([]);
    const [showAdminForm, setShowAdminForm] = useState(false);
    const [editingAdmin, setEditingAdmin] = useState(null);
    const [adminForm, setAdminForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        role: 'Zone',
        region_name: '',
        zone_name: ''
    });
    const [adminError, setAdminError] = useState('');

    // Stats State
    const [stats, setStats] = useState({
        totalRequests: 0,
        pendingRequests: 0,
        approvedRequests: 0,
        rejectedRequests: 0,
        totalAdmins: 0,
        totalProducts: 0
    });

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Region' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
        // Pre-fill region name from logged in user
        setAdminForm(prev => ({
            ...prev,
            region_name: userData.region_name || ''
        }));
        fetchDashboardData();
    }, [navigate]);

    const fetchDashboardData = async() => {
        try {
            const token = localStorage.getItem('token');

            // Fetch products
            const productsResponse = await fetch('http://localhost:5000/api/admins/products', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
                setStats(prev => ({...prev, totalProducts: productsData.length }));
            }

            // Fetch admins in scope
            const adminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (adminsResponse.ok) {
                const adminsData = await adminsResponse.json();
                // Filter to only show Zone admins (Region admin can only create Zone admins)
                const zoneAdmins = adminsData.filter(admin => admin.role === 'Zone');
                setAdmins(zoneAdmins);
                setStats(prev => ({...prev, totalAdmins: zoneAdmins.length }));
            }

            // Fetch requests stats
            const requestsResponse = await fetch('http://localhost:5000/api/admins/requests', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                // Calculate stats from requests data
                const totalRequests = requestsData.length;
                const pendingRequests = requestsData.filter(req => req.status === 'pending').length;
                const approvedRequests = requestsData.filter(req => req.status === 'accepted').length;
                const rejectedRequests = requestsData.filter(req => req.status === 'rejected').length;

                setStats(prev => ({
                    ...prev,
                    totalRequests,
                    pendingRequests,
                    approvedRequests,
                    rejectedRequests
                }));
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('userType');
        navigate('/login');
    };

    // Product Management Functions
    const handleProductSubmit = async(e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const url = editingProduct ?
                `http://localhost:5000/api/admins/products/${editingProduct.id}` :
                'http://localhost:5000/api/admins/products';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(productForm)
            });

            if (response.ok) {
                setShowProductForm(false);
                setEditingProduct(null);
                setProductForm({ name: '', category: '', description: '' });
                fetchDashboardData();
            } else {
                const errorData = await response.json();
                console.error('Error saving product:', errorData.message);
            }
        } catch (error) {
            console.error('Error saving product:', error);
        }
    };

    const deleteProduct = async(productId) => {
        if (!window.confirm('Are you sure you want to delete this product?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/products/${productId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
            }
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    // Admin Management Functions
    const handleAdminSubmit = async(e) => {
        e.preventDefault();
        setAdminError('');

        if (adminForm.password !== adminForm.confirmPassword && !editingAdmin) {
            setAdminError('Passwords do not match');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            let url, method;

            if (editingAdmin) {
                // Update existing admin
                url = `http://localhost:5000/api/admins/edit/${editingAdmin.id}`;
                method = 'PUT';
            } else {
                // Create new admin
                url = 'http://localhost:5000/api/admins/register';
                method = 'POST';
            }

            // Prepare data for submission (don't send password if editing and password is empty)
            const submitData = {...adminForm };
            if (editingAdmin && !submitData.password) {
                delete submitData.password;
                delete submitData.confirmPassword;
            }

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(submitData)
            });

            const data = await response.json();

            if (response.ok) {
                setShowAdminForm(false);
                setEditingAdmin(null);
                setAdminForm({
                    fullName: '',
                    phoneNumber: '',
                    password: '',
                    confirmPassword: '',
                    role: 'Zone',
                    region_name: user.region_name,
                    zone_name: ''
                });
                fetchDashboardData();
            } else {
                setAdminError(data.message || 'Error processing admin');
            }
        } catch (error) {
            console.error('Error processing admin:', error);
            setAdminError('Server error occurred');
        }
    };

    const deleteAdmin = async(adminId) => {
        if (!window.confirm('Are you sure you want to delete this admin?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/admins/delete/${adminId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
            }
        } catch (error) {
            console.error('Error deleting admin:', error);
        }
    };

    const editAdmin = (admin) => {
        setEditingAdmin(admin);
        setAdminForm({
            fullName: admin.full_name || '',
            phoneNumber: admin.phone_number || '',
            password: '',
            confirmPassword: '',
            role: admin.role || 'Zone',
            region_name: admin.region_name || user.region_name,
            zone_name: admin.zone_name || ''
        });
        setShowAdminForm(true);
    };

    if (!user) {
        return <div className = "loading" > Loading... < /div>;
    }

    return ( <
        div className = "region-dashboard" > { /* Sidebar */ } <
        div className = "sidebar" >
        <
        h2 > Region Dashboard < /h2> <
        nav >
        <
        button className = { activeTab === 'overview' ? 'active' : '' }
        onClick = {
            () => setActiveTab('overview') } > 📊Overview < /button> <
        button className = { activeTab === 'products' ? 'active' : '' }
        onClick = {
            () => setActiveTab('products') } > 📦Products < /button> <
        button className = { activeTab === 'admins' ? 'active' : '' }
        onClick = {
            () => setActiveTab('admins') } > 👥Zone Admins < /button> <
        button className = { activeTab === 'requests' ? 'active' : '' }
        onClick = {
            () => setActiveTab('requests') } > 📋Requests < /button> <
        /nav> <
        /div>

        { /* Main Content */ } <
        div className = "main-content" >
        <
        header >
        <
        h1 > Welcome, { user.fullName }(Region Admin) < /h1> <
        button onClick = { handleLogout }
        className = "logout-btn" >
        Logout <
        /button> <
        /header>

        <
        main > { /* Overview Tab */ } {
            activeTab === 'overview' && ( <
                div className = "overview-tab" >
                <
                h2 > System Overview < /h2> <
                div className = "stats-grid" >
                <
                div className = "stat-card" >
                <
                h3 > { stats.totalRequests } < /h3> <
                p > Total Requests < /p> <
                /div> <
                div className = "stat-card" >
                <
                h3 > { stats.pendingRequests } < /h3> <
                p > Pending Requests < /p> <
                /div> <
                div className = "stat-card" >
                <
                h3 > { stats.approvedRequests } < /h3> <
                p > Approved Requests < /p> <
                /div> <
                div className = "stat-card" >
                <
                h3 > { stats.rejectedRequests } < /h3> <
                p > Rejected Requests < /p> <
                /div> <
                div className = "stat-card" >
                <
                h3 > { stats.totalAdmins } < /h3> <
                p > Zone Admins < /p> <
                /div> <
                div className = "stat-card" >
                <
                h3 > { stats.totalProducts } < /h3> <
                p > Products < /p> <
                /div> <
                /div>

                <
                div className = "quick-actions" >
                <
                h2 > Quick Actions < /h2> <
                div className = "actions-grid" >
                <
                button onClick = {
                    () => setActiveTab('admins') }
                className = "action-btn" > 👥Register Zone Admin <
                /button> <
                button onClick = {
                    () => setActiveTab('products') }
                className = "action-btn" > 📦Add Product <
                /button> <
                button className = "action-btn" > 📊View Reports < /button> <
                button className = "action-btn" > ⚙️Settings < /button> <
                /div> <
                /div> <
                /div>
            )
        }

        { /* Products Tab */ } {
            activeTab === 'products' && ( <
                div className = "products-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Product Management < /h2> <
                button onClick = {
                    () => setShowProductForm(true) }
                className = "add-btn" >
                Add Product <
                /button> <
                /div>

                { /* Product Form Modal */ } {
                    showProductForm && ( <
                        div className = "modal" >
                        <
                        div className = "modal-content" >
                        <
                        h3 > { editingProduct ? 'Edit Product' : 'Add Product' } < /h3> <
                        form onSubmit = { handleProductSubmit } >
                        <
                        input type = "text"
                        placeholder = "Product Name"
                        value = { productForm.name }
                        onChange = {
                            (e) => setProductForm({...productForm, name: e.target.value }) }
                        required /
                        >
                        <
                        input type = "text"
                        placeholder = "Category"
                        value = { productForm.category }
                        onChange = {
                            (e) => setProductForm({...productForm, category: e.target.value }) }
                        required /
                        >
                        <
                        textarea placeholder = "Description"
                        value = { productForm.description }
                        onChange = {
                            (e) => setProductForm({...productForm, description: e.target.value }) }
                        rows = "3"
                        required /
                        >
                        <
                        div className = "modal-actions" >
                        <
                        button type = "submit" > Save < /button> <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowProductForm(false);
                                setEditingProduct(null);
                                setProductForm({ name: '', category: '', description: '' });
                            }
                        } >
                        Cancel <
                        /button> <
                        /div> <
                        /form> <
                        /div> <
                        /div>
                    )
                }

                { /* Products List */ } <
                div className = "products-grid" > {
                    products.map((product) => ( <
                        div key = { product.id }
                        className = "product-card" >
                        <
                        h3 > { product.name } < /h3> <
                        p className = "category" > { product.category } < /p> <
                        p className = "description" > { product.description } < /p> <
                        div className = "product-actions" >
                        <
                        button onClick = {
                            () => {
                                setEditingProduct(product);
                                setProductForm({
                                    name: product.name,
                                    category: product.category,
                                    description: product.description
                                });
                                setShowProductForm(true);
                            }
                        }
                        className = "edit-btn" >
                        Edit <
                        /button> <
                        button onClick = {
                            () => deleteProduct(product.id) }
                        className = "delete-btn" >
                        Delete <
                        /button> <
                        /div> <
                        /div>
                    ))
                } <
                /div> <
                /div>
            )
        }

        { /* Admins Tab */ } {
            activeTab === 'admins' && ( <
                div className = "admins-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Zone Admin Management < /h2> <
                button onClick = {
                    () => {
                        setEditingAdmin(null);
                        setAdminForm({
                            fullName: '',
                            phoneNumber: '',
                            password: '',
                            confirmPassword: '',
                            role: 'Zone',
                            region_name: user.region_name,
                            zone_name: ''
                        });
                        setShowAdminForm(true);
                    }
                }
                className = "add-btn" >
                Register Zone Admin <
                /button> <
                /div>

                { /* Admin Form Modal */ } {
                    showAdminForm && ( <
                        div className = "modal" >
                        <
                        div className = "modal-content" >
                        <
                        h3 > { editingAdmin ? 'Edit Zone Admin' : 'Register Zone Admin' } < /h3> {
                            adminError && ( <
                                div className = "error-message" > { adminError } < /div>
                            )
                        } <
                        form onSubmit = { handleAdminSubmit } >
                        <
                        input type = "text"
                        placeholder = "Full Name"
                        value = { adminForm.fullName }
                        onChange = {
                            (e) => setAdminForm({...adminForm, fullName: e.target.value }) }
                        required /
                        >
                        <
                        input type = "tel"
                        placeholder = "Phone Number"
                        value = { adminForm.phoneNumber }
                        onChange = {
                            (e) => setAdminForm({...adminForm, phoneNumber: e.target.value }) }
                        required /
                        > {!editingAdmin && ( <
                                >
                                <
                                input type = "password"
                                placeholder = "Password"
                                value = { adminForm.password }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, password: e.target.value }) }
                                required /
                                >
                                <
                                input type = "password"
                                placeholder = "Confirm Password"
                                value = { adminForm.confirmPassword }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, confirmPassword: e.target.value }) }
                                required /
                                >
                                <
                                />
                            )
                        } {
                            editingAdmin && ( <
                                >
                                <
                                input type = "password"
                                placeholder = "New Password (leave empty to keep current)"
                                value = { adminForm.password }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, password: e.target.value }) }
                                /> <
                                input type = "password"
                                placeholder = "Confirm New Password"
                                value = { adminForm.confirmPassword }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, confirmPassword: e.target.value }) }
                                /> <
                                />
                            )
                        } <
                        input type = "text"
                        placeholder = "Region Name"
                        value = { adminForm.region_name }
                        onChange = {
                            (e) => setAdminForm({...adminForm, region_name: e.target.value }) }
                        required disabled = { true }
                        /> <
                        input type = "text"
                        placeholder = "Zone Name (Must be unique)"
                        value = { adminForm.zone_name }
                        onChange = {
                            (e) => setAdminForm({...adminForm, zone_name: e.target.value }) }
                        required /
                        >
                        <
                        div className = "modal-actions" >
                        <
                        button type = "submit" > { editingAdmin ? 'Update' : 'Register' } < /button> <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowAdminForm(false);
                                setEditingAdmin(null);
                                setAdminError('');
                                setAdminForm({
                                    fullName: '',
                                    phoneNumber: '',
                                    password: '',
                                    confirmPassword: '',
                                    role: 'Zone',
                                    region_name: user.region_name,
                                    zone_name: ''
                                });
                            }
                        } >
                        Cancel <
                        /button> <
                        /div> <
                        /form> <
                        /div> <
                        /div>
                    )
                }

                { /* Admins List */ } <
                div className = "admins-list" > {
                    admins.map((admin) => ( <
                        div key = { admin.id }
                        className = "admin-card" >
                        <
                        h3 > { admin.full_name } < /h3> <
                        p > Phone: { admin.phone_number } < /p> <
                        p > Role: { admin.role } < /p> <
                        p > Region: { admin.region_name } < /p> <
                        p > Zone: { admin.zone_name } < /p> <
                        div className = "admin-actions" >
                        <
                        button onClick = {
                            () => editAdmin(admin) }
                        className = "edit-btn" >
                        Edit <
                        /button> <
                        button onClick = {
                            () => deleteAdmin(admin.id) }
                        className = "delete-btn" >
                        Delete <
                        /button> <
                        /div> <
                        /div>
                    ))
                } <
                /div> <
                /div>
            )
        }

        { /* Requests Tab */ } {
            activeTab === 'requests' && ( <
                div className = "requests-tab" >
                <
                h2 > Request Management < /h2> <
                p > Request management functionality will be implemented here. < /p> <
                /div>
            )
        } <
        /main> <
        /div> <
        /div>
    );
};

export default RegionDashboard;
