import React, { useState, useEffect } from 'react';
import { api } from './api';
import FederalDashboard from './dashboards/FederalDashboard';
import RegionDashboard from './dashboards/RegionDashboard';
import ZoneDashboard from './dashboards/ZoneDashboard';
import WoredaDashboard from './dashboards/WoredaDashboard';
import KebeleDashboard from './dashboards/KebeleDashboard';
import FarmerDashboard from './dashboards/FarmerDashboard';

function App() {
    const [currentUser, setCurrentUser] = useState(null);
    const [currentPage, setCurrentPage] = useState('login');
    const [requests, setRequests] = useState([]);
    const [products, setProducts] = useState([]);

    useEffect(() => {
        if (currentUser) fetchData();
    }, [currentUser]);

    const fetchData = async() => {
        try {
            const resRequests = await api.getRequests();
            const resProducts = await api.getProducts();
            setRequests(resRequests);
            setProducts(resProducts);
        } catch (err) {
            console.error('Fetch error:', err);
        }
    };

    const handleLogin = async({ phone, password }) => {
        try {
            const user = await api.login({ phone, password });
            setCurrentUser(user);
            setCurrentPage('dashboard');
        } catch (err) {
            alert('Login failed: ' + (err.response && err.response.data && err.response.data.message));
        }
    };

    const renderDashboard = () => {
        if (!currentUser) return null;

        switch (currentUser.role) {
            case 'Federal':
                return <FederalDashboard requests = { requests }
                currentUser = { currentUser }
                />;
            case 'Region':
                return <RegionDashboard requests = { requests }
                currentUser = { currentUser }
                />;
            case 'Zone':
                return <ZoneDashboard requests = { requests }
                currentUser = { currentUser }
                />;
            case 'Woreda':
                return <WoredaDashboard requests = { requests }
                currentUser = { currentUser }
                />;
            case 'Kebele':
                return <KebeleDashboard requests = { requests }
                currentUser = { currentUser }
                />;
            case 'Farmer':
                return <FarmerDashboard currentUser = { currentUser }
                products = { products }
                requests = { requests }
                />;
            default:
                return null;
        }
    };

    return ( <
        div className = "min-h-screen bg-gray-100" > {
            currentPage === 'login' && < LoginForm onLogin = { handleLogin }
            />} { currentPage === 'dashboard' && renderDashboard() } <
            /div>
        );
    }

    export default App;

    // Login form using phone and password
    function LoginForm({ onLogin }) {
        const [phone, setPhone] = useState('');
        const [password, setPassword] = useState('');

        const submit = (e) => {
            e.preventDefault();
            onLogin({ phone, password });
        };

        return ( <
            div className = "flex items-center justify-center h-screen" >
            <
            form onSubmit = { submit }
            className = "bg-white p-8 rounded shadow-md w-96 space-y-4" >
            <
            h2 className = "text-2xl font-bold text-center" > Login < /h2> <
            input type = "text"
            placeholder = "Phone Number"
            value = { phone }
            onChange = {
                (e) => setPhone(e.target.value) }
            className = "w-full border border-gray-300 p-2 rounded" /
            >
            <
            input type = "password"
            placeholder = "Password"
            value = { password }
            onChange = {
                (e) => setPassword(e.target.value) }
            className = "w-full border border-gray-300 p-2 rounded" /
            >
            <
            button type = "submit"
            className = "w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600" >
            Login <
            /button> <
            /form> <
            /div>
        );
    }
