import React, { useState } from 'react';
import Header from './components/Header';
import Card from './components/Card';
import RequestForm from './components/RequestForm';
import { departmentsData } from './data/data';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleSignIn = () => {
        // This is a placeholder for your actual sign-in logic
        setIsLoggedIn(true);
        alert('Signed in!');
    };

    const handleSignOut = () => {
        // This is a placeholder for your actual sign-out logic
        setIsLoggedIn(false);
        alert('Signed out!');
    };

    return ( <
        div className = "bg-gray-100 min-h-screen font-sans antialiased text-gray-800" >
        <
        Header isLoggedIn = { isLoggedIn }
        onSignIn = { handleSignIn }
        onSignOut = { handleSignOut }
        /> <
        main className = "container mx-auto p-4 md:p-8" > {
            departmentsData.map((department, index) => ( <
                div key = { index }
                className = "mb-12" >
                <
                h2 className = "text-3xl font-bold text-center text-gray-900 mb-8" > { department.name } < /h2> <
                div className = "grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6" > {
                    department.items.map((item, itemIndex) => ( <
                        Card key = { itemIndex }
                        title = { item.title }
                        content = { item.description }
                        />
                    ))
                } <
                /div> <
                /div>
            ))
        } <
        h2 className = "text-3xl font-bold text-center text-gray-900 mt-12 mb-8" > Request Form < /h2> <
        div className = "max-w-xl mx-auto" >
        <
        RequestForm / >
        <
        /div> <
        /main> <
        /div>
    );
}

export default App;
