import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './FederalDashboard.css';

const FederalDashboard = () => {
        const [activeTab, setActiveTab] = useState('overview');
        const [user, setUser] = useState(null);
        const navigate = useNavigate();

        // Product Management State
        const [products, setProducts] = useState([]);
        const [showProductForm, setShowProductForm] = useState(false);
        const [editingProduct, setEditingProduct] = useState(null);
        const [productForm, setProductForm] = useState({
            name: '',
            category: '',
            description: '',
            amount: ''
        });

        // Admin Management State
        const [admins, setAdmins] = useState([]);
        const [showAdminForm, setShowAdminForm] = useState(false);
        const [adminForm, setAdminForm] = useState({
            fullName: '',
            phoneNumber: '',
            password: '',
            confirmPassword: '',
            role: 'Region',
            region_name: ''
        });

        // Stats State
        const [stats, setStats] = useState({
            totalRequests: 0,
            pendingRequests: 0,
            approvedRequests: 0,
            rejectedRequests: 0,
            totalAdmins: 0,
            totalProducts: 0
        });

        useEffect(() => {
            // Check authentication
            const token = localStorage.getItem('token');
            const userData = JSON.parse(localStorage.getItem('user') || 'null');
            const userType = localStorage.getItem('userType');

            if (!token || userType !== 'Federal' || !userData) {
                navigate('/login');
                return;
            }

            setUser(userData);
            fetchDashboardData();
        }, [navigate]);

        const fetchDashboardData = async() => {
            try {
                const token = localStorage.getItem('token');

                // Fetch products
                const productsResponse = await fetch('http://localhost:5000/api/admins/products', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (productsResponse.ok) {
                    const productsData = await productsResponse.json();
                    setProducts(productsData);
                    setStats(prev => ({...prev, totalProducts: productsData.length }));
                }

                // Fetch admins
                const adminsResponse = await fetch('http://localhost:5000/api/admins/list', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (adminsResponse.ok) {
                    const adminsData = await adminsResponse.json();
                    setAdmins(adminsData);
                    setStats(prev => ({...prev, totalAdmins: adminsData.length }));
                }

                // Fetch requests stats (you'll need to implement this endpoint)
                const requestsResponse = await fetch('http://localhost:5000/api/requests/stats', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (requestsResponse.ok) {
                    const requestsData = await requestsResponse.json();
                    setStats(prev => ({...prev, ...requestsData }));
                }

            } catch (error) {
                console.error('Error fetching dashboard data:', error);
            }
        };

        const handleLogout = () => {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            localStorage.removeItem('userType');
            navigate('/login');
        };

        // Product Management Functions
        const handleProductSubmit = async(e) => {
            e.preventDefault();
            try {
                const token = localStorage.getItem('token');
                const url = editingProduct ?
                    `http://localhost:5000/api/admins/products/${editingProduct.id}` :
                    'http://localhost:5000/api/admins/products';

                const method = editingProduct ? 'PUT' : 'POST';

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(productForm)
                });

                if (response.ok) {
                    setShowProductForm(false);
                    setEditingProduct(null);
                    setProductForm({ name: '', category: '', description: '', amount: '' });
                    fetchDashboardData();
                }
            } catch (error) {
                console.error('Error saving product:', error);
            }
        };

        const deleteProduct = async(productId) => {
            if (!window.confirm('Are you sure you want to delete this product?')) {
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/products/${productId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                }
            } catch (error) {
                console.error('Error deleting product:', error);
            }
        };

        // Admin Management Functions
        const handleAdminSubmit = async(e) => {
            e.preventDefault();

            if (adminForm.password !== adminForm.confirmPassword) {
                alert('Passwords do not match');
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const response = await fetch('http://localhost:5000/api/admins/create', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(adminForm)
                });

                if (response.ok) {
                    setShowAdminForm(false);
                    setAdminForm({
                        fullName: '',
                        phoneNumber: '',
                        password: '',
                        confirmPassword: '',
                        role: 'Region',
                        region_name: ''
                    });
                    fetchDashboardData();
                }
            } catch (error) {
                console.error('Error creating admin:', error);
            }
        };

        const deleteAdmin = async(adminId) => {
            if (!window.confirm('Are you sure you want to delete this admin?')) {
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/${adminId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                }
            } catch (error) {
                console.error('Error deleting admin:', error);
            }
        };

        if (!user) {
            return <div className = "loading" > Loading... < /div>;
        }

        return ( <
                div className = "federal-dashboard" > { /* Sidebar */ } <
                div className = "sidebar" >
                <
                h2 > Federal Dashboard < /h2> <
                nav >
                <
                button className = { activeTab === 'overview' ? 'active' : '' }
                onClick = {
                    () => setActiveTab('overview') } >
                ðŸ“ŠOverview <
                /button> <
                button className = { activeTab === 'products' ? 'active' : '' }
                onClick = {
                    () => setActiveTab('products') } >
                ðŸ“¦Products <
                /button> <
                button className = { activeTab === 'admins' ? 'active' : '' }
                onClick = {
                    () => setActiveTab('admins') } >
                ðŸ‘¥Admins <
                /button> <
                button className = { activeTab === 'requests' ? 'active' : '' }
                onClick = {
                    () => setActiveTab('requests') } >
                ðŸ“‹Requests <
                /button> <
                /nav> <
                /div>

                { /* Main Content */ } <
                div className = "main-content" >
                <
                header >
                <
                h1 > Welcome, { user.fullName }(Federal Admin) < /h1> <
                button onClick = { handleLogout }
                className = "logout-btn" >
                Logout <
                /button> <
                /header>

                <
                main > { /* Overview Tab */ } {
                    activeTab === 'overview' && ( <
                        div className = "overview-tab" >
                        <
                        h2 > System Overview < /h2> <
                        div className = "stats-grid" >
                        <
                        div className = "stat-card" >
                        <
                        h3 > { stats.totalRequests } < /h3> <
                        p > Total Requests < /p> <
                        /div> <
                        div className = "stat-card" >
                        <
                        h3 > { stats.pendingRequests } < /h3> <
                        p > Pending Requests < /p> <
                        /div> <
                        div className = "stat-card" >
                        <
                        h3 > { stats.approvedRequests } < /h3> <
                        p > Approved Requests < /p> <
                        /div> <
                        div className = "stat-card" >
                        <
                        h3 > { stats.rejectedRequests } < /h3> <
                        p > Rejected Requests < /p> <
                        /div> <
                        div className = "stat-card" >
                        <
                        h3 > { stats.totalAdmins } < /h3> <
                        p > System Admins < /p> <
                        /div> <
                        div className = "stat-card" >
                        <
                        h3 > { stats.totalProducts } < /h3> <
                        p > Products < /p> <
                        /div> <
                        /div>

                        <
                        div className = "quick-actions" >
                        <
                        h2 > Quick Actions < /h2> <
                        div className = "actions-grid" >
                        <
                        button onClick = {
                            () => setActiveTab('admins') }
                        className = "action-btn" > ðŸ‘¥Create Admin <
                        /button> <
                        button onClick = {
                            () => setActiveTab('products') }
                        className = "action-btn" > ðŸ“¦Add Product <
                        /button> <
                        button className = "action-btn" > ðŸ“ŠGenerate Reports <
                        /button> <
                        button className = "action-btn" > âš™ï¸System Settings <
                        /button> <
                        /div> <
                        /div> <
                        /div>
                    )
                }

                { /* Products Tab */ } {
                    activeTab === 'products' && ( <
                        div className = "products-tab" >
                        <
                        div className = "tab-header" >
                        <
                        h2 > Product Management < /h2> <
                        button onClick = {
                            () => setShowProductForm(true) }
                        className = "add-btn" >
                        Add Product <
                        /button> <
                        /div>

                        { /* Product Form Modal */ } {
                            showProductForm && ( <
                                div className = "modal" >
                                <
                                div className = "modal-content" >
                                <
                                h3 > { editingProduct ? 'Edit Product' : 'Add Product' } < /h3> <
                                form onSubmit = { handleProductSubmit } >
                                <
                                input type = "text"
                                placeholder = "Product Name"
                                value = { productForm.name }
                                onChange = {
                                    (e) => setProductForm({...productForm, name: e.target.value }) }
                                required /
                                >
                                <
                                input type = "text"
                                placeholder = "Category"
                                value = { productForm.category }
                                onChange = {
                                    (e) => setProductForm({...productForm, category: e.target.value }) }
                                required /
                                >
                                <
                                textarea placeholder = "Description"
                                value = { productForm.description }
                                onChange = {
                                    (e) => setProductForm({...productForm, description: e.target.value }) }
                                rows = "3"
                                required /
                                >
                                <
                                input type = "number"
                                placeholder = "Amount"
                                value = { productForm.amount }
                                onChange = {
                                    (e) => setProductForm({...productForm, amount: e.target.value }) }
                                required /
                                >
                                <
                                div className = "modal-actions" >
                                <
                                button type = "submit" > Save < /button> <
                                button type = "button"
                                onClick = {
                                    () => {
                                        setShowProductForm(false);
                                        setEditingProduct(null);
                                        setProductForm({ name: '', category: '', description: '', amount: '' });
                                    }
                                } >
                                Cancel <
                                /button> <
                                /div> <
                                /form> <
                                /div> <
                                /div>
                            )
                        }

                        { /* Products List */ } <
                        div className = "products-grid" > {
                            products.map((product) => ( <
                                div key = { product.id }
                                className = "product-card" >
                                <
                                h3 > { product.name } < /h3> <
                                p className = "category" > { product.category } < /p> <
                                p className = "description" > { product.description } < /p> <
                                p className = "amount" > Amount: { product.amount } < /p> <
                                div className = "product-actions" >
                                <
                                button onClick = {
                                    () => {
                                        setEditingProduct(product);
                                        setProductForm({
                                            name: product.name,
                                            category: product.category,
                                            description: product.description,
                                            amount: product.amount
                                        });
                                        setShowProductForm(true);
                                    }
                                }
                                className = "edit-btn" >
                                Edit <
                                /button> <
                                button onClick = {
                                    () => deleteProduct(product.id) }
                                className = "delete-btn" >
                                Delete <
                                /button> <
                                /div> <
                                /div>
                            ))
                        } <
                        /div> <
                        /div>
                    )
                }

                { /* Admins Tab */ } {
                    activeTab === 'admins' && ( <
                        div className = "admins-tab" >
                        <
                        div className = "tab-header" >
                        <
                        h2 > Admin Management < /h2> <
                        button onClick = {
                            () => setShowAdminForm(true) }
                        className = "add-btn" >
                        Register Admin <
                        /button> <
                        /div>

                        { /* Admin Form Modal */ } {
                            showAdminForm && ( <
                                div className = "modal" >
                                <
                                div className = "modal-content" >
                                <
                                h3 > Register Admin < /h3> <
                                form onSubmit = { handleAdminSubmit } >
                                <
                                select value = { adminForm.role }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, role: e.target.value }) }
                                required >
                                <
                                option value = "Region" > Region Admin < /option> <
                                option value = "Zone" > Zone Admin < /option> <
                                option value = "Woreda" > Woreda Admin < /option> <
                                option value = "Kebele" > Kebele Admin < /option> <
                                /select> <
                                input type = "text"
                                placeholder = "Full Name"
                                value = { adminForm.fullName }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, fullName: e.target.value }) }
                                required /
                                >
                                <
                                input type = "tel"
                                placeholder = "Phone Number"
                                value = { adminForm.phoneNumber }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, phoneNumber: e.target.value }) }
                                required /
                                >
                                <
                                input type = "password"
                                placeholder = "Password"
                                value = { adminForm.password }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, password: e.target.value }) }
                                required /
                                >
                                <
                                input type = "password"
                                placeholder = "Confirm Password"
                                value = { adminForm.confirmPassword }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, confirmPassword: e.target.value }) }
                                required /
                                >
                                <
                                input type = "text"
                                placeholder = "Region Name"
                                value = { adminForm.region_name }
                                onChange = {
                                    (e) => setAdminForm({...adminForm, region_name: e.target.value }) }
                                required /
                                >
                                <
                                div className = "modal-actions" >
                                <
                                button type = "submit" > Register < /button> <
                                button type = "button"
                                onClick = {
                                    () => setShowAdminForm(false) } >
                                Cancel <
                                /button> <
                                /div> <
                                /form> <
                                /div> <
                                /div>
                            )
                        }

                        { /* Admins List */ } <
                        div className = "admins-list" > {
                            admins.map((admin) => ( <
                                    div key = { admin.id }
                                    className = "admin-card" >
                                    <
                                    h3 > { admin.full_name } < /h3> <
                                    p > Phone: { admin.phone_number } < /p> <
                                    p > Role: { admin.role } < /p> <
                                    p > Region: { admin.region_name } < /p> {
                                        admin.zone_name && < p > Zone: { admin.zone_name } < /p>} {
                                            admin.woreda_name && < p > Woreda: { admin.woreda_name } < /p>} {
                                                admin.kebele_name && < p > Kebele: { admin.kebele_name } < /p>} <
                                                    div className = "admin-actions" >
                                                    <
                                                    button className = "edit-btn" > Edit < /button> <
                                                    button
                                                onClick = {
                                                    () => deleteAdmin(admin.id) }
                                                className = "delete-btn" >
                                                    Delete <
                                                    /button> <
                                                    /div> <
                                                    /div>
                                            ))
                                    } <
                                    /div> <
                                    /div>
                                )
                            }

                            { /* Requests Tab */ } {
                                activeTab === 'requests' && ( <
                                    div className = "requests-tab" >
                                    <
                                    h2 > Request Management < /h2> <
                                    p > Request management functionality will be implemented here. < /p> <
                                    /div>
                                )
                            } <
                            /main> <
                            /div> <
                            /div>
                        );
                    };

                    export default FederalDashboard;
